pragma solidity 0.8.23;

import "../interfaces/IController.sol";
import "../interfaces/IOracle.sol";
import "../interfaces/IStatController.sol";
import "../interfaces/IStoryController.sol";
import "../interfaces/ITreasury.sol";
import "../interfaces/IDungeonFactory.sol";
import "../interfaces/IReinforcementController.sol";
import "../interfaces/IGameToken.sol";
import "../interfaces/IGOC.sol";
import "../interfaces/IItemController.sol";
import "../interfaces/IHeroController.sol";
import "../interfaces/IUserController.sol";
import "../interfaces/IGuildController.sol";
import "../interfaces/IRewardsPool.sol";
import "../interfaces/IPvpController.sol";
import "../interfaces/IItemBoxController.sol";

/// @notice Provide context-struct with all controller addresses and routines for lazy init
/// Usage:
///       Create an instance of the structure
///               cc = ControllerContextLib.init(controller);
///       access controller directly
///               cc.controller.xxx();
///       access other contracts indirectly
///               sc = ControllerContextLib.statController(cc);
library ControllerContextLib {
  //region ----------------- Data types
  enum CacheIndex {
    STAT_CONTROLLER_0,
    STORY_CONTROLLER_1,
    ORACLE_2,
    TREASURY_3,
    DUNGEON_FACTORY_4,
    GOC_5,
    REINFORCEMENT_CONTROLLER_6,
    ITEM_CONTROLLER_7,
    HERO_CONTROLLER_8,
    GAME_TOKEN_9,
    USER_CONTROLLER_10,
    GUILD_CONTROLLER_11,
    PVP_CONTROLLER_12,
    REWARDS_POOL_13,
    ITEM_BOX_CONTROLLER_14
  }

  uint constant private CACHE_SIZE = 15;

  struct ControllerContext {
    /// @notice Direct access to the controller
    IController controller;

    /// @notice All lazy-initialized addresses in order of {CacheIndex}
    address[CACHE_SIZE] cache;
  }
  //endregion ----------------- Data types

  //region ----------------- Initialization and _lazyInit
  function init(IController controller) internal pure returns (ControllerContext memory cc) {
    cc.controller = controller;
    return cc;
  }

  function _lazyInit(
    ControllerContext memory cc,
    CacheIndex index,
    function () external view returns(address) getter
  ) internal view returns (address) {
    address a = cc.cache[uint(index)];
    if (a != address(0)) return a;

    cc.cache[uint(index)] = getter();
    return cc.cache[uint(index)];
  }
  //endregion ----------------- Initialization and _lazyInit

  //region ----------------- Access with lazy initialization
  function statController(ControllerContext memory cc) internal view returns (IStatController) {
    return IStatController(_lazyInit(cc, CacheIndex.STAT_CONTROLLER_0, cc.controller.statController));
  }

  function storyController(ControllerContext memory cc) internal view returns (IStoryController) {
    return IStoryController(_lazyInit(cc, CacheIndex.STORY_CONTROLLER_1, cc.controller.storyController));
  }

  function oracle(ControllerContext memory cc) internal view returns (IOracle) {
    return IOracle(_lazyInit(cc, CacheIndex.ORACLE_2, cc.controller.oracle));
  }

  function treasury(ControllerContext memory cc) internal view returns (ITreasury) {
    return ITreasury(_lazyInit(cc, CacheIndex.TREASURY_3, cc.controller.treasury));
  }

  function dungeonFactory(ControllerContext memory cc) internal view returns (IDungeonFactory) {
    return IDungeonFactory(_lazyInit(cc, CacheIndex.DUNGEON_FACTORY_4, cc.controller.dungeonFactory));
  }

  function gameObjectController(ControllerContext memory cc) internal view returns (IGOC) {
    return IGOC(_lazyInit(cc, CacheIndex.GOC_5, cc.controller.gameObjectController));
  }

  function reinforcementController(ControllerContext memory cc) internal view returns (IReinforcementController) {
    return IReinforcementController(_lazyInit(cc, CacheIndex.REINFORCEMENT_CONTROLLER_6, cc.controller.reinforcementController));
  }

  function itemController(ControllerContext memory cc) internal view returns (IItemController) {
    return IItemController(_lazyInit(cc, CacheIndex.ITEM_CONTROLLER_7, cc.controller.itemController));
  }

  function heroController(ControllerContext memory cc) internal view returns (IHeroController) {
    return IHeroController(_lazyInit(cc, CacheIndex.HERO_CONTROLLER_8, cc.controller.heroController));
  }

  function gameToken(ControllerContext memory cc) internal view returns (IGameToken) {
    return IGameToken(_lazyInit(cc, CacheIndex.GAME_TOKEN_9, cc.controller.gameToken));
  }

  function userController(ControllerContext memory cc) internal view returns (IUserController) {
    return IUserController(_lazyInit(cc, CacheIndex.USER_CONTROLLER_10, cc.controller.userController));
  }

  function guildController(ControllerContext memory cc) internal view returns (IGuildController) {
    return IGuildController(_lazyInit(cc, CacheIndex.GUILD_CONTROLLER_11, cc.controller.guildController));
  }

  function pvpController(ControllerContext memory cc) internal view returns (IPvpController) {
    return IPvpController(_lazyInit(cc, CacheIndex.PVP_CONTROLLER_12, cc.controller.pvpController));
  }

  function rewardsPool(ControllerContext memory cc) internal view returns (IRewardsPool) {
    return IRewardsPool(_lazyInit(cc, CacheIndex.REWARDS_POOL_13, cc.controller.rewardsPool));
  }

  function itemBoxController(ControllerContext memory cc) internal view returns (IItemBoxController) {
    return IItemBoxController(_lazyInit(cc, CacheIndex.ITEM_BOX_CONTROLLER_14, cc.controller.itemBoxController));
  }
  //endregion ----------------- Access with lazy initialization
}","keccak256":"0x8fa8be52cfba698f7e5a39c5043f05bddd9629868982402238b4294a9bbadb68","license":"BUSL-1.1"},"contracts/lib/HeroControllerLib.sol":{"content":"// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.23;

import "../interfaces/IHeroController.sol";
import "../interfaces/IUserController.sol";
import "../lib/HeroLib.sol";
import "../lib/PackingLib.sol";
import "../lib/ScoreLib.sol";
import "../proxy/Controllable.sol";
import "../relay/ERC2771Context.sol";
import "./ControllerContextLib.sol";

library HeroControllerLib {
  using PackingLib for bytes32;
  using PackingLib for address;
  using EnumerableSet for EnumerableSet.UintSet;

  //region ------------------------ Restrictions

  function onlyDeployer(IController controller) internal view {
    if (! controller.isDeployer(msg.sender)) revert IAppErrors.ErrorNotDeployer(msg.sender);
  }

  function onlyEOA(bool isEoa) internal view {
    if (!isEoa) {
      revert IAppErrors.NotEOA(msg.sender);
    }
  }

  function onlyItemController(IController controller_) internal view {
    if (controller_.itemController() != msg.sender) revert IAppErrors.ErrorNotItemController(msg.sender);
  }
  //endregion ------------------------ Restrictions

  //region ------------------------ Views

  function _S() internal pure returns (IHeroController.MainState storage s) {
    return HeroLib._S();
  }

  function payTokenInfo(address hero) internal view returns (address token, uint amount) {
    return _S().payToken[hero].unpackAddressWithAmount();
  }

  function heroClass(address hero) internal view returns (uint8) {
    return _S().heroClass[hero];
  }

  function heroName(address hero, uint heroId) internal view returns (string memory) {
    return _S().heroName[hero.packNftId(heroId)];
  }

  function nameToHero(string memory name) internal view returns (address hero, uint heroId) {
    return _S().nameToHero[name].unpackNftId();
  }

  function heroBiome(address hero, uint heroId) internal view returns (uint8) {
    return _S().heroBiome[hero.packNftId(heroId)];
  }

  function heroReinforcementHelp(address hero, uint heroId) internal view returns (
    address helperHeroToken,
    uint helperHeroId
  ) {
    return _S().reinforcementHero[hero.packNftId(heroId)].unpackNftId();
  }

  function score(IController controller, address hero, uint heroId) internal view returns (uint) {
    IStatController _statController = IStatController(controller.statController());
    return ScoreLib.heroScore(
      _statController.heroAttributes(hero, heroId),
      _statController.heroStats(hero, heroId).level
    );
  }

  function isAllowedToTransfer(IController controller, address hero, uint heroId) internal view returns (bool) {
    return HeroLib.isAllowedToTransfer(controller, hero, heroId);
  }

  function countHeroTransfers(address hero, uint heroId) internal view returns (uint) {
    return _S().countHeroTransfers[PackingLib.packNftId(hero, heroId)];
  }

  function getTier(uint8 tier, address hero) internal view returns (uint payAmount, uint8[] memory slots, address[][] memory items) {
    if (tier == HeroLib.TIER_1) {
      (, payAmount) = payTokenInfo(hero);
    }
    if (tier == HeroLib.TIER_2 || tier == HeroLib.TIER_3) {
      IHeroController.TierInfo storage tierInfo = _S().tiers[PackingLib.packTierHero(tier, hero)];
      payAmount = tierInfo.amount;

      uint len = tierInfo.slots.length();

      slots = new uint8[](len);
      items = new address[][](len);

      for (uint i; i < len; ++i) {
        slots[i] = uint8(tierInfo.slots.at(i));
        items[i] = tierInfo.itemsToMint[slots[i]];
      }
    }

    return (payAmount, slots, items);
  }

  function sandboxMode(address hero, uint heroId) internal view returns (IHeroController.SandboxMode) {
    return _S().sandbox[PackingLib.packNftId(hero, heroId)];
  }
  //endregion ------------------------ Views

  //region ------------------------ Governance actions

  function registerHero(IController controller, address hero, uint8 heroClass_, address payToken, uint payAmount) internal {
    onlyDeployer(controller);
    HeroLib.registerHero(hero, heroClass_, payToken, payAmount);
  }

  /// @dev payAmount is limited by uint72, see remarks to IHeroController.HeroInfo
  function setTier(IController controller, uint8 tier, address hero, uint72 payAmount, uint8[] memory slots, address[][] memory items) internal {
    onlyDeployer(controller);

    if (tier != HeroLib.TIER_2 && tier != HeroLib.TIER_3) revert IAppErrors.WrongTier(tier);

    IHeroController.TierInfo storage tierInfo = _S().tiers[PackingLib.packTierHero(tier, hero)];

    // ------- clear prev stored tier data if any
    // tierInfo.amount is not cleared, set 0 if you need to reset it

    uint prevLen = tierInfo.slots.length();
    if (prevLen != 0) {
      for (uint i; i < prevLen; ++i) {
        uint8 slot = uint8(tierInfo.slots.at(0));
        tierInfo.slots.remove(slot);
        delete tierInfo.itemsToMint[slot];
      }
    }

    // ------- register new tier data
    uint len = slots.length;
    if (len != items.length) revert IAppErrors.LengthsMismatch();

    tierInfo.amount = payAmount;
    for (uint i; i < len; ++i) {
      tierInfo.slots.add(slots[i]);
      tierInfo.itemsToMint[slots[i]] = items[i];
    }

    emit IApplicationEvents.TierSetup(tier, hero, payAmount, slots, items);
  }
  //endregion ------------------------ Governance actions

  //region ------------------------ User actions - create hero
  function createHero(IController controller, address msgSender, address hero, IHeroController.HeroCreationData memory data) external returns (
    uint heroId
  ) {
    return _createHero(controller, msgSender, hero, data);
  }

  function create(
    IController controller,
    address msgSender,
    address hero,
    string calldata _heroName,
    bool enter
  ) external returns (uint) {
    // allow create for contracts for SponsoredHero flow  // onlyEOA(isEoa);
    return _create(controller, msgSender, hero, _heroName, "", enter);
  }

  function createWithRefCode(
    bool isEoa,
    IController controller,
    address msgSender,
    address hero,
    string calldata _heroName,
    string memory refCode,
    bool enter
  ) external returns (uint) {
    onlyEOA(isEoa);
    return _create(controller, msgSender, hero, _heroName, refCode, enter);
  }

  /// @notice Init new hero, set biome 1, generate hero id, call process() to take specific amount from the sender
  /// @param hero Should support IHero
  /// @param heroName_ length must be < 20 chars, all chars should be ASCII chars in the range [32, 127]
  /// @param enter Enter to default biome (==1)
  function _create(IController controller, address msgSender, address hero, string calldata heroName_, string memory refCode, bool enter)
  internal returns (uint heroId) {
    IHeroController.HeroCreationData memory data;
    data.heroName = heroName_;
    data.refCode = refCode;
    data.enter = enter;
    data.tier = HeroLib.TIER_DEFAULT;

    return _createHero(controller, msgSender, hero, data);
  }

  function _createHero(IController controller, address msgSender, address hero, IHeroController.HeroCreationData memory data) internal returns (
    uint heroId
  ) {
    ControllerContextLib.ControllerContext memory cc = ControllerContextLib.init(controller);
    HeroLib._checkRegisteredNotPaused(controller, hero);
    if (_S().nameToHero[data.heroName] != bytes32(0)) revert IAppErrors.NameTaken();
    if (bytes(data.heroName).length >= 20) revert IAppErrors.TooBigName();
    if (!StringLib.isASCIILettersOnly(data.heroName)) revert IAppErrors.WrongSymbolsInTheName();

    if (data.targetUserAccount == address(0)) {
      data.targetUserAccount = msgSender;
    }

    // ----------- get pay token and pay amount
    (address payToken, uint amount) = _S().payToken[hero].unpackAddressWithAmount();
    if (payToken == address(0)) revert IAppErrors.ZeroToken();

    bool prepayment = payToken != address(ControllerContextLib.gameToken(cc));
    if (prepayment || data.sandboxMode) {
      if (data.tier == HeroLib.TIER_DEFAULT) data.tier = HeroLib.TIER_1; // use tier 1 by default
    }
    if (!data.sandboxMode) {
      if (prepayment) {
        if (data.tier > HeroLib.TIER_1) {
          amount = _S().tiers[PackingLib.packTierHero(data.tier, hero)].amount;
        }
      } else {
        if (data.tier != HeroLib.TIER_DEFAULT) revert IAppErrors.NgpNotActive(hero);
      }
    }

    if (amount == 0) revert IAppErrors.ZeroAmount();
    if (_S().maxUserNgLevel[data.targetUserAccount] < data.ngLevel) revert IAppErrors.NotEnoughNgLevel(data.ngLevel);

    // ----------- Check sandbox limitations
    if (data.sandboxMode) {
      if (data.tier != HeroLib.TIER_1) revert IAppErrors.SandboxTierForbidden();
      if (! prepayment) revert IAppErrors.SandboxPrepaidOnly();
      if (data.ngLevel != 0) revert IAppErrors.SandboxNgZeroOnly();
    }

    // ----------- mint new hero on selected NG+ level
    heroId = IHero(hero).mintFor(data.targetUserAccount);
    bytes32 packedId = hero.packNftId(heroId);

    _S().heroName[packedId] = data.heroName;
    _S().nameToHero[data.heroName] = packedId;

    // ----------- calculate discount if any
    if (data.ngLevel != 0 && prepayment) {
      // discount for hero = 100% of lost profit
      uint lostProfitPercent = ControllerContextLib.rewardsPool(cc).lostProfitPercent(
        ControllerContextLib.dungeonFactory(cc).maxAvailableBiome(),
        _S().maxOpenedNgLevel,
        data.ngLevel
      );
      uint discount = amount * lostProfitPercent / 1e18;
      if (discount > amount) revert IAppErrors.TooHighValue(discount);
      amount -= discount;
    }

    // ----------- register and initialize the hero
    _S().heroInfo[packedId] = IHeroController.HeroInfo({
      tier: data.tier,
      ngLevel: data.ngLevel,
      rebornAllowed: false,
      paidAmount: data.sandboxMode || !prepayment
        ? 0
        : amount > type(uint72).max ? type(uint72).max : uint72(amount), // edge case, uint72 is enough for any reasonable amount of stable coins with decimals 18
      paidToken: data.sandboxMode || !prepayment
        ? address(0)
        : payToken
    });
    if (data.sandboxMode) {
      _S().sandbox[packedId] = IHeroController.SandboxMode.SANDBOX_MODE_1;
    }

    ControllerContextLib.statController(cc).initNewHero(hero, heroId, _S().heroClass[hero]);

    // ----------- pay for the hero creation
    emit IApplicationEvents.HeroCreatedNgpSandbox(hero, heroId, data.heroName, data.targetUserAccount, data.refCode, data.tier, data.ngLevel, data.sandboxMode);
    if (!data.sandboxMode) {
      controller.process(payToken, amount, msgSender);
    }

    // ----------- enter to the first biome/dungeon
    // set first biome by default
    _S().heroBiome[packedId] = 1;
    emit IApplicationEvents.BiomeChanged(hero, heroId, 1);

    // ----------- mint and equip items
    if (data.tier > HeroLib.TIER_1) {
      // attributes before items equipment
      int32[] memory attributes = ControllerContextLib.statController(cc).heroAttributes(hero, heroId);

      _mintAndEquipItems(
        ControllerContextLib.itemController(cc),
        _S().tiers[PackingLib.packTierHero(data.tier, hero)],
        hero,
        heroId,
        data.targetUserAccount
      );

      // restore hp/mp after equipments: increase life and mana on {current attr.value - attr.value before equip}
      ControllerContextLib.statController(cc).restoreLifeAndMana(hero, heroId, attributes);
    }

    // enter to the first dungeon
    if (data.enter) {
      ControllerContextLib.dungeonFactory(cc).launchForNewHero(hero, heroId, data.targetUserAccount);
    }

    return heroId;
  }

  /// @notice Mint and equip all items specified in the tire
  function _mintAndEquipItems(
    IItemController itemController,
    IHeroController.TierInfo storage tierInfo,
    address hero,
    uint heroId,
    address msgSender
  ) internal {
    EnumerableSet.UintSet storage slots = tierInfo.slots;
    mapping(uint8 slot => address[] items) storage itemsToMint = tierInfo.itemsToMint;

    uint len = slots.length();
    if (len != 0) {
      address[] memory items = new address[](len);
      uint[] memory itemIds = new uint[](len);
      uint8[] memory itemSlots = new uint8[](len);

      for (uint i; i < len; ++i) {
        itemSlots[i] = uint8(slots.at(i));
        address[] storage listItems = itemsToMint[itemSlots[i]];
        items[i] = listItems[CalcLib.pseudoRandom(listItems.length - 1)];
        itemIds[i] = itemController.mint(items[i], msgSender);
      }

      itemController.equip(hero, heroId, items, itemIds, itemSlots);
    }
  }
  //endregion ------------------------ User actions - create hero

  //region ------------------------ User actions - biome, level up, reborn
  function setBiome(bool isEoa, IController controller, address msgSender, address hero, uint heroId, uint8 biome) internal {
    onlyEOA(isEoa);
    HeroLib.setBiome(controller, msgSender, hero, heroId, biome);
  }

  function levelUp(
    bool isEoa,
    IController controller,
    address msgSender,
    address hero,
    uint heroId,
    IStatController.CoreAttributes memory change
  ) internal {
    onlyEOA(isEoa);
    HeroLib.levelUp(controller, msgSender, hero, heroId, change);
  }

  function beforeTokenTransfer(IController controller, address msgSender, address hero, uint heroId) internal returns (
    bool isAllowedToTransferOut
  ) {
    if (msgSender != hero) revert IAppErrors.ErrorForbidden(msgSender);
    HeroLib.onlyRegisteredHero(hero);

    // --------------- don't allow to transfer sandbox hero but allow to burn it if the hero was killed
    bytes32 packedHero = PackingLib.packNftId(hero, heroId);
    if (IStatController(controller.statController()).isHeroAlive(hero, heroId)) {
      HeroLib.checkSandboxMode(packedHero, false);
    }

    isAllowedToTransferOut = HeroLib.isAllowedToTransfer(controller, hero, heroId);
    _S().countHeroTransfers[packedHero] += 1;
  }

  function reborn(IController controller, address msgSender, address hero, uint heroId) external {
    bytes32 packedHero = PackingLib.packNftId(hero, heroId);

    HeroLib._checkRegisteredNotPaused(controller, hero);
    HeroLib.onlyOwner(hero, heroId, msgSender);
    HeroLib.checkSandboxMode(packedHero, false);
    (IDungeonFactory dungFactory, IStatController statController) = HeroLib._checkOutDungeonNotStakedAlive(controller, hero, heroId);
    // restriction "no equipped items" is checked on statController side

    // -------------- update HeroInfo
    IHeroController.HeroInfo memory heroInfo = _S().heroInfo[packedHero];
    if (!heroInfo.rebornAllowed) revert IAppErrors.RebornNotAllowed();

    heroInfo = _upgradeHero(controller, heroInfo, hero, msgSender);

    if (heroInfo.ngLevel == HeroLib.MAX_NG_LEVEL) revert IAppErrors.TooHighValue(heroInfo.ngLevel);
    uint8 newNgLevel = heroInfo.ngLevel + 1;
    _S().heroInfo[packedHero] = IHeroController.HeroInfo({
      tier: heroInfo.tier,
      ngLevel: newNgLevel,
      rebornAllowed: false,
      paidToken: heroInfo.paidToken,
      paidAmount: heroInfo.paidAmount
    });

    // statController.reborn expects that it's called AFTER incrementing NG_LVL
    statController.reborn(hero, heroId, _S().heroClass[hero]);

    // -------------- update max-ng-level, register the hero in Hall Of Fame
    uint8 _maxUserNgLevel = _S().maxUserNgLevel[msgSender];
    if (newNgLevel > _maxUserNgLevel) {
      _S().maxUserNgLevel[msgSender] = newNgLevel;

      // assume that if for a user it is new max ng then possible it is new max ng globally
      // the hero who has opened NG_LEVEL first is registered in Hall of Fame
      if (newNgLevel > _S().maxOpenedNgLevel) {
        IUserController(controller.userController()).registerFameHallHero(hero, heroId, newNgLevel);
        _S().maxOpenedNgLevel = newNgLevel;
      }
    }

    dungFactory.reborn(hero, heroId);

    emit IApplicationEvents.Reborn(hero, heroId, newNgLevel);
  }

  /// @notice Upgrade sandbox hero to the ordinal pre-paid hero.
  /// The hero is upgraded to tier=1 always
  /// Approve to controller for {payTokenInfo.amount} in {payTokenInfo.token} is required
  function upgradeSandboxHero(IController controller, address msgSender, address hero, uint heroId) external {
    bytes32 packedHero = PackingLib.packNftId(hero, heroId);

    // -------------- check requirements
    HeroLib._checkRegisteredNotPaused(controller, hero);
    HeroLib.onlyOwner(hero, heroId, msgSender);
    HeroLib.onlyAlive(IStatController(controller.statController()), hero, heroId);
    HeroLib.checkSandboxMode(packedHero, true);
    // equipped items are not forbidden, also the hero can be inside the dungeon

    // -------------- upgrade the hero
    IHeroController.HeroInfo memory heroInfo = _S().heroInfo[packedHero];
    _S().heroInfo[packedHero] = _upgradeHero(controller, heroInfo, hero, msgSender);
    _S().sandbox[packedHero] = IHeroController.SandboxMode.UPGRADED_TO_NORMAL_2;

    IItemBoxController(controller.itemBoxController()).registerSandboxUpgrade(packedHero);

    emit IApplicationEvents.SandboxUpgraded(hero, heroId);
  }

  /// @notice Update Post-paid hero to Pre-paid hero if it's necessary and allowed
  function _upgradeHero(
    IController controller,
    IHeroController.HeroInfo memory heroInfo,
    address hero,
    address msgSender
  ) internal returns (IHeroController.HeroInfo memory) {
    if (heroInfo.paidToken == address(0)) {
      // the hero is post-paid or sandbox, need to upgrade
      address gameToken = controller.gameToken();

      (address payToken, uint payAmountForTier1) = payTokenInfo(hero);
      if (payToken != gameToken) {
        // hero token is not game token, post-paid hero can be upgraded to pre-paid with tier 1
        if (payAmountForTier1 == 0) revert IAppErrors.ZeroAmount();

        heroInfo.paidAmount = payAmountForTier1 > type(uint72).max ? type(uint72).max : uint72(payAmountForTier1);
        heroInfo.paidToken = payToken;
        heroInfo.tier = HeroLib.TIER_1;

        controller.process(payToken, heroInfo.paidAmount, msgSender);
      }
    }

    return heroInfo;
  }

  //endregion ------------------------ User actions

  //region ------------------------ Dungeon actions
  function registerKilledBoss(IController controller, address msgSender, address hero, uint heroId, uint32 bossObjectId) external {
    HeroLib.onlyDungeonFactory(controller.dungeonFactory(), msgSender);

    uint biome = _S().heroBiome[PackingLib.packNftId(hero, heroId)];

    (uint8 bossBiome, ) = IGOC(controller.gameObjectController()).getObjectMeta(bossObjectId);
    if (bossBiome == biome) {
      IHeroController.HeroInfo memory heroInfo = _S().heroInfo[PackingLib.packNftId(hero, heroId)];

      bytes32 packedBiomeNgLevel = PackingLib.packUint8Array3(uint8(biome), heroInfo.ngLevel, 0);

      mapping (bytes32 packedBiomeNgLevel => uint timestamp) storage killedBosses = _S().killedBosses[PackingLib.packNftId(hero, heroId)];
      uint8 maxAvailableBiome = IDungeonFactory(controller.dungeonFactory()).maxAvailableBiome();
      if (killedBosses[packedBiomeNgLevel] == 0) {
        // the boss is killed first time - pay reward to pre-paid hero
        killedBosses[packedBiomeNgLevel] = block.timestamp;

        uint rewardAmount;
        if (heroInfo.paidToken != controller.gameToken()) {
          // The hero is pre-paid, he is allowed to receive rewards from reward pool
          IRewardsPool rewardsPool = IRewardsPool(controller.rewardsPool());
          rewardAmount = rewardsPool.rewardAmount(
            heroInfo.paidToken,
            maxAvailableBiome,
            _S().maxOpenedNgLevel,
            biome,
            heroInfo.ngLevel
          );
          if (rewardAmount != 0) {
            rewardsPool.sendReward(heroInfo.paidToken, rewardAmount, IERC721(hero).ownerOf(heroId));
          }
        }

        emit IApplicationEvents.BossKilled(msgSender, hero, heroId, bossBiome, heroInfo.ngLevel, maxAvailableBiome == bossBiome, rewardAmount);
      }

      if(maxAvailableBiome == bossBiome) {
        _S().heroInfo[PackingLib.packNftId(hero, heroId)].rebornAllowed = true;
      }
    }

  }
  //endregion ------------------------ Dungeon actions

  //region ------------------------ Classic and guild reinforcement
  function askReinforcement(bool isEoa, IController controller, address msgSender, address hero, uint heroId, address helper, uint helperId) internal {
    onlyEOA(isEoa);
    HeroLib.askReinforcement(controller, msgSender, hero, heroId, helper, helperId);
  }

  function askGuildReinforcement(IController controller, address hero, uint heroId, address helper, uint helperId) internal {
    onlyItemController(controller);
    HeroLib.askGuildReinforcement(controller, hero, heroId, helper, helperId);
  }

  //endregion ------------------------ Classic and guild reinforcement
}

","keccak256":"0xadab0afbbbf9eef8d280235fcaa11dd7226d96d5b3cb83a541847185427e34cb","license":"BUSL-1.1"},"contracts/lib/HeroLib.sol":{"content":"// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.23;

import "../interfaces/IAppErrors.sol";
import "../interfaces/IApplicationEvents.sol";
import "../interfaces/IDungeonFactory.sol";
import "../interfaces/IERC721.sol";
import "../interfaces/IHero.sol";
import "../interfaces/IHeroController.sol";
import "../interfaces/IReinforcementController.sol";
import "../interfaces/IRewardsPool.sol";
import "../interfaces/IStatController.sol";
import "../lib/StringLib.sol";
import "./PackingLib.sol";

library HeroLib {
  using PackingLib for int32[];
  using PackingLib for bytes32[];
  using PackingLib for address;
  using PackingLib for bytes32;

  /// @dev keccak256(abi.encode(uint256(keccak256("hero.controller.main")) - 1)) & ~bytes32(uint256(0xff))
  bytes32 private constant HERO_CONTROLLER_STORAGE_LOCATION = 0xd333325b749986e76669f0e0c2c1aa0e0abd19e216c3678477196e4089241400;
  uint public constant KILL_PENALTY = 70;

  uint8 public constant TIER_DEFAULT = 0;
  uint8 public constant TIER_1 = 1;
  uint8 public constant TIER_2 = 2;
  uint8 public constant TIER_3 = 3;

  uint8 internal constant MAX_NG_LEVEL = 99;

  /// @notice Cost of level up in game token, final amount is adjusted by game token price
  /// @dev The case: payToken for the hero is changed, postpaid hero makes level up, base amount should be paid.
  uint public constant BASE_AMOUNT_LEVEL_UP = 10e18;

  //region ------------------------ Storage

  function _S() internal pure returns (IHeroController.MainState storage s) {
    assembly {
      s.slot := HERO_CONTROLLER_STORAGE_LOCATION
    }
    return s;
  }
  //endregion ------------------------ Storage

  //region ------------------------ Restrictions

  function onlyDungeonFactory(address dungeonFactory, address sender) internal pure {
    if (dungeonFactory != sender) revert IAppErrors.ErrorNotDungeonFactory(sender);
  }

  function onlyDungeonFactoryOrPvpController(IController controller, address sender) internal view {
    if (
      controller.dungeonFactory() != sender
      && controller.pvpController() != sender
    ) revert IAppErrors.ErrorNotAllowedSender();
  }

  function onlyOwner(address token, uint tokenId, address sender) internal view {
    if (IERC721(token).ownerOf(tokenId) != sender) revert IAppErrors.ErrorNotOwner(token, tokenId);
  }

  function onlyNotStaked(IController controller_, address hero, uint heroId) internal view {
    if (IReinforcementController(controller_.reinforcementController()).isStaked(hero, heroId)) revert IAppErrors.Staked(hero, heroId);

    address pc = controller_.pvpController();
    if (pc != address(0) && IPvpController(pc).isHeroStakedCurrently(hero, heroId)) revert IAppErrors.PvpStaked();
  }

  function checkSandboxMode(bytes32 packedHero, bool sandboxRequired) internal view {
    bool isSandboxMode = _S().sandbox[packedHero] == IHeroController.SandboxMode.SANDBOX_MODE_1;

    if (isSandboxMode != sandboxRequired) {
      if (sandboxRequired) {
        revert IAppErrors.SandboxModeRequired();
      } else {
        revert IAppErrors.SandboxModeNotAllowed();
      }
    }
  }

  function onlyInDungeon(IDungeonFactory dungeonFactory, address hero, uint heroId) internal view {
    if (dungeonFactory.currentDungeon(hero, heroId) == 0) revert IAppErrors.ErrorHeroNotInDungeon();
  }

  function onlyNotInDungeon(IDungeonFactory dungeonFactory, address hero, uint heroId) internal view {
    if (dungeonFactory.currentDungeon(hero, heroId) != 0) revert IAppErrors.HeroInDungeon();
  }

  function isAllowedToTransfer(IController controller_, address hero, uint heroId) internal view returns (bool) {
    onlyNotInDungeon(IDungeonFactory(controller_.dungeonFactory()), hero, heroId);
    if (
      IStatController(controller_.statController()).heroItemSlots(hero, heroId).length != 0
    ) revert IAppErrors.EquippedItemsExist();
    onlyNotStaked(controller_, hero, heroId);
    return true;
  }

  function onlyRegisteredHero(address hero) internal view {
    if (_S().heroClass[hero] == 0) revert IAppErrors.ErrorHeroIsNotRegistered(hero);
  }

  function _checkRegisteredNotPaused(IController c, address hero) internal view {
    onlyRegisteredHero(hero);
    if (c.onPause()) revert IAppErrors.ErrorPaused();
  }

  function onlyAlive(IStatController statController, address hero, uint heroId) internal view {
    if (!statController.isHeroAlive(hero, heroId)) revert IAppErrors.ErrorHeroIsDead(hero, heroId);
  }

  function _checkOutDungeonNotStakedAlive(IController c, address hero, uint heroId) internal view returns (
    IDungeonFactory dungFactory,
    IStatController statController
  ) {
    dungFactory = IDungeonFactory(c.dungeonFactory());
    statController = IStatController(c.statController());

    onlyNotInDungeon(dungFactory, hero, heroId);
    onlyNotStaked(c, hero, heroId);
    onlyAlive(statController, hero, heroId);
  }
  //endregion ------------------------ Restrictions

  //region ------------------------ Views
  function getHeroInfo(address hero, uint heroId) internal view returns (IHeroController.HeroInfo memory data) {
    return _S().heroInfo[PackingLib.packNftId(hero, heroId)];
  }

  function maxOpenedNgLevel() internal view returns (uint) {
    return _S().maxOpenedNgLevel;
  }

  /// @return time stamp of the moment when the boss of the given biome at the given NG_LEVEL was killed by the hero
  function killedBosses(address hero, uint heroId, uint8 biome, uint8 ngLevel) internal view returns (uint) {
    return _S().killedBosses[PackingLib.packNftId(hero, heroId)][PackingLib.packUint8Array3(uint8(biome), ngLevel, 0)];
  }

  function maxUserNgLevel(address user) internal view returns (uint) {
    return _S().maxUserNgLevel[user];
  }

  //endregion ------------------------ Views

  //region ------------------------ Gov actions

  function registerHero(address hero, uint8 heroClass, address payToken, uint payAmount) internal {
    _S().heroClass[hero] = heroClass;
    _S().payToken[hero] = payToken.packAddressWithAmount(payAmount);

    emit IApplicationEvents.HeroRegistered(hero, heroClass, payToken, payAmount);
  }
  //endregion ------------------------ Gov actions

  //region ------------------------ User actions: setBiome, levelUp
  /// @notice Set hero biome to {biome}, ensure that it's allowed
  /// @param msgSender Sender must be the owner of the hero
  /// @param biome New biome value: (0, maxBiomeCompleted + 1]
  function setBiome(IController controller, address msgSender, address hero, uint heroId, uint8 biome) internal {
    onlyOwner(hero, heroId, msgSender);
    _checkRegisteredNotPaused(controller, hero);
    (IDungeonFactory dungFactory, ) = _checkOutDungeonNotStakedAlive(controller, hero, heroId);

    if (biome == 0 || biome > dungFactory.maxAvailableBiome()) revert IAppErrors.ErrorIncorrectBiome(biome);

    uint8 maxBiomeCompleted = dungFactory.maxBiomeCompleted(hero, heroId);
    if (biome > maxBiomeCompleted + 1) revert IAppErrors.TooHighBiome(biome);

    _S().heroBiome[hero.packNftId(heroId)] = biome;
    emit IApplicationEvents.BiomeChanged(hero, heroId, biome);
  }

  /// @notice Set level up according to {change}, call process() to take (payTokenAmount * level) from the sender
  function levelUp(
    IController controller,
    address msgSender,
    address hero,
    uint heroId,
    IStatController.CoreAttributes memory change
  ) internal {
    onlyOwner(hero, heroId, msgSender);
    _checkRegisteredNotPaused(controller, hero);
    _checkOutDungeonNotStakedAlive(controller, hero, heroId);

    IHeroController.HeroInfo memory heroInfo = getHeroInfo(hero, heroId);

    // update stats
    IStatController _statController = IStatController(controller.statController());
    uint level = _statController.levelUp(hero, heroId, _S().heroClass[hero], change);

    // NG+ has free level up
    // all heroes created before NG+ and not upgraded to NG+ require payment as before
    if (heroInfo.tier == HeroLib.TIER_DEFAULT) {
      address gameToken = controller.gameToken();
      (address token, uint payTokenAmount) = _S().payToken[hero].unpackAddressWithAmount();
      if (token == address(0)) revert IAppErrors.NoPayToken(token, payTokenAmount);

      if (token != gameToken) {
        token = gameToken;
        payTokenAmount = BASE_AMOUNT_LEVEL_UP;
      }

      uint amount = payTokenAmount * level;
      controller.process(token, amount, msgSender);
    }

    emit IApplicationEvents.LevelUp(hero, heroId, msgSender, change);
  }

  //endregion ------------------------ User actions: setBiome, levelUp

  //region ------------------------ User actions: reinforcement

  /// @notice Ask random other-hero for reinforcement
  function askReinforcement(IController controller, address msgSender, address hero, uint heroId, address helper, uint helperId) internal {
    onlyOwner(hero, heroId, msgSender);
    _askReinforcement(controller, hero, heroId, false, helper, helperId);
  }

  /// @notice Ask random staked guild-hero for reinforcement
  function askGuildReinforcement(IController controller, address hero, uint heroId, address helper, uint helperId) internal {
    _askReinforcement(controller, hero, heroId, true, helper, helperId);
  }

  function _askReinforcement(IController controller, address hero, uint heroId, bool guildReinforcement, address helper, uint helperId) internal {
    _checkRegisteredNotPaused(controller, hero);

    onlyInDungeon(IDungeonFactory(controller.dungeonFactory()), hero, heroId);

    bytes32 packedHero = hero.packNftId(heroId);
    if (_S().reinforcementHero[packedHero] != bytes32(0)) revert IAppErrors.AlreadyHaveReinforcement();

    IStatController _statController = IStatController(controller.statController());
    IReinforcementController rc = IReinforcementController(controller.reinforcementController());

    // scb-1009: Life and mana are restored during reinforcement as following:
    // Reinforcement increases max value of life/mana on DELTA, current value of life/mana is increased on DELTA too
    int32[] memory helpAttributes;
    helpAttributes = guildReinforcement
      ? rc.askGuildHero(hero, heroId, helper, helperId)
      : rc.askHeroV2(hero, heroId, helper, helperId);

    int32[] memory attributes = _statController.heroAttributes(hero, heroId);

    _statController.changeBonusAttributes(IStatController.ChangeAttributesInfo({
      heroToken: hero,
      heroTokenId: heroId,
      changeAttributes: helpAttributes,
      add: true,
      temporally: false
    }));

    _S().reinforcementHero[packedHero] = helper.packNftId(helperId);
    _S().reinforcementHeroAttributes[packedHero] = helpAttributes.toBytes32Array();

    // restore life and mana to default values from the total attributes
    _statController.restoreLifeAndMana(hero, heroId, attributes);

    if (guildReinforcement) {
      emit IApplicationEvents.GuildReinforcementAsked(hero, heroId, helper, helperId);
    } else {
      emit IApplicationEvents.ReinforcementAsked(hero, heroId, helper, helperId);
    }
  }

  /// @notice Release any reinforcement (v1, v2 or guild)
  function releaseReinforcement(IController controller, address msgSender, address hero, uint heroId) internal returns (
    address helperToken,
    uint helperId
  ) {
    onlyDungeonFactory(controller.dungeonFactory(), msgSender);
    onlyRegisteredHero(hero);

    bytes32 packedId = hero.packNftId(heroId);

    (helperToken, helperId) = _S().reinforcementHero[packedId].unpackNftId();

    if (helperToken != address(0)) {
      IStatController _statController = IStatController(controller.statController());

      int32[] memory attributes = _S().reinforcementHeroAttributes[packedId].toInt32Array(uint(IStatController.ATTRIBUTES.END_SLOT));

      _statController.changeBonusAttributes(IStatController.ChangeAttributesInfo({
        heroToken: hero,
        heroTokenId: heroId,
        changeAttributes: attributes,
        add: false,
        temporally: false
      }));

      delete _S().reinforcementHero[packedId];
      delete _S().reinforcementHeroAttributes[packedId];

      IReinforcementController rc = IReinforcementController(controller.reinforcementController());
      uint guildId = rc.busyGuildHelperOf(helperToken, helperId);
      if (guildId == 0) {
        emit IApplicationEvents.ReinforcementReleased(hero, heroId, helperToken, helperId);
      } else {
        rc.releaseGuildHero(helperToken, helperId);
        emit IApplicationEvents.GuildReinforcementReleased(hero, heroId, helperToken, helperId);
      }
    }
  }
  //endregion ------------------------ User actions: reinforcement

  //region ------------------------ Dungeon actions
  /// @return dropItems List of items (packed: item NFT address + item id)
  function kill(IController controller, address msgSender, address hero, uint heroId) internal returns (
    bytes32[] memory dropItems
  ) {
    // restrictions are checked inside softKill
    dropItems = softKill(controller, msgSender, hero, heroId, true, false);

    IHero(hero).burn(heroId);

    emit IApplicationEvents.Killed(hero, heroId, msgSender, dropItems, 0);
  }

  /// @notice Take off all items from the hero, reduce life to 1
  /// Optionally reduce mana to zero and/or decrease life chance
  function softKill(IController controller, address msgSender, address hero, uint heroId, bool decLifeChances, bool resetMana) internal returns (
    bytes32[] memory dropItems
  ) {
    onlyDungeonFactoryOrPvpController(controller, msgSender);
    onlyRegisteredHero(hero);

    IStatController statController = IStatController(controller.statController());
    dropItems = _takeOffAll(IItemController(controller.itemController()), statController, hero, heroId, msgSender, true);

    _resetLife(statController, hero, heroId, decLifeChances, resetMana);
  }

  /// @notice Life => 1, mana => 0
  function resetLifeAndMana(IController controller, address msgSender, address hero, uint heroId) internal {
    onlyDungeonFactory(controller.dungeonFactory(), msgSender);
    _resetLife(IStatController(controller.statController()), hero, heroId, false, true);
  }
  //endregion ------------------------ Dungeon actions

  //region ------------------------ Kill internal

  function _resetLife(IStatController statController, address hero, uint heroId, bool decLifeChances, bool resetMana) internal {
    IStatController.ChangeableStats memory heroStats = statController.heroStats(hero, heroId);

    // set life to zero, reduce life-chances on 1
    statController.changeCurrentStats(
      hero,
      heroId,
      IStatController.ChangeableStats({
        level: 0,
        experience: 0,
        life: heroStats.life,
        mana: resetMana ? heroStats.mana : 0,
        lifeChances: decLifeChances ? 1 : 0
      }),
      false
    );
  }

  function _takeOffAll(
    IItemController ic,
    IStatController statController,
    address hero,
    uint heroId,
    address recipient,
    bool broken
  ) internal returns (bytes32[] memory items) {
    uint8[] memory busySlots = statController.heroItemSlots(hero, heroId);
    uint len = busySlots.length;
    items = new bytes32[](len);
    for (uint i; i < len; ++i) {
      bytes32 data = statController.heroItemSlot(hero, uint64(heroId), busySlots[i]);
      (address itemAdr, uint itemId) = data.unpackNftId();

      ic.takeOffDirectly(itemAdr, itemId, hero, heroId, busySlots[i], recipient, broken);
      items[i] = data;
    }
  }
  //endregion ------------------------ Kill internal

}
","keccak256":"0x338be6f79ddd64b153318a66be3817ec0df19af1c36784101a7c4f7b8d8d4b0e","license":"BUSL-1.1"},"contracts/lib/PackingLib.sol":{"content":"// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.23;

import "../interfaces/IItemController.sol";
import "../interfaces/IStatController.sol";
import "../interfaces/IAppErrors.sol";

library PackingLib {

  //////////////////////////
  // ---- PACKING LOGIC ----
  //////////////////////////

  //region ------------------------------------ COMMON

  function packNftId(address token, uint id) internal pure returns (bytes32 serialized) {
    if (id > uint(type(uint64).max)) revert IAppErrors.TooHighValue(id);
    serialized = bytes32(uint(uint160(token)));
    serialized |= bytes32(uint(uint64(id))) << 160;
  }

  function unpackNftId(bytes32 data) internal pure returns (address token, uint id) {
    token = address(uint160(uint(data)));
    id = uint(data) >> 160;
  }

  function packAddressWithAmount(address token, uint amount) internal pure returns (bytes32 data) {
    if (amount > uint(type(uint96).max)) revert IAppErrors.TooHighValue(amount);
    data = bytes32(uint(uint160(token)));
    data |= bytes32(uint(uint96(amount))) << 160;
  }

  function unpackAddressWithAmount(bytes32 data) internal pure returns (address token, uint amount) {
    token = address(uint160(uint(data)));
    amount = uint(data) >> 160;
  }

  function packItemMintInfo(address item, uint32 chance) internal pure returns (bytes32 data) {
    data = bytes32(uint(uint160(item)));
    data |= bytes32(uint(chance)) << 160;
  }

  function unpackItemMintInfo(bytes32 data) internal pure returns (address item, uint32 chance) {
    item = address(uint160(uint(data)));
    chance = uint32(uint(data) >> 160);
  }

  /// @param customDataIndex We assume, that two lowest bytes of this string are always zero
  /// So, the string looks like following: 0xXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXX0000
  /// Last 2 bytes will be used to encode {value}
  function packCustomDataChange(bytes32 customDataIndex, int16 value) internal pure returns (bytes32 data) {
    if (uint(customDataIndex) != (uint(customDataIndex) >> 16) << 16) revert IAppErrors.IncompatibleInputString();
    data = bytes32(uint(customDataIndex));
    data |= bytes32(uint(uint16(value)));
  }

  function unpackCustomDataChange(bytes32 data) internal pure returns (bytes32 customDataIndex, int16 value) {
    customDataIndex = bytes32((uint(data) >> 16) << 16);
    value = int16(int(uint(uint16(uint(data)))));
  }

  /// @dev min(uint64) + max(uint64) + isHeroData/isMandatory(uint8)
  function packCustomDataRequirements(uint64 min, uint64 max, bool key) internal pure returns (bytes32 data) {
    data = bytes32(uint(min));
    data |= bytes32(uint(max)) << 64;
    data |= bytes32(uint(key ? uint8(1) : uint8(0))) << (64 + 64);
  }

  function unpackCustomDataRequirements(bytes32 data) internal pure returns (uint64 min, uint64 max, bool key) {
    min = uint64(uint(data));
    max = uint64(uint(data) >> 64);
    key = uint8(uint(data) >> (64 + 64)) == uint8(1);
  }

  function packStatsChange(
    uint32 experience,
    int32 heal,
    int32 manaRegen,
    int32 lifeChancesRecovered,
    int32 damage,
    int32 manaConsumed
  ) internal pure returns (bytes32 data) {
    data = bytes32(uint(experience));
    data |= bytes32(uint(uint32(heal))) << 32;
    data |= bytes32(uint(uint32(manaRegen))) << (32 + 32);
    data |= bytes32(uint(uint32(lifeChancesRecovered))) << (32 + 32 + 32);
    data |= bytes32(uint(uint32(damage))) << (32 + 32 + 32 + 32);
    data |= bytes32(uint(uint32(manaConsumed))) << (32 + 32 + 32 + 32 + 32);
  }

  function unpackStatsChange(bytes32 data) internal pure returns (
    uint32 experience,
    int32 heal,
    int32 manaRegen,
    int32 lifeChancesRecovered,
    int32 damage,
    int32 manaConsumed
  ) {
    experience = uint32(uint(data));
    heal = int32(int(uint(data) >> 32));
    manaRegen = int32(int(uint(data) >> (32 + 32)));
    lifeChancesRecovered = int32(int(uint(data) >> (32 + 32 + 32)));
    damage = int32(int(uint(data) >> (32 + 32 + 32 + 32)));
    manaConsumed = int32(int(uint(data) >> (32 + 32 + 32 + 32 + 32)));
  }

  function packNftIdWithValue(address token, uint id, uint32 value) internal pure returns (bytes32 serialized) {
    if (id > uint(type(uint64).max)) revert IAppErrors.TooHighValue(id);
    serialized = bytes32(uint(uint160(token)));
    serialized |= bytes32(uint(uint64(id))) << 160;
    serialized |= bytes32(uint(value)) << 160 + 64;
  }

  function unpackNftIdWithValue(bytes32 data) internal pure returns (address token, uint id, uint32 value) {
    token = address(uint160(uint(data)));
    id = uint64(uint(data) >> 160);
    value = uint32(uint(data) >> 160 + 64);
  }
  //endregion ------------------------------------ COMMON

  //region ------------------------------------ WORLD/BATTLEFIELD MAP

  function packMapObject(address objectAddress, uint64 objectId, uint8 objectType) internal pure returns (bytes32 packedData) {
    packedData = bytes32(bytes20(objectAddress));
    packedData |= bytes32(uint(objectId) << 32);
    packedData |= bytes32(uint(objectType) << 24);
  }

  function unpackMapObject(bytes32 packedData) internal pure returns (address objectAddress, uint64 objectId, uint8 objectType) {
    objectAddress = address(bytes20(packedData));
    objectId = uint64(uint(packedData) >> 32);
    objectType = uint8(uint(packedData) >> 24);
  }

  function packCoordinate(uint128 x, uint128 y) internal pure returns (bytes32 packedData) {
    packedData = bytes32(uint(x));
    packedData |= bytes32(uint(y) << 128);
  }

  function unpackCoordinate(bytes32 packedData) internal pure returns (uint128 x, uint128 y) {
    x = uint128(uint(packedData));
    y = uint128(uint(packedData) >> 128);
  }

  /// @param x Assume x <= max uint64
  /// @param y Assume y <= max uint64
  function packBattlefieldId(uint8 biomeMapFieldId, uint8 territoryNumber, uint128 x, uint128 y) internal pure returns (bytes32 packedData) {
    // 256 => 128 + 128;
    // 1) 128 is used for biomeMapFieldId, territoryNumber and probably other fields in the future
    // 2) 128 is used to store x, y as uint64, uint64

    // we will use uint64 for coordinates assuming it is more than enough for biome map
    packedData = bytes32(uint(biomeMapFieldId));
    packedData |= bytes32(uint(territoryNumber) << (8));
    packedData |= bytes32(uint(uint64(x)) << 128);
    packedData |= bytes32(uint(uint64(y)) << (64 + 128));
  }

  function unpackBattlefieldId(bytes32 packedData) internal pure returns (uint8 biomeMapFieldId, uint8 territoryNumber, uint128 x, uint128 y) {
    biomeMapFieldId = uint8(uint(packedData));
    territoryNumber = uint8(uint(packedData) >> (8));
    x = uint128(uint64(uint(packedData) >> (128)));
    y = uint128(uint64(uint(packedData) >> (64 + 128)));
  }
  //endregion ------------------------------------ WORLD/BATTLEFIELD MAP

  //region ------------------------------------ REINFORCEMENT

  function packReinforcementHeroInfo(uint8 biome, uint128 score, uint8 fee, uint64 stakeTs) internal pure returns (bytes32 packedData) {
    packedData = bytes32(uint(biome));
    packedData |= bytes32(uint(score) << 8);
    packedData |= bytes32(uint(fee) << (8 + 128));
    packedData |= bytes32(uint(stakeTs) << (8 + 128 + 8));
  }

  function unpackReinforcementHeroInfo(bytes32 packedData) internal pure returns (uint8 biome, uint128 score, uint8 fee, uint64 stakeTs) {
    biome = uint8(uint(packedData));
    score = uint128(uint(packedData) >> 8);
    fee = uint8(uint(packedData) >> (8 + 128));
    stakeTs = uint64(uint(packedData) >> (8 + 128 + 8));
  }

  function packConfigReinforcementV2(uint32 min, uint32 max, uint32 lowDivider, uint32 highDivider, uint8 levelLimit) internal pure returns (bytes32 packedData) {
    packedData = bytes32(uint(min));
    packedData |= bytes32(uint(max) << 32);
    packedData |= bytes32(uint(lowDivider) << 64);
    packedData |= bytes32(uint(highDivider) << 96);
    packedData |= bytes32(uint(levelLimit) << 128);
  }

  function unpackConfigReinforcementV2(bytes32 packedData) internal pure returns (uint32 min, uint32 max, uint32 lowDivider, uint32 highDivider, uint8 levelLimit) {
    min = uint32(uint(packedData));
    max = uint32(uint(packedData) >> 32);
    lowDivider = uint32(uint(packedData) >> 64);
    highDivider = uint32(uint(packedData) >> 96);
    levelLimit = uint8(uint(packedData) >> 128);
  }
  //endregion ------------------------------------ REINFORCEMENT

  //region ------------------------------------ DUNGEON

  function packDungeonKey(address heroAdr, uint80 heroId, uint16 dungLogicNum) internal pure returns (bytes32 data) {
    data = bytes32(uint(uint160(heroAdr)));
    data |= bytes32(uint(heroId)) << 160;
    data |= bytes32(uint(dungLogicNum)) << (160 + 80);
  }

  function unpackDungeonKey(bytes32 data) internal pure returns (address heroAdr, uint80 heroId, uint16 dungLogicNum) {
    heroAdr = address(uint160(uint(data)));
    heroId = uint80(uint(data) >> 160);
    dungLogicNum = uint16(uint(data) >> (160 + 80));
  }

  // --- GAME OBJECTS ---

  function packIterationKey(address heroAdr, uint64 heroId, uint32 objId) internal pure returns (bytes32 data) {
    data = bytes32(uint(uint160(heroAdr)));
    data |= bytes32(uint(heroId)) << 160;
    data |= bytes32(uint(objId)) << (160 + 64);
  }

  function unpackIterationKey(bytes32 data) internal pure returns (address heroAdr, uint64 heroId, uint32 objId) {
    heroAdr = address(uint160(uint(data)));
    heroId = uint64(uint(data) >> 160);
    objId = uint32(uint(data) >> (160 + 64));
  }

  function packMonsterStats(
    uint8 level,
    uint8 race,
    uint32 experience,
    uint8 maxDropItems
  ) internal pure returns (bytes32 data) {
    data = bytes32(uint(level));
    data |= bytes32(uint(race)) << 8;
    data |= bytes32(uint(experience)) << (8 + 8);
    data |= bytes32(uint(maxDropItems)) << (8 + 8 + 32);
  }

  function unpackMonsterStats(bytes32 data) internal pure returns (
    uint8 level,
    uint8 race,
    uint32 experience,
    uint8 maxDropItems
  ) {
    level = uint8(uint(data));
    race = uint8(uint(data) >> 8);
    experience = uint32(uint(data) >> (8 + 8));
    maxDropItems = uint8(uint(data) >> (8 + 8 + 32));
  }

  function packAttackInfo(
    address attackToken,
    uint64 attackTokenId,
    uint8 attackType
  ) internal pure returns (bytes32 data) {
    data = bytes32(uint(uint160(attackToken)));
    data |= bytes32(uint(attackTokenId)) << 160;
    data |= bytes32(uint(attackType)) << (160 + 64);
  }

  function unpackAttackInfo(bytes32 data) internal pure returns (
    address attackToken,
    uint64 attackTokenId,
    uint8 attackType
  ) {
    attackToken = address(uint160(uint(data)));
    attackTokenId = uint64(uint(data) >> 160);
    attackType = uint8(uint(data) >> (160 + 64));
  }

  function packPlayedObjKey(address heroAdr, uint64 heroId, uint8 oType, uint8 biome) internal pure returns (bytes32 data) {
    data = bytes32(uint(uint160(heroAdr)));
    data |= bytes32(uint(heroId)) << 160;
    data |= bytes32(uint(oType)) << (160 + 64);
    data |= bytes32(uint(biome)) << (160 + 64 + 8);
  }

  function unpackPlayedObjKey(bytes32 data) internal pure returns (address heroAdr, uint64 heroId, uint8 oType, uint8 biome) {
    heroAdr = address(uint160(uint(data)));
    heroId = uint64(uint(data) >> 160);
    oType = uint8(uint(data) >> (160 + 64));
    biome = uint8(uint(data) >> (160 + 64 + 8));
  }

  function packGeneratedMonster(bool generated, uint32 amplifier, int32 hp, uint8 turnCounter) internal pure returns (bytes32 data) {
    data = bytes32(uint(uint8(generated ? 1 : 0)));
    data |= bytes32(uint(amplifier)) << 8;
    data |= bytes32(uint(uint32(hp))) << (8 + 32);
    data |= bytes32(uint(turnCounter)) << (8 + 32 + 32);
  }

  function unpackGeneratedMonster(bytes32 data) internal pure returns (bool generated, uint32 amplifier, int32 hp, uint8 turnCounter) {
    generated = uint8(uint(data)) == uint8(1);
    amplifier = uint32(uint(data) >> 8);
    hp = int32(int(uint(data) >> (8 + 32)));
    turnCounter = uint8(uint(data) >> (8 + 32 + 32));
  }
  //endregion ------------------------------------ DUNGEON

  //region ------------------------------------ ITEMS

  /// @notice itemMetaType8 + itemLvl8 + itemType8 + baseDurability16 + defaultRarity8 + minAttr8 + maxAttr8 + manaCost32 + req(packed core 128)
  /// @param itemType This is ItemType enum
  function packItemMeta(
    uint8 itemMetaType,
    uint8 itemLvl,
    uint8 itemType,
    uint16 baseDurability,
    uint8 defaultRarity,
    uint8 minAttr,
    uint8 maxAttr,
    uint32 manaCost,
    IStatController.CoreAttributes memory req
  ) internal pure returns (bytes32 data) {
    data = bytes32(uint(itemMetaType));
    data |= bytes32(uint(itemLvl)) << 8;
    data |= bytes32(uint(itemType)) << (8 + 8);
    data |= bytes32(uint(baseDurability)) << (8 + 8 + 8);
    data |= bytes32(uint(defaultRarity)) << (8 + 8 + 8 + 16);
    data |= bytes32(uint(minAttr)) << (8 + 8 + 8 + 16 + 8);
    data |= bytes32(uint(maxAttr)) << (8 + 8 + 8 + 16 + 8 + 8);
    data |= bytes32(uint(manaCost)) << (8 + 8 + 8 + 16 + 8 + 8 + 8);
    data |= bytes32(uint(int(req.strength))) << (8 + 8 + 8 + 16 + 8 + 8 + 8 + 32);
    data |= bytes32(uint(int(req.dexterity))) << (8 + 8 + 8 + 16 + 8 + 8 + 8 + 32 + 32);
    data |= bytes32(uint(int(req.vitality))) << (8 + 8 + 8 + 16 + 8 + 8 + 8 + 32 + 32 + 32);
    data |= bytes32(uint(int(req.energy))) << (8 + 8 + 8 + 16 + 8 + 8 + 8 + 32 + 32 + 32 + 32);
  }

  function unpackItemMeta(bytes32 data) internal pure returns (IItemController.ItemMeta memory) {
    IItemController.ItemMeta memory result;

    result.itemMetaType = uint8(uint(data));
    result.itemLevel = uint8(uint(data) >> 8);
    result.itemType = IItemController.ItemType(uint8(uint(data) >> (8 + 8)));
    result.baseDurability = uint16(uint(data) >> (8 + 8 + 8));
    result.defaultRarity = uint8(uint(data) >> (8 + 8 + 8 + 16));
    result.minRandomAttributes = uint8(uint(data) >> (8 + 8 + 8 + 16 + 8));
    result.maxRandomAttributes = uint8(uint(data) >> (8 + 8 + 8 + 16 + 8 + 8));
    result.manaCost = uint32(uint(data) >> (8 + 8 + 8 + 16 + 8 + 8 + 8));
    result.requirements.strength = int32(int(uint(data) >> (8 + 8 + 8 + 16 + 8 + 8 + 8 + 32)));
    result.requirements.dexterity = int32(int(uint(data) >> (8 + 8 + 8 + 16 + 8 + 8 + 8 + 32 + 32)));
    result.requirements.vitality = int32(int(uint(data) >> (8 + 8 + 8 + 16 + 8 + 8 + 8 + 32 + 32 + 32)));
    result.requirements.energy = int32(int(uint(data) >> (8 + 8 + 8 + 16 + 8 + 8 + 8 + 32 + 32 + 32 + 32)));

    return result;
  }

  function packItemGenerateInfo(uint8 id, int32 min, int32 max, uint32 chance) internal pure returns (bytes32 data) {
    data = bytes32(uint(id));
    data |= bytes32(uint(uint32(min))) << 8;
    data |= bytes32(uint(uint32(max))) << (8 + 32);
    data |= bytes32(uint(chance)) << (8 + 32 + 32);
  }

  function unpackItemGenerateInfo(bytes32 data) internal pure returns (uint8 id, int32 min, int32 max, uint32 chance) {
    id = uint8(uint(data));
    min = int32(int(uint(data) >> 8));
    max = int32(int(uint(data) >> (8 + 32)));
    chance = uint32(uint(data) >> (8 + 32 + 32));
  }

  function packItemAttackInfo(
    uint8 attackType,
    int32 min,
    int32 max,
    int32 factorStr,
    int32 factorDex,
    int32 factorVit,
    int32 factorEng
  ) internal pure returns (bytes32 data) {
    data = bytes32(uint(attackType));
    data |= bytes32(uint(uint32(min))) << 8;
    data |= bytes32(uint(uint32(max))) << (8 + 32);
    data |= bytes32(uint(int(factorStr))) << (8 + 32 + 32);
    data |= bytes32(uint(int(factorDex))) << (8 + 32 + 32 + 32);
    data |= bytes32(uint(int(factorVit))) << (8 + 32 + 32 + 32 + 32);
    data |= bytes32(uint(int(factorEng))) << (8 + 32 + 32 + 32 + 32 + 32);
  }

  function unpackItemAttackInfo(bytes32 data) internal pure returns (
    uint8 attackType,
    int32 min,
    int32 max,
    int32 factorStr,
    int32 factorDex,
    int32 factorVit,
    int32 factorEng
  ) {
    attackType = uint8(uint(data));
    min = int32(int(uint(data) >> 8));
    max = int32(int(uint(data) >> (8 + 32)));
    factorStr = int32(int(uint(data) >> (8 + 32 + 32)));
    factorDex = int32(int(uint(data) >> (8 + 32 + 32 + 32)));
    factorVit = int32(int(uint(data) >> (8 + 32 + 32 + 32 + 32)));
    factorEng = int32(int(uint(data) >> (8 + 32 + 32 + 32 + 32 + 32)));
  }

  function packItemInfo(uint8 rarity, uint8 augmentationLevel, uint16 durability) internal pure returns (bytes32 data) {
    data = bytes32(uint(rarity));
    data |= bytes32(uint(augmentationLevel)) << 8;
    data |= bytes32(uint(durability)) << (8 + 8);
  }

  function unpackItemInfo(bytes32 data) internal pure returns (uint8 rarity, uint8 augmentationLevel, uint16 durability) {
    rarity = uint8(uint(data));
    augmentationLevel = uint8(uint(data) >> 8);
    durability = uint16(uint(data) >> (8 + 8));
  }

  function packItemBoxItemInfo(bool withdrawn, uint64 timestamp) internal pure returns (bytes32 data) {
    data = bytes32(uint(uint8(withdrawn ? 1 : 0)));
    data |= bytes32(uint(timestamp)) << 8;
  }

  function unpackItemBoxItemInfo(bytes32 data) internal pure returns (bool withdrawn, uint64 timestamp) {
    withdrawn = uint8(uint(data)) != 0;
    timestamp = uint64(uint(data) >> 8);
  }
  //endregion ------------------------------------ ITEMS

  //region ------------------------------------ STORIES

  function packStoryPageId(uint16 storyId, uint16 pageId, uint8 heroClass) internal pure returns (bytes32 data) {
    data = bytes32(uint(storyId));
    data |= bytes32(uint(pageId)) << 16;
    data |= bytes32(uint(heroClass)) << (16 + 16);
  }

  function unpackStoryPageId(bytes32 data) internal pure returns (uint16 storyId, uint16 pageId, uint8 heroClass) {
    storyId = uint16(uint(data));
    pageId = uint16(uint(data) >> 16);
    heroClass = uint8(uint(data) >> (16 + 16));
  }

  function packStoryAnswerId(uint16 storyId, uint16 pageId, uint8 heroClass, uint16 answerId) internal pure returns (bytes32 data) {
    data = bytes32(uint(storyId));
    data |= bytes32(uint(pageId)) << 16;
    data |= bytes32(uint(heroClass)) << (16 + 16);
    data |= bytes32(uint(answerId)) << (16 + 16 + 8);
  }

  function unpackStoryAnswerId(bytes32 data) internal pure returns (uint16 storyId, uint16 pageId, uint8 heroClass, uint16 answerId) {
    storyId = uint16(uint(data));
    pageId = uint16(uint(data) >> 16);
    heroClass = uint8(uint(data) >> (16 + 16));
    answerId = uint16(uint(data) >> (16 + 16 + 8));
  }

  function packStoryNextPagesId(uint16 storyId, uint16 pageId, uint8 heroClass, uint16 answerId, uint8 resultId) internal pure returns (bytes32 data) {
    data = bytes32(uint(storyId));
    data |= bytes32(uint(pageId)) << 16;
    data |= bytes32(uint(heroClass)) << (16 + 16);
    data |= bytes32(uint(answerId)) << (16 + 16 + 8);
    data |= bytes32(uint(resultId)) << (16 + 16 + 8 + 16);
  }

  function unpackStoryNextPagesId(bytes32 data) internal pure returns (uint16 storyId, uint16 pageId, uint8 heroClass, uint16 answerId, uint8 resultId) {
    storyId = uint16(uint(data));
    pageId = uint16(uint(data) >> 16);
    heroClass = uint8(uint(data) >> (16 + 16));
    answerId = uint16(uint(data) >> (16 + 16 + 8));
    resultId = uint8(uint(data) >> (16 + 16 + 8 + 16));
  }

  function packStoryAttributeRequirement(uint8 attributeIndex, int32 value, bool isCore) internal pure returns (bytes32 data) {
    data = bytes32(uint(attributeIndex));
    data |= bytes32(uint(uint32(value))) << 8;
    data |= bytes32(uint(isCore ? uint8(1) : uint8(0))) << (8 + 32);
  }

  function unpackStoryAttributeRequirement(bytes32 data) internal pure returns (uint8 attributeIndex, int32 value, bool isCore) {
    attributeIndex = uint8(uint(data));
    value = int32(int(uint(data) >> 8));
    isCore = uint8(uint(data) >> (8 + 32)) == uint8(1);
  }

  function packStoryItemRequirement(address item, bool requireItemBurn, bool requireItemEquipped) internal pure returns (bytes32 data) {
    data = bytes32(uint(uint160(item)));
    data |= bytes32(uint(requireItemBurn ? uint8(1) : uint8(0))) << 160;
    data |= bytes32(uint(requireItemEquipped ? uint8(1) : uint8(0))) << (160 + 8);
  }

  function unpackStoryItemRequirement(bytes32 data) internal pure returns (address item, bool requireItemBurn, bool requireItemEquipped) {
    item = address(uint160(uint(data)));
    requireItemBurn = uint8(uint(data) >> 160) == uint8(1);
    requireItemEquipped = uint8(uint(data) >> (160 + 8)) == uint8(1);
  }

  /// @dev max amount is 309,485,009 for token with 18 decimals
  function packStoryTokenRequirement(address token, uint88 amount, bool requireTransfer) internal pure returns (bytes32 data) {
    data = bytes32(uint(uint160(token)));
    data |= bytes32(uint(amount)) << 160;
    data |= bytes32(uint(requireTransfer ? uint8(1) : uint8(0))) << (160 + 88);
  }

  function unpackStoryTokenRequirement(bytes32 data) internal pure returns (address token, uint88 amount, bool requireTransfer) {
    token = address(uint160(uint(data)));
    amount = uint88(uint(data) >> 160);
    requireTransfer = uint8(uint(data) >> (160 + 88)) == uint8(1);
  }

  function packStoryCustomDataResult(uint16 storyId, uint16 pageId, uint8 heroClass, uint16 answerId, uint8 customDataResultId) internal pure returns (bytes32 data) {
    data = bytes32(uint(storyId));
    data |= bytes32(uint(pageId)) << 16;
    data |= bytes32(uint(heroClass)) << (16 + 16);
    data |= bytes32(uint(answerId)) << (16 + 16 + 8);
    data |= bytes32(uint(customDataResultId)) << (16 + 16 + 8 + 16);
  }

  function unpackStoryCustomDataResult(bytes32 data) internal pure returns (uint16 storyId, uint16 pageId, uint8 heroClass, uint16 answerId, uint8 customDataResultId) {
    storyId = uint16(uint(data));
    pageId = uint16(uint(data) >> 16);
    heroClass = uint8(uint(data) >> (16 + 16));
    answerId = uint16(uint(data) >> (16 + 16 + 8));
    customDataResultId = uint8(uint(data) >> (16 + 16 + 8 + 16));
  }

  function packStoryHeroState(uint16 pageId, uint40 heroLastActionTS) internal pure returns (bytes32 data) {
    data = bytes32(uint(pageId));
    data |= bytes32(uint(heroLastActionTS)) << 16;
  }

  function unpackStoryHeroState(bytes32 data) internal pure returns (uint16 pageId, uint40 heroLastActionTS) {
    pageId = uint16(uint(data));
    heroLastActionTS = uint40(uint(data) >> 16);
  }

  function packStoryHeroStateId(address heroAdr, uint80 heroId, uint16 storyId) internal pure returns (bytes32 data) {
    data = bytes32(uint(uint160(heroAdr)));
    data |= bytes32(uint(heroId)) << 160;
    data |= bytes32(uint(storyId)) << (160 + 80);
  }

  function unpackStoryHeroStateId(bytes32 data) internal pure returns (address heroAdr, uint80 heroId, uint16 storyId) {
    heroAdr = address(uint160(uint(data)));
    heroId = uint80(uint(data) >> 160);
    storyId = uint16(uint(data) >> (160 + 80));
  }

  function packStorySimpleRequirement(uint32 randomRequirement, uint32 delayRequirement, bool isFinalAnswer) internal pure returns (bytes32 data) {
    data = bytes32(uint(randomRequirement));
    data |= bytes32(uint(delayRequirement)) << 32;
    data |= bytes32(uint(isFinalAnswer ? uint8(1) : uint8(0))) << (32 + 32);
  }

  function unpackStorySimpleRequirement(bytes32 data) internal pure returns (uint32 randomRequirement, uint32 delayRequirement, bool isFinalAnswer) {
    randomRequirement = uint32(uint(data));
    delayRequirement = uint32(uint(data) >> 32);
    isFinalAnswer = uint8(uint(data) >> (32 + 32)) == uint8(1);
  }

  function packBreakInfo(uint8 slot, uint64 chance, bool stopIfBroken) internal pure returns (bytes32 data) {
    data = bytes32(uint(slot));
    data |= bytes32(uint(chance)) << 8;
    data |= bytes32(uint(stopIfBroken ? uint8(1) : uint8(0))) << (8 + 64);
  }

  function unpackBreakInfo(bytes32 data) internal pure returns (uint8 slot, uint64 chance, bool stopIfBurned) {
    slot = uint8(uint(data));
    chance = uint64(uint(data) >> 8);
    stopIfBurned = uint8(uint(data) >> (8 + 64)) == uint8(1);
  }
  //endregion ------------------------------------ STORIES

  //region ------------------------------------ Hero controller
  function packTierHero(uint8 tier, address hero) internal pure returns (bytes32 packedTierHero) {
    packedTierHero = bytes32(uint(tier));
    packedTierHero |= bytes32(uint(uint160(hero)) << 8);
  }

  function unpackTierHero(bytes32 packedTierHero) internal pure returns (uint8 tier, address hero) {
    tier = uint8(uint(packedTierHero));
    hero = address(uint160(uint(packedTierHero) >> 8));
  }

  //endregion ------------------------------------ Hero controller

  ////////////////////////////////////////////////////////////////////////////////////
  // ---- ARRAYS LOGIC ----
  ////////////////////////////////////////////////////////////////////////////////////

  //region ------------------------------------ SIMPLE ARRAYS


  function packUint8Array(uint8[] memory data) internal pure returns (bytes32) {
    uint len = data.length;
    if (len > 32) revert IAppErrors.OutOfBounds(len, 32);
    bytes32 result;
    for (uint i = 0; i < len; i++) {
      result |= bytes32(uint(data[i])) << (i * 8);
    }
    return result;
  }

  /// @notice Simple faster version of {packUint8Array} for small number of items
  ///         It allows to exclude dynamic array creation.
  function packUint8Array3(uint8 a, uint8 b, uint8 c) internal pure returns (bytes32) {
    bytes32 result = bytes32(uint(a));
    result |= bytes32(uint(b)) << (1 * 8);
    result |= bytes32(uint(c)) << (2 * 8);
    return result;
  }


  function unpackUint8Array(bytes32 data) internal pure returns (uint8[] memory) {
    uint8[] memory result = new uint8[](32);
    for (uint i = 0; i < 32; i++) {
      result[i] = uint8(uint(data) >> (i * 8));
    }
    return result;
  }

  /// @notice Simple faster version of {unpackUint8Array} for small number of items
  ///         It allows to exclude only first 3 values
  function unpackUint8Array3(bytes32 data) internal pure returns (uint8 a, uint8 b, uint8 c) {
    a = uint8(uint(data));
    b = uint8(uint(data) >> (1 * 8));
    c = uint8(uint(data) >> (2 * 8));
  }

  function changeUnit8ArrayWithCheck(bytes32 data, uint index, uint8 value, uint8 expectedPrevValue) internal pure returns (bytes32 newData) {
    uint8[] memory arr = unpackUint8Array(data);
    if (arr[index] != expectedPrevValue) revert IAppErrors.UnexpectedValue(uint(expectedPrevValue), uint(arr[index]));
    arr[index] = value;
    return packUint8Array(arr);
  }

  function packInt32Array(int32[] memory data) internal pure returns (bytes32) {
    uint len = data.length;
    if (len > 8) revert IAppErrors.OutOfBounds(len, 8);
    bytes32 result;
    for (uint i; i < len; i++) {
      result |= bytes32(uint(uint32(data[i]))) << (i * 32);
    }
    return result;
  }

  function unpackInt32Array(bytes32 data) internal pure returns (int32[] memory) {
    int32[] memory result = new int32[](8);
    for (uint i = 0; i < 8; i++) {
      result[i] = int32(int(uint(data) >> (i * 32)));
    }
    return result;
  }

  function packUint32Array(uint32[] memory data) internal pure returns (bytes32) {
    uint len = data.length;
    if (len > 8) revert IAppErrors.OutOfBounds(len, 8);
    bytes32 result;
    for (uint i = 0; i < len; i++) {
      result |= bytes32(uint(data[i])) << (i * 32);
    }
    return result;
  }

  function unpackUint32Array(bytes32 data) internal pure returns (uint32[] memory) {
    uint32[] memory result = new uint32[](8);
    for (uint i = 0; i < 8; i++) {
      result[i] = uint32(uint(data) >> (i * 32));
    }
    return result;
  }
  //endregion ------------------------------------ SIMPLE ARRAYS

  //region ------------------------------------ COMPLEX ARRAYS

  // We should represent arrays without concrete size.
  // For this reason we must not revert IAppErrors.on out of bounds but return zero value instead.

  // we need it for properly unpack packed arrays with ids
//  function getInt32AsInt24(bytes32[] memory arr, uint idx) internal pure returns (int32) {
//    if (idx / 8 >= arr.length) {
//      return int32(0);
//    }
//    return int32(int24(int(uint(arr[idx / 8]) >> ((idx % 8) * 32))));
//  }

  // we need it for properly unpack packed arrays with ids
//  function getUnit8From32Step(bytes32[] memory arr, uint idx) internal pure returns (uint8) {
//    if (idx / 8 >= arr.length) {
//      return uint8(0);
//    }
//    return uint8(uint(arr[idx / 8]) >> ((idx % 8) * 32 + 24));
//  }

  function getInt32Memory(bytes32[] memory arr, uint idx) internal pure returns (int32) {
    if (idx / 8 >= arr.length) {
      return int32(0);
    }
    return int32(int(uint(arr[idx / 8]) >> ((idx % 8) * 32)));
  }

  function getInt32(bytes32[] storage arr, uint idx) internal view returns (int32) {
    // additional gas usage, but we should not revert IAppErrors.on out of bounds
    if (idx / 8 >= arr.length) {
      return int32(0);
    }
    return int32(int(uint(arr[idx / 8]) >> ((idx % 8) * 32)));
  }

  function setInt32(bytes32[] storage arr, uint idx, int32 value) internal {
    uint pos = idx / 8;
    uint shift = (idx % 8) * 32;

    uint curLength = arr.length;
    if (pos >= curLength) {
      arr.push(0);
      for (uint i = curLength; i < pos; ++i) {
        arr.push(0);
      }
    }

    arr[pos] = bytes32(uint(arr[pos]) & ~(uint(0xffffffff) << shift) | (uint(uint32(value)) & 0xffffffff) << shift);
  }

  /// @notice Increment {idx}-th item on {value}
  function changeInt32(bytes32[] storage arr, uint idx, int32 value) internal returns (int32 newValue, int32 change) {
    int32 cur = int32(int(getInt32(arr, idx)));
    int newValueI = int(cur) + int(value);
    newValue = int32(newValueI);
    change = int32(newValueI - int(cur));

    setInt32(arr, idx, newValue);
  }

  function toInt32Array(bytes32[] memory arr, uint size) internal pure returns (int32[] memory) {
    int32[] memory result = new int32[](size);
    for (uint i = 0; i < arr.length; i++) {
      for (uint j; j < 8; ++j) {
        uint idx = i * 8 + j;
        if (idx >= size) break;
        result[idx] = getInt32Memory(arr, idx);
      }
    }
    return result;
  }

  /// @dev pack int32 array into bytes32 array
  function toBytes32Array(int32[] memory arr) internal pure returns (bytes32[] memory) {
    uint size = arr.length / 8 + 1;
    bytes32[] memory result = new bytes32[](size);
    for (uint i; i < size; ++i) {
      for (uint j; j < 8; ++j) {
        uint idx = i * 8 + j;
        if (idx >= arr.length) break;
        result[i] |= bytes32(uint(uint32(arr[idx]))) << (j * 32);
      }
    }
    return result;
  }

  /// @dev pack int32 array into bytes32 array using last 8bytes for ids
  ///      we can not use zero values coz will not able to properly unpack it later
  function toBytes32ArrayWithIds(int32[] memory arr, uint8[] memory ids) internal pure returns (bytes32[] memory) {
    if (arr.length != ids.length) revert IAppErrors.LengthsMismatch();

    uint size = arr.length / 8 + 1;
    bytes32[] memory result = new bytes32[](size);
    for (uint i; i < size; ++i) {
      for (uint j; j < 8; ++j) {
        uint idx = i * 8 + j;
        if (idx >= arr.length) break;

        if (arr[idx] > type(int24).max || arr[idx] < type(int24).min) revert IAppErrors.IntOutOfRange(int(arr[idx]));
        if (arr[idx] == 0) revert IAppErrors.ZeroValue();
        result[i] |= bytes32(uint(uint24(int24(arr[idx])))) << (j * 32);
        result[i] |= bytes32(uint(ids[idx])) << (j * 32 + 24);
      }
    }
    return result;
  }

  /// @dev we do not know exact size of array, assume zero values is not acceptable for this array
  function toInt32ArrayWithIds(bytes32[] memory arr) internal pure returns (int32[] memory values, uint8[] memory ids) {
    uint len = arr.length;
    uint size = len * 8;
    int32[] memory valuesTmp = new int32[](size);
    uint8[] memory idsTmp = new uint8[](size);
    uint counter;
    for (uint i = 0; i < len; i++) {
      for (uint j; j < 8; ++j) {
        uint idx = i * 8 + j;
        // if (idx >= size) break;  // it looks like a useless check
        valuesTmp[idx] = int32(int24(int(uint(arr[i]) >> (j * 32)))); // getInt32AsInt24(arr, idx);
        idsTmp[idx] = uint8(uint(arr[i]) >> (j * 32 + 24)); // getUnit8From32Step(arr, idx);
        if (valuesTmp[idx] == 0) {
          break;
        }
        counter++;
      }
    }

    values = new int32[](counter);
    ids = new uint8[](counter);
    for (uint i; i < counter; ++i) {
      values[i] = valuesTmp[i];
      ids[i] = idsTmp[i];
    }
  }
  //endregion ------------------------------------ COMPLEX ARRAYS

  //region ------------------------------------ Guilds
  /// @dev ShelterID is uint. But in the code we assume that this ID can be stored as uint64 (see auctions)
  /// @param biome 1, 2, 3...
  /// @param shelterLevel 1, 2 or 3.
  /// @param shelterIndex 0, 1, 2 ...
  function packShelterId(uint8 biome, uint8 shelterLevel, uint8 shelterIndex) internal pure returns (uint) {
    return uint(biome) | (uint(shelterLevel) << 8) | (uint(shelterIndex) << 16);
  }

  function unpackShelterId(uint shelterId) internal pure returns (uint8 biome, uint8 shelterLevel, uint8 shelterIndex) {
    return (uint8(shelterId), uint8(shelterId >> 8), uint8(shelterId >> 16));
  }
  //endregion ------------------------------------ Guilds

  //region ------------------------------------ Metadata of IItemController.OtherSubtypeKind

  function getOtherItemTypeKind(bytes memory packedData) internal pure returns (IItemController.OtherSubtypeKind) {
    bytes32 serialized;
    assembly {
      serialized := mload(add(packedData, 32))
    }
    uint8 kind = uint8(uint(serialized));
    if (kind == 0 || kind >= uint8(IItemController.OtherSubtypeKind.END_SLOT)) revert IAppErrors.IncorrectOtherItemTypeKind(kind);
    return IItemController.OtherSubtypeKind(kind);
  }

  function packOtherItemReduceFragility(uint value) internal pure returns (bytes memory packedData) {
    bytes32 serialized = bytes32(uint(uint8(IItemController.OtherSubtypeKind.REDUCE_FRAGILITY_1)));
    serialized |= bytes32(uint(uint248(value))) << 8;
    return bytes.concat(serialized);
  }

  function unpackOtherItemReduceFragility(bytes memory packedData) internal pure returns (uint) {
    bytes32 serialized;
    assembly {
      serialized := mload(add(packedData, 32))
    }
    uint8 kind = uint8(uint(serialized));
    if (kind != uint8(IItemController.OtherSubtypeKind.REDUCE_FRAGILITY_1)) revert IAppErrors.IncorrectOtherItemTypeKind(kind);
    uint value = uint248(uint(serialized) >> 8);
    return value;
  }
  //endregion ------------------------------------ Metadata of IItemController.OtherSubtypeKind

  //region ------------------------------------ Metadata of IPvpController.PvpAttackInfoDefaultStrategy
  function getPvpBehaviourStrategyKind(bytes memory encodedData) internal pure returns (uint) {
    bytes32 serialized;
    assembly {
      serialized := mload(add(encodedData, 64)) // first 32 bytes contain 0x20 and indicate array, we need to read second 32 bytes to get first uint in the struct
    }

    return uint(serialized);
  }
  //endregion ------------------------------------ Metadata of IPvpController.PvpAttackInfoDefaultStrategy

}
","keccak256":"0x2c8652b2e7d685dc36cefd6c6bb81f116b72909e103879c1afb2e2e92821f83e","license":"BUSL-1.1"},"contracts/lib/ScoreLib.sol":{"content":"// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.23;

import "../openzeppelin/Math.sol";
import "../interfaces/IStatController.sol";
import "./CalcLib.sol";

library ScoreLib {
  using CalcLib for int32;

  // core
  uint public constant STRENGTH = 100;
  uint public constant DEXTERITY = 100;
  uint public constant VITALITY = 100;
  uint public constant ENERGY = 100;

  // attributes
  uint public constant MELEE_DAMAGE = 10;
  uint public constant ATTACK_RATING = 3;
  uint public constant DEFENCE = 10;
  uint public constant BLOCK_RATING = 500;
  uint public constant LIFE = 10;
  uint public constant MANA = 10;

  uint public constant LIFE_CHANCES = 10_000;
  uint public constant MAGIC_FIND = 300;
  uint public constant CRITICAL_HIT = 150;
  uint public constant DMG_FACTOR = 200;

  uint public constant AR_FACTOR = 200;
  uint public constant LIFE_STOLEN_PER_HIT = 1000;
  uint public constant MANA_AFTER_KILL = 1000;
  uint public constant DAMAGE_REDUCTION = 500;
  uint public constant REFLECT_DAMAGE = 250;
  uint public constant RESIST_TO_STATUSES = 70;

  // resistance
  uint public constant ELEMENT_RESIST = 100;

  // race specific attributes
  uint public constant RACE_SPECIFIC = 20;

  // statuses
  uint public constant STATUSES = 100;

  // items
  uint public constant DURABILITY_SCORE = 1;

  // hero
  uint public constant HERO_LEVEL_SCORE = 1000;

  /// @param isForReinforcement If true calculate score using 12 main attributes only. Otherwise use all attributes.
  function attributesScore(int32[] memory attributes, bool isForReinforcement) internal pure returns (uint) {
    uint result;
    {
      result += (attributes[uint(IStatController.ATTRIBUTES.STRENGTH)]).toUint() * STRENGTH
        + (attributes[uint(IStatController.ATTRIBUTES.DEXTERITY)]).toUint() * DEXTERITY
        + (attributes[uint(IStatController.ATTRIBUTES.VITALITY)]).toUint() * VITALITY
        + (attributes[uint(IStatController.ATTRIBUTES.ENERGY)]).toUint() * ENERGY
        + (attributes[uint(IStatController.ATTRIBUTES.ATTACK_RATING)]).toUint() * ATTACK_RATING
        + (attributes[uint(IStatController.ATTRIBUTES.DEFENSE)]).toUint() * DEFENCE
        + (attributes[uint(IStatController.ATTRIBUTES.BLOCK_RATING)]).toUint() * BLOCK_RATING
        + Math.average(attributes[uint(IStatController.ATTRIBUTES.DAMAGE_MIN)].toUint(), attributes[uint(IStatController.ATTRIBUTES.DAMAGE_MAX)].toUint()) * MELEE_DAMAGE
      ;
    }
    {
      result +=
        (attributes[uint(IStatController.ATTRIBUTES.FIRE_RESISTANCE)]).toUint() * ELEMENT_RESIST
        + (attributes[uint(IStatController.ATTRIBUTES.COLD_RESISTANCE)]).toUint() * ELEMENT_RESIST
        + (attributes[uint(IStatController.ATTRIBUTES.LIGHTNING_RESISTANCE)]).toUint() * ELEMENT_RESIST;
    }

    if (! isForReinforcement) {
      {
        result +=
          (attributes[uint(IStatController.ATTRIBUTES.LIFE)]).toUint() * LIFE
          + (attributes[uint(IStatController.ATTRIBUTES.MANA)]).toUint() * MANA;
      }
      {
        result +=
          (attributes[uint(IStatController.ATTRIBUTES.DMG_AGAINST_HUMAN)]).toUint() * RACE_SPECIFIC
          + (attributes[uint(IStatController.ATTRIBUTES.DMG_AGAINST_UNDEAD)]).toUint() * RACE_SPECIFIC
          + (attributes[uint(IStatController.ATTRIBUTES.DMG_AGAINST_DAEMON)]).toUint() * RACE_SPECIFIC
          + (attributes[uint(IStatController.ATTRIBUTES.DMG_AGAINST_BEAST)]).toUint() * RACE_SPECIFIC
          + (attributes[uint(IStatController.ATTRIBUTES.DEF_AGAINST_HUMAN)]).toUint() * RACE_SPECIFIC
          + (attributes[uint(IStatController.ATTRIBUTES.DEF_AGAINST_UNDEAD)]).toUint() * RACE_SPECIFIC
          + (attributes[uint(IStatController.ATTRIBUTES.DEF_AGAINST_DAEMON)]).toUint() * RACE_SPECIFIC
          + (attributes[uint(IStatController.ATTRIBUTES.DEF_AGAINST_BEAST)]).toUint() * RACE_SPECIFIC;
      }
      {
        result +=
          (attributes[uint(IStatController.ATTRIBUTES.STUN)]).toUint() * STATUSES
          + (attributes[uint(IStatController.ATTRIBUTES.BURN)]).toUint() * STATUSES
          + (attributes[uint(IStatController.ATTRIBUTES.FREEZE)]).toUint() * STATUSES
          + (attributes[uint(IStatController.ATTRIBUTES.CONFUSE)]).toUint() * STATUSES
          + (attributes[uint(IStatController.ATTRIBUTES.CURSE)]).toUint() * STATUSES
          + (attributes[uint(IStatController.ATTRIBUTES.POISON)]).toUint() * STATUSES;
      }
      {
        result +=
          (attributes[uint(IStatController.ATTRIBUTES.LIFE_CHANCES)]).toUint() * LIFE_CHANCES
          + (attributes[uint(IStatController.ATTRIBUTES.MAGIC_FIND)]).toUint() * MAGIC_FIND
          + (attributes[uint(IStatController.ATTRIBUTES.CRITICAL_HIT)]).toUint() * CRITICAL_HIT
          + (attributes[uint(IStatController.ATTRIBUTES.MELEE_DMG_FACTOR)]).toUint() * DMG_FACTOR
          + (attributes[uint(IStatController.ATTRIBUTES.FIRE_DMG_FACTOR)]).toUint() * DMG_FACTOR
          + (attributes[uint(IStatController.ATTRIBUTES.COLD_DMG_FACTOR)]).toUint() * DMG_FACTOR
          + (attributes[uint(IStatController.ATTRIBUTES.LIGHTNING_DMG_FACTOR)]).toUint() * DMG_FACTOR;
      }
      {
        result +=
          (attributes[uint(IStatController.ATTRIBUTES.AR_FACTOR)]).toUint() * AR_FACTOR
          + (attributes[uint(IStatController.ATTRIBUTES.LIFE_STOLEN_PER_HIT)]).toUint() * LIFE_STOLEN_PER_HIT
          + (attributes[uint(IStatController.ATTRIBUTES.MANA_AFTER_KILL)]).toUint() * MANA_AFTER_KILL
          + (attributes[uint(IStatController.ATTRIBUTES.DAMAGE_REDUCTION)]).toUint() * DAMAGE_REDUCTION
          + (attributes[uint(IStatController.ATTRIBUTES.REFLECT_DAMAGE_MELEE)]).toUint() * REFLECT_DAMAGE
          + (attributes[uint(IStatController.ATTRIBUTES.REFLECT_DAMAGE_MAGIC)]).toUint() * REFLECT_DAMAGE
          + (attributes[uint(IStatController.ATTRIBUTES.RESIST_TO_STATUSES)]).toUint() * RESIST_TO_STATUSES;
      }
    }
    return result;
  }

  function itemScore(int32[] memory attributes, uint16 baseDurability) internal pure returns (uint) {
    return attributesScore(attributes, false) + baseDurability * DURABILITY_SCORE;
  }

  function heroScore(int32[] memory attributes, uint level) internal pure returns (uint) {
    return attributesScore(attributes, true) + level * HERO_LEVEL_SCORE;
  }

}
