import "../proxy/Controllable.sol";
import "../interfaces/ITreasury.sol";
import "../interfaces/IApplicationEvents.sol";
import "../lib/TreasuryLib.sol";

contract Treasury is Initializable, Controllable, ITreasury {

  //region ------------------------ CONSTANTS

  /// @notice Version of the contract
  /// @dev Should be incremented when contract changed
  string public constant override VERSION = "1.0.3";
  uint public constant AUGMENT_GOV_FEE = TreasuryLib.AUGMENT_GOV_FEE;
  uint public constant REPAIR_GOV_FEE = TreasuryLib.REPAIR_GOV_FEE;
  //endregion ------------------------ CONSTANTS

  //region ------------------------ INITIALIZER

  function init(address controller_) external initializer {
    __Controllable_init(controller_);
  }
  //endregion ------------------------ INITIALIZER

  //region ------------------------ VIEWS

  function balanceOfToken(address token) external view override returns (uint) {
    return TreasuryLib.balanceOfToken(token);
  }
  //endregion ------------------------ VIEWS

  //region ------------------------ ACTIONS

  function sendToDungeon(address dungeon, address token, uint amount) external override {
    TreasuryLib.sendToDungeon(IController(controller()), dungeon, token, amount);
  }

  /// @dev Assume approve. Move fee to treasury and governance from msg.sender.
  ///      Anyone can call
  function sendFee(address token, uint amount, IItemController.FeeType feeType) external override {
    TreasuryLib.sendFee(IController(controller()), token, amount, feeType);
  }
  //endregion ------------------------ ACTIONS

}

