// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.23;

import {IController} from "../interfaces/IController.sol";
import {IOracle} from "../interfaces/IOracle.sol";
import {IStatController} from "../interfaces/IStatController.sol";
import {IStoryController} from "../interfaces/IStoryController.sol";
import {ITreasury} from "../interfaces/ITreasury.sol";
import {IDungeonFactory} from "../interfaces/IDungeonFactory.sol";
import {IReinforcementController} from "../interfaces/IReinforcementController.sol";
import {IGameToken} from "../interfaces/IGameToken.sol";
import {IGOC} from "../interfaces/IGOC.sol";
import {IItemController} from "../interfaces/IItemController.sol";
import {IHeroController} from "../interfaces/IHeroController.sol";

/// @notice Provide context-struct with all controller addresses and routines for lazy init
/// Usage:
///       Create an instance of the structure
///               cc = ControllerContextLib.init(controller);
///       access controller directly
///               cc.controller.xxx();
///       access other contracts indirectly
///               sc = ControllerContextLib.getStatController(cc);
library ControllerContextLib {
  struct ControllerContext {
    IController controller;
    IStatController statController;
    IStoryController storyController;
    IOracle oracle;
    ITreasury treasury;
    IDungeonFactory dungeonFactory;
    IGOC gameObjectController;
    IReinforcementController reinforcementController;
    IItemController itemController;
    IHeroController heroController;
    IGameToken gameToken;
  }

  function init(IController controller) internal pure returns (ControllerContext memory cc) {
    cc.controller = controller;
    return cc;
  }

  function getStatController(ControllerContext memory cc) internal view returns (IStatController statController) {
    if (address(cc.statController) == address(0)) {
      cc.statController = IStatController(cc.controller.statController());
    }
    return cc.statController;
  }

  function getStoryController(ControllerContext memory cc) internal view returns (IStoryController storyController) {
    if (address(cc.storyController) == address(0)) {
      cc.storyController = IStoryController(cc.controller.storyController());
    }
    return cc.storyController;
  }

  function getOracle(ControllerContext memory cc) internal view returns (IOracle oracle) {
    if (address(cc.oracle) == address(0)) {
      cc.oracle = IOracle(cc.controller.oracle());
    }
    return cc.oracle;
  }

  function getTreasury(ControllerContext memory cc) internal view returns (ITreasury treasury) {
    if (address(cc.treasury) == address(0)) {
      cc.treasury = ITreasury(cc.controller.treasury());
    }
    return cc.treasury;
  }

  function getDungeonFactory(ControllerContext memory cc) internal view returns (IDungeonFactory dungeonFactory) {
    if (address(cc.dungeonFactory) == address(0)) {
      cc.dungeonFactory = IDungeonFactory(cc.controller.dungeonFactory());
    }
    return cc.dungeonFactory;
  }

  function getGameObjectController(ControllerContext memory cc) internal view returns (IGOC gameObjectController) {
    if (address(cc.gameObjectController) == address(0)) {
      cc.gameObjectController = IGOC(cc.controller.gameObjectController());
    }
    return cc.gameObjectController;
  }

  function getReinforcementController(ControllerContext memory cc) internal view returns (IReinforcementController reinforcementController) {
    if (address(cc.reinforcementController) == address(0)) {
      cc.reinforcementController = IReinforcementController(cc.controller.reinforcementController());
    }
    return cc.reinforcementController;
  }

  function getItemController(ControllerContext memory cc) internal view returns (IItemController itemController) {
    if (address(cc.itemController) == address(0)) {
      cc.itemController = IItemController(cc.controller.itemController());
    }
    return cc.itemController;
  }

  function getHeroController(ControllerContext memory cc) internal view returns (IHeroController heroController) {
    if (address(cc.heroController) == address(0)) {
      cc.heroController = IHeroController(cc.controller.heroController());
    }
    return cc.heroController;
  }

  function getGameToken(ControllerContext memory cc) internal view returns (IGameToken gameToken) {
    if (address(cc.gameToken) == address(0)) {
      cc.gameToken = IGameToken(cc.controller.gameToken());
    }
    return cc.gameToken;
  }
}

contracts/lib/ControllerLib.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.23;

import "../interfaces/IERC20.sol";
import "../interfaces/IAppErrors.sol";
import "../interfaces/IApplicationEvents.sol";
import "../interfaces/IProxyControlled.sol";

library ControllerLib {
  //region ------------------------ Constants
  /// @dev keccak256(abi.encode(uint256(keccak256("controller.main")) - 1)) & ~bytes32(uint256(0xff))
  bytes32 private constant CONTROLLER_STORAGE_LOCATION = 0x4d96152d518acf5697a667aeb82f27b3218b679995afa077296a84fdcb65bb00;
  uint internal constant DEPLOYER_ELIGIBILITY_PERIOD = 7 days;
  //endregion ------------------------ Constants

  //region ------------------------ Data types

  /// @custom:storage-location erc7201:controller.main
  struct MainState {
    address governance;
    address futureGovernance;

    address statController;
    address storyController;
    address oracle;
    address treasury;
    address dungeonFactory;
    address gameObjectController;
    address reinforcementController;
    address itemController;
    address heroController;
    address gameToken;

    mapping(address => bool) validTreasuryTokens;
    /// @dev EOA => eligibility time. We assume that deployer is fresh EOA and will be changed every deploy cycle for security reasons.
    mapping(address => uint) deployers;
    /// @dev In emergency case governance can pause all game
    bool onPause;
  }
  //endregion ------------------------ Data types

  //region ------------------------ Restrictions

  function onlyGovernance() internal view {
    if (!_isGovernance(msg.sender)) revert IAppErrors.NotGovernance(msg.sender);
  }

  function onlyDeployer() internal view {
    if (!isDeployer(msg.sender)) revert IAppErrors.ErrorNotDeployer(msg.sender);
  }
  //endregion ------------------------ Restrictions

  //region ------------------------ Views

  function _S() internal pure returns (MainState storage s) {
    assembly {
      s.slot := CONTROLLER_STORAGE_LOCATION
    }
    return s;
  }

  function isDeployer(address adr) internal view returns (bool) {
    uint t = _S().deployers[adr];
    return (t != 0 && block.timestamp < t) || _isGovernance(adr);
  }

  function governance() internal view returns (address) {return _S().governance;}

  function futureGovernance() internal view returns (address) {return _S().futureGovernance;}

  function statController() internal view returns (address) {return _S().statController;}

  function storyController() internal view returns (address) {return _S().storyController;}

  function oracle() internal view returns (address) {return _S().oracle;}

  function treasury() internal view returns (address) {return _S().treasury;}

  function dungeonFactory() internal view returns (address) {return _S().dungeonFactory;}

  function gameObjectController() internal view returns (address) {return _S().gameObjectController;}

  function reinforcementController() internal view returns (address) {return _S().reinforcementController;}

  function itemController() internal view returns (address) {return _S().itemController;}

  function heroController() internal view returns (address) {return _S().heroController;}

  function gameToken() internal view returns (address) {return _S().gameToken;}

  function validTreasuryTokens(address token) internal view returns (bool) {
    return _S().validTreasuryTokens[token];
  }

  function onPause() internal view returns (bool) {return _S().onPause;}
  //endregion ------------------------ Views

  //region ------------------------ Gov actions - setters

  function changePause(bool value) internal {
    onlyDeployer();
    _S().onPause = value;
  }

  function offerGovernance(address newGov) internal {
    onlyGovernance();
    _S().futureGovernance = newGov;
    emit IApplicationEvents.OfferGovernance(newGov);
  }

  function acceptGovernance() internal {
    if (_S().futureGovernance != msg.sender) revert IAppErrors.NotFutureGovernance(msg.sender);
    _S().governance = msg.sender;
    delete _S().futureGovernance;
    emit IApplicationEvents.GovernanceAccepted(msg.sender);
  }

  function setStatController(address value) internal {
    onlyGovernance();
    if (value == address(0)) revert IAppErrors.ZeroAddress();
    _S().statController = value;
    emit IApplicationEvents.StatControllerChanged(value);
  }

  function setStoryController(address value) internal {
    onlyGovernance();
    if (value == address(0)) revert IAppErrors.ZeroAddress();
    _S().storyController = value;
    emit IApplicationEvents.StoryControllerChanged(value);
  }

  function setGameObjectController(address value) internal {
    onlyGovernance();
    if (value == address(0)) revert IAppErrors.ZeroAddress();
    _S().gameObjectController = value;
    emit IApplicationEvents.GameObjectControllerChanged(value);
  }

  function setReinforcementController(address value) internal {
    onlyGovernance();
    if (value == address(0)) revert IAppErrors.ZeroAddress();
    _S().reinforcementController = value;
    emit IApplicationEvents.ReinforcementControllerChanged(value);
  }

  function setOracle(address value) internal {
    onlyGovernance();
    if (value == address(0)) revert IAppErrors.ZeroAddress();
    _S().oracle = value;
    emit IApplicationEvents.OracleChanged(value);
  }

  function setTreasury(address value) internal {
    onlyGovernance();
    if (value == address(0)) revert IAppErrors.ZeroAddress();
    _S().treasury = value;
    emit IApplicationEvents.TreasuryChanged(value);
  }

  function setItemController(address value) internal {
    onlyGovernance();
    if (value == address(0)) revert IAppErrors.ZeroAddress();
    _S().itemController = value;
    emit IApplicationEvents.ItemControllerChanged(value);
  }

  function setHeroController(address value) internal {
    onlyGovernance();
    if (value == address(0)) revert IAppErrors.ZeroAddress();
    _S().heroController = value;
    emit IApplicationEvents.HeroControllerChanged(value);
  }

  function setGameToken(address value) internal {
    onlyGovernance();
    if (value == address(0)) revert IAppErrors.ZeroAddress();
    _S().gameToken = value;
    emit IApplicationEvents.GameTokenChanged(value);
  }

  function setDungeonFactory(address value) internal {
    onlyGovernance();
    if (value == address(0)) revert IAppErrors.ZeroAddress();
    _S().dungeonFactory = value;
    emit IApplicationEvents.DungeonFactoryChanged(value);
  }

  function changeDeployer(address eoa, bool remove) internal {
    onlyGovernance();
    if (remove) {
      delete _S().deployers[eoa];
    } else {
      _S().deployers[eoa] = block.timestamp + DEPLOYER_ELIGIBILITY_PERIOD;
    }
  }
  //endregion ------------------------ Gov actions - setters

  //region ------------------------ Gov actions - others

  function updateProxies(address[] memory proxies, address newLogic) internal {
    onlyDeployer();
    for (uint i; i < proxies.length; i++) {
      IProxyControlled(proxies[i]).upgrade(newLogic);
      emit IApplicationEvents.ProxyUpdated(proxies[i], newLogic);
    }
  }

  function claimToGovernance(address token) internal {
    onlyGovernance();
    uint amount = IERC20(token).balanceOf(address(this));
    if (amount != 0) {
      IERC20(token).transfer(_S().governance, amount);
      emit IApplicationEvents.Claimed(token, amount);
    }
  }
  //endregion ------------------------ Gov actions - others

  //region ------------------------ REGISTER ACTIONS

  function changeTreasuryTokenStatus(address token, bool status) internal {
    onlyGovernance();
    _S().validTreasuryTokens[token] = status;
    emit IApplicationEvents.TokenStatusChanged(token, status);
  }
  //endregion ------------------------ REGISTER ACTIONS

  //region ------------------------  Internal logic
  function _isGovernance(address _value) internal view returns (bool) {
    return IController(address(this)).governance() == _value;
  }
  //endregion ------------------------  Internal logic
}

contracts/lib/DungeonFactoryLib.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.23;

import "../interfaces/IERC721.sol";
import "../interfaces/IOracle.sol";
import "../interfaces/ITreasury.sol";
import "../interfaces/IStatController.sol";
import "../interfaces/IDungeonFactory.sol";
import "../interfaces/IGameToken.sol";
import "../interfaces/IMinter.sol";
import "../interfaces/IReinforcementController.sol";
import "../interfaces/IGOC.sol";
import "../interfaces/IAppErrors.sol";
import "../interfaces/IApplicationEvents.sol";
import "../proxy/Controllable.sol";
import "../lib/StringLib.sol";
import "../lib/DungeonLib.sol";
import "../relay/ERC2771Context.sol";
import "../lib/ControllerContextLib.sol";

library DungeonFactoryLib {
  using EnumerableSet for EnumerableSet.UintSet;
  using EnumerableMap for EnumerableMap.AddressToUintMap;
  using PackingLib for bytes32;
  using PackingLib for uint16;
  using PackingLib for uint8;
  using PackingLib for address;
  using PackingLib for uint32[];
  using PackingLib for uint32;
  using PackingLib for uint64;
  using PackingLib for int32[];
  using PackingLib for int32;

  //region ------------------------ Data types
  struct ObjectActionLocal {
    bool isCompleted;
    bool needClear;
    uint32 currentObjectId;
    uint32 newCurrentObjectId;
    uint newCurrentStage;
  }

  //endregion ------------------------ Data types

  //region ------------------------ RESTRICTIONS

  function onlyDeployer(IController controller) internal view {
    if (!controller.isDeployer(msg.sender)) revert IAppErrors.ErrorNotDeployer(msg.sender);
  }

  function _onlyEoa(bool isEoa) internal pure {
    if (!isEoa) revert IAppErrors.ErrorOnlyEoa();
  }

  function onlyHeroController(IController controller) internal view {
    if (IController(controller).heroController() != msg.sender) revert IAppErrors.ErrorNotHeroController(msg.sender);
  }

  function _checkOwnerRegisteredNotPaused(
    address heroToken,
    uint heroTokenId,
    address msgSender,
    ControllerContextLib.ControllerContext memory cc
  ) internal view {
    if (IERC721(heroToken).ownerOf(heroTokenId) != msgSender) revert IAppErrors.ErrorNotHeroOwner(heroToken, msgSender);
    if (ControllerContextLib.getHeroController(cc).heroClass(heroToken) == 0) revert IAppErrors.ErrorHeroIsNotRegistered(heroToken);
    if (cc.controller.onPause()) revert IAppErrors.ErrorPaused();
  }

  //endregion ------------------------ RESTRICTIONS

  //region ------------------------ VIEWS
  function _S() internal pure returns (IDungeonFactory.MainState storage s) {
    return DungeonLib._S();
  }

  function dungeonAttributes(uint16 dungNum) internal view returns (IDungeonFactory.DungeonAttributes memory) {
    return _S().dungeonAttributes[dungNum];
  }

  function dungeonStatus(uint64 dungeonId) internal view returns (
    uint16 dungNum,
    bool isCompleted,
    address heroToken,
    uint heroTokenId,
    uint32 currentObject,
    uint8 currentStage,
    address[] memory treasuryTokens_,
    uint[] memory treasuryTokensAmounts_,
    bytes32[] memory treasuryItems,
    uint8 stages,
    uint32[] memory uniqObjects
  ) {
    IDungeonFactory.DungeonStatus storage dungStatus = _S().dungeonStatuses[dungeonId];

    dungNum = dungStatus.dungNum;
    isCompleted = dungStatus.isCompleted;
    heroToken = dungStatus.heroToken;
    heroTokenId = dungStatus.heroTokenId;
    currentObject = dungStatus.currentObject;
    currentStage = dungStatus.currentStage;
    treasuryItems = dungStatus.treasuryItems;
    stages = dungStatus.stages;
    uniqObjects = dungStatus.uniqObjects;

    uint tokensLength = dungStatus.treasuryTokens.length();

    treasuryTokens_ = new address[](tokensLength);
    treasuryTokensAmounts_ = new uint[](tokensLength);

    for (uint i; i < tokensLength; ++i) {
      (treasuryTokens_[i], treasuryTokensAmounts_[i]) = dungStatus.treasuryTokens.at(i);
    }
  }

  function dungeonCounter() internal view returns (uint64) {
    return _S().dungeonCounter;
  }

  function maxBiomeCompleted(address heroToken, uint heroTokenId) internal view returns (uint8) {
    return _S().maxBiomeCompleted[heroToken.packNftId(heroTokenId)];
  }

  function currentDungeon(address heroToken, uint heroTokenId) internal view returns (uint64) {
    return _S().heroCurrentDungeon[heroToken.packNftId(heroTokenId)];
  }

  function minLevelForTreasury(address token) internal view returns (uint) {
    return _S().minLevelForTreasury[token];
  }

  function skillSlotsForDurabilityReduction(address heroToken, uint heroTokenId) internal view returns (
    uint8[] memory result
  ) {
    return _S().skillSlotsForDurabilityReduction[heroToken.packNftId(heroTokenId)].unpackUint8Array();
  }

  /// @return Length of the items in freeDungeons map for the given {biome}
  function freeDungeonsByLevelLength(uint biome) internal view returns (uint) {
    return _S().freeDungeons[biome].length();
  }

  /// @param index Index of the free dungeon inside freeDungeons map
  /// @return dungeonId
  function freeDungeonsByLevel(uint index, uint biome) internal view returns (uint64) {
    return uint64(_S().freeDungeons[biome].at(index));
  }

  function getDungeonTreasuryAmount(IController controller, address token, uint heroLevel, uint biome)
  internal view returns (
    uint totalAmount,
    uint amountForDungeon,
    uint mintAmount
  ) {
    totalAmount = ITreasury(controller.treasury()).balanceOfToken(token);
    mintAmount = IMinter(IGameToken(controller.gameToken()).minter()).amountForDungeon(biome, heroLevel);
    amountForDungeon = DungeonLib.dungeonTreasuryReward(
      token,
      uint(_S().maxBiome),
      totalAmount,
      uint8(heroLevel),
      uint8(biome)
    );
  }

  /// @notice Check if biome boss completed by the hero.
  /// @dev isBiomeBossCompleted would be more correct title, but isBiomeBoss is already used
  function isBiomeBoss(IController controller, address heroToken, uint heroTokenId)
  internal view returns (bool) {
    uint8 heroBiome = IHeroController(controller.heroController()).heroBiome(heroToken, heroTokenId);
    return _S().bossCompleted[heroToken.packMapObject(uint64(heroTokenId), heroBiome)];
  }
  //endregion ------------------------ VIEWS

  //region ------------------------ ACTIONS
  function launch(
    bool isEoa,
    IController controller,
    address msgSender,
    address heroToken,
    uint heroTokenId,
    address treasuryToken
  ) external returns (uint64 dungeonId) {
    _onlyEoa(isEoa);
    return _launch(controller, msgSender, heroToken, heroTokenId, treasuryToken);
  }

  function launchForNewHero(
    IController controller,
    address msgSender,
    address heroToken,
    uint heroTokenId
  ) external returns (uint64 dungeonId) {
    onlyHeroController(controller);
    return _launch(controller, msgSender, heroToken, heroTokenId, controller.gameToken());
  }

  /// @notice Create new dungeon and enter to it. Treasury reward is sent by treasury to the dungeon.
  function _launch(
    IController controller,
    address msgSender,
    address heroToken,
    uint heroTokenId,
    address treasuryToken
  ) internal returns (uint64 dungeonId) {
    IDungeonFactory.MainState storage s = _S();
    ControllerContextLib.ControllerContext memory cc = ControllerContextLib.init(controller);

    // check part of restrictions; other part is checked inside DungeonLib._enter
    _checkOwnerRegisteredNotPaused(heroToken, heroTokenId, msgSender, cc);
    if (!controller.validTreasuryTokens(treasuryToken)) revert IAppErrors.ErrorNotValidTreasureToken(treasuryToken);

    // select a logic for new dungeon
    uint8 heroLevel = uint8(ControllerContextLib.getStatController(cc).heroStats(heroToken, heroTokenId).level);
    uint16 dungNum = DungeonLib.getDungeonLogic(
      s,
      cc,
      heroLevel,
      heroToken,
      heroTokenId,
      ControllerContextLib.getOracle(cc).getRandomNumber(1e18, heroLevel)
    );

    // register new dungeon
    dungeonId = s.dungeonCounter + 1;
    s.dungeonCounter = dungeonId;

    IDungeonFactory.DungeonAttributes storage dungAttr = s.dungeonAttributes[dungNum];
    IDungeonFactory.DungeonStatus storage dungStatus = s.dungeonStatuses[dungeonId];

    if (dungStatus.isCompleted) revert IAppErrors.ErrorDungeonCompleted();

    dungStatus.dungeonId = dungeonId;
    dungStatus.dungNum = dungNum;
    dungStatus.stages = dungAttr.stages;
    dungStatus.uniqObjects = dungAttr.uniqObjects;

    emit IApplicationEvents.DungeonRegistered(dungNum, dungeonId);

    // enter to the dungeon
    DungeonLib._enter(cc, dungStatus, dungAttr, dungNum, dungeonId, heroToken, heroTokenId);

    // when entered, open the first object for reduce txs
    _openObject(cc, msgSender, dungeonId);

    // send treasury to the dungeon
    uint treasuryAmount = DungeonLib.dungeonTreasuryReward(
      treasuryToken,
      uint(_S().maxBiome),
      ControllerContextLib.getTreasury(cc).balanceOfToken(treasuryToken),
      heroLevel,
      dungAttr.biome
    );

    if (treasuryAmount != 0) {
      ControllerContextLib.getTreasury(cc).sendToDungeon(address(this), treasuryToken, treasuryAmount);
      DungeonLib._registerTreasuryToken(treasuryToken, s.dungeonStatuses[dungeonId].treasuryTokens, treasuryAmount);
    }

    emit IApplicationEvents.DungeonLaunched(dungNum, dungeonId, heroToken, heroTokenId, treasuryToken, treasuryAmount);
  }

  /// @notice Set boss completed for the given hero and given biome.
  /// @dev Set custom data for the hero: BOSS_COMPLETED_ = 1
  function setBossCompleted(IController controller, uint32 objectId, address heroToken, uint heroTokenId, uint8 heroBiome) internal {
    if (controller.gameObjectController() != msg.sender) revert IAppErrors.ErrorNotGoc();

    IDungeonFactory.MainState storage s = _S();

    if (!s.bossCompleted[heroToken.packMapObject(uint64(heroTokenId), heroBiome)]) {
      s.bossCompleted[heroToken.packMapObject(uint64(heroTokenId), heroBiome)] = true;
    }

    if (s.maxBiomeCompleted[heroToken.packNftId(heroTokenId)] < heroBiome) {
      s.maxBiomeCompleted[heroToken.packNftId(heroTokenId)] = heroBiome;
    }

    bytes32 index = _getBossCompletedIndex(heroBiome);
    IStatController(controller.statController()).setHeroCustomData(heroToken, heroTokenId, index, 1);

    emit IApplicationEvents.BossCompleted(objectId, heroBiome, heroToken, heroTokenId);
  }
  //endregion ------------------------ ACTIONS

  //region ------------------------ DUNGEON LOGIC - GOV ACTIONS

  /// @notice Register ordinal or specific dungeon
  /// @dev can be called for exist dungeon - will rewrite dungeon data
  /// @param dungNum Dungeon logic id
  /// @param biome Assume biome > 0
  /// @param isSpecific The dungeon is specific, so it shouldn't be registered in dungeonsLogicByBiome
  function registerDungeonLogic(
    IController controller,
    uint16 dungNum,
    uint8 biome,
    IDungeonFactory.DungeonGenerateInfo calldata genInfo,
    uint8 specReqBiome,
    uint8 specReqHeroClass,
    bool isSpecific
  ) internal {
    onlyDeployer(controller);
    IDungeonFactory.MainState storage s = _S();

    uint len = genInfo.objChancesByStages.length;
    if (len != genInfo.objTypesByStages.length || len != genInfo.uniqObjects.length) revert IAppErrors.ErrorNotStages();

    for (uint i; i < len; ++i) {
      if (genInfo.objChancesByStages[i].length != genInfo.objTypesByStages[i].length) revert IAppErrors.ErrorNotChances();
    }

    IDungeonFactory.DungeonAttributes storage info = s.dungeonAttributes[dungNum];

    if (biome > s.maxBiome) {
      s.maxBiome = biome;
    }

    info.stages = uint8(len); // info.stages can be increased later by chamber story
    info.biome = biome;

    info.uniqObjects = genInfo.uniqObjects;
    info.minMaxLevel = DungeonLib._toUint8PackedArray(genInfo.minLevel, genInfo.maxLevel);

    info.requiredCustomDataIndex = genInfo.requiredCustomDataIndex;
    bytes32[] storage requiredCustomDataValue = info.requiredCustomDataValue;

    for (uint i; i < genInfo.requiredCustomDataMinValue.length; ++i) {
      requiredCustomDataValue.push(
        PackingLib.packCustomDataRequirements(
          genInfo.requiredCustomDataMinValue[i],
          genInfo.requiredCustomDataMaxValue[i],
          genInfo.requiredCustomDataIsHero[i]
        )
      );
    }

    for (uint i; i < len; ++i) {
      info.info.objTypesByStages.push(PackingLib.packUint8Array(genInfo.objTypesByStages[i]));
      info.info.objChancesByStages.push(genInfo.objChancesByStages[i]);
    }

    if (isSpecific) {
      bytes32 packedId = DungeonLib._toUint8PackedArray(specReqBiome, specReqHeroClass);
      if (s.dungeonSpecific[packedId] != 0) revert IAppErrors.DungeonAlreadySpecific(dungNum);
      s.dungeonSpecific[packedId] = dungNum;

      if (s.allSpecificDungeons.contains(dungNum)) revert IAppErrors.DungeonAlreadySpecific2(dungNum);

      s.allSpecificDungeons.add(dungNum);

      emit IApplicationEvents.DungeonSpecificLogicRegistered(dungNum, specReqBiome, specReqHeroClass);
    } else {
      s.dungeonsLogicByBiome[info.biome].add(dungNum);
    }

    emit IApplicationEvents.DungeonLogicRegistered(dungNum, genInfo);
  }

  /// @dev Remove the dungeon logic (both ordinal and specific logics are supported)
  /// @param dungNum Dungeon logic id
  function removeDungeonLogic(IController controller, uint16 dungNum, uint8 specReqBiome, uint8 specReqHeroClass) internal {
    onlyDeployer(controller);
    IDungeonFactory.MainState storage s = _S();

    uint8 biome = s.dungeonAttributes[dungNum].biome;
    delete s.dungeonAttributes[dungNum];

    if (s.dungeonsLogicByBiome[biome].contains(dungNum)) {
      s.dungeonsLogicByBiome[biome].remove(dungNum);
      emit IApplicationEvents.DungeonLogicRemoved(dungNum);
    }

    if (s.allSpecificDungeons.contains(dungNum)) {
      bytes32 packedId = DungeonLib._toUint8PackedArray(specReqBiome, specReqHeroClass);
      if (s.dungeonSpecific[packedId] != dungNum) revert IAppErrors.WrongSpecificDungeon();

      delete s.dungeonSpecific[packedId];
      s.allSpecificDungeons.remove(dungNum);
      emit IApplicationEvents.DungeonSpecificLogicRemoved(dungNum, specReqBiome, specReqHeroClass);
    }
  }

  /// @dev Set eligible hero level for treasury tokens
  function setMinLevelForTreasury(IController controller, address token, uint heroLevel) internal {
    onlyDeployer(controller);

    if (heroLevel < DungeonLib.MIN_LEVEL_FOR_TREASURY_DEFAULT) {
      revert IAppErrors.ErrorLevelTooLow(heroLevel);
    }

    _S().minLevelForTreasury[token] = heroLevel;
    emit IApplicationEvents.MinLevelForTreasuryChanged(token, heroLevel);
  }

  /// @dev Governance can drop hero from dungeon in emergency case
  function emergencyExit(IController controller, uint64 dungId) internal {
    onlyDeployer(controller);
    DungeonLib.emergencyExit(controller, dungId);
  }
  //endregion ------------------------ DUNGEON LOGIC - GOV ACTIONS

  //region ------------------------ DUNGEON LOGIC - USER ACTIONS

  /// @notice Enter to the exist dungeon
  function enter(bool isEoa, IController controller, address msgSender, uint64 dungId, address heroToken, uint heroTokenId) external {
    IDungeonFactory.MainState storage s = _S();
    ControllerContextLib.ControllerContext memory cc = ControllerContextLib.init(controller);
    IDungeonFactory.DungeonStatus storage dungStatus = s.dungeonStatuses[dungId];

    // check part of restrictions; other part is checked inside DungeonLib._enter
    _onlyEoa(isEoa);
    _checkOwnerRegisteredNotPaused(heroToken, heroTokenId, msgSender, cc);
    if (dungStatus.isCompleted) revert IAppErrors.ErrorDungeonCompleted();

    // enter to the dungeon
    uint16 dungNum = dungStatus.dungNum;
    DungeonLib._enter(cc, dungStatus, s.dungeonAttributes[dungNum], dungNum, dungId, heroToken, heroTokenId);

    // when entered, open the first object for reduce txs
    _openObject(cc, msgSender, dungId);
  }

  function openObject(bool isEoa, IController controller, address msgSender, uint64 dungId) internal {
    _onlyEoa(isEoa);
    ControllerContextLib.ControllerContext memory cc = ControllerContextLib.init(controller);
    _openObject(cc, msgSender, dungId);
  }

  /// @notice Set new current object for the dungeon
  function _openObject(ControllerContextLib.ControllerContext memory cc, address msgSender, uint64 dungId) internal {
    IDungeonFactory.MainState storage s = _S();
    IDungeonFactory.DungeonStatus storage dungStatus = s.dungeonStatuses[dungId];
    IDungeonFactory.DungeonAttributes storage dungAttributes = s.dungeonAttributes[dungStatus.dungNum];

    IGOC goc = ControllerContextLib.getGameObjectController(cc);

    // check restrictions
    if (dungStatus.currentObject != 0) revert IAppErrors.ErrorNotReady();
    (address dungHero, uint dungHeroId) = _checkCurrentHero(dungStatus, msgSender, cc);

    // select new object and set it as current object in the dungeon
    uint currentStage = dungStatus.currentStage;
    uint32 objectId = _generateObject(dungAttributes, dungStatus, currentStage, goc, dungHero, dungHeroId);
    if (objectId == 0) revert IAppErrors.ErrorNotObject1();
    dungStatus.currentObject = objectId;

    // generate some info for UI
    uint iteration = goc.open(dungHero, dungHeroId, objectId);
    emit IApplicationEvents.ObjectOpened(dungId, dungHero, dungHeroId, objectId, iteration, currentStage);
  }

  /// @notice Do action and handle results
  /// @param data AttackInfo struct encoded using abi.encode
  function objectAction(bool isEoa, IController controller, address msgSender, uint64 dungId, bytes memory data) internal {
    IDungeonFactory.MainState storage s = _S();

    ControllerContextLib.ControllerContext memory cc = ControllerContextLib.init(controller);
    ObjectActionLocal memory v;

    IDungeonFactory.DungeonStatus storage dungStatus = s.dungeonStatuses[dungId];
    IDungeonFactory.DungeonAttributes storage dungAttributes = s.dungeonAttributes[dungStatus.dungNum];

    // check restrictions, some restrictions are checked inside objectAction
    _onlyEoa(isEoa);
    _checkCurrentHero(dungStatus, msgSender, cc);

    v.currentObjectId = dungStatus.currentObject;

    (v.isCompleted, v.newCurrentStage, v.newCurrentObjectId, v.needClear) = DungeonLib.objectAction(
      dungStatus,
      dungAttributes,
      dungId,
      msgSender,
      data,
      controller, // we pass controller, not cc, because objectAction is external
      v.currentObjectId
    );

    if (v.isCompleted) {
      dungStatus.isCompleted = true;
    }

    if (v.newCurrentStage != 0) {
      dungStatus.currentStage = uint8(v.newCurrentStage);
    }

    if (v.newCurrentObjectId != v.currentObjectId) {
      dungStatus.currentObject = v.newCurrentObjectId;
    }

    if (v.needClear) {
      _clear(dungStatus, dungAttributes.biome, dungId);
    }

    // if dungeon is not ended and current object is empty we can open next object for reduce users transactions
    if (!v.isCompleted && dungStatus.currentObject == 0 && !v.needClear) {
      _openObject(cc, msgSender, dungId);
    }
  }

  /// @notice Exit from completed dungeon
  function exit(bool isEoa, IController controller, address msgSender, uint64 dungId, bool claim) internal {
    _onlyEoa(isEoa);
    DungeonLib.exitDungeon(controller, dungId, claim, msgSender);
  }
  //endregion ------------------------ DUNGEON LOGIC - USER ACTIONS

  //region ------------------------ DUNGEON LOGIC - INTERNAL LOGIC

  /// @notice Generate object for the current stage
  /// @return objectId Either uniqObj or randomly generated object if uniqObj is not specified for the stage
  function _generateObject(
    IDungeonFactory.DungeonAttributes storage dungAttributes,
    IDungeonFactory.DungeonStatus storage dungStatus,
    uint currentStage,
    IGOC goc,
    address heroToken,
    uint heroTokenId
  ) internal returns (uint32 objectId) {
    if (currentStage >= dungStatus.stages) revert IAppErrors.ErrorWrongStage(currentStage);

    objectId = dungStatus.uniqObjects[currentStage];
    if (objectId == 0) {
      IDungeonFactory.ObjectGenerateInfo memory info = dungAttributes.info;
      objectId = goc.getRandomObject(
        DungeonLib._toUint8ArrayWithoutZeroes(info.objTypesByStages[currentStage]),
        info.objChancesByStages[currentStage],
        dungAttributes.biome,
        heroToken,
        heroTokenId
      );
    }
  }

  /// @notice Clear hero info in dungeon status, add dungeon to the list of free dungeons
  function _clear(IDungeonFactory.DungeonStatus storage dungStatus, uint8 biome, uint64 dungId) internal {
    delete dungStatus.heroToken;
    delete dungStatus.heroTokenId;
    delete dungStatus.currentObject;
    delete dungStatus.currentStage;
    _addFreeDungeon(biome, dungId);
    emit IApplicationEvents.Clear(dungId);
  }

  /// @notice Check: hero is registered, not dead, in the dungeon, sender is the owner, the dungeon is not completed,
  /// controller is not paused
  /// @return heroToken Token of the hero who is in the dungeon
  /// @return heroTokenId Token ID of the hero who is in the dungeon
  function _checkCurrentHero(
    IDungeonFactory.DungeonStatus storage dungStatus,
    address msgSender,
    ControllerContextLib.ControllerContext memory cc
  ) internal view returns (address heroToken, uint heroTokenId) {

    heroToken = dungStatus.heroToken;
    heroTokenId = dungStatus.heroTokenId;

    if (dungStatus.isCompleted) revert IAppErrors.ErrorDungeonCompleted();
    _checkOwnerRegisteredNotPaused(heroToken, heroTokenId, msgSender, cc);

    if (!ControllerContextLib.getStatController(cc).isHeroAlive(heroToken, heroTokenId)) revert IAppErrors.ErrorHeroIsDead(heroToken, heroTokenId);
    if (currentDungeon(heroToken, heroTokenId) != dungStatus.dungeonId) revert IAppErrors.ErrorHeroNotInDungeon();
  }

  /// @notice Add the {dungeonId} to the list of free dungeons (available to pass) of the given {biome}
  function _addFreeDungeon(uint8 biome, uint64 dungeonId) internal {
    if (!_S().freeDungeons[biome].add(dungeonId)) revert IAppErrors.ErrorDungeonIsFreeAlready();
    emit IApplicationEvents.FreeDungeonAdded(biome, dungeonId);
  }
  //endregion ------------------------ DUNGEON LOGIC - INTERNAL LOGIC

  //region ------------------------ Utils
  /// @dev We need separate utility function for tests
  function _getBossCompletedIndex(uint8 heroBiome) internal pure returns (bytes32) {
    return bytes32(abi.encodePacked("BOSS_COMPLETED_", StringLib._toString(heroBiome)));
  }
  //endregion ------------------------ Utils

}

contracts/lib/DungeonLib.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.23;

import "../interfaces/IAppErrors.sol";
import "../interfaces/IApplicationEvents.sol";
import "../interfaces/IController.sol";
import "../interfaces/IDungeonFactory.sol";
import "../interfaces/IERC20.sol";
import "../interfaces/IERC721.sol";
import "../interfaces/IFightCalculator.sol";
import "../interfaces/IGOC.sol";
import "../interfaces/IGameToken.sol";
import "../interfaces/IHeroController.sol";
import "../interfaces/IItemController.sol";
import "../interfaces/IMinter.sol";
import "../interfaces/IReinforcementController.sol";
import "../interfaces/IStatController.sol";
import "../interfaces/IStoryController.sol";
import "../openzeppelin/EnumerableMap.sol";
import "./CalcLib.sol";
import "./ControllerContextLib.sol";
import "./PackingLib.sol";
import "./StatControllerLib.sol";
import "./StatLib.sol";

library DungeonLib {
  using EnumerableMap for EnumerableMap.AddressToUintMap;
  using EnumerableSet for EnumerableSet.UintSet;
  using CalcLib for int32;
  using PackingLib for bytes32;
  using PackingLib for uint16;
  using PackingLib for uint8;
  using PackingLib for address;
  using PackingLib for uint8[];
  using PackingLib for uint32[];
  using PackingLib for uint32;
  using PackingLib for uint64;
  using PackingLib for int32[];
  using PackingLib for int32;

  /// @dev keccak256(abi.encode(uint256(keccak256("dungeon.factory.main")) - 1)) & ~bytes32(uint256(0xff))
  bytes32 private constant DUNGEON_FACTORY_STORAGE_LOCATION = 0xae5971282b317bbed599861775fe0712755bb3b2f655bfe8fb14280d8429f600;

  /// @notice Treasure reward is available starting from level 5. We need some initial gap as protection against bots
  uint public constant MIN_LEVEL_FOR_TREASURY_DEFAULT = 5;

  /// @notice Max possible default minLevelForTreasury (= 95)
  uint internal constant MAX_NIM_LEVEL_FOR_TREASURE = StatLib.MAX_LEVEL - StatLib.BIOME_LEVEL_STEP + 1;

  //region ------------------------ Data types

  struct ObjectActionInternalData {
    address msgSender;
    address heroToken;
    IStatController statController;
    bytes data;
    uint stages;
    uint biome;
    uint heroTokenId;
    uint64 dungId;
    uint32 objectId;
    uint8 currentStage;
    bool isBattleObj;
    IGOC.ActionResult result;
    IStatController.ChangeableStats stats;
  }

  /// @notice Lazy initialization data for _claimAll
  struct ClaimContext {
    address helpHeroToken;
    address heroPayToken;
    address msgSender;
    address[] tokens;
    uint64 dungId;
    uint helpHeroId;

    /// @dev Limited by ReinforcementController._TO_HELPER_RATIO_MAX
    uint toHelperRatio;
    uint itemLength;
    uint tokenLength;
    uint[] amounts;
  }
  //endregion ------------------------ Data types

  //region ------------------------ Common

  function _S() internal pure returns (IDungeonFactory.MainState storage s) {
    assembly {
      s.slot := DUNGEON_FACTORY_STORAGE_LOCATION
    }
    return s;
  }

  /// @notice Calculate amount of treasure reward that a hero can count on
  /// @param token Treasury token
  /// @param maxAvailableBiome Max deployed biome
  /// @param treasuryBalance Total treasury of the dungeon
  /// @param heroLevel Current level of the hero
  /// @param dungeonBiome Biome to which the dungeon belongs
  function dungeonTreasuryReward(
    address token,
    uint maxAvailableBiome,
    uint treasuryBalance,
    uint heroLevel,
    uint dungeonBiome
  ) internal view returns (uint) {
    if (dungeonBiome < maxAvailableBiome) {
      return 0;
    }

    uint customMinLevel = _S().minLevelForTreasury[token];
    if (customMinLevel != 0 && heroLevel < customMinLevel) {
      return 0;
    }

    if (heroLevel > StatLib.MAX_LEVEL) revert IAppErrors.ErrorWrongLevel(heroLevel);
    if (dungeonBiome > StatLib.MAX_POSSIBLE_BIOME) revert IAppErrors.ErrorIncorrectBiome(dungeonBiome);

    uint biomeLevel = dungeonBiome * StatLib.BIOME_LEVEL_STEP;

    // CalcLib.log2((StatLib.MAX_LEVEL + 1) * 1e18);
    uint maxMultiplier = 6643856189774724682;
    uint multiplier = (maxMultiplier - CalcLib.log2((StatLib.MAX_LEVEL - biomeLevel + 1) * 1e18)) / 100;
    if (multiplier >= 1e18) revert IAppErrors.ErrorWrongMultiplier(multiplier);
    uint base = treasuryBalance * multiplier / 1e18;

    if (biomeLevel < heroLevel) {
      // reduce base on biome difference
      base = base / 2 ** (heroLevel - biomeLevel);
    }
    return base;
  }
  //endregion ------------------------ Common

  //region ------------------------ Main logic

  /// @notice Make an action with object, update hero params according results
  function objectAction(
    IDungeonFactory.DungeonStatus storage dungStatus,
    IDungeonFactory.DungeonAttributes storage dungAttributes,
    uint64 dungId,
    address msgSender,
    bytes memory data,
    IController controller,
    uint32 currentObject_
  ) external returns (
    bool isCompleted,
    uint currentStage,
    uint32 currentObject,
    bool clear
  ) {
    IGOC.ActionResult memory a;
    ControllerContextLib.ControllerContext memory cc = ControllerContextLib.init(controller);
    return _objectAction(
      ObjectActionInternalData({
        dungId: dungId,
        msgSender: msgSender,
        data: data,
        heroToken: dungStatus.heroToken,
        heroTokenId: dungStatus.heroTokenId,
        objectId: currentObject_,
        currentStage: dungStatus.currentStage,
        biome: uint(dungAttributes.biome),
        statController: ControllerContextLib.getStatController(cc),
        result: a,
        stats: IStatController.ChangeableStats(0, 0, 0, 0, 0),
        stages: uint(dungStatus.stages),
        isBattleObj: false
      }),
      dungStatus,
      dungAttributes,
      cc
    );
  }

  /// @notice Make an action with object, update hero params according results
  /// @param c Context
  /// @return isCompleted The dungeon is completed (there is no new stage to pass)
  /// @return newStage Next stage (0 if the dungeon is completed)
  /// @return currentObject Id of the current object. It's always 0 if new stage is selected (new object is not opened)
  /// @return clear True if dungStatus of the hero should be cleared and the dungeon should be added to free dungeon list
  function _objectAction(
    ObjectActionInternalData memory c,
    IDungeonFactory.DungeonStatus storage dungStatus,
    IDungeonFactory.DungeonAttributes storage dungAttributes,
    ControllerContextLib.ControllerContext memory cc
  ) internal returns (
    bool isCompleted,
    uint newStage,
    uint32 currentObject,
    bool clear
  ) {
    // isCompleted = false;
    currentObject = c.objectId;
    // newStage = 0;

    // check restrictions, most of them are checked by the caller
    if (c.objectId == 0) revert IAppErrors.ErrorNotObject2();

    c.isBattleObj = ControllerContextLib.getGameObjectController(cc).isBattleObject(c.objectId);
    c.result = ControllerContextLib.getGameObjectController(cc).action(
      c.msgSender, c.dungId, c.objectId, c.heroToken, c.heroTokenId, c.currentStage, c.data
    );

    if (c.isBattleObj) {
      _markSkillSlotsForDurabilityReduction(
        _S(),
        c.statController,
        ControllerContextLib.getItemController(cc),
        c.data,
        c.heroToken,
        c.heroTokenId
      );
    }

    c.stats = c.statController.heroStats(c.heroToken, c.heroTokenId);
    if (c.stats.mana < c.result.manaConsumed.toUint()) {
      revert IAppErrors.ErrorNotEnoughMana(c.stats.mana, c.result.manaConsumed.toUint());
    }

    if (c.result.kill || c.stats.life <= c.result.damage.toUint()) {
      c.result.kill = true;
      _changeCurrentDungeon(_S(), c.heroToken, c.heroTokenId, 0);
      IHeroController hc = ControllerContextLib.getHeroController(cc);
      hc.releaseReinforcement(c.heroToken, c.heroTokenId);

      // in case of death we need to remove rewrote objects and reset initial stages
      _resetUniqueObjects(dungStatus, dungAttributes);

      // no need to release if we completed the dungeon - we will never back on the same
      _releaseSkillSlotsForDurabilityReduction(_S(), c.heroToken, c.heroTokenId);

      // if it was the last life chance - kill the hero
      if (c.stats.lifeChances <= 1) {
        _killHero(hc, c.dungId, c.heroToken, c.heroTokenId, dungStatus.treasuryItems);
      } else {
        _afterObjCompleteForSurvivedHero(c, cc);
        _reduceLifeChances(c.statController, c.heroToken, c.heroTokenId, c.stats.life, c.stats.mana);

        // scb-1000: soft death resets used consumables
        c.statController.clearUsedConsumables(c.heroToken, c.heroTokenId);
        // also soft death reset all buffs
        c.statController.clearTemporallyAttributes(c.heroToken, c.heroTokenId);
      }

      // scb-994: increment death count counter
      uint deathCounter = c.statController.heroCustomData(c.heroToken, c.heroTokenId, StatControllerLib.DEATH_COUNT_HASH);
      c.statController.setHeroCustomData(c.heroToken, c.heroTokenId, StatControllerLib.DEATH_COUNT_HASH, deathCounter + 1);

      clear = true;
    } else {
      _increaseChangeableStats(c.statController, c.heroToken, c.heroTokenId, c.result);
      _decreaseChangeableStats(c.statController, c.heroToken, c.heroTokenId, c.result);
      _mintItems(c, cc, dungStatus.treasuryItems);
      if (c.result.completed) {
        _afterObjCompleteForSurvivedHero(c, cc);
        (isCompleted, newStage, currentObject) = _nextRoomOrComplete(c, cc, dungStatus, c.stages, dungStatus.treasuryTokens);
      }
      // clear = false;
    }

    emit IApplicationEvents.ObjectAction(c.dungId, c.result, c.currentStage, c.heroToken, c.heroTokenId, newStage);
    return (isCompleted, newStage, currentObject, clear);
  }

  //endregion ------------------------ Main logic

  //region ------------------------ Main logic - auxiliary functions

  /// @notice If hero has dead in the dungeon, it's necessary to restore initial set of unique objects,
  ///         in other words, all changes introduces by {_nextRoomOrComplete} should be thrown away.
  function _resetUniqueObjects(
    IDungeonFactory.DungeonStatus storage dungStatus,
    IDungeonFactory.DungeonAttributes storage dungAttributes
  ) internal {
    dungStatus.stages = dungAttributes.stages;
    delete dungStatus.uniqObjects;

    uint32[] memory uniqObjects = dungAttributes.uniqObjects;
    for (uint i; i < uniqObjects.length; ++i) {
      dungStatus.uniqObjects.push(uniqObjects[i]);
    }
  }

  /// @notice Kill the hero, take hero's tokens and items
  function _killHero(
    IHeroController heroController,
    uint64 dungId,
    address heroToken,
    uint heroTokenId,
    bytes32[] storage treasuryItems
  ) internal {
    (bytes32[] memory drop) = heroController.kill(heroToken, heroTokenId);

    // all hero's items are taken by the dungeon
    uint dropLength = drop.length;
    for (uint i; i < dropLength; ++i) {
      treasuryItems.push(drop[i]);
      (address itemAdr, uint itemId) = drop[i].unpackNftId();
      emit IApplicationEvents.AddTreasuryItem(dungId, itemAdr, itemId);
    }
  }

  /// @notice If battle object: reduce equipped items durability and clear temporally attributes
  /// @dev Not necessary to call if a hero is dead
  function _afterObjCompleteForSurvivedHero(
    ObjectActionInternalData memory context,
    ControllerContextLib.ControllerContext memory cc
  ) internal {
    if (context.isBattleObj) {
      // reduce equipped items durability
      ControllerContextLib.getItemController(cc).reduceDurability(context.heroToken, context.heroTokenId, uint8(context.biome));
      // clear temporally attributes
      context.statController.clearTemporallyAttributes(context.heroToken, context.heroTokenId);
    }
  }

  /// @notice Check if the dungeon is completed, calculate index of the next stage.
  /// @dev Take {rewriteNextObject} from the results of the previous action and set next objects for the dungeon
  /// @param curStages Current value of dungStatus.stages
  /// @return isCompleted The dungeon is completed
  /// @return currentStage Next stage (0 if the dungeon is completed)
  /// @return currentObj Always 0. It means, that new current object should be opened.
  function _nextRoomOrComplete(
    ObjectActionInternalData memory context,
    ControllerContextLib.ControllerContext memory cc,
    IDungeonFactory.DungeonStatus storage dungStatus,
    uint curStages,
    EnumerableMap.AddressToUintMap storage treasuryTokens
  ) internal returns (
    bool isCompleted,
    uint currentStage,
    uint32 currentObj
  ) {
    uint len = context.result.rewriteNextObject.length;

    if (context.currentStage + 1 >= curStages && len == 0) {
      _mintGameTokens(context.dungId, cc, context.stats.level, context.biome, treasuryTokens);
      isCompleted = true;
    } else {
      // need to extend stages for new rewrite objects size
      uint newStages = context.currentStage + 1 + len;
      if (curStages < newStages) {
        dungStatus.stages = uint8(newStages);

        // need to extend exist array
        dungStatus.uniqObjects = new uint32[](newStages);
        // no need to write again old uniq objects, they will be updated in case of hero death
      }

      for (uint i; i < len; ++i) {
        uint32 nextObjId = context.result.rewriteNextObject[i];
        dungStatus.uniqObjects[context.currentStage + 1 + i] = nextObjId;
      }

      currentStage = context.currentStage + 1;
    }

    // currentObj is 0 by default
    return (isCompleted, currentStage, currentObj);
  }

  /// @notice Increase life, mana and lifeChances according to the action {result}
  function _increaseChangeableStats(
    IStatController statController,
    address heroToken,
    uint heroTokenId,
    IGOC.ActionResult memory result
  ) internal {
    if (result.heal != 0 || result.manaRegen != 0 || result.experience != 0 || result.lifeChancesRecovered != 0) {
      statController.changeCurrentStats(
        heroToken,
        heroTokenId,
        IStatController.ChangeableStats({
          level: 0,
          experience: result.experience,
          life: uint32(result.heal.toUint()),
          mana: uint32(result.manaRegen.toUint()),
          lifeChances: uint32(result.lifeChancesRecovered.toUint())
        }),
        true
      );
    }
  }

  /// @notice Decrease life and mana according to the action {result}
  function _decreaseChangeableStats(
    IStatController statController,
    address heroToken,
    uint heroTokenId,
    IGOC.ActionResult memory result
  ) internal {
    // decrease changeable stats
    if (result.damage != 0 || result.manaConsumed != 0) {
      statController.changeCurrentStats(
        heroToken,
        heroTokenId,
        IStatController.ChangeableStats({
          level: 0,
          experience: 0,
          life: uint32(result.damage.toUint()),
          mana: uint32(result.manaConsumed.toUint()),
          lifeChances: 0
        }),
        false
      );
    }
  }

  /// @notice Reduce life to 1, decrease lifeChances on 1, set mana to 0
  function _reduceLifeChances(IStatController statController, address heroToken, uint heroTokenId, uint32 curLife, uint32 curMana) internal {
    statController.changeCurrentStats(
      heroToken,
      heroTokenId,
      IStatController.ChangeableStats({
        level: 0,
        experience: 0,
        life: uint32(curLife - 1),
        mana: uint32(curMana),
        lifeChances: 1
      }),
      false
    );
  }

  /// @notice Mint mint-items from {result}, add them to {treasuryItems}
  function _mintItems(
    ObjectActionInternalData memory context,
    ControllerContextLib.ControllerContext memory cc,
    bytes32[] storage treasuryItems
  ) internal {
    uint64 dungId = context.dungId;
    IGOC.ActionResult memory result = context.result;

    IItemController ic = ControllerContextLib.getItemController(cc);

    for (uint i; i < result.mintItems.length; i++) {
      if (result.mintItems[i] == address(0)) {
        continue;
      }
      uint itemId = ic.mint(result.mintItems[i], address(this));
      treasuryItems.push(result.mintItems[i].packNftId(itemId));
      emit IApplicationEvents.AddTreasuryItem(dungId, result.mintItems[i], itemId);
    }
  }

  /// @notice Register game-token in {treasuryTokens}, mint dungeon reward
  function _mintGameTokens(
    uint64 dungId,
    ControllerContextLib.ControllerContext memory cc,
    uint heroLevel,
    uint biome,
    EnumerableMap.AddressToUintMap storage treasuryTokens
  ) private {
    IGameToken gameToken = ControllerContextLib.getGameToken(cc);
    uint amount = IMinter(gameToken.minter()).mintDungeonReward(dungId, biome, heroLevel);
    _registerTreasuryToken(address(gameToken), treasuryTokens, amount);
    emit IApplicationEvents.AddTreasuryToken(dungId, address(gameToken), amount);
  }

  /// @notice Add {rewardToken} to {treasuryTokens} if it's not add there already
  function _registerTreasuryToken(address rewardToken, EnumerableMap.AddressToUintMap storage treasuryTokens, uint amount) internal {
    (bool exist, uint existAmount) = treasuryTokens.tryGet(rewardToken);

    if (!exist || existAmount + amount > 0) {
      uint balance = IERC20(rewardToken).balanceOf(address(this));
      if (balance < existAmount + amount) {
        revert IAppErrors.NotEnoughTokens(balance, existAmount + amount);
      }

      treasuryTokens.set(rewardToken, existAmount + amount);
    }
  }
  //endregion ------------------------ Main logic - auxiliary functions

  //region ------------------------ ENTER/EXIT

  /// @notice Hero enters to the dungeon. Check requirements before entering, update status of the hero and the dungeon.
  function _enter(
    ControllerContextLib.ControllerContext memory cc,
    IDungeonFactory.DungeonStatus storage dungStatus,
    IDungeonFactory.DungeonAttributes storage dungAttrs,
    uint16 dungNum,
    uint64 dungId,
    address heroToken,
    uint heroTokenId
  ) internal {
    IDungeonFactory.MainState storage s = _S();

    IStatController.ChangeableStats memory stats = ControllerContextLib.getStatController(cc).heroStats(heroToken, heroTokenId);
    uint8 dungBiome = dungAttrs.biome;

    if (ControllerContextLib.getReinforcementController(cc).isStaked(heroToken, heroTokenId)) revert IAppErrors.Staked(heroToken, heroTokenId);
    if (stats.lifeChances == 0) revert IAppErrors.ErrorHeroIsDead(heroToken, heroTokenId);
    if (s.heroCurrentDungeon[heroToken.packNftId(heroTokenId)] != 0) revert IAppErrors.ErrorAlreadyInDungeon();
    // assume here that onlyEnteredHeroOwner is already checked by the caller

    if (ControllerContextLib.getHeroController(cc).heroBiome(heroToken, heroTokenId) != dungBiome) revert IAppErrors.ErrorNotBiome();
    if (dungStatus.heroToken != address(0)) revert IAppErrors.ErrorDungeonBusy();
    if (!isDungeonEligibleForHero(s, ControllerContextLib.getStatController(cc), dungNum, uint8(stats.level), heroToken, heroTokenId)) {
      revert IAppErrors.ErrorNotEligible(heroToken, dungNum);
    }

    // remove free dungeon
    if (s.freeDungeons[dungBiome].remove(uint(dungId))) {
      emit IApplicationEvents.FreeDungeonRemoved(dungBiome, dungId);
    }

    _changeCurrentDungeon(s, heroToken, heroTokenId, dungId);
    if (dungStatus.currentStage != 0) {
      dungStatus.currentStage = uint8(0);
    }
    dungStatus.heroToken = heroToken;
    dungStatus.heroTokenId = heroTokenId;

    emit IApplicationEvents.Entered(dungId, heroToken, heroTokenId);
  }

  /// @notice Check if dungeon is eligible for the hero
  /// @param dungNum Dungeon logic id
  function isDungeonEligibleForHero(
    IDungeonFactory.MainState storage s,
    IStatController statController,
    uint16 dungNum,
    uint8 heroLevel,
    address heroToken,
    uint heroTokenId
  ) internal view returns (bool) {
    IDungeonFactory.DungeonAttributes storage dungAttr = s.dungeonAttributes[dungNum];

    // check if the hero level is in the range required by the dungeon
    {
      (uint minLevel, uint maxLevel,) = dungAttr.minMaxLevel.unpackUint8Array3();
      if (heroLevel < minLevel || heroLevel > maxLevel) {
        return false;
      }
    }

    // check if hero/global custom values are in the ranges required by the dungeon
    bytes32[] memory requiredCustomDataIndex = dungAttr.requiredCustomDataIndex;
    bytes32[] memory requiredCustomDataValue = dungAttr.requiredCustomDataValue;

    uint len = requiredCustomDataIndex.length;
    for (uint i; i < len; ++i) {
      bytes32 index = requiredCustomDataIndex[i];
      if (index == bytes32(0)) continue;

      (uint64 min, uint64 max, bool isHeroValue) = requiredCustomDataValue[i].unpackCustomDataRequirements();

      uint value = isHeroValue
        ? statController.heroCustomData(heroToken, heroTokenId, index)
        : statController.globalCustomData(index);

      if (value < uint(min) || value > uint(max)) {
        return false;
      }
    }

    return true;
  }

  /// @notice Select logic for the new dungeon
  function getDungeonLogic(
    IDungeonFactory.MainState storage s_,
    ControllerContextLib.ControllerContext memory cc,
    uint8 heroLevel,
    address heroToken,
    uint heroTokenId,
    uint random
  ) internal view returns (uint16) {
    if (heroLevel == 0) revert IAppErrors.ErrorHeroLevelStartFrom1();

    uint8 heroBiome;
    {
      IHeroController hc = ControllerContextLib.getHeroController(cc);
      heroBiome = hc.heroBiome(heroToken, heroTokenId);

      // try to get specific dungeon
      // specific dungeon for concrete level and class
      uint16 specificDungeon = s_.dungeonSpecific[_toUint8PackedArray(heroLevel / uint8(StatLib.BIOME_LEVEL_STEP) + 1, hc.heroClass(heroToken))];
      // if no specific dungeon for concrete class try to find for all classes
      if (specificDungeon == 0) {
        specificDungeon = s_.dungeonSpecific[_toUint8PackedArray(heroLevel / uint8(StatLib.BIOME_LEVEL_STEP) + 1, 0)];
      }
      // if no specific dungeon for concrete class and level try to find for all classes and all levels
      if (specificDungeon == 0) {
        // in this case we have 1 specific dungeon for all classes and levels, and only 1, suppose to be initial territory
        specificDungeon = s_.dungeonSpecific[_toUint8PackedArray(0, 0)];
      }

      if (specificDungeon != 0) {
        if (!s_.specificDungeonCompleted[heroToken.packDungeonKey(uint64(heroTokenId), specificDungeon)]
        && s_.dungeonAttributes[specificDungeon].biome == heroBiome) {
          return specificDungeon;
        }
      }
    }

    EnumerableSet.UintSet storage dungs = s_.dungeonsLogicByBiome[heroBiome];
    uint size = dungs.length();
    if (size == 0) revert IAppErrors.ErrorNoDungeonsForBiome(heroBiome);

    IStatController statController = ControllerContextLib.getStatController(cc);
    uint16 dungeonLogic;
    uint dungeonIndex = random % size;
    for (uint i; i < size; ++i) {
      dungeonLogic = uint16(dungs.at(dungeonIndex));

      if (isDungeonEligibleForHero(s_, statController, dungeonLogic, heroLevel, heroToken, heroTokenId)) {
        return dungeonLogic;
      }
      dungeonIndex++;
      if (dungeonIndex >= size) {
        dungeonIndex = 0;
      }
    }

    revert IAppErrors.ErrorNoEligibleDungeons();
  }

  /// @notice Exit the dungeon
  /// @param claim Claim treasure items and tokens
  function exitDungeon(IController controller, uint64 dungId, bool claim, address msgSender) external {
    IDungeonFactory.DungeonStatus storage dungStatus = _S().dungeonStatuses[dungId];
    ControllerContextLib.ControllerContext memory cc = ControllerContextLib.init(controller);
    address heroToken = dungStatus.heroToken;
    uint heroTokenId = dungStatus.heroTokenId;

    if (!dungStatus.isCompleted) revert IAppErrors.ErrorNotCompleted();
    if (controller.onPause()) revert IAppErrors.ErrorPaused();
    if (IERC721(heroToken).ownerOf(heroTokenId) != msgSender) revert IAppErrors.ErrorNotHeroOwner(heroToken, msgSender);
    if (_S().heroCurrentDungeon[heroToken.packNftId(heroTokenId)] != dungId) revert IAppErrors.ErrorHeroNotInDungeon();

    IHeroController heroController = ControllerContextLib.getHeroController(cc);
    (address payToken,) = heroController.payTokenInfo(heroToken);

    _setDungeonCompleted(_S(), dungStatus.dungNum, dungId, heroToken, heroTokenId);

    if (claim) {
      _claimAll(cc, msgSender, dungId, dungStatus, heroToken, heroTokenId, payToken);
    }
    _heroExit(_S(), heroController, heroToken, heroTokenId);

    if (payToken == address(0)) {
      // F2P hero doesn't have pay token, he is destroyed after exit of the dungeon
      _killHero(heroController, dungId, heroToken, heroTokenId, dungStatus.treasuryItems);
    }

    emit IApplicationEvents.Exit(dungId, claim);
  }

  /// @notice Emergency exit: the governance can drop the hero from dungeon in emergency
  function emergencyExit(IController controller, uint64 dungId) external {
    IDungeonFactory.MainState storage s = _S();
    // assume that governance-restriction is checked on caller side
    IDungeonFactory.DungeonStatus storage dungStatus = s.dungeonStatuses[dungId];

    _heroExit(s, IHeroController(controller.heroController()), dungStatus.heroToken, dungStatus.heroTokenId);

    dungStatus.isCompleted = true;
    dungStatus.heroToken = address(0);
    dungStatus.heroTokenId = 0;

    emit IApplicationEvents.Exit(dungId, false);
  }

  //endregion ------------------------ ENTER/EXIT

  //region ------------------------ ENTER/EXIT auxiliary functions
  /// @dev this function should emit event to indicate dungeon remove
  function _setDungeonCompleted(IDungeonFactory.MainState storage s, uint16 dungNum, uint64 dungeonId, address heroToken, uint heroTokenId) internal {
    if (s.allSpecificDungeons.contains(dungNum)) {
      s.specificDungeonCompleted[heroToken.packDungeonKey(uint64(heroTokenId), dungNum)] = true;
    }
    emit IApplicationEvents.DungeonCompleted(dungNum, dungeonId, heroToken, heroTokenId);
  }

  /// @notice Change current dungeon of the hero to 0 and release his reinforcement
  function _heroExit(IDungeonFactory.MainState storage s, IHeroController heroController, address heroToken, uint heroTokenId) internal {
    _changeCurrentDungeon(s, heroToken, heroTokenId, 0);
    heroController.releaseReinforcement(heroToken, heroTokenId);
  }

  /// @notice Change current dungeon of the hero to the {dungeonId}
  function _changeCurrentDungeon(IDungeonFactory.MainState storage s, address hero, uint heroId, uint64 dungeonId) internal {
    s.heroCurrentDungeon[hero.packNftId(heroId)] = dungeonId;
    emit IApplicationEvents.HeroCurrentDungeonChanged(hero, heroId, dungeonId);
  }

  /// @notice Enumerate busy slots of the hero, find all SKILL_XXX and return their addresses and ids
  /// @return skillSlotAdr Addresses of available skills. 0 - SKILL_1, 1 - SKILL_2, 2 - SKILL_3.
  ///                      Address is zero if the hero doesn't have the corresponded skill.
  /// @return skillSlotIds Ids of available skills. 0 - SKILL_1, 1 - SKILL_2, 2 - SKILL_3
  ///                      ID is zero if the hero doesn't have the corresponded skill.
  function _getSkillSlotsForHero(IStatController statCtr, address heroToken, uint heroTokenId) internal view returns (
    address[3] memory skillSlotAdr,
    uint[3] memory skillSlotIds
  ) {
    uint8[] memory busySlots = statCtr.heroItemSlots(heroToken, uint64(heroTokenId));

    for (uint i; i < busySlots.length; ++i) {
      if (busySlots[i] == uint8(IStatController.ItemSlots.SKILL_1)) {
        (skillSlotAdr[0], skillSlotIds[0]) = statCtr.heroItemSlot(heroToken, uint64(heroTokenId), busySlots[i]).unpackNftId();
      }
      if (busySlots[i] == uint8(IStatController.ItemSlots.SKILL_2)) {
        (skillSlotAdr[1], skillSlotIds[1]) = statCtr.heroItemSlot(heroToken, uint64(heroTokenId), busySlots[i]).unpackNftId();
      }
      if (busySlots[i] == uint8(IStatController.ItemSlots.SKILL_3)) {
        (skillSlotAdr[2], skillSlotIds[2]) = statCtr.heroItemSlot(heroToken, uint64(heroTokenId), busySlots[i]).unpackNftId();
      }
    }

    return (skillSlotAdr, skillSlotIds);
  }

  /// @notice Generate map[3] for SKILL_1, SKILL_2, SKILL_3 (0 - not marked, 1 - marked)
  ///         and save the map to {s_}._skillSlotsForDurabilityReduction as packed uint8[]
  /// @dev mark skill slots for durability reduction
  /// SIP-001: take into account hero's skills only and ignore skills of the helper
  /// @param data abi.encoded IFightCalculator.AttackInfo
  function _markSkillSlotsForDurabilityReduction(
    IDungeonFactory.MainState storage s_,
    IStatController sc,
    IItemController itemController,
    bytes memory data,
    address heroToken,
    uint heroTokenId
  ) internal {
    uint8[] memory map = new uint8[](3);
    (IFightCalculator.AttackInfo memory attackInfo) = abi.decode(data, (IFightCalculator.AttackInfo));

    uint length = attackInfo.skillTokens.length;

    if (length != 0 || attackInfo.attackToken != address(0)) {

      (address[3] memory skillSlotAdr, uint[3] memory skillSlotIds) = _getSkillSlotsForHero(sc, heroToken, heroTokenId);

      for (uint i; i < length; ++i) {
        address token = attackInfo.skillTokens[i];
        uint tokenId = attackInfo.skillTokenIds[i];

        // The hero is able to use own skills OR the skills of the helper. Take into account only own hero's skills here
        (address h, ) = itemController.equippedOn(token, tokenId);
        if (h == heroToken) {
          if (token == skillSlotAdr[0] && tokenId == skillSlotIds[0]) {
            map[0] = 1;
          } else if (token == skillSlotAdr[1] && tokenId == skillSlotIds[1]) {
            map[1] = 1;
          } else if (token == skillSlotAdr[2] && tokenId == skillSlotIds[2]) {
            map[2] = 1;
          }
        }
      }

      if (attackInfo.attackToken == skillSlotAdr[0] && attackInfo.attackTokenId == skillSlotIds[0]) {
        map[0] = 1;
      } else if (attackInfo.attackToken == skillSlotAdr[1] && attackInfo.attackTokenId == skillSlotIds[1]) {
        map[1] = 1;
      } else if (attackInfo.attackToken == skillSlotAdr[2] && attackInfo.attackTokenId == skillSlotIds[2]) {
        map[2] = 1;
      }
    }

    // write even empty map for clear prev values
    s_.skillSlotsForDurabilityReduction[heroToken.packNftId(heroTokenId)] = map.packUint8Array();
  }

  /// @dev clear all skill slots marks
  function _releaseSkillSlotsForDurabilityReduction(IDungeonFactory.MainState storage s_, address heroToken, uint heroTokenId) internal {
    delete s_.skillSlotsForDurabilityReduction[heroToken.packNftId(heroTokenId)];
  }

  //endregion ------------------------ ENTER/EXIT auxiliary functions

  //region ------------------------ CLAIM

  /// @notice Claim all treasure tokens and items registered for the given hero.
  ///         The tokens are send to msgSender and/or helper, or they can be send to controller or burned.
  ///         The items are transferred to msgSender or helper (random choice) or destroyed (F2P hero).
  /// @dev ClaimContext is used both for lazy initialization and to extend limits of allowed local vars.
  /// @param heroPayToken Hero pay token. It's zero for hero 5.
  function _claimAll(
    ControllerContextLib.ControllerContext memory cc,
    address msgSender,
    uint64 dungId,
    IDungeonFactory.DungeonStatus storage dungStatus,
    address heroToken,
    uint heroTokenId,
    address heroPayToken
  ) internal {
    ClaimContext memory context;

    context.msgSender = msgSender;
    context.dungId = dungId;

    (context.helpHeroToken, context.helpHeroId) = ControllerContextLib.getHeroController(cc).heroReinforcementHelp(heroToken, heroTokenId);
    context.toHelperRatio = ControllerContextLib.getReinforcementController(cc).toHelperRatio(context.helpHeroToken, context.helpHeroId);

    context.itemLength = dungStatus.treasuryItems.length;
    context.tokenLength = dungStatus.treasuryTokens.length();
    context.tokens = new address[](context.tokenLength);
    context.amounts = new uint[](context.tokenLength);

    context.heroPayToken = heroPayToken;

    // need to write tokens separately coz we need to delete them from map
    for (uint i; i < context.tokenLength; i++) {
      (context.tokens[i], context.amounts[i]) = dungStatus.treasuryTokens.at(i);
    }

    for (uint i; i < context.tokenLength; i++) {
      _claimToken(dungStatus.treasuryTokens, context, cc, context.tokens[i], context.amounts[i]);
    }

    for (uint i; i < context.itemLength; i++) {
      (address itemAdr, uint itemId) = dungStatus.treasuryItems[i].unpackNftId();
      _claimItem(context, cc, itemAdr, itemId);
    }

    delete dungStatus.treasuryItems;
  }

  /// @notice Remove {token} from treasuryTokens, transfer/burn token {amount}
  function _claimToken(
    EnumerableMap.AddressToUintMap storage treasuryTokens,
    ClaimContext memory context,
    ControllerContextLib.ControllerContext memory cc,
    address token,
    uint amount
  ) internal {
    treasuryTokens.remove(token);
    if (amount != 0) {

      if (context.heroPayToken == address(0)) {
        if (token == address(ControllerContextLib.getGameToken(cc)) ) {
          IGameToken(token).burn(amount);
        } else {
          IERC20(token).transfer(address(cc.controller), amount);
        }

      } else {
        uint toHelper = context.helpHeroToken == address(0)
          ? 0
          : amount * context.toHelperRatio / 100;

        uint toHeroOwner = amount - toHelper;
        if (toHeroOwner != 0) {
          IERC20(token).transfer(context.msgSender, toHeroOwner);
        }

        if (toHelper != 0) {
          IReinforcementController reinforcementController = ControllerContextLib.getReinforcementController(cc);
          IERC20(token).transfer(address(reinforcementController), toHelper);
          reinforcementController.registerTokenReward(context.helpHeroToken, context.helpHeroId, token, toHelper);
        }

        emit IApplicationEvents.ClaimToken(context.dungId, token, amount);
      }
    }
  }

  /// @notice Destroy item (for F2P) or transfer the item to helper/sender (random choice)
  function _claimItem(
    ClaimContext memory context,
    ControllerContextLib.ControllerContext memory cc,
    address token,
    uint tokenId
  ) internal {
    if (IERC721(token).ownerOf(tokenId) == address(this)) {

      if (context.heroPayToken == address(0)) {
        // if it is F2P hero destroy all drop
        ControllerContextLib.getItemController(cc).destroy(token, tokenId);
      } else {

        bool toHelper = false;
        if (context.helpHeroToken != address(0)) {
          toHelper = ControllerContextLib.getOracle(cc).getRandomNumber(100, 0) < context.toHelperRatio;
        }

        if (toHelper) {
          IReinforcementController reinforcementController = ControllerContextLib.getReinforcementController(cc);
          IERC721(token).safeTransferFrom(address(this), address(reinforcementController), tokenId);
          reinforcementController.registerNftReward(context.helpHeroToken, context.helpHeroId, token, tokenId);
        } else {
          IERC721(token).safeTransferFrom(address(this), context.msgSender, tokenId);
        }

        emit IApplicationEvents.ClaimItem(context.dungId, token, tokenId);
      }
    }
  }
  //endregion ------------------------ CLAIM

  //region ------------------------ Utils
  function _toUint8PackedArray(uint8 val0, uint8 val1) internal pure returns (bytes32 key) {
    return PackingLib.packUint8Array3(val0, val1, 0);
  }

  function _toUint8ArrayWithoutZeroes(bytes32 data) internal pure returns (uint8[] memory result) {
    uint8[] memory arr = data.unpackUint8Array();

    uint newSize;
    for (uint i; i < arr.length; ++i) {
      if (arr[i] == 0) {
        break;
      }
      newSize++;
    }

    result = new uint8[](newSize);
    for (uint i; i < newSize; ++i) {
      result[i] = arr[i];
    }
  }
  //endregion ------------------------ Utils
}

contracts/lib/EventLib.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.23;

import "./CalcLib.sol";
import "./PackingLib.sol";
import "./ItemLib.sol";
import "./StoryLib.sol";
import "../interfaces/IStatController.sol";
import "../interfaces/IOracle.sol";
import "../interfaces/IGOC.sol";
import "../interfaces/IAppErrors.sol";
import "../interfaces/IApplicationEvents.sol";

library EventLib {
  using CalcLib for int32;
  using PackingLib for bytes32;
  using PackingLib for bytes32[];
  using PackingLib for uint16;
  using PackingLib for uint8;
  using PackingLib for address;
  using PackingLib for uint32[];
  using PackingLib for uint32;
  using PackingLib for uint64;
  using PackingLib for int32[];
  using PackingLib for int32;

  //region ------------------------ Main logic

  function action(IGOC.ActionContext calldata ctx, IGOC.EventInfo storage info) external returns (
    IGOC.ActionResult memory
  ) {
    (bool accept) = abi.decode(ctx.data, (bool));
    return accept
      ? _eventAcceptResult(ctx, info)
      : _noActionResult();
  }

  /// @notice Save data from {regInfo} to {info}
  function eventRegInfoToInfo(IGOC.EventRegInfo calldata regInfo, IGOC.EventInfo storage info) external {
    info.goodChance = regInfo.goodChance;
    info.goodAttributes = regInfo.goodAttributes.values.toBytes32ArrayWithIds(regInfo.goodAttributes.ids);
    info.badAttributes = regInfo.badAttributes.values.toBytes32ArrayWithIds(regInfo.badAttributes.ids);
    info.statsChange = regInfo.experience.packStatsChange(
      regInfo.heal,
      regInfo.manaRegen,
      regInfo.lifeChancesRecovered,
      regInfo.damage,
      regInfo.manaConsumed
    );

    bytes32[] memory mintItems = new bytes32[](regInfo.mintItems.length);

    for (uint i; i < mintItems.length; ++i) {
      mintItems[i] = regInfo.mintItems[i].packItemMintInfo(regInfo.mintItemsChances[i]);
    }
    info.mintItems = mintItems;
  }
  //endregion ------------------------ Main logic

  //region ------------------------ Internal logic
  function _eventAcceptResult(IGOC.ActionContext calldata ctx, IGOC.EventInfo storage info) internal returns (
    IGOC.ActionResult memory result
  ) {
    IStatController sc = IStatController(ctx.controller.statController());

    IStatController.ActionInternalInfo memory gen = _generate(ctx, info, sc);

    if (gen.posAttributes.length != 0) {
      sc.changeBonusAttributes(IStatController.ChangeAttributesInfo({
        heroToken: ctx.heroToken,
        heroTokenId: ctx.heroTokenId,
        changeAttributes: gen.posAttributes,
        add: true,
        temporally: true
      }));
    }

    if (gen.negAttributes.length != 0) {
      sc.changeBonusAttributes(IStatController.ChangeAttributesInfo({
        heroToken: ctx.heroToken,
        heroTokenId: ctx.heroTokenId,
        changeAttributes: gen.negAttributes,
        add: true,
        temporally: true
      }));
    }

    // refreshed stats
    IStatController.ChangeableStats memory stats = sc.heroStats(ctx.heroToken, ctx.heroTokenId);

    result.completed = true;
    result.experience = gen.experience;
    result.heal = gen.heal;
    result.manaRegen = gen.manaRegen;
    result.lifeChancesRecovered = gen.lifeChancesRecovered;
    result.damage = gen.damage;
    result.manaConsumed = CalcLib.minI32(gen.manaConsumed, int32(stats.mana));
    result.mintItems = gen.mintedItems;

    if (stats.life <= gen.damage.toUint()) {
      result.kill = true;
    }

    emit IApplicationEvents.EventResult(ctx.dungeonId, ctx.heroToken, ctx.heroTokenId, ctx.stageId, gen, ctx.iteration);
    return result;
  }

  /// @notice Generate empty result structure, only "completed" is true
  function _noActionResult() internal pure returns (IGOC.ActionResult memory result) {
    result.completed = true;
    return result;
  }

  /// @notice Generate either positive or negative attributes, mint single item in any case
  function _generate(IGOC.ActionContext calldata ctx, IGOC.EventInfo storage info, IStatController sc) internal returns (
    IStatController.ActionInternalInfo memory result
  ) {
    uint32 goodChance = info.goodChance;
    if (goodChance > CalcLib.MAX_CHANCE) revert IAppErrors.TooHighChance(goodChance);

    IOracle oracle = IOracle(ctx.controller.oracle());

    uint random = goodChance == CalcLib.MAX_CHANCE ? CalcLib.MAX_CHANCE : oracle.getRandomNumber(CalcLib.MAX_CHANCE, 0);
    if (random <= goodChance) {
      result.posAttributes = StoryLib._generateAttributes(info.goodAttributes);
      (result.experience,
        result.heal,
        result.manaRegen,
        result.lifeChancesRecovered,,) = info.statsChange.unpackStatsChange();
    } else {
      result.negAttributes = StoryLib._generateAttributes(info.badAttributes);
      (,,,, result.damage, result.manaConsumed) = info.statsChange.unpackStatsChange();
    }

    // always mint possible items even if bad result
    result.mintedItems = _mintRandomItem(ctx, info, oracle, sc, CalcLib.nextPrng);

    return result;
  }

  /// @notice Mint single random item
  /// @param nextPrng_ CalcLib.nextPrng, param is required by unit tests
  function _mintRandomItem(
    IGOC.ActionContext calldata ctx,
    IGOC.EventInfo storage info,
    IOracle oracle,
    IStatController sc,
    function (LibPRNG.PRNG memory, uint) internal view returns (uint) nextPrng_
  ) internal returns (address[] memory minted) {
    bytes32[] memory mintItemsPacked = info.mintItems;
    if (mintItemsPacked.length == 0) {
      return minted;
    }

    IStatController.ChangeableStats memory stats = sc.heroStats(ctx.heroToken, ctx.heroTokenId);

    address[] memory mintItems = new address[](mintItemsPacked.length);
    uint32[] memory mintItemsChances = new uint32[](mintItemsPacked.length);

    for (uint i = 0; i < mintItemsPacked.length; i++) {
      (mintItems[i], mintItemsChances[i]) = mintItemsPacked[i].unpackItemMintInfo();
    }

    return ItemLib._mintRandomItems(
      ItemLib.MintItemInfo({
        mintItems: mintItems,
        mintItemsChances: mintItemsChances,
        biome: ctx.biome,
        amplifier: 0,
        seed: 0,
        oracle: oracle,
        heroExp: stats.experience,
        heroCurrentLvl: uint8(stats.level),
        magicFind: 0,
        destroyItems: 0,
        maxItems: 1 // MINT ONLY 1 ITEM!
      }),
      nextPrng_
    );
  }
  //endregion ------------------------ Internal logic
}

contracts/lib/FightLib.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.23;

import "../interfaces/IStatController.sol";
import "../interfaces/IItemController.sol";
import "../interfaces/IFightCalculator.sol";
import "../interfaces/IAppErrors.sol";
import "../interfaces/IApplicationEvents.sol";
import "../lib/StatLib.sol";
import "../lib/CalcLib.sol";
import "../lib/PackingLib.sol";

library FightLib {
  using PackingLib for bytes32;
  using CalcLib for int32;

  //region ------------------------ Data types
  struct AttackResult {
    int32 defenderHealth;
    int32 damage;
    int32 lifeStolen;
    int32 reflectDamage;
    uint8 critical;
    uint8 missed;
    uint8 blocked;
  }
  //endregion ------------------------ Data types

  //region ------------------------ Constants
  uint internal constant MAX_FIGHT_CYCLES = 100;
  int32 internal constant RESISTANCE_DENOMINATOR = 100;
  int32 internal constant _MAX_RESIST = 90;

  //endregion ------------------------ Constants

  //region ------------------------ Main logic

  /// @dev Items ownership must be checked before
  ///      it is no write actions but we need to emit an event for properly handle the battle on UI
  ///      return huge structs more expensive that call an event here
  /// @param random_ Pass _pseudoRandom here, param is required for unit tests
  function fight(
    IItemController ic,
    IFightCalculator.FightCall memory callData,
    function (uint) internal view returns (uint) random_
  ) internal returns (
    IFightCalculator.FightResult memory
  ) {
    IFightCalculator.FightInfoInternal memory fResult = prepareFightInternalInfo(ic, callData.fighterA, callData.fighterB);

    fightProcessing(fResult, random_);

    emit IApplicationEvents.FightResultProcessed(msg.sender, fResult, callData, callData.iteration);

    return IFightCalculator.FightResult({
      healthA: fResult.fighterA.health,
      healthB: fResult.fighterB.health,
      manaConsumedA: fResult.fighterA.manaConsumed,
      manaConsumedB: fResult.fighterB.manaConsumed
    });
  }
  //endregion ------------------------ Main logic

  //region ------------------------ High level of internal logic
  function fightProcessing(
    IFightCalculator.FightInfoInternal memory fResult,
    function (uint) internal view returns (uint) random_
  ) internal view {

    bool firstA = calcFirstHit(fResult);

    setStatuses(fResult, firstA, random_);
    setStatuses(fResult, !firstA, random_);

    reduceAttributesByStatuses(fResult.fighterA.info.fighterAttributes, fResult.fighterA.statuses, fResult.fighterB.info.fighterAttributes);
    reduceAttributesByStatuses(fResult.fighterB.info.fighterAttributes, fResult.fighterB.statuses, fResult.fighterA.info.fighterAttributes);

    AttackResult memory resultA = processAttack(fResult, true, random_);
    AttackResult memory resultB = processAttack(fResult, false, random_);

    fResult.fighterA.statuses.gotCriticalHit = resultA.critical != 0;
    fResult.fighterA.statuses.missed = resultA.missed != 0;
    fResult.fighterA.statuses.hitBlocked = resultA.blocked != 0;

    fResult.fighterB.statuses.gotCriticalHit = resultB.critical != 0;
    fResult.fighterB.statuses.missed = resultB.missed != 0;
    fResult.fighterB.statuses.hitBlocked = resultB.blocked != 0;

    reduceHp(
      firstA ? resultA : resultB,
      firstA ? resultB : resultA,
      firstA ? fResult.fighterA : fResult.fighterB,
      firstA ? fResult.fighterB : fResult.fighterA
    );

    // restore health from stolen life
    stealLife(fResult.fighterA, resultA);
    stealLife(fResult.fighterB, resultB);
  }

  function processAttack(
    IFightCalculator.FightInfoInternal memory fResult,
    bool isA,
    function (uint) internal view returns (uint) random_
  ) internal view returns (AttackResult memory attackResult) {

    int32 defenderHealth = isA ? fResult.fighterB.health : fResult.fighterA.health;

    if (skipTurn(fResult, isA)) {
      return AttackResult({
        defenderHealth: defenderHealth,
        damage: 0,
        lifeStolen: 0,
        reflectDamage: 0,
        critical: 0,
        missed: 0,
        blocked: 0
      });
    }

    IFightCalculator.FighterInfo memory attackerInfo = isA ? fResult.fighterA.info : fResult.fighterB.info;
    IFightCalculator.FighterInfo memory defenderInfo = isA ? fResult.fighterB.info : fResult.fighterA.info;

    if (attackerInfo.attackType == IFightCalculator.AttackType.MELEE) {
      attackResult = meleeDamageCalculation(attackerInfo, defenderInfo, defenderHealth, random_);
    } else if (attackerInfo.attackType == IFightCalculator.AttackType.MAGIC) {
      attackResult = magicDamageCalculation(
        attackerInfo,
        defenderInfo,
        isA ? fResult.fighterA.magicAttack : fResult.fighterB.magicAttack,
        defenderHealth,
        random_
      );
    } else {
      revert IAppErrors.NotAType(uint(attackerInfo.attackType));
    }
  }
  //endregion ------------------------ High level of internal logic

  //region ------------------------ Internal logic
  function prepareFightInternalInfo(
    IItemController ic,
    IFightCalculator.FighterInfo memory fighterA,
    IFightCalculator.FighterInfo memory fighterB
  ) internal view returns (IFightCalculator.FightInfoInternal memory) {
    IFightCalculator.FightInfoInternal memory fInfo;
    _setFightData(ic, fighterA, fInfo.fighterA);
    _setFightData(ic, fighterB, fInfo.fighterB);
    return fInfo;
  }

  /// @dev A part of prepareFightInternalInfo
  function _setFightData(
    IItemController ic,
    IFightCalculator.FighterInfo memory fighter,
    IFightCalculator.Fighter memory dest
  ) internal view {
    dest.info = fighter;
    dest.health = int32(fighter.fighterStats.life);
    if (fighter.attackToken != address(0)) {
      if (fighter.attackType != IFightCalculator.AttackType.MAGIC) revert IAppErrors.NotMagic();
      dest.magicAttack = ic.itemAttackInfo(fighter.attackToken, fighter.attackTokenId);
    }
    // dest.manaConsumed is 0 by default, in current implementation we don't need to change it
  }

  /// @param random_ Either _pseudoRandom or pseudo-random for ut
  function statusChance(
    IFightCalculator.FighterInfo memory attackerInfo,
    IItemController.AttackInfo memory attackerMA,
    IStatController.ATTRIBUTES index,
    int32 resist,
    function (uint) internal view returns (uint) random_
  ) internal view returns (bool) {
    int32 chance = _getChance(attackerInfo, attackerMA.aType, index, resist);
    if (chance == 0) {
      return false;
    }
    if (chance >= RESISTANCE_DENOMINATOR) {
      return true;
    }
    return random_(RESISTANCE_DENOMINATOR.toUint()) < chance.toUint();
  }

  /// @notice set fResult.fighterB.statuses (for isA = true) or fResult.fighterA.statuses (for isA = false)
  /// @param random_ Either _pseudoRandom or pseudo-random for ut
  function setStatuses(
    IFightCalculator.FightInfoInternal memory fResult,
    bool isA,
    function (uint) internal view returns (uint) random_
  ) internal view {
    // setStatuses is called twice one by one: first time for A, second time for B
    // if stun is set for A, setStatuses is skipped for B completely
    if (!skipTurn(fResult, isA)) {
      IFightCalculator.FighterInfo memory attackerInfo = isA ? fResult.fighterA.info : fResult.fighterB.info;
      IFightCalculator.FighterInfo memory defenderInfo = isA ? fResult.fighterB.info : fResult.fighterA.info;

      IItemController.AttackInfo memory attackerMA = isA ? fResult.fighterA.magicAttack : fResult.fighterB.magicAttack;

      IFightCalculator.Statuses memory statuses = isA ? fResult.fighterB.statuses : fResult.fighterA.statuses;

      int32 resist = defenderInfo.fighterAttributes[uint(IStatController.ATTRIBUTES.RESIST_TO_STATUSES)];

      statuses.stun = statusChance(attackerInfo, attackerMA, IStatController.ATTRIBUTES.STUN, resist, random_);
      statuses.burn = statusChance(
        attackerInfo,
        attackerMA,
        IStatController.ATTRIBUTES.BURN,
        defenderInfo.fighterAttributes[uint(IStatController.ATTRIBUTES.FIRE_RESISTANCE)],
        random_
      );
      statuses.freeze = statusChance(
        attackerInfo,
        attackerMA,
        IStatController.ATTRIBUTES.FREEZE,
        defenderInfo.fighterAttributes[uint(IStatController.ATTRIBUTES.COLD_RESISTANCE)],
        random_
      );
      statuses.confuse = statusChance(attackerInfo, attackerMA, IStatController.ATTRIBUTES.CONFUSE, resist, random_);
      statuses.curse = statusChance(attackerInfo, attackerMA, IStatController.ATTRIBUTES.CURSE, resist, random_);
      statuses.poison = statusChance(attackerInfo, attackerMA, IStatController.ATTRIBUTES.POISON, resist, random_);
    }
  }

  function magicDamageCalculation(
    IFightCalculator.FighterInfo memory attackerInfo,
    IFightCalculator.FighterInfo memory defenderInfo,
    IItemController.AttackInfo memory magicAttack,
    int32 defenderHealth,
    function (uint) internal view returns (uint) random_
  ) internal view returns (AttackResult memory attackResult) {
    // generate damage
    int32 damage = getMagicDamage(
      attackerInfo,
      magicAttack,
      CalcLib.pseudoRandomInRangeFlex(magicAttack.min.toUint(), magicAttack.max.toUint(), random_)
    );
    damage = increaseMagicDmgByFactor(damage, attackerInfo, magicAttack.aType);
    damage = increaseRaceDmg(damage, attackerInfo, defenderInfo.race);
    bool critical = isCriticalHit(attackerInfo, random_(RESISTANCE_DENOMINATOR.toUint()));
    damage = critical ? damage * 2 : damage;

    // decrease damage
    damage = decreaseRaceDmg(damage, defenderInfo, attackerInfo.race);
    damage = decreaseDmgByDmgReduction(damage, defenderInfo);

    if (magicAttack.aType == IItemController.AttackType.FIRE) {
      damage -= _calcDmgInline(damage, defenderInfo, IStatController.ATTRIBUTES.FIRE_RESISTANCE);
    } else if (magicAttack.aType == IItemController.AttackType.COLD) {
      damage -= _calcDmgInline(damage, defenderInfo, IStatController.ATTRIBUTES.COLD_RESISTANCE);
    } else if (magicAttack.aType == IItemController.AttackType.LIGHTNING) {
      damage -= _calcDmgInline(damage, defenderInfo, IStatController.ATTRIBUTES.LIGHTNING_RESISTANCE);
    }

    int32 defenderHealthResult = defenderHealth < damage ? int32(0) : defenderHealth - damage;
    damage = defenderHealth - defenderHealthResult;

    return AttackResult({
      defenderHealth: defenderHealthResult,
      damage: damage,
      lifeStolen: lifeStolenPerHit(damage, attackerInfo),
      reflectDamage: reflectMagicDmg(damage, defenderInfo) + reflectChaos(magicAttack, attackerInfo, random_(1e18)),
      critical: critical ? uint8(1) : uint8(0),
      missed: 0,
      blocked: 0
    });
  }

  function meleeDamageCalculation(
    IFightCalculator.FighterInfo memory attackerInfo,
    IFightCalculator.FighterInfo memory defenderInfo,
    int32 defenderHealth,
    function (uint) internal view returns (uint) random_
  ) internal view returns (AttackResult memory attackResult) {
    attackResult = (new AttackResult[](1))[0];

    // generate damage
    int32 damage = getDamage(attackerInfo.fighterAttributes, random_);
    damage = increaseMeleeDmgByFactor(damage, attackerInfo);
    damage = increaseRaceDmg(damage, attackerInfo, defenderInfo.race);
    attackResult.critical = isCriticalHit(attackerInfo, random_(RESISTANCE_DENOMINATOR.toUint())) ? uint8(1) : uint8(0);
    damage = attackResult.critical == 0 ? damage : damage * 2;

    // decrease damage
    damage = decreaseRaceDmg(damage, defenderInfo, attackerInfo.race);
    damage = decreaseDmgByDmgReduction(damage, defenderInfo);

    attackResult.missed = random_(1e18) > StatLib.chanceToHit(
      attackerInfo.fighterAttributes[uint(IStatController.ATTRIBUTES.ATTACK_RATING)].toUint(),
      defenderInfo.fighterAttributes[uint(IStatController.ATTRIBUTES.DEFENSE)].toUint(),
      attackerInfo.fighterStats.level,
      defenderInfo.fighterStats.level,
      attackerInfo.fighterAttributes[uint(IStatController.ATTRIBUTES.AR_FACTOR)].toUint()
    ) ? 1 : 0;

    attackResult.blocked = (random_(100) < defenderInfo.fighterAttributes[uint(IStatController.ATTRIBUTES.BLOCK_RATING)].toUint()) ? 1 : 0;

    if (attackResult.missed != 0 || attackResult.blocked != 0) {
      damage = 0;
    }

    int32 defenderHealthResult = defenderHealth <= damage ? int32(0) : defenderHealth - damage;
    damage = defenderHealth - defenderHealthResult;


    attackResult.defenderHealth = defenderHealthResult;
    attackResult.damage = damage;
    attackResult.lifeStolen = lifeStolenPerHit(damage, attackerInfo);
    attackResult.reflectDamage = reflectMeleeDmg(damage, defenderInfo);
  }

  function getDamage(
    int32[] memory attributes,
    function (uint) internal view returns (uint) random_
  ) internal view returns (int32) {
    return int32(int(CalcLib.pseudoRandomInRangeFlex(
      attributes[uint(IStatController.ATTRIBUTES.DAMAGE_MIN)].toUint(),
      attributes[uint(IStatController.ATTRIBUTES.DAMAGE_MAX)].toUint(),
      random_
    )));
  }

  //endregion ------------------------ Internal logic

  //region ------------------------ Pure utils

  /// @notice Modify values in {targetAttributes} and {casterAttributes} according to {statuses}
  function reduceAttributesByStatuses(
    int32[] memory targetAttributes,
    IFightCalculator.Statuses memory statuses,
    int32[] memory casterAttributes
  ) internal pure {

    if (statuses.burn) {
      targetAttributes[uint(IStatController.ATTRIBUTES.DEFENSE)] -= (targetAttributes[uint(IStatController.ATTRIBUTES.DEFENSE)] / 3);
      targetAttributes[uint(IStatController.ATTRIBUTES.COLD_RESISTANCE)] += 50;
      casterAttributes[uint(IStatController.ATTRIBUTES.CRITICAL_HIT)] += 10;
      casterAttributes[uint(IStatController.ATTRIBUTES.DESTROY_ITEMS)] += 20;
    }
    if (statuses.freeze) {
      targetAttributes[uint(IStatController.ATTRIBUTES.DAMAGE_MIN)] /= 2;
      targetAttributes[uint(IStatController.ATTRIBUTES.DAMAGE_MAX)] /= 2;
      targetAttributes[uint(IStatController.ATTRIBUTES.ATTACK_RATING)] -= targetAttributes[uint(IStatController.ATTRIBUTES.ATTACK_RATING)] / 3;
      targetAttributes[uint(IStatController.ATTRIBUTES.BLOCK_RATING)] /= 2;
      targetAttributes[uint(IStatController.ATTRIBUTES.FIRE_RESISTANCE)] += 50;
    }
    if (statuses.confuse) {
      targetAttributes[uint(IStatController.ATTRIBUTES.ATTACK_RATING)] /= 2;
    }
    if (statuses.curse) {
      targetAttributes[uint(IStatController.ATTRIBUTES.FIRE_RESISTANCE)] /= 2;
      targetAttributes[uint(IStatController.ATTRIBUTES.COLD_RESISTANCE)] /= 2;
      targetAttributes[uint(IStatController.ATTRIBUTES.LIGHTNING_RESISTANCE)] /= 2;
    }
    if (statuses.stun) {
      casterAttributes[uint(IStatController.ATTRIBUTES.CRITICAL_HIT)] += 10;
    }
    if (statuses.poison) {
      targetAttributes[uint(IStatController.ATTRIBUTES.ATTACK_RATING)] /= 2;
    }

  }

  /// @notice Calculate new damage value depending on {defenderRace} and value of corresponded DMG_AGAINST_XXX attribute
  /// @param defenderRace See IStatController.Race
  /// @return Updated damage value
  function increaseRaceDmg(int32 dmg, IFightCalculator.FighterInfo memory attackerInfo, uint defenderRace)
  internal pure returns (int32) {
    if (defenderRace == uint(IStatController.Race.HUMAN)) {
      return dmg + attackerInfo.fighterAttributes[uint(IStatController.ATTRIBUTES.DMG_AGAINST_HUMAN)] * dmg / RESISTANCE_DENOMINATOR;
    } else if (defenderRace == uint(IStatController.Race.UNDEAD)) {
      return dmg + attackerInfo.fighterAttributes[uint(IStatController.ATTRIBUTES.DMG_AGAINST_UNDEAD)] * dmg / RESISTANCE_DENOMINATOR;
    } else if (defenderRace == uint(IStatController.Race.DAEMON)) {
      return dmg + attackerInfo.fighterAttributes[uint(IStatController.ATTRIBUTES.DMG_AGAINST_DAEMON)] * dmg / RESISTANCE_DENOMINATOR;
    } else if (defenderRace == uint(IStatController.Race.BEAST)) {
      return dmg + attackerInfo.fighterAttributes[uint(IStatController.ATTRIBUTES.DMG_AGAINST_BEAST)] * dmg / RESISTANCE_DENOMINATOR;
    } else {
      return dmg;
    }
  }

  /// @notice Decrease damage depending on {attackerRace}
  function decreaseRaceDmg(int32 dmg, IFightCalculator.FighterInfo memory defenderInfo, uint attackerRace) internal pure returns (int32) {
    if (attackerRace == uint(IStatController.Race.HUMAN)) {
      return dmg - _calcDmgInline(dmg, defenderInfo, IStatController.ATTRIBUTES.DEF_AGAINST_HUMAN);
    } else if (attackerRace == uint(IStatController.Race.UNDEAD)) {
      return dmg - _calcDmgInline(dmg, defenderInfo, IStatController.ATTRIBUTES.DEF_AGAINST_UNDEAD);
    } else if (attackerRace == uint(IStatController.Race.DAEMON)) {
      return dmg - _calcDmgInline(dmg, defenderInfo, IStatController.ATTRIBUTES.DEF_AGAINST_DAEMON);
    } else if (attackerRace == uint(IStatController.Race.BEAST)) {
      return dmg - _calcDmgInline(dmg, defenderInfo, IStatController.ATTRIBUTES.DEF_AGAINST_BEAST);
    } else {
      return dmg;
    }
  }

  /// @notice Calculate damage after Melee-attack
  function increaseMeleeDmgByFactor(int32 dmg, IFightCalculator.FighterInfo memory attackerInfo) internal pure returns (int32){
    return dmg + attackerInfo.fighterAttributes[uint(IStatController.ATTRIBUTES.MELEE_DMG_FACTOR)] * dmg / RESISTANCE_DENOMINATOR;
  }

  /// @notice Calculate damage after Magic-attack
  function increaseMagicDmgByFactor(int32 dmg, IFightCalculator.FighterInfo memory attackerInfo, IItemController.AttackType aType) internal pure returns (int32) {
    if (aType == IItemController.AttackType.FIRE) {
      return dmg + dmg * attackerInfo.fighterAttributes[uint(IStatController.ATTRIBUTES.FIRE_DMG_FACTOR)] / RESISTANCE_DENOMINATOR;
    } else if (aType == IItemController.AttackType.COLD) {
      return dmg + dmg * attackerInfo.fighterAttributes[uint(IStatController.ATTRIBUTES.COLD_DMG_FACTOR)] / RESISTANCE_DENOMINATOR;
    } else if (aType == IItemController.AttackType.LIGHTNING) {
      return dmg + dmg * attackerInfo.fighterAttributes[uint(IStatController.ATTRIBUTES.LIGHTNING_DMG_FACTOR)] / RESISTANCE_DENOMINATOR;
    } else {
      return dmg;
    }
  }

  /// @notice Reduce damage depending on value of Damage Reduction attribute
  function decreaseDmgByDmgReduction(int32 dmg, IFightCalculator.FighterInfo memory defenderInfo) internal pure returns (int32) {
    return dmg - _calcDmgInline(dmg, defenderInfo, IStatController.ATTRIBUTES.DAMAGE_REDUCTION);
  }

  /// @notice Calculate poison damage < {health}
  function poisonDmg(int32 health, IFightCalculator.Statuses memory statuses) internal pure returns (int32) {
    // poison should not kill
    if (statuses.poison && health.toUint() > 1) {
      // at least 1 dmg
      return int32(int(Math.max(health.toUint() / 10, 1)));
    }
    return 0;
  }

  /// @notice Reduce health of the fighters according to attacks results, calc damagePoison, damage and damageReflect.
  function reduceHp(
    AttackResult memory firstAttack,
    AttackResult memory secondAttack,
    IFightCalculator.Fighter memory firstFighter,
    IFightCalculator.Fighter memory secondFighter
  ) internal pure {
    secondFighter.health = firstAttack.defenderHealth;
    firstFighter.damage = firstAttack.damage;

    // hit only if second fighter survived
    if (secondFighter.health != 0) {
      firstFighter.health = secondAttack.defenderHealth;
      secondFighter.damage = secondAttack.damage;

      // reflect damage from second to first
      secondFighter.damageReflect = (CalcLib.minI32(firstAttack.reflectDamage, firstFighter.health));
      firstFighter.health -= secondFighter.damageReflect;

      // reflect damage from first to second
      firstFighter.damageReflect = (CalcLib.minI32(secondAttack.reflectDamage, secondFighter.health));
      secondFighter.health -= firstFighter.damageReflect;
    }

    // poison second firstly (he got damage and statuses early)
    firstFighter.damagePoison = poisonDmg(secondFighter.health, secondFighter.statuses);
    secondFighter.health -= firstFighter.damagePoison;

    // poison first fighter
    secondFighter.damagePoison = poisonDmg(firstFighter.health, firstFighter.statuses);
    firstFighter.health -= secondFighter.damagePoison;
  }

  /// @notice Calculate life-stolen-per-hit value for the given {damage} value
  function lifeStolenPerHit(int32 dmg, IFightCalculator.FighterInfo memory attackerInfo) internal pure returns (int32) {
    return dmg * attackerInfo.fighterAttributes[uint(IStatController.ATTRIBUTES.LIFE_STOLEN_PER_HIT)] / RESISTANCE_DENOMINATOR;
  }

  /// @notice Increase {fighter.health} on the value of life-stolen-per-hit (only if the health > 0)
  function stealLife(IFightCalculator.Fighter memory fighter, AttackResult memory attackResult) internal pure {
    if (fighter.health != 0) {
      int32 newHealth = fighter.health + attackResult.lifeStolen;
      int32 maxHealth = fighter.info.fighterAttributes[uint(IStatController.ATTRIBUTES.LIFE)];
      fighter.health = (CalcLib.minI32(newHealth, maxHealth));
    }
  }

  function skipTurn(IFightCalculator.FightInfoInternal memory fResult, bool isA) internal pure returns (bool) {
    return isA ? fResult.fighterA.statuses.stun : fResult.fighterB.statuses.stun;
  }

  /// @notice Detect which hero is faster and makes the hit first. Magic is faster melee.
  /// Otherwise first hit is made by the fighter with higher attack rating (A is selected if the ratings are equal)
  function calcFirstHit(IFightCalculator.FightInfoInternal memory fInfo) internal pure returns (bool aFirst){
    if (fInfo.fighterA.info.attackType == IFightCalculator.AttackType.MAGIC) {
      if (fInfo.fighterB.info.attackType == IFightCalculator.AttackType.MAGIC) {
        // if both fighters use magic we check attack rating
        aFirst = isAttackerFaster(fInfo.fighterA.info, fInfo.fighterB.info);
      } else {
        // otherwise, magic always faster than melee
        aFirst = true;
      }
    } else {
      if (fInfo.fighterB.info.attackType == IFightCalculator.AttackType.MAGIC) {
        // if fighter use magic he will be faster
        aFirst = false;
      } else {
        // otherwise, check attack rating
        aFirst = isAttackerFaster(fInfo.fighterA.info, fInfo.fighterB.info);
      }
    }
  }

  function isAttackerFaster(
    IFightCalculator.FighterInfo memory fighterAInfo,
    IFightCalculator.FighterInfo memory fighterBInfo
  ) internal pure returns (bool) {
    return fighterAInfo.fighterAttributes[uint(IStatController.ATTRIBUTES.ATTACK_RATING)]
      >= fighterBInfo.fighterAttributes[uint(IStatController.ATTRIBUTES.ATTACK_RATING)];
  }

  function reflectMeleeDmg(int32 dmg, IFightCalculator.FighterInfo memory defenderInfo) internal pure returns (int32) {
    return dmg * defenderInfo.fighterAttributes[uint(IStatController.ATTRIBUTES.REFLECT_DAMAGE_MELEE)] / RESISTANCE_DENOMINATOR;
  }

  function reflectMagicDmg(int32 dmg, IFightCalculator.FighterInfo memory defenderInfo) internal pure returns (int32) {
    return dmg * defenderInfo.fighterAttributes[uint(IStatController.ATTRIBUTES.REFLECT_DAMAGE_MAGIC)] / RESISTANCE_DENOMINATOR;
  }

  function _getChance(
    IFightCalculator.FighterInfo memory attackerInfo,
    IItemController.AttackType aType,
    IStatController.ATTRIBUTES index,
    int32 resist
  ) internal pure returns (int32 chance) {
    chance = attackerInfo.fighterAttributes[uint(index)];

    if (attackerInfo.attackType == IFightCalculator.AttackType.MAGIC) {
      if (index == IStatController.ATTRIBUTES.BURN && aType == IItemController.AttackType.FIRE) {
        chance += int32(20);
      }
      if (index == IStatController.ATTRIBUTES.FREEZE && aType == IItemController.AttackType.COLD) {
        chance += int32(20);
      }
      if (index == IStatController.ATTRIBUTES.CONFUSE && aType == IItemController.AttackType.LIGHTNING) {
        chance += int32(20);
      }
    }

    return chance - chance * (CalcLib.minI32(resist, _MAX_RESIST)) / RESISTANCE_DENOMINATOR;
  }

  /// @param randomValue Result of call _pseudoRandom, value in the range [0...RESISTANCE_DENOMINATOR)
  function isCriticalHit(
    IFightCalculator.FighterInfo memory attackerInfo,
    uint randomValue
  ) internal pure returns (bool) {
    return randomValue < attackerInfo.fighterAttributes[uint(IStatController.ATTRIBUTES.CRITICAL_HIT)].toUint();
  }

  /// @param randomValue Result of call CalcLib.pseudoRandom(1e18)
  function reflectChaos(
    IItemController.AttackInfo memory magicAttack,
    IFightCalculator.FighterInfo memory attackerInfo,
    uint randomValue
  ) internal pure returns (int32) {
    return (magicAttack.aType == IItemController.AttackType.CHAOS && randomValue > 5e17)
      ? int32(attackerInfo.fighterStats.life) / int32(2)
      : int32(0);
  }

  function _calcDmgInline(int32 dmg, IFightCalculator.FighterInfo memory info, IStatController.ATTRIBUTES index) internal pure returns (int32) {
    return dmg * (CalcLib.minI32(info.fighterAttributes[uint(index)], _MAX_RESIST)) / RESISTANCE_DENOMINATOR;
  }

  function getMagicDamage(
    IFightCalculator.FighterInfo memory attackerInfo,
    IItemController.AttackInfo memory mAttack,
    uint randomValue_
  ) internal pure returns (int32) {

    int32 attributeFactorResult = (attackerInfo.fighterAttributes[uint(IStatController.ATTRIBUTES.STRENGTH)] * mAttack.attributeFactors.strength / 100);
    attributeFactorResult += (attackerInfo.fighterAttributes[uint(IStatController.ATTRIBUTES.DEXTERITY)] * mAttack.attributeFactors.dexterity / 100);
    attributeFactorResult += (attackerInfo.fighterAttributes[uint(IStatController.ATTRIBUTES.VITALITY)] * mAttack.attributeFactors.vitality / 100);
    attributeFactorResult += (attackerInfo.fighterAttributes[uint(IStatController.ATTRIBUTES.ENERGY)] * mAttack.attributeFactors.energy / 100);

    return int32(int(randomValue_)) + attributeFactorResult;
  }

  //endregion ------------------------ Pure utils

}

contracts/lib/GameObjectControllerLib.so.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.23;

import "../openzeppelin/EnumerableSet.sol";
import "../interfaces/IGOC.sol";
import "../interfaces/IAppErrors.sol";
import "../interfaces/IApplicationEvents.sol";
import "../proxy/Controllable.sol";
import "../lib/PackingLib.sol";
import "../lib/EventLib.sol";
import "../lib/StoryLib.sol";
import "../lib/MonsterLib.sol";
import "../lib/GOCLib.sol";

library GameObjectControllerLib {
  using EnumerableSet for EnumerableSet.UintSet;
  using PackingLib for bytes32;
  using PackingLib for uint16;
  using PackingLib for uint8;
  using PackingLib for address;
  using PackingLib for uint32[];
  using PackingLib for uint32;
  using PackingLib for uint64;
  using PackingLib for int32[];
  using PackingLib for int32;

  //region ------------------------ CONSTANTS
  /// @dev keccak256(abi.encode(uint256(keccak256("game.object.controller.main")) - 1)) & ~bytes32(uint256(0xff))
  bytes32 internal constant MAIN_STORAGE_LOCATION = 0xfa9e067a92ca4a9057b7b4465a8f29d633e1758238bd3a4a8ec5d0f904f6b900;
  //endregion ------------------------ CONSTANTS

  //region ------------------------ RESTRICTIONS

  function onlyDeployer(IController controller) internal view {
    if (!controller.isDeployer(msg.sender)) revert IAppErrors.ErrorNotDeployer(msg.sender);
  }

  function onlyDungeonFactory(IController c) internal view {
    if (c.dungeonFactory() != msg.sender) revert IAppErrors.ErrorNotDungeonFactory(msg.sender);
  }
  //endregion ------------------------ RESTRICTIONS

  //region ------------------------ VIEWS

  function _S() internal pure returns (IGOC.MainState storage s) {
    assembly {
      s.slot := MAIN_STORAGE_LOCATION
    }
    return s;
  }

  function getObjectMeta(uint32 objectId) internal view returns (uint8 biome, uint8 objectSubType) {
    return GOCLib.unpackObjectMeta(_S().objectMeta[objectId]);
  }

  function isAvailableForHero(IController controller, address heroToken, uint heroTokenId, uint32 objId) internal view returns (bool) {
    (, uint8 objectSubType) = getObjectMeta(objId);
    return GOCLib.isAvailableForHero(IStoryController(controller.storyController()), objId, objectSubType, heroToken, heroTokenId);
  }

  function isBattleObject(uint32 objectId) internal view returns (bool) {
    (,uint8 objectSubType) = GOCLib.unpackObjectMeta(_S().objectMeta[objectId]);
    return GOCLib.getObjectTypeBySubType(IGOC.ObjectSubType(objectSubType)) == IGOC.ObjectType.MONSTER;
  }

  function getObjectTypeBySubType(uint32 objectId) internal view returns (IGOC.ObjectType) {
    (,uint8 objectSubType) = GOCLib.unpackObjectMeta(_S().objectMeta[objectId]);
    return GOCLib.getObjectTypeBySubType(IGOC.ObjectSubType(objectSubType));
  }

  function getMonsterInfo(address hero, uint heroId, uint32 objectId) internal view returns (IGOC.MonsterGenInfo memory mGenInfo, IGOC.GeneratedMonster memory gen) {
    uint iteration = _S().iterations[hero.packIterationKey(uint64(heroId), objectId)];
    mGenInfo = MonsterLib.unpackMonsterInfo(_S().monsterInfos[objectId]);
    gen = MonsterLib.unpackGeneratedMonster(_S().monsterInfos[objectId]._generatedMonsters[hero.packNftId(heroId)][iteration]);
  }

  function getIteration(address heroToken, uint heroTokenId, uint32 objId) internal view returns (uint) {
    return _S().iterations[_iterationKey(heroToken, heroTokenId, objId)];
  }

  function getLastHeroFightTs(address heroToken, uint heroTokenId) internal view returns (uint) {
    return _S().lastHeroFightTs[heroToken.packNftId(heroTokenId)];
  }

  function getFightDelay() internal view returns (uint) {
    return _S().fightDelay;
  }

  function getStoryId(uint32 objectId) internal view returns (uint16) {
    return _S().storyIds[objectId];
  }

  function getEventInfo(uint32 objectId) internal view returns (IGOC.EventInfo memory) {
    return _S().eventInfos[objectId];
  }

  function getObjectIds(uint8 biome, IGOC.ObjectSubType subType) internal view returns (uint[] memory) {
    return _S().objectIds[GOCLib.packObjectMeta(biome, uint8(subType))].values();
  }
  //endregion ------------------------ VIEWS

  //region ------------------------ REGISTRATION

  function registerEvent(IController controller, IGOC.EventRegInfo memory regInfo) internal {
    onlyDeployer(controller);
    _checkMintItems(regInfo.mintItems, regInfo.mintItemsChances);
    uint32 objectId = _registerMetaId(regInfo.biome, regInfo.subType, regInfo.eventId);
    EventLib.eventRegInfoToInfo(regInfo, _S().eventInfos[objectId]);
    emit IApplicationEvents.EventRegistered(objectId, regInfo);
  }

  function registerStory(IController controller, uint16 storyId, uint8 biome, IGOC.ObjectSubType subType) internal {
    onlyDeployer(controller);
    uint32 objectId = _registerMetaId(biome, subType, storyId);
    _S().storyIds[objectId] = storyId;
    emit IApplicationEvents.StoryRegistered(objectId, storyId);
  }

  function registerMonster(IController controller, IGOC.MonsterGenInfo memory monsterGenInfo) internal {
    onlyDeployer(controller);
    _checkMintItems(monsterGenInfo.mintItems, monsterGenInfo.mintItemsChances);
    uint32 objectId = _registerMetaId(monsterGenInfo.biome, monsterGenInfo.subType, monsterGenInfo.monsterId);

    delete _S().monsterInfos[objectId];

    MonsterLib.packMonsterInfo(monsterGenInfo, _S().monsterInfos[objectId]);
    emit IApplicationEvents.MonsterRegistered(objectId, monsterGenInfo);
  }

  function removeObject(IController controller, uint32 objectId) internal {
    onlyDeployer(controller);
    bytes32 meta = _S().objectMeta[objectId];
    delete _S().objectMeta[objectId];
    _S().objectIds[meta].remove(objectId);

    emit IApplicationEvents.ObjectRemoved(objectId);
  }
  //endregion ------------------------ REGISTRATION

  //region ------------------------ OBJECT ACTIONS

  /// @param cTypes Array of object subtypes, see IGOC.ObjectSubType.XXX
  /// @param chances Chances in range 0-1e9, chances are corresponded to {cTypes} array
  function getRandomObject(
    IController c,
    uint8[] memory cTypes,
    uint32[] memory chances,
    uint8 biome,
    address heroToken,
    uint heroTokenId
  ) internal returns (uint32 objectId) {
    onlyDungeonFactory(c);
    return GOCLib.getRandomObject(
      _S(),
      IStoryController(c.storyController()),
      cTypes,
      chances,
      biome,
      heroToken,
      heroTokenId
    );
  }

  /// @notice Open {object}: increase iteration, [generate monsters]
  function open(IController c, address heroToken, uint heroTokenId, uint32 objectId) internal returns (uint iteration) {
    onlyDungeonFactory(c);

    iteration = _increaseIteration(heroToken, heroTokenId, objectId);

    (, uint8 objectSubType) = getObjectMeta(objectId);
    uint8 t = uint8(GOCLib.getObjectTypeBySubType(IGOC.ObjectSubType(objectSubType)));

    if (t == uint8(IGOC.ObjectType.EVENT)) {
      // noop
    } else if (t == uint8(IGOC.ObjectType.MONSTER)) {
      MonsterLib.initialGeneration(_S().monsterInfos[objectId], heroToken, heroTokenId, iteration, IERC20(c.gameToken()).totalSupply());
    } else if (t == uint8(IGOC.ObjectType.STORY)) {
      // noop
    } else {
      revert IAppErrors.UnknownObjectTypeGocLib1(t);
    }
  }

  /// @notice Execute event/story/monster action
  /// @param data Object type-specified data packed using abi.encode.
  /// For events: bool (accept / not accept results)
  /// For monsters: AttackInfo
  /// For story: bytes32 (answer id)
  function action(
    IController c,
    address sender,
    uint64 dungeonId,
    uint32 objectId,
    address heroToken,
    uint heroTokenId,
    uint8 stageId,
    bytes memory data
  ) internal returns (IGOC.ActionResult memory) {
    onlyDungeonFactory(c);

    IGOC.ActionContext memory ctx;

    ctx.objectId = objectId;
    ctx.sender = sender;
    ctx.heroToken = heroToken;
    ctx.heroTokenId = heroTokenId;
    ctx.stageId = stageId;
    ctx.data = data;
    (ctx.biome, ctx.objectSubType) = getObjectMeta(objectId);

    ctx.dungeonId = dungeonId;
    ctx.iteration = _S().iterations[_iterationKey(heroToken, heroTokenId, objectId)];
    ctx.controller = c;

    IGOC.ActionResult memory r;
    uint8 t = uint8(GOCLib.getObjectTypeBySubType(IGOC.ObjectSubType(ctx.objectSubType)));
    ctx.salt = block.number;

    if (t == uint8(IGOC.ObjectType.EVENT)) {
      r = EventLib.action(ctx, _S().eventInfos[objectId]);
    } else if (t == uint8(IGOC.ObjectType.MONSTER)) {
      _checkAndRefreshFightTs(heroToken, heroTokenId);
      (r, ctx.salt) = MonsterLib.action(ctx, _S().monsterInfos[objectId]);
    } else if (t == uint8(IGOC.ObjectType.STORY)) {
      r = StoryLib.action(ctx, _S().storyIds[objectId]);
    } else {
      revert IAppErrors.UnknownObjectTypeGocLib2(t);
    }

    r.objectId = ctx.objectId;
    r.heroToken = heroToken;
    r.heroTokenId = heroTokenId;
    r.iteration = ctx.iteration;


    emit IApplicationEvents.ObjectResultEvent(
      dungeonId,
      objectId,
      IGOC.ObjectType(t),
      heroToken,
      heroTokenId,
      stageId,
      ctx.iteration,
      data,
      r,
      ctx.salt
    );
    return r;
  }
//endregion ------------------------ OBJECT ACTIONS

  //region ------------------------ Utils

  /// @notice Generate object ID using (biome, subType, id)
  /// @param biome Biome to which the object belongs. [1..99]
  /// @param subType Subtype of the object, see IGOC.ObjectSubType.XXX. [1..99]
  /// @param id Id of the event / story / monster. [1..10_000]
  function _genObjectId(uint8 biome, uint8 subType, uint16 id) internal pure returns (uint32 objectId) {
    if (biome == 0 || subType == 0 || id == 0) revert IAppErrors.ZeroValueNotAllowed();
    if (biome >= 100) revert IAppErrors.GenObjectIdBiomeOverflow(biome);
    if (uint(subType) >= 100) revert IAppErrors.GenObjectIdSubTypeOverflow(subType);
    if (id > 10_000) revert IAppErrors.GenObjectIdIdOverflow(id);
    objectId = uint32(biome) * 1_000_000 + uint32(subType) * 10_000 + uint32(id);
  }

  /// @notice Register the object in objectMeta and objectIds
  /// @param biome Biome to which the object belongs. [1..99]
  /// @param subType Subtype of the object, [1..99]
  /// @param id Id of the event / story / monster. [1..10_000]
  /// @return objectId Object id generated by {_genObjectId}
  function _registerMetaId(uint8 biome, IGOC.ObjectSubType subType, uint16 id) internal returns (uint32 objectId) {
    IGOC.MainState storage s = _S();
    objectId = _genObjectId(biome, uint8(subType), id);
    bytes32 meta = GOCLib.packObjectMeta(biome, uint8(subType));
    s.objectMeta[objectId] = meta;
    s.objectIds[meta].add(objectId);
  }

  /// @notice Update last-hero-fight-timestamp
  function _checkAndRefreshFightTs(address heroToken, uint heroTokenId) internal {
    IGOC.MainState storage s = _S();
    bytes32 key = heroToken.packNftId(heroTokenId);
    if (s.lastHeroFightTs[key] + s.fightDelay > block.timestamp) revert IAppErrors.FightDelay();
    s.lastHeroFightTs[key] = block.timestamp;
  }

  function _increaseIteration(address heroToken, uint heroTokenId, uint32 objId) internal returns (uint iteration) {
    IGOC.MainState storage s = _S();
    bytes32 key = _iterationKey(heroToken, heroTokenId, objId);
    iteration = s.iterations[key] + 1;
    s.iterations[key] = iteration;
  }

  function _iterationKey(address heroToken, uint heroTokenId, uint32 objId) internal pure returns (bytes32) {
    return heroToken.packIterationKey(uint64(heroTokenId), objId);
  }

  /// @notice Validate passed {mintItems_} and {mintItemsChances_}
  function _checkMintItems(address[] memory mintItems_, uint32[] memory mintItemsChances_) internal pure {
    uint length = mintItems_.length;
    if (mintItemsChances_.length != length) revert IAppErrors.LengthsMismatch();

    for (uint i; i < length; ++i) {
      if (mintItems_[i] == address(0)) revert IAppErrors.ZeroAddress();
      if (mintItemsChances_[i] == 0) revert IAppErrors.ZeroChance();
      if (mintItemsChances_[i] > CalcLib.MAX_CHANCE) revert IAppErrors.TooHighChance(mintItemsChances_[i]);
    }
  }
  //endregion ------------------------ Utils
}

contracts/lib/GOCLib.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.23;

import "../interfaces/IAppErrors.sol";
import "../interfaces/IGOC.sol";
import "../interfaces/IStoryController.sol";
import "./CalcLib.sol";
import "./PackingLib.sol";

library GOCLib {
  using EnumerableSet for EnumerableSet.UintSet;
  using PackingLib for address;

  /// @param cTypes Array of object subtypes, see IGOC.ObjectSubType.XXX
  /// @param chances Chances in range 0-1e9, chances are corresponded to {cTypes} array
  function getRandomObject(
    IGOC.MainState storage s,
    IStoryController sc,
    uint8[] memory cTypes,
    uint32[] memory chances,
    uint8 biome,
    address heroToken,
    uint heroTokenId
  ) internal returns (uint32 objectId) {

    uint8 cType = _getObjectType(cTypes, chances, CalcLib.pseudoRandom);

    EnumerableSet.UintSet storage objects = s.objectIds[packObjectMeta(biome, cType)];
    uint len = objects.length();
    if (len == 0) revert IAppErrors.EmptyObjects();
    uint objectArrayIdx = len == 1
      ? 0
      : CalcLib.pseudoRandom(len - 1);

    EnumerableSet.UintSet storage played = s.playedObjects[heroToken.packPlayedObjKey(uint64(heroTokenId), cType, biome)];
    objectId = _searchObject(sc, len, objects, played, objectArrayIdx, false, heroToken, heroTokenId, cType);

    if (objectId == 0) revert IAppErrors.ObjectNotFound();
    played.add(objectId);
  }

  /// @notice Select cType using pseudo-random value according to the given {chances}
  /// @param cTypes Zero values are ignored.
  /// @param chances [0..100], decimals 9. At least once item should have value 100 to avoid {UnknownObjectType} error.
  /// @param random_ CalcLib.pseudoRandom, required for unit tests
  function _getObjectType(
    uint8[] memory cTypes,
    uint32[] memory chances,
    function (uint) internal view returns (uint) random_
  ) internal view returns (uint8 cType) {
    uint len = cTypes.length;
    if (len == 0 || len != chances.length) revert IAppErrors.WrongGetObjectTypeInput();

    if (len == 1) {
      cType = cTypes[0];
    } else {
      uint random = random_(CalcLib.MAX_CHANCE);
      uint minChance = CalcLib.MAX_CHANCE + 1;
      for (uint i; i < len; ++i) {
        // obj set can contain empty values, ignore them
        if (cTypes[i] == 0) continue;
        if (chances[i] > CalcLib.MAX_CHANCE) revert IAppErrors.WrongChances(chances[i], CalcLib.MAX_CHANCE);
        if ((CalcLib.MAX_CHANCE - chances[i]) <= random) {
          if (chances[i] < minChance) {
            minChance = chances[i];
            cType = cTypes[i];
          }
        }
      }
    }

    if (cType == 0) revert IAppErrors.UnknownObjectTypeGoc1(0);
    return cType;
  }

  /// @notice Find first object in {objects} available for the hero starting from {objArrayIdx}
  /// If object not found clear {played} and try to search again.
  /// @param lenObjects Length of {objects}
  /// @param objArrayIdx Start index in objects
  /// @param cType Object subtype
  /// @param skipPlayed true - don't check if the found object was already played
  /// @return objectId ID of the found object or 0 if the object is not found
  function _searchObject(
    IStoryController sc,
    uint lenObjects,
    EnumerableSet.UintSet storage objects,
    EnumerableSet.UintSet storage played,
    uint objArrayIdx,
    bool skipPlayed,
    address heroToken,
    uint heroTokenId,
    uint8 cType
  ) internal returns (uint32 objectId) {

    // clear played objects if we played them all at the current biome
    if (played.length() >= lenObjects) {
      skipPlayed = true;
      _clearPlayedObjects(played);
    }

    bool foundValid;

    unchecked {
    // search in a loop available objects
      for (uint i; i < lenObjects; ++i) {
        if (objArrayIdx >= lenObjects) {
          objArrayIdx = 0;
        }
        uint32 objId = uint32(objects.at(objArrayIdx));
        if (
          isAvailableForHero(sc, objId, cType, heroToken, heroTokenId)
          && (skipPlayed || !played.contains(objId))
        ) {
          foundValid = true;
          objectId = objId;
          break;
        }

        ++objArrayIdx;
      }
    }
    // in case when we do not have available objects it is possible they are not eligible and need to reset counter
    if (!foundValid && !skipPlayed) {
      _clearPlayedObjects(played);
      objectId = _searchObject(sc, lenObjects, objects, played, objArrayIdx, true, heroToken, heroTokenId, cType);
    }

    return objectId;
  }

  function _clearPlayedObjects(EnumerableSet.UintSet storage played) internal {
    uint[] memory values = played.values();
    for (uint i; i < values.length; ++i) {
      played.remove(values[i]);
    }
  }

  /// @notice Check if the object subtype is available for the hero
  function isAvailableForHero(IStoryController sc, uint32 objId, uint8 objectSubType, address hero, uint heroId) internal view returns (bool) {
    IGOC.ObjectType objType = getObjectTypeBySubType(IGOC.ObjectSubType(objectSubType));
    if (objType == IGOC.ObjectType.EVENT) {
      // no checks
      return true;
    } else if (objType == IGOC.ObjectType.MONSTER) {
      // no checks
      return true;
    } else if (objType == IGOC.ObjectType.STORY) {
      return sc.isStoryAvailableForHero(objId, hero, heroId);
    } else {
      // actually, this case is impossible, getObjectTypeBySubType will revert above if objectSubType is incorrect
      revert IAppErrors.UnknownObjectTypeForSubtype(objectSubType);
    }
  }

  function packObjectMeta(uint8 biome, uint8 oType) internal pure returns (bytes32) {
    return PackingLib.packUint8Array3(biome, oType, 0);
  }

  function unpackObjectMeta(bytes32 data) internal pure returns (uint8 biome, uint8 oType) {
    (biome, oType,) = PackingLib.unpackUint8Array3(data);
  }

  /// @notice Get object type for the given {subType}
  function getObjectTypeBySubType(IGOC.ObjectSubType subType) internal pure returns (IGOC.ObjectType) {
    if (
      subType == IGOC.ObjectSubType.SHRINE_4
      || subType == IGOC.ObjectSubType.CHEST_5
      || subType == IGOC.ObjectSubType.SHRINE_UNIQUE_8
    ) {
      return IGOC.ObjectType.EVENT;
    } else if (
      subType == IGOC.ObjectSubType.ENEMY_NPC_1
      || subType == IGOC.ObjectSubType.ENEMY_NPC_SUPER_RARE_2
      || subType == IGOC.ObjectSubType.BOSS_3
      || subType == IGOC.ObjectSubType.ENEMY_NPC_UNIQUE_10
      || subType == IGOC.ObjectSubType.ENEMY_NPC_INSIDE_32
      || subType == IGOC.ObjectSubType.ENEMY_NPC_INSIDE_RARE_33
      || subType == IGOC.ObjectSubType.ENEMY_NPC_OUTSIDE_34
      || subType == IGOC.ObjectSubType.ENEMY_NPC_OUTSIDE_RARE_35
    ) {
      return IGOC.ObjectType.MONSTER;
    } else if (
      subType == IGOC.ObjectSubType.STORY_6
      || subType == IGOC.ObjectSubType.STORY_UNIQUE_7
      || subType == IGOC.ObjectSubType.STORY_ON_ROAD_11
      || subType == IGOC.ObjectSubType.STORY_UNDERGROUND_12
      || subType == IGOC.ObjectSubType.STORY_NIGHT_CAMP_13
      || subType == IGOC.ObjectSubType.STORY_MOUNTAIN_14
      || subType == IGOC.ObjectSubType.STORY_WATER_15
      || subType == IGOC.ObjectSubType.STORY_CASTLE_16
      || subType == IGOC.ObjectSubType.STORY_HELL_17
      || subType == IGOC.ObjectSubType.STORY_SPACE_18
      || subType == IGOC.ObjectSubType.STORY_WOOD_19
      || subType == IGOC.ObjectSubType.STORY_CATACOMBS_20
      || subType == IGOC.ObjectSubType.STORY_BAD_HOUSE_21
      || subType == IGOC.ObjectSubType.STORY_GOOD_TOWN_22
      || subType == IGOC.ObjectSubType.STORY_BAD_TOWN_23
      || subType == IGOC.ObjectSubType.STORY_BANDIT_CAMP_24
      || subType == IGOC.ObjectSubType.STORY_BEAST_LAIR_25
      || subType == IGOC.ObjectSubType.STORY_PRISON_26
      || subType == IGOC.ObjectSubType.STORY_SWAMP_27
      || subType == IGOC.ObjectSubType.STORY_INSIDE_28
      || subType == IGOC.ObjectSubType.STORY_OUTSIDE_29
      || subType == IGOC.ObjectSubType.STORY_INSIDE_RARE_30
      || subType == IGOC.ObjectSubType.STORY_OUTSIDE_RARE_31
    ) {
      return IGOC.ObjectType.STORY;
    } else {
      revert IAppErrors.UnknownObjectTypeGoc2(uint8(subType));
    }
  }
}

