// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.23;

import "./CalcLib.sol";
import "./StatLib.sol";
import "../interfaces/IOracle.sol";
import "../interfaces/IItemController.sol";
import "../interfaces/IItem.sol";
import "../interfaces/IAppErrors.sol";
import "../interfaces/IApplicationEvents.sol";
import "../openzeppelin/Math.sol";
import "../solady/LibPRNG.sol";

library ItemLib {
  using CalcLib for int32;
  using PackingLib for address;
  using PackingLib for bytes32;
  using PackingLib for bytes32[];
  using PackingLib for uint32[];
  using PackingLib for int32[];

  //region ------------------------ Data types
  struct GenerateAttributesContext {
    /// @notice True if max allowed amount of random attributes were reached inside {_prepareAttributes}
    bool stopGenerateRandom;
    /// @notice Flag - attribute was generated. The array matches to info.ids
    bool[] usedIndexes;
    /// @notice Ids of the generated attributes
    uint8[] ids;
    /// @notice Randomly selected values of the generated attributes
    int32[] values;
    /// @notice Counter of the stored values into {ids} and {values}
    uint counter;
    /// @notice Total number of random attributes that were generated inside {_prepareAttributes}
    uint randomAttrCounter;
    /// @notice Total sum of all {random} values for random attributes generated in {_prepareAttributes}
    uint randomSum;
    /// @notice Total sum of all chances of the random attributes generated in {_prepareAttributes}
    uint chancesSum;
    /// @notice Total number of random attributes that can be generated
    uint totalRandomAttrsPossible;
  }

  struct MintItemInfo {
    uint8 biome;
    uint8 heroCurrentLvl;
    uint8 maxItems;
    IOracle oracle;
    address[] mintItems;
    int32 magicFind;
    int32 destroyItems;
    uint32[] mintItemsChances;
    uint32 heroExp;
    uint amplifier;
    uint seed;
  }
  //endregion ------------------------ Data types

  //region ------------------------ Main logic

  /// @notice Mint new item, setup attributes, make extra setup if necessary (setup attack item, buff item)
  /// @param sender Dungeon Factory only is allowed
  /// @param item Item to be minted
  /// @param recipient The item is minted for the given recipient
  /// @return itemId Id of the newly minted item
  function mintNewItem(
    IItemController.MainState storage s,
    IController controller,
    address sender,
    address item,
    address recipient
  ) external returns (uint itemId) {
    if (controller.dungeonFactory() != sender) revert IAppErrors.ErrorNotDungeonFactory(sender);

    itemId = IItem(item).mintFor(recipient);

    IItemController.MintInfo memory info;

    (
      info.meta,
      info.attributesIds,
      info.attributesValues,
      info.itemRarity
    ) = _setupNewAttributes(s, item, itemId, CalcLib.pseudoRandom);

    // setup extra info

    if (info.meta.itemMetaType == uint8(IItemController.ItemMetaType.ATTACK)) {
      info.attackInfo = unpackItemAttackInfo(_setupNewAttackItem(s, item, itemId));
    } else if (info.meta.itemMetaType == uint8(IItemController.ItemMetaType.BUFF)) {
      (
        info.casterIds,
        info.casterValues,
        info.targetIds,
        info.targetValues
      ) = _setupNewBuffItem(s, item, itemId, CalcLib.pseudoRandom);
    }
    // consumable stats unchangeable, get them by address

    emit IApplicationEvents.NewItemMinted(item, itemId, info);
  }

  /// @notice Mint random items, not more than {info.maxItems}
  function mintRandomItems(MintItemInfo memory info) internal returns (address[] memory) {
    return _mintRandomItems(info, CalcLib.nextPrng);
  }

  //endregion ------------------------ Main logic

  //region ------------------------ Internal logic
  /// @param nextPrng_ CalcLib.nextPrng, param is required by unit tests
  function _mintRandomItems(
    MintItemInfo memory info,
    function (LibPRNG.PRNG memory, uint) internal view returns (uint) nextPrng_
  ) internal returns (address[] memory) {
    unchecked {
      uint len = info.mintItems.length;

    // Fisher–Yates shuffle
      LibPRNG.PRNG memory prng = LibPRNG.PRNG(info.oracle.getRandomNumber(CalcLib.MAX_CHANCE, info.seed));
      uint[] memory indices = new uint[](len);
      for (uint i = 1; i < len; ++i) {
        indices[i] = i;
      }
      LibPRNG.shuffle(prng, indices);

      address[] memory minted = new address[](len);
      uint mintedLength;
      uint delta = StatLib.mintDropChanceDelta(info.heroExp, info.heroCurrentLvl, info.biome);
      uint di = Math.min(uint(int(info.destroyItems)), 100);

      for (uint i; i < len; ++i) {
        if (info.mintItemsChances[indices[i]] > CalcLib.MAX_CHANCE) {
          revert IAppErrors.TooHighChance(info.mintItemsChances[indices[i]]);
        }
        uint chance = StatLib.mintDropChance(info.mintItemsChances[indices[i]], info.amplifier, delta);
        chance += chance * uint(int(info.magicFind)) / 100;
        chance -= chance * di / 100;

        // need to call random in each loop coz each minted item should have dedicated chance
        uint rnd = nextPrng_(prng, CalcLib.MAX_CHANCE); // randomWithSeed_(CalcLib.MAX_CHANCE, rndSeed);

        if (chance != 0 && (chance >= CalcLib.MAX_CHANCE || rnd < chance)) {
          // There is no break here: the cycle is continued even if the number of the minted items reaches the max.
          // The reason: gas consumption of success operation must be great of equal of the gas consumption of fail op.
          if (mintedLength < info.maxItems) {
            minted[i] = info.mintItems[indices[i]];
            ++mintedLength;
          }
        }
      }

      address[] memory mintedAdjusted = new address[](mintedLength);
      uint j;
      for (uint i; i < len; ++i) {
        if (minted[i] != address(0)) {
          mintedAdjusted[j] = minted[i];
          ++j;
        }
      }

      return mintedAdjusted;
    }
  }

  function _setupNewAttributes(
    IItemController.MainState storage s,
    address item,
    uint itemId,
    function (uint) internal view returns (uint) random_
  ) internal returns (
    IItemController.ItemMeta memory meta,
    uint8[] memory ids,
    int32[] memory values,
    IItemController.ItemRarity itemRarity
  ){
    meta = unpackedItemMeta(s.itemMeta[item]);
    (ids, values, itemRarity) = _generateAttributes(unpackItemGenerateInfo(s.generateInfoAttributes[item]), meta, random_);

    bytes32 packedItemId = item.packNftId(itemId);
    if (ids.length != 0) {
      s._itemAttributes[packedItemId] = values.toBytes32ArrayWithIds(ids);
    }

    s.itemInfo[packedItemId] = PackingLib.packItemInfo(uint8(itemRarity), 0, meta.baseDurability);
  }

  function _setupNewAttackItem(IItemController.MainState storage s, address item, uint itemId) internal returns (bytes32 attackInfo){
    // we just write data for attack item, no need to generate, it will be augmented later so need individual data for itemId
    attackInfo = s.generateInfoAttack[item];
    s._itemAttackInfo[item.packNftId(itemId)] = attackInfo;
  }

  function _setupNewBuffItem(
    IItemController.MainState storage s,
    address item,
    uint itemId,
    function (uint) internal view returns (uint) random_
  ) internal returns (
    uint8[] memory casterIds,
    int32[] memory casterValues,
    uint8[] memory targetIds,
    int32[] memory targetValues
  ){

    // CASTER
    (casterIds, casterValues) = _generateSimpleAttributes(
      unpackItemGenerateInfo(s.generateInfoCasterAttributes[item]),
      true,
      random_
    );

    if (casterIds.length != 0) {
      s._itemCasterAttributes[item.packNftId(itemId)] = casterValues.toBytes32ArrayWithIds(casterIds);
    }

    // TARGET
    (targetIds, targetValues) = _generateSimpleAttributes(
      unpackItemGenerateInfo(s.generateInfoTargetAttributes[item]),
      true,
      random_
    );

    if (targetIds.length != 0) {
      s._itemTargetAttributes[item.packNftId(itemId)] = targetValues.toBytes32ArrayWithIds(targetIds);
    }
  }

  /// @notice Generate all mandatory attributes and try to generate required number of random attributes.
  /// Generate at least {info.minRandomAttributes} of random attributes if it's possible
  /// but not more than {info.maxRandomAttributes}. Value of each attribute is generated randomly according its chances.
  /// @param meta Assume, that meta.min != 0, meta.max != 0 and both meta.min and meta.min should have same sign
  /// because results value cannot be 0
  /// @return ids Ids of the attributes, zero id is allowed
  /// @return values Randomly generated attributes values, min <= value <= max
  /// @return itemRarity Rarity of the item (Either meta.defaultRarity or calculated if there is no default rarity)
  function _generateAttributes(
    IItemController.ItemGenerateInfo memory info,
    IItemController.ItemMeta memory meta,
    function (uint) internal view returns (uint) random_
  ) internal view returns (
    uint8[] memory ids,
    int32[] memory values,
    IItemController.ItemRarity itemRarity
  ) {
    GenerateAttributesContext memory ctx;

    uint len = info.ids.length;
    if (len != 0) {
      ctx.ids = new uint8[](len);
      ctx.values = new int32[](len);
      ctx.usedIndexes = new bool[](len);

      // Fisher–Yates shuffle
      _shuffleInfo(info, random_);

      // initialize ctx by initial values
      // generate all mandatory attributes, try to generate not more than {meta.maxRandomAttributes} random attributes
      _prepareAttributes(info, meta.maxRandomAttributes, ctx, random_);

      // generate missing random attributes if it's necessary, ctx.counter is incremented
      _generateMissingRandomAttributes(info, meta.minRandomAttributes, ctx, random_);

      itemRarity = meta.defaultRarity == 0
        ? _calculateRarity(ctx.randomSum, ctx.chancesSum, ctx.randomAttrCounter, meta.maxRandomAttributes)
        : IItemController.ItemRarity(meta.defaultRarity);
    } else {
      itemRarity = IItemController.ItemRarity.UNKNOWN;
    }

    (ids, values) = _fixLengthsIdsValues(ctx.ids, ctx.values, ctx.counter);
  }

  /// @notice Generate missing random attributes if necessary
  function _generateMissingRandomAttributes(
    IItemController.ItemGenerateInfo memory info,
    uint8 minRandomAttributes,
    GenerateAttributesContext memory ctx,
    function (uint) internal view returns (uint) random_
  ) internal view {
    uint attrToGen = Math.min(ctx.totalRandomAttrsPossible, minRandomAttributes);
    if (ctx.randomAttrCounter < attrToGen && ctx.totalRandomAttrsPossible > ctx.randomAttrCounter) {
      // it's necessary AND possible to generate more random attributes
      uint possibleRemainingAttrs = ctx.totalRandomAttrsPossible - ctx.randomAttrCounter;
      uint remainingAttrsToGen = attrToGen - ctx.randomAttrCounter;

      uint[] memory indicesToGen = new uint[](possibleRemainingAttrs);
      uint indicesToGenCounter;

      // enumerate all attributes, add all indices of not-generated attributes to {indexesToGen}
      for (uint i; i < info.ids.length; ++i) {
        // mandatory attrs should be already generated and no need to check
        if (!ctx.usedIndexes[i]) {
          indicesToGen[indicesToGenCounter] = i;
          indicesToGenCounter++;
        }
      }

      // Shuffle indices of not-generated attributes using Fisher–Yates shuffle
      if (possibleRemainingAttrs > 1) {
        for (uint i; i < possibleRemainingAttrs - 1; ++i) {
          uint randomIndex = CalcLib.pseudoRandomInRangeFlex(i, possibleRemainingAttrs - 1, random_);
          (indicesToGen[randomIndex], indicesToGen[i]) = (indicesToGen[i], indicesToGen[randomIndex]);
        }
      }
      // Generate necessary amount of attributes. Fist (shuffled) attributes are selected (MAX_CHANCE is used for each)
      for (uint i; i < remainingAttrsToGen; ++i) {
        uint idx = indicesToGen[i];
        (int32 attr,) = _generateAttribute(info.mins[idx], info.maxs[idx], CalcLib.MAX_CHANCE, random_);
        ctx.ids[ctx.counter] = info.ids[idx];
        ctx.values[ctx.counter] = attr;
        ctx.counter++;
      }
    }
  }

  /// @notice Generate all mandatory attributes, generate not more than {meta.maxRandomAttributes} random attributes.
  /// Updates context:
  ///   {ctx.totalRandomAttrsPossible} - total number of possible random attributes
  ///   {ctx.randomAttrCounter} - total number of generated random attributes  <= {maxRandomAttributes}
  ///   {ctx.randomSum} = sum of random of all random attributes.
  ///   {ctx.chancesSum} = sum of chances of all random attributes.
  ///   {ctx.counter} = total number of generated attributes. Values of ctx.ids, ctx.values, ctx.usedIndexes are
  ///   initialized in the range [0...ctx.counter)
  /// @param ctx Empty struct but arrays ids, values and usedIndexes should be allocated for info.ids.length items
  function _prepareAttributes(
    IItemController.ItemGenerateInfo memory info,
    uint8 maxRandomAttributes,
    GenerateAttributesContext memory ctx,
    function (uint) internal view returns (uint) random_
  ) internal view {
    uint len = info.ids.length;
    for (uint i; i < len; ++i) {
      if (info.chances[i] != CalcLib.MAX_CHANCE) {
        ctx.totalRandomAttrsPossible++;
      }

      if (info.chances[i] >= CalcLib.MAX_CHANCE || !ctx.stopGenerateRandom) {
        (int32 attr, uint random) = _generateAttribute(info.mins[i], info.maxs[i], info.chances[i], random_);
//          console.log("GEN id: %s, value: %s%s", _info.ids[i], attr >= 0 ? '' : '-', attr >= 0 ? uint(int(attr)) : uint(int(- attr)));

        // count only random attributes for calc rarity
        if (attr != 0) {

          if (
            info.chances[i] < CalcLib.MAX_CHANCE
            // && random != 0 // commented: random = 0 can produce crash in _generateMissingRandomAttributes
          ) {
            ctx.randomAttrCounter++;
            ctx.randomSum += random;
            ctx.chancesSum += info.chances[i];
          }
          ctx.ids[ctx.counter] = info.ids[i];
          ctx.values[ctx.counter] = attr;
          ctx.counter++;
          ctx.usedIndexes[i] = true;
        }

        // it is a bit less fair random for attrs in the end of the list, however we assume it should be pretty rare case
        if (ctx.randomAttrCounter == maxRandomAttributes) {
          ctx.stopGenerateRandom = true;
        }
      }
    }
  }

  /// @notice Shuffle info arrays using Fisher–Yates shuffle algo
  function _shuffleInfo(
    IItemController.ItemGenerateInfo memory info,
    function (uint) internal view returns (uint) random_
  ) internal view {
    uint len = info.ids.length;
    if (len > 1) {
      for (uint i; i < len - 1; i++) {
        uint randomIndex = CalcLib.pseudoRandomInRangeFlex(i, len - 1, random_);

        (info.ids[randomIndex], info.ids[i]) = (info.ids[i], info.ids[randomIndex]);
        (info.mins[randomIndex], info.mins[i]) = (info.mins[i], info.mins[randomIndex]);
        (info.maxs[randomIndex], info.maxs[i]) = (info.maxs[i], info.maxs[randomIndex]);
        (info.chances[randomIndex], info.chances[i]) = (info.chances[i], info.chances[randomIndex]);
      }
    }
  }

  /// @notice Generate array [0,1,2.. N-1] and shuffle it using Fisher–Yates shuffle algo
  function _shuffleIndices(
    uint countItems,
    function (uint) internal view returns (uint) random_
  ) internal view returns (uint[] memory indices){
    indices = new uint[](countItems);
    for (uint i = 1; i < countItems; ++i) {
      indices[i] = i;
    }
    if (countItems > 1) {
      for (uint i; i < countItems - 1; i++) {
        uint randomIndex = CalcLib.pseudoRandomInRangeFlex(i, countItems - 1, random_);
        (indices[randomIndex], indices[i]) = (indices[i], indices[randomIndex]);
      }
    }
  }

  /// @notice Reduce lengths of {ids} and {values} to {count}
  function _fixLengthsIdsValues(uint8[] memory ids, int32[] memory values, uint count) internal pure returns (
    uint8[] memory idsOut,
    int32[] memory valuesOut
  ) {
    if (count == ids.length) {
      return (ids, values);
    }

    idsOut = new uint8[](count);
    valuesOut = new int32[](count);
    for (uint i; i < count; ++i) {
      idsOut[i] = ids[i];
      valuesOut[i] = values[i];
    }
    return (idsOut, valuesOut);
  }

  /// @param random_ Pass CalcLib.pseudoRandom here, param is required for unit tests. Max value is MAX_CHANCE
  function _generateSimpleAttributes(
    IItemController.ItemGenerateInfo memory info,
    bool maxChance,
    function (uint) internal view returns (uint) random_
  ) internal view returns (
    uint8[] memory ids,
    int32[] memory values
  ) {
    uint len = info.ids.length;
    ids = new uint8[](len);
    values = new int32[](len);

    uint n = 0;
    for (uint i; i < len; ++i) {
      (int32 attr,) = _generateAttribute(
        info.mins[i],
        info.maxs[i],
        maxChance ? CalcLib.MAX_CHANCE : info.chances[i],
        random_
      );
      if (attr != 0) {
        ids[n] = info.ids[i];
        values[n] = attr;
        ++n;
      }
    }

    return _fixLengthsIdsValues(ids, values, n);
  }

  //endregion ------------------------ Internal logic

  //region ------------------------ Internal utils
  /// @param chance Chance in the range [0...MAX_CHANCE], MAX_CHANCE=1e9 means "mandatory" element.
  /// @param random_ Pass CalcLib.pseudoRandom here, param is required for unit tests
  /// @return attr Either 0 or min <= attr <= max
  /// @return rnd Random value in the range [0...MAX_CHANCE]; It's always 0 for mandatory elements
  function _generateAttribute(
    int32 min,
    int32 max,
    uint32 chance,
    function (uint) internal view returns (uint) random_
  ) internal view returns (
    int32 attr,
    uint rnd
  ) {
    if (chance > CalcLib.MAX_CHANCE) revert IAppErrors.TooHighChance(chance);

    uint diff = uint(CalcLib.absDiff(min, max));

    if (chance < CalcLib.MAX_CHANCE) {
      uint32 random = CalcLib.pseudoRandomUint32Flex(CalcLib.MAX_CHANCE, random_);
      if (random < chance) {
        uint r = uint(CalcLib.MAX_CHANCE - random * (CalcLib.MAX_CHANCE / chance));
        int32 k = int32(int(r * diff / uint(CalcLib.MAX_CHANCE)));
        return (min + k, random);
      }
    } else { // chance == CalcLib.MAX_CHANCE => mandatory element
      if (diff == 0) {
        return (min, 0);
      } else {
        uint r = uint(CalcLib.pseudoRandomUint32Flex(CalcLib.MAX_CHANCE, random_));
        int32 k = int32(int(r % (diff + 1)));

        // return zero random - no need to calc rarity for mandatory elements
        return (min + k, 0);
      }
    }

    return (0, 0);
  }

  /// @notice Calculate item rarity
  /// @param randomSum Total sum random values of all random attributes in ItemGenerateInfo, [0...MAX_CHANCE/attrCounter]
  /// @param chancesSum Total sum of all random chances in ItemGenerateInfo
  /// @param attrCounter Count of random attributes in ItemGenerateInfo
  /// @param maxAttr Index of max allowed random attribute (all attributes with higher indices are not random)
  /// @return item rarity
  function _calculateRarity(uint randomSum, uint chancesSum, uint attrCounter, uint maxAttr) internal pure returns (
    IItemController.ItemRarity
  ) {
    if (attrCounter == 0) {
      return IItemController.ItemRarity.NORMAL;
    }

    uint random = randomSum / attrCounter;
    uint averageChance = chancesSum / attrCounter;

    if (random > CalcLib.MAX_CHANCE) revert IAppErrors.TooHighRandom(random);

    if (random < averageChance / 4 && attrCounter == maxAttr) {
      return IItemController.ItemRarity.RARE;
    } else if (random < averageChance * 3 / 4) {
      return attrCounter > 2
        ? IItemController.ItemRarity.RARE
        : IItemController.ItemRarity.MAGIC;
    } else {
      return attrCounter > 1
        ? IItemController.ItemRarity.MAGIC
        : IItemController.ItemRarity.NORMAL;
    }
  }
  //endregion ------------------------ Internal utils

  //region ------------------------ PACKING

  function packItemGenerateInfo(IItemController.ItemGenerateInfo memory info) internal pure returns (bytes32[] memory result) {
    uint len = info.ids.length;
    if (len != info.mins.length || len != info.maxs.length || len != info.chances.length) {
      revert IAppErrors.LengthsMismatch();
    }

    result = new bytes32[](len);

    for (uint i; i < len; ++i) {
      result[i] = PackingLib.packItemGenerateInfo(info.ids[i], info.mins[i], info.maxs[i], info.chances[i]);
    }
  }

  function unpackItemGenerateInfo(bytes32[] memory gen) internal pure returns (
    IItemController.ItemGenerateInfo memory
  ) {
    uint length = gen.length;

    uint8[] memory ids = new uint8[](length);
    int32[] memory mins = new int32[](length);
    int32[] memory maxs = new int32[](length);
    uint32[] memory chances = new uint32[](length);

    for (uint i; i < length; ++i) {
      (ids[i], mins[i], maxs[i], chances[i]) = gen[i].unpackItemGenerateInfo();
    }

    return IItemController.ItemGenerateInfo(ids, mins, maxs, chances);
  }

  function packItemMeta(IItemController.ItemMeta memory meta) internal pure returns (bytes32) {
    return PackingLib.packItemMeta(
      meta.itemMetaType,
      meta.itemLevel,
      uint8(meta.itemType),
      meta.baseDurability,
      meta.defaultRarity,
      meta.minRandomAttributes,
      meta.maxRandomAttributes,
      meta.manaCost,
      meta.requirements
    );
  }

  function unpackedItemMeta(bytes32 meta) internal pure returns (IItemController.ItemMeta memory result) {
    return meta.unpackItemMeta();
  }

  function packItemInfo(IItemController.ItemInfo memory info) internal pure returns (bytes32) {
    return PackingLib.packItemInfo(uint8(info.rarity), info.augmentationLevel, info.durability);
  }

  function unpackedItemInfo(bytes32 info) internal pure returns (IItemController.ItemInfo memory result) {
    uint8 rarity;
    (rarity, result.augmentationLevel, result.durability) = info.unpackItemInfo();

    result.rarity = IItemController.ItemRarity(rarity);
    return result;
  }

  function packItemAttackInfo(IItemController.AttackInfo memory info) internal pure returns (bytes32) {
    return PackingLib.packItemAttackInfo(
      uint8(info.aType),
      info.min,
      info.max,
      info.attributeFactors.strength,
      info.attributeFactors.dexterity,
      info.attributeFactors.vitality,
      info.attributeFactors.energy
    );
  }

  function unpackItemAttackInfo(bytes32 info) internal pure returns (IItemController.AttackInfo memory result) {
    IStatController.CoreAttributes memory fs;
    uint8 aType;
    (aType, result.min, result.max, fs.strength, fs.dexterity, fs.vitality, fs.energy) = info.unpackItemAttackInfo();

    result.aType = IItemController.AttackType(aType);
    result.attributeFactors = fs;

    return result;
  }
  //endregion ------------------------ PACKING
}

contracts/lib/ItemStatsLib.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.23;


import "./PackingLib.sol";
import "./ItemLib.sol";
import "./AppLib.sol";
import "./CalcLib.sol";
import "./ScoreLib.sol";
import "../interfaces/IERC721.sol";
import "../interfaces/IItemController.sol";
import "../interfaces/IReinforcementController.sol";
import "../interfaces/ITreasury.sol";
import "../interfaces/IItem.sol";
import "../interfaces/IDungeonFactory.sol";
import "../interfaces/IApplicationEvents.sol";
import "../interfaces/IAppErrors.sol";

library ItemStatsLib {
  using EnumerableSet for EnumerableSet.AddressSet;
  using CalcLib for int32;
  using PackingLib for address;
  using PackingLib for bytes32;
  using PackingLib for bytes32[];
  using PackingLib for uint32[];
  using PackingLib for int32[];

  //region ------------------------ CONSTANTS

  uint private constant AUGMENT_CHANCE = 0.7e18;
  /// @dev should be 20%
  uint private constant AUGMENT_FACTOR = 5;
  uint private constant DURABILITY_REDUCTION = 3;
  uint private constant MAX_AUGMENTATION_LEVEL = 10;

  /// @dev keccak256(abi.encode(uint256(keccak256("item.controller.main")) - 1)) & ~bytes32(uint256(0xff))
  bytes32 internal constant MAIN_STORAGE_LOCATION = 0xe78a2879cd91c3f7b62ea14e72546fed47c40919bca4daada532a5fa05ac6700;

  //endregion ------------------------ CONSTANTS

  //region ------------------------ STRUCTS

  struct EquipLocalContext {
    IStatController statController;
    IDungeonFactory dungeonFactory;
    IHeroController hc;
    address payToken;
    address heroToken;
    uint heroTokenId;
//    IItemController.ItemMeta meta;
//    IItemController.ItemInfo itemInfo;
//    bytes32[] attributes;
  }

  struct ReduceDurabilityContext {
    /// @notice values 0 or 1 for SKILL_1, SKILL_2, SKILL_3
    uint8[] skillSlots;
    uint8[] busySlots;
    IStatController statController;
    address itemAdr;
    uint16 durability;
    uint itemId;
  }

  struct TakeOffContext {
    bool broken;
    IController controller;

    address msgSender;
    address heroToken;
    address destination;
    IHeroController heroController;
    IDungeonFactory dungeonFactory;
    IStatController statController;

    uint heroTokenId;
  }
  //endregion ------------------------ STRUCTS

  //region ------------------------ STORAGE
  function _S() internal pure returns (IItemController.MainState storage s) {
    assembly {
      s.slot := MAIN_STORAGE_LOCATION
    }
    return s;
  }
  //endregion ------------------------ STORAGE

  //region ------------------------ RESTRICTIONS

  function onlyDeployer(IController c, address sender) internal view {
    if (!c.isDeployer(sender)) revert IAppErrors.ErrorNotDeployer(sender);
  }

  function onlyOwner(address token, uint tokenId, address sender) internal view {
    if (IERC721(token).ownerOf(tokenId) != sender) revert IAppErrors.ErrorNotHeroOwner(token, sender);
  }

  function onlyEOA(bool isEoa) internal view {
    if (!isEoa) {
      revert IAppErrors.NotEOA(msg.sender);
    }
  }
  //endregion ------------------------ RESTRICTIONS

  //region ------------------------ REGISTER

  function registerItem(
    IController controller,
    address msgSender,
    address item,
    IItemController.RegisterItemParams calldata info
  ) internal {
    onlyDeployer(controller, msgSender);
    IItemController.MainState storage s = _S();

    if (info.itemMeta.itemMetaType == 0) revert IAppErrors.ZeroItemMetaType();
    if (info.itemMeta.itemLevel == 0) revert IAppErrors.ZeroLevel();

    if (!s.items.add(item)) {
      IItemController.ItemMeta memory existMeta = ItemLib.unpackedItemMeta(s.itemMeta[item]);
        // we should not change types for existed items
      if (existMeta.itemType != info.itemMeta.itemType) revert IAppErrors.ItemTypeChanged();
      if (existMeta.itemMetaType != info.itemMeta.itemMetaType) revert IAppErrors.ItemMetaTypeChanged();
    }

    s.itemMeta[item] = ItemLib.packItemMeta(info.itemMeta);

    s.augmentInfo[item] = PackingLib.packAddressWithAmount(info.augmentToken, info.augmentAmount);

    _setAttributesWithCheck(item, info.commonAttributes, s.generateInfoAttributes);

    if (info.itemMeta.itemMetaType == uint8(IItemController.ItemMetaType.CONSUMABLE)) {
      s._itemConsumableAttributes[item] = info.consumableAttributes.values.toBytes32ArrayWithIds(info.consumableAttributes.ids);

      s.itemConsumableStats[item] = StatLib.packChangeableStats(info.consumableStats);
    }

    if (info.itemMeta.itemMetaType == uint8(IItemController.ItemMetaType.BUFF)) {
      _setAttributesWithCheck(item, info.casterAttributes, s.generateInfoCasterAttributes);

      _setAttributesWithCheck(item, info.targetAttributes, s.generateInfoTargetAttributes);
    }

    if (info.itemMeta.itemMetaType == uint8(IItemController.ItemMetaType.ATTACK)) {
      s.generateInfoAttack[item] = ItemLib.packItemAttackInfo(info.genAttackInfo);

      // need to set default attack info for zero id, it will be used in monsters attacks
      s._itemAttackInfo[item.packNftId(0)] = ItemLib.packItemAttackInfo(info.genAttackInfo);
    }

    emit IApplicationEvents.ItemRegistered(item, info);
  }

  /// @notice  Ensure: min != 0, max != 0 and both min and min should have same sign
  /// Value of attribute cannot be equal to 0 because toBytes32ArrayWithIds cannot store zero values.
  function _setAttributesWithCheck(
    address item,
    IItemController.ItemGenerateInfo memory data,
    mapping(address => bytes32[]) storage dest
  ) internal {
    for (uint i = 0; i < data.ids.length; ++i) {
      if (
        data.mins[i] == 0
        || data.maxs[i] == 0
        || data.mins[i] > data.maxs[i]
        || (data.mins[i] < 0 && data.maxs[i] > 0)
      ) revert IAppErrors.IncorrectMinMaxAttributeRange(data.mins[i], data.maxs[i]);
    }

    dest[item] = ItemLib.packItemGenerateInfo(data);
  }

  function removeItem(IController controller, address msgSender, address item) internal {
    IItemController.MainState storage s = _S();
    onlyDeployer(controller, msgSender);

    s.items.remove(item);

    delete s.itemMeta[item];
    delete s.augmentInfo[item];
    delete s.generateInfoAttributes[item];
    delete s._itemConsumableAttributes[item];
    delete s.itemConsumableStats[item];
    delete s.generateInfoCasterAttributes[item];
    delete s.generateInfoTargetAttributes[item];
    delete s.generateInfoAttack[item];
    delete s._itemAttackInfo[item.packNftId(0)];

    emit IApplicationEvents.ItemRemoved(item);
  }
  //endregion ------------------------ REGISTER

  //region ------------------------ EQUIP

  function isItemEquipped(IItemController.MainState storage s, address item, uint itemId) internal view returns (bool) {
    return s.equippedOn[item.packNftId(itemId)] != bytes32(0);
  }

  function equipMany(
    bool isEoa,
    IController controller,
    address msgSender,
    address heroToken,
    uint heroTokenId,
    address[] calldata items,
    uint[] calldata itemIds,
    uint8[] calldata itemSlots
  ) internal {
    onlyEOA(isEoa);
    IItemController.MainState storage s = _S();
    if (items.length != itemIds.length || items.length != itemSlots.length) revert IAppErrors.LengthsMismatch();

    EquipLocalContext memory ctx;
    ctx.statController = IStatController(controller.statController());
    ctx.dungeonFactory = IDungeonFactory(controller.dungeonFactory());
    ctx.hc = IHeroController(controller.heroController());
    (ctx.payToken,) = ctx.hc.payTokenInfo(heroToken);
    ctx.heroTokenId = heroTokenId;
    ctx.heroToken = heroToken;

    _checkHeroAndController(controller, ctx.hc, msgSender, heroToken, heroTokenId);
    if (ctx.dungeonFactory.currentDungeon(heroToken, heroTokenId) != 0) revert IAppErrors.EquipForbiddenInDungeon();

    if (ctx.payToken == address(0)) revert IAppErrors.ErrorEquipForbidden();

    for (uint i; i < items.length; ++i) {
      _equip(s, ctx, msgSender, items[i], itemIds[i], itemSlots[i]);
    }
  }

  /// @notice Equip the item, add bonus attributes, transfer the item from the sender to the hero token
  function _equip(
    IItemController.MainState storage s,
    EquipLocalContext memory c,
    address msgSender,
    address item,
    uint itemId,
    uint8 itemSlot
  ) internal {
    onlyOwner(item, itemId, msgSender);

    IItemController.ItemMeta memory meta = ItemLib.unpackedItemMeta(s.itemMeta[item]);
    IItemController.ItemInfo memory _itemInfo = ItemLib.unpackedItemInfo(s.itemInfo[item.packNftId(itemId)]);

    if (meta.itemMetaType == 0) revert IAppErrors.UnknownItem(item);
    if (isItemEquipped(s, item, itemId)) revert IAppErrors.ItemIsAlreadyEquipped(item);

    if (uint(meta.itemType) == 0) revert IAppErrors.Consumable(item);
    if (meta.baseDurability != 0 && _itemInfo.durability == 0) revert IAppErrors.Broken(item);
    _checkRequirements(c.statController, c.heroToken, c.heroTokenId, meta.requirements);

    c.statController.changeHeroItemSlot(
      c.heroToken,
      uint64(c.heroTokenId),
      uint(meta.itemType),
      itemSlot,
      item,
      itemId,
      true
    );

    bytes32[] memory attributes = s._itemAttributes[item.packNftId(itemId)];
    if (attributes.length != 0) {
      c.statController.changeBonusAttributes(IStatController.ChangeAttributesInfo({
        heroToken: c.heroToken,
        heroTokenId: c.heroTokenId,
        changeAttributes: StatLib.bytesToFullAttributesArray(attributes),
        add: true,
        temporally: false
      }));

      // some items can reduce hero life to zero, prevent this
      if (c.statController.heroStats(c.heroToken, c.heroTokenId).life == 0) revert IAppErrors.ZeroLife();
    }

    // transfer item to hero
    IItem(item).controlledTransfer(msgSender, c.heroToken, itemId);
    // need to equip after transfer for properly checks
    s.equippedOn[item.packNftId(itemId)] = c.heroToken.packNftId(c.heroTokenId);

    emit IApplicationEvents.Equipped(item, itemId, c.heroToken, c.heroTokenId, itemSlot);
  }

  function _checkRequirements(
    IStatController statController,
    address heroToken,
    uint heroTokenId,
    IStatController.CoreAttributes memory requirements
  ) internal view {
    IStatController.CoreAttributes memory attributes = statController.heroBaseAttributes(heroToken, heroTokenId);
    if (
      requirements.strength > attributes.strength
      || requirements.dexterity > attributes.dexterity
      || requirements.vitality > attributes.vitality
      || requirements.energy > attributes.energy
    ) revert IAppErrors.RequirementsToItemAttributes();
  }

  /// @notice Check requirements for the hero and for the controller state before equip/take off/use items
  function _checkHeroAndController(
    IController controller,
    IHeroController heroController,
    address msgSender,
    address heroToken,
    uint heroTokenId
  ) internal view {
    onlyOwner(heroToken, heroTokenId, msgSender);
    if (IReinforcementController(controller.reinforcementController()).isStaked(heroToken, heroTokenId)) revert IAppErrors.Staked(heroToken, heroTokenId);
    if (controller.onPause()) revert IAppErrors.ErrorPaused();
    if (heroController.heroClass(heroToken) == 0) revert IAppErrors.ErrorHeroIsNotRegistered(heroToken);
  }
  //endregion ------------------------ EQUIP

  //region ------------------------ TAKE OFF

  function takeOffMany(
    bool isEoa,
    IController controller,
    address msgSender,
    address heroToken,
    uint heroTokenId,
    address[] calldata items,
    uint[] calldata tokenIds,
    uint8[] calldata itemSlots
  ) internal {
    onlyEOA(isEoa);

    TakeOffContext memory ctx = ItemStatsLib.TakeOffContext({
      controller: controller,
      msgSender: msgSender,
      heroToken: heroToken,
      heroTokenId: heroTokenId,
      destination: msgSender,
      broken: false,
      heroController: IHeroController(controller.heroController()),
      dungeonFactory: IDungeonFactory(controller.dungeonFactory()),
      statController: IStatController(controller.statController())
    });

    IItemController.MainState storage s = _S();
    uint len = items.length;
    if (len != tokenIds.length || len != itemSlots.length) revert IAppErrors.LengthsMismatch();

    for (uint i; i < len; ++i) {
      _takeOffWithChecks(s, ctx, items[i], tokenIds[i], itemSlots[i]);
    }
  }

  /// @dev Some stories can manipulate items
  function takeOffDirectly(
    IController controller,
    address item,
    uint itemId,
    address hero,
    uint heroId,
    uint8 itemSlot,
    address destination,
    bool broken
  ) internal {
    if (controller.storyController() != msg.sender && controller.heroController() != msg.sender) {
      revert IAppErrors.ErrorForbidden(msg.sender);
    }
    ItemStatsLib._takeOff(_S(), IStatController(controller.statController()), item, itemId, hero, heroId, itemSlot, destination, broken);
  }

  function _takeOffWithChecks(
    IItemController.MainState storage s,
    TakeOffContext memory ctx,
    address item,
    uint itemId,
    uint8 itemSlot
  ) internal {
    _checkHeroAndController(ctx.controller,
      ctx.heroController,
      ctx.msgSender,
      ctx.heroToken,
      ctx.heroTokenId
    );
    if (ctx.dungeonFactory.currentDungeon(ctx.heroToken, ctx.heroTokenId) != 0) revert IAppErrors.TakeOffForbiddenInDungeon();

    if (s.equippedOn[item.packNftId(itemId)] != ctx.heroToken.packNftId(ctx.heroTokenId)) revert IAppErrors.NotEquipped(item);

    _takeOff(s, ctx.statController, item, itemId, ctx.heroToken, ctx.heroTokenId, itemSlot, ctx.destination, ctx.broken);
  }

  /// @notice Take off the item, remove bonus attributes, transfer the item from the hero token to {destination}
  /// @param broken True if the item is broken. The durability of the broken item will be set to 0.
  function _takeOff(
    IItemController.MainState storage s,
    IStatController statController,
    address item,
    uint itemId,
    address heroToken,
    uint heroTokenId,
    uint8 itemSlot,
    address destination,
    bool broken
  ) internal {
    bytes32 packedItemId = item.packNftId(itemId);
    IItemController.ItemMeta memory meta = ItemLib.unpackedItemMeta(s.itemMeta[item]);
    IItemController.ItemInfo memory _itemInfo = ItemLib.unpackedItemInfo(s.itemInfo[packedItemId]);

    if (uint(meta.itemType) == 0) revert IAppErrors.Consumable(item);

    statController.changeHeroItemSlot(
      heroToken,
      uint64(heroTokenId),
      uint(meta.itemType),
      itemSlot,
      item,
      itemId,
      false
    );

    if (broken) {
      _itemInfo.durability = 0;
      s.itemInfo[packedItemId] = ItemLib.packItemInfo(_itemInfo);
    }

    bytes32[] memory attributes = s._itemAttributes[packedItemId];
    if (attributes.length != 0) {
      statController.changeBonusAttributes(IStatController.ChangeAttributesInfo({
        heroToken: heroToken,
        heroTokenId: heroTokenId,
        changeAttributes: StatLib.bytesToFullAttributesArray(attributes),
        add: false,
        temporally: false
      }));
    }

    // need to take off before transfer for properly checks
    s.equippedOn[packedItemId] = bytes32(0);
    IItem(item).controlledTransfer(heroToken, destination, itemId);

    emit IApplicationEvents.TakenOff(item, itemId, heroToken, heroTokenId, itemSlot, destination);
  }
  //endregion ------------------------ TAKE OFF

  //region ------------------------ AUGMENT and REPAIR

  /// @notice Initialization for augment() and repairDurability()
  /// Get {meta} and {info}, check some restrictions
  function _prepareToAugment(
    IItemController.MainState storage s,
    bool isEoa,
    IController controller,
    address msgSender,
    address item,
    uint itemId,
    uint consumedItemId
  ) internal view returns(
    IItemController.ItemMeta memory meta,
    IItemController.ItemInfo memory info
  ) {
    onlyEOA(isEoa);
    if (itemId == consumedItemId) revert IAppErrors.SameIdsNotAllowed();
    onlyOwner(item, itemId, msgSender);
    onlyOwner(item, consumedItemId, msgSender);

    meta = ItemLib.unpackedItemMeta(s.itemMeta[item]);
    info = ItemLib.unpackedItemInfo(s.itemInfo[item.packNftId(itemId)]);

    if (isItemEquipped(s, item, itemId) || isItemEquipped(s, item, consumedItemId)) revert IAppErrors.ItemEquipped();
    if (controller.onPause()) revert IAppErrors.ErrorPaused();
  }

  /// @notice Destroy {consumed item} to repair durability of the {item}
  function repairDurability(
    bool isEoa,
    IController controller,
    address msgSender,
    address item,
    uint itemId,
    uint consumedItemId
  ) external {
    // restrictions are checked inside {_prepareToAugment}
    IItemController.MainState storage s = _S();
    (
      IItemController.ItemMeta memory meta,
      IItemController.ItemInfo memory _itemInfo
    ) = _prepareToAugment(s, isEoa, controller, msgSender, item, itemId, consumedItemId);

    if (meta.baseDurability == 0) revert IAppErrors.ZeroDurability();

    _destroy(item, consumedItemId);
    _sendFee(s, controller, item, msgSender, IItemController.FeeType.REPAIR);

    _itemInfo.durability = meta.baseDurability;
    s.itemInfo[item.packNftId(itemId)] = ItemLib.packItemInfo(_itemInfo);

    emit IApplicationEvents.ItemRepaired(item, itemId, consumedItemId, meta.baseDurability);
  }

  /// @notice Destroy {consumed item} to augment given {item}.
  /// There is a chance of 30% that the item will be destroyed instead of augmentation.
  function augment(
    bool isEoa,
    IController controller,
    address msgSender,
    address item,
    uint itemId,
    uint consumedItemId
  ) external {
    // restrictions are checked inside {_prepareToAugment}
    IItemController.MainState storage s = _S();
    (
      IItemController.ItemMeta memory meta,
      IItemController.ItemInfo memory _itemInfo
    ) = _prepareToAugment(s, isEoa, controller, msgSender, item, itemId, consumedItemId);

    if (meta.itemMetaType == uint8(IItemController.ItemMetaType.CONSUMABLE)) revert IAppErrors.Consumable(item);
    if (_itemInfo.augmentationLevel >= MAX_AUGMENTATION_LEVEL) revert IAppErrors.TooHighAgLevel(_itemInfo.augmentationLevel);

    _destroy(item, consumedItemId);

    address augToken = _sendFee(s, controller, item, msgSender, IItemController.FeeType.AUGMENT);
    // we check augToken for 0 AFTER sendFee to avoid second reading of augmentInfo
    if (augToken == address(0)) revert IAppErrors.ZeroAugmentation();

    if (IOracle(controller.oracle()).getRandomNumber(1e18, 0) < AUGMENT_CHANCE) {
      IItemController.AugmentInfo memory _augmentInfo;
      bytes32 packedItemId = item.packNftId(itemId);

      // augment base
      (_augmentInfo.attributesValues, _augmentInfo.attributesIds) = _augmentAttributes(s._itemAttributes[packedItemId], true);
      s._itemAttributes[packedItemId] = _augmentInfo.attributesValues.toBytes32ArrayWithIds(_augmentInfo.attributesIds);

      // additionally
      if (meta.itemMetaType == uint8(IItemController.ItemMetaType.ATTACK)) {
        _augmentInfo.attackInfo = ItemLib.unpackItemAttackInfo(s._itemAttackInfo[packedItemId]);
        _augmentInfo.attackInfo.min = _augmentAttribute(_augmentInfo.attackInfo.min);
        _augmentInfo.attackInfo.max = _augmentAttribute(_augmentInfo.attackInfo.max);
        s._itemAttackInfo[packedItemId] = ItemLib.packItemAttackInfo(_augmentInfo.attackInfo);
      } else if (meta.itemMetaType == uint8(IItemController.ItemMetaType.BUFF)) {
        // caster
        (_augmentInfo.casterValues, _augmentInfo.casterIds) = _augmentAttributes(s._itemCasterAttributes[packedItemId], true);
        s._itemCasterAttributes[packedItemId] = _augmentInfo.casterValues.toBytes32ArrayWithIds(_augmentInfo.casterIds);

        // target
        (_augmentInfo.targetValues, _augmentInfo.targetIds) = _augmentAttributes(s._itemTargetAttributes[packedItemId], false);
        s._itemTargetAttributes[packedItemId] = _augmentInfo.targetValues.toBytes32ArrayWithIds(_augmentInfo.targetIds);
      }

      // increase aug level
      _itemInfo.augmentationLevel = _itemInfo.augmentationLevel + 1;
      s.itemInfo[packedItemId] = ItemLib.packItemInfo(_itemInfo);

      emit IApplicationEvents.Augmented(item, itemId, consumedItemId, _itemInfo.augmentationLevel, _augmentInfo);
    } else {
      _destroy(item, itemId);
      emit IApplicationEvents.NotAugmented(item, itemId, consumedItemId, _itemInfo.augmentationLevel);
    }
  }

  /// @notice Modify either positive or negative values
  /// @param ignoreNegative True - leave unchanged all negative values, False - don't change all positive values
  function _augmentAttributes(bytes32[] memory packedAttr, bool ignoreNegative) internal pure returns (
    int32[] memory values,
    uint8[] memory ids
  ) {
    (values, ids) = packedAttr.toInt32ArrayWithIds();
    for (uint i; i < values.length; ++i) {
      // do not increase destroy item attribute
      if(uint(ids[i]) == uint(IStatController.ATTRIBUTES.DESTROY_ITEMS)) {
        continue;
      }
      if ((ignoreNegative && values[i] > 0) || (!ignoreNegative && values[i] < 0)) {
        values[i] = _augmentAttribute(values[i]);
      }
    }
  }

  /// @notice Increase/decrease positive/negative value on ceil(value/20) but at least on 1
  function _augmentAttribute(int32 value) internal pure returns (int32) {
    if (value == 0) {
      return 0;
    }
    // bonus must be not lower than 1
    if (value > 0) {
      return value + int32(int(Math.max(Math.ceilDiv(value.toUint(), AUGMENT_FACTOR), 1)));
    } else {
      return value - int32(int(Math.max(Math.ceilDiv((- value).toUint(), AUGMENT_FACTOR), 1)));
    }
  }
  //endregion ------------------------ AUGMENT and REPAIR

  //region ------------------------ NEW ITEM CREATION

  function mintNewItem(
    IController controller,
    address sender,
    address item,
    address recipient
  ) internal returns (uint itemId) {
    return ItemLib.mintNewItem(_S(), controller, sender, item, recipient);
  }
  //endregion ------------------------ NEW ITEM CREATION

  //region ------------------------ REDUCE DURABILITY

  /// @notice Reduce durability of all equipped items except items of SKILL-type.
  function reduceEquippedItemsDurability(
    IController controller,
    address msgSender,
    address hero,
    uint heroId,
    uint8 biome
  ) external {
    address dungeonFactory = controller.dungeonFactory();
    if (dungeonFactory != msgSender) revert IAppErrors.ErrorNotDungeonFactory(msgSender);
    IItemController.MainState storage s = _S();

    ReduceDurabilityContext memory ctx;
    ctx.skillSlots = IDungeonFactory(dungeonFactory).skillSlotsForDurabilityReduction(hero, heroId);
    ctx.statController = IStatController(controller.statController());
    ctx.busySlots = ctx.statController.heroItemSlots(hero, heroId);

    for (uint i; i < ctx.busySlots.length; ++i) {

      if (
        (ctx.busySlots[i] == uint8(IStatController.ItemSlots.SKILL_1) && ctx.skillSlots[0] == 0)
        || (ctx.busySlots[i] == uint8(IStatController.ItemSlots.SKILL_2) && ctx.skillSlots[1] == 0)
        || (ctx.busySlots[i] == uint8(IStatController.ItemSlots.SKILL_3) && ctx.skillSlots[2] == 0)
      ) {
        continue;
      }

      (ctx.itemAdr, ctx.itemId) = ctx.statController.heroItemSlot(hero, uint64(heroId), ctx.busySlots[i]).unpackNftId();
      ctx.durability = _reduceDurabilityForItem(s, ctx.itemAdr, ctx.itemId, biome);

      // if broken need to take off
      if (ctx.durability == 0) {
        _takeOff(
          s,
          ctx.statController,
          ctx.itemAdr,
          ctx.itemId,
          hero,
          heroId,
          ctx.busySlots[i],
          IERC721(hero).ownerOf(heroId),
          false
        );
      }

    }
  }

  /// @notice newDurability Calculate new durability for the {item}, update {itemInfo}
  function _reduceDurabilityForItem(
    IItemController.MainState storage s,
    address item,
    uint itemId,
    uint biome
  ) internal returns (uint16 newDurability) {
    IItemController.ItemMeta memory meta = ItemLib.unpackedItemMeta(s.itemMeta[item]);
    IItemController.ItemInfo memory _itemInfo = ItemLib.unpackedItemInfo(s.itemInfo[item.packNftId(itemId)]);

    newDurability = uint16(_calcReduceDurability(biome, _itemInfo.durability, meta.itemLevel, meta.itemType));

    _itemInfo.durability = newDurability;
    s.itemInfo[item.packNftId(itemId)] = ItemLib.packItemInfo(_itemInfo);

    emit IApplicationEvents.ReduceDurability(item, itemId, newDurability);
  }

  /// @return New (reduced) value for the current durability
  function _calcReduceDurability(
    uint biome,
    uint currentDurability,
    uint8 itemLevel,
    IItemController.ItemType itemType
  ) internal pure returns (uint) {
    uint value = DURABILITY_REDUCTION;

    if (itemType != IItemController.ItemType.SKILL) {
      uint itemBiomeLevel = uint(itemLevel) / StatLib.BIOME_LEVEL_STEP + 1;
      if (itemBiomeLevel < biome) {
        value = DURABILITY_REDUCTION * ((biome - itemBiomeLevel + 1) ** 2 / 2);
      }
    }

    return currentDurability > value
      ? currentDurability - value
      : 0;
  }
  //endregion ------------------------ REDUCE DURABILITY

  //region ------------------------ DESTROY

  function destroy(IController controller, address msgSender, address item, uint itemId) external {
    if (
      controller.gameObjectController() != msgSender
      && controller.storyController() != msgSender
      && IERC721(item).ownerOf(itemId) != msgSender
    ) {
      revert IAppErrors.ErrorForbidden(msgSender);
    }

    if (isItemEquipped(_S(), item, itemId)) revert IAppErrors.ItemEquipped();

    _destroy(item, itemId);
  }

  function _destroy(address item, uint itemId) internal {
    IItem(item).burn(itemId);
    emit IApplicationEvents.Destroyed(item, itemId);
  }
  //endregion ------------------------ DESTROY

  //region ------------------------ FEE

  /// @return augToken Return augToken to avoid repeat reading of augmentInfo inside augment()
  function _sendFee(
    IItemController.MainState storage s,
    IController controller,
    address item,
    address msgSender,
    IItemController.FeeType feeType
  ) internal returns (address augToken) {
    (address token, uint amount) = s.augmentInfo[item].unpackAddressWithAmount();
    if (token != address(0)) {
      address treasury = controller.treasury();
      IERC20(token).transferFrom(msgSender, address(this), amount);

      AppLib.approveIfNeeded(token, amount, treasury);
      ITreasury(treasury).sendFee(token, amount, feeType);
    }
    return token;
  }
  //endregion ------------------------ FEE

  //region ------------------------ USE

  /// @notice Use consumable item to temporally increase bonus attributes and destroy the item
  function use(
    bool isEoa,
    IController controller,
    address msgSender,
    address item,
    uint itemId,
    address heroToken,
    uint heroTokenId
  ) external {
    onlyEOA(isEoa);
    onlyOwner(item, itemId, msgSender);

    IItemController.MainState storage s = _S();

    IStatController statController = IStatController(controller.statController());
    IHeroController hc = IHeroController(controller.heroController());

    (address payToken,) = hc.payTokenInfo(heroToken);
    if (payToken == address(0)) revert IAppErrors.UseForbiddenZeroPayToken();

    _checkHeroAndController(controller, hc, msgSender, heroToken, heroTokenId);

    IItemController.ItemMeta memory meta = ItemLib.unpackedItemMeta(s.itemMeta[item]);
    if (uint8(meta.itemType) != 0) revert IAppErrors.NotConsumable(item);
    _checkRequirements(statController, heroToken, heroTokenId, meta.requirements);

    statController.registerConsumableUsage(heroToken, heroTokenId, item);

    statController.changeCurrentStats(heroToken, heroTokenId, StatLib.unpackChangeableStats(s.itemConsumableStats[item]), true);

    bytes32[] memory itemConsumableAttributes = s._itemConsumableAttributes[item];
    if (itemConsumableAttributes.length != 0) {
      int32[] memory attributes = StatLib.bytesToFullAttributesArray(itemConsumableAttributes);
      statController.changeBonusAttributes(IStatController.ChangeAttributesInfo({
        heroToken: heroToken,
        heroTokenId: heroTokenId,
        changeAttributes: attributes,
        add: true,
        temporally: true
      }));
    }

    _destroy(item, itemId);
    emit IApplicationEvents.Used(item, itemId, heroToken, heroTokenId);
  }
  //endregion ------------------------ USE

  //region ------------------------ VIEWS
  function itemByIndex(uint idx) internal view returns (address) {
    return _S().items.at(idx);
  }

  function itemsLength() internal view returns (uint) {
    return _S().items.length();
  }

  function itemMeta(address item) internal view returns (IItemController.ItemMeta memory meta) {
    return ItemLib.unpackedItemMeta(_S().itemMeta[item]);
  }

  function augmentInfo(address item) internal view returns (address token, uint amount) {
    return PackingLib.unpackAddressWithAmount(_S().augmentInfo[item]);
  }

  function genAttributeInfo(address item) internal view returns (IItemController.ItemGenerateInfo memory info) {
    return ItemLib.unpackItemGenerateInfo(_S().generateInfoAttributes[item]);
  }

  function genCasterAttributeInfo(address item) internal view returns (IItemController.ItemGenerateInfo memory info) {
    return ItemLib.unpackItemGenerateInfo(_S().generateInfoCasterAttributes[item]);
  }

  function genTargetAttributeInfo(address item) internal view returns (IItemController.ItemGenerateInfo memory info) {
    return ItemLib.unpackItemGenerateInfo(_S().generateInfoTargetAttributes[item]);
  }

  function genAttackInfo(address item) internal view returns (IItemController.AttackInfo memory info) {
    return ItemLib.unpackItemAttackInfo(_S().generateInfoAttack[item]);
  }

  function itemInfo(address item, uint itemId) internal view returns (IItemController.ItemInfo memory info) {
    return ItemLib.unpackedItemInfo(_S().itemInfo[PackingLib.packNftId(item, itemId)]);
  }

  function equippedOn(address item, uint itemId) internal view returns (address hero, uint heroId) {
    return PackingLib.unpackNftId(_S().equippedOn[item.packNftId(itemId)]);
  }

  function itemAttributes(address item, uint itemId) internal view returns (int32[] memory values, uint8[] memory ids) {
    return PackingLib.toInt32ArrayWithIds(_S()._itemAttributes[PackingLib.packNftId(item, itemId)]);
  }

  function consumableAttributes(address item) internal view returns (int32[] memory values, uint8[] memory ids) {
    return PackingLib.toInt32ArrayWithIds(_S()._itemConsumableAttributes[item]);
  }

  function consumableStats(address item) internal view returns (IStatController.ChangeableStats memory stats) {
    return StatLib.unpackChangeableStats(_S().itemConsumableStats[item]);
  }

  function casterAttributes(address item, uint itemId) internal view returns (int32[] memory values, uint8[] memory ids) {
    return PackingLib.toInt32ArrayWithIds(_S()._itemCasterAttributes[PackingLib.packNftId(item, itemId)]);
  }

  function targetAttributes(address item, uint itemId) internal view returns (int32[] memory values, uint8[] memory ids) {
    return PackingLib.toInt32ArrayWithIds(_S()._itemTargetAttributes[PackingLib.packNftId(item, itemId)]);
  }

  function itemAttackInfo(address item, uint itemId) internal view returns (IItemController.AttackInfo memory info) {
    return ItemLib.unpackItemAttackInfo(_S()._itemAttackInfo[PackingLib.packNftId(item, itemId)]);
  }

  function score(address item, uint itemId) external view returns (uint) {
    return ScoreLib.itemScore(
      StatLib.bytesToFullAttributesArray(_S()._itemAttributes[PackingLib.packNftId(item, itemId)]),
      ItemLib.unpackedItemMeta(_S().itemMeta[item]).baseDurability
    );
  }

  function isAllowedToTransfer(address item, uint itemId) internal view returns (bool) {
    return _S().equippedOn[item.packNftId(itemId)] == bytes32(0);
  }
  //endregion ------------------------ VIEWS

}

contracts/lib/MonsterLib.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.23;

import "../interfaces/IGOC.sol";
import "./CalcLib.sol";
import "./PackingLib.sol";
import "./StatLib.sol";
import "./ItemLib.sol";
import "./StringLib.sol";
import "./FightLib.sol";
import "../interfaces/IController.sol";
import "../interfaces/IStatController.sol";
import "../interfaces/IOracle.sol";
import "../interfaces/IFightCalculator.sol";
import "../interfaces/IDungeonFactory.sol";
import "../interfaces/IItemController.sol";
import "../interfaces/IERC20.sol";

library MonsterLib {
  using CalcLib for int32;
  using PackingLib for bytes32;
  using PackingLib for bytes32[];
  using PackingLib for uint16;
  using PackingLib for uint8;
  using PackingLib for address;
  using PackingLib for uint32[];
  using PackingLib for uint32;
  using PackingLib for uint64;
  using PackingLib for int32[];
  using PackingLib for int32;

  /// @notice Max value for monster rarity and monster/dungeon multiplier
  uint32 internal constant _MAX_AMPLIFIER = 1e9;
  uint private constant _TOTAL_SUPPLY_BASE = 10_000_000e18;

  //region ------------------------ Data types
  struct AdrContext {
    address sender;
    address heroToken;
    IController controller;
    IOracle oracle;
    IStatController statController;
    IItemController itemController;
    uint heroTokenId;
  }

  struct FightInternalInfo {
    int32 manaConsumed;
    int32 damage;
    int32 heroLifeRegen;
    int32 heroHp;
    int32 monsterHp;
    uint32 monsterRarity;
    IFightCalculator.FighterInfo heroFightInfo;
    IFightCalculator.FighterInfo monsterFightInfo;
  }
  //endregion ------------------------ Data types

  //region ------------------------ Main logic

  function initialGeneration(IGOC.MonsterInfo storage mInfo, address heroToken, uint heroTokenId, uint iteration, uint totalSupply) internal {
    return _initialGeneration(mInfo, heroToken, heroTokenId, iteration, _pseudoRandom, totalSupply);
  }

  /// @notice Fight, post fight, generate fight results
  /// @return result Fields objectId, heroToken, heroTokenId, iteration remain uninitialized here.
  /// Caller is responsible to set that values.
  /// @dev weird, but memory ctx is more efficient here than calldata ctx
  function action(IGOC.ActionContext memory ctx, IGOC.MonsterInfo storage mInfo) external returns (
    IGOC.ActionResult memory result,
    uint8 turn
  ) {
    return _action(ctx, mInfo, _pseudoRandom, FightLib.fight);
  }

  //endregion ------------------------ Main logic

  //region ------------------------ Internal calculations
  function _action(
    IGOC.ActionContext memory ctx,
    IGOC.MonsterInfo storage mInfo,
    function (uint) internal view returns (uint) random_,
    function(
      IItemController,
      IFightCalculator.FightCall memory,
      function (uint) internal view returns (uint)
    ) internal returns (IFightCalculator.FightResult memory) fight_
  ) internal returns (
    IGOC.ActionResult memory result,
    uint8 turn
  ) {
    AdrContext memory adrCtx = _context(ctx);
    IGOC.GeneratedMonster memory gen = unpackGeneratedMonster(mInfo._generatedMonsters[ctx.heroToken.packNftId(ctx.heroTokenId)][ctx.iteration]);
    turn = gen.turnCounter;

    (FightInternalInfo memory fInfo, IGOC.MonsterGenInfo memory genInfo) = _fight(ctx, mInfo, gen, adrCtx, random_, fight_);
    result = _postFight(mInfo, ctx, adrCtx, fInfo, genInfo, gen);
  }

  /// @dev This function was extracted from {action()} to simplify unit testing
  /// @param gen These values CAN BE modified in place in some cases.
  /// @return result Fields objectId, heroToken, heroTokenId, iteration remain uninitialized here.
  /// Caller is responsible to set that values.
  function _postFight(
    IGOC.MonsterInfo storage mInfo,
    IGOC.ActionContext memory ctx,
    AdrContext memory adrCtx,
    FightInternalInfo memory fInfo,
    IGOC.MonsterGenInfo memory genInfo,
    IGOC.GeneratedMonster memory gen
  ) internal returns (
    IGOC.ActionResult memory result
  ) {
    bytes32 heroPackedId = ctx.heroToken.packNftId(ctx.heroTokenId);
    if (gen.turnCounter > 100) {
      // instant kill hero if too long battle
      fInfo.heroHp = 0;
    }

    bool isMonsterDead = fInfo.monsterHp == 0;
    bool isHeroDead = fInfo.heroHp == 0;

    if (isMonsterDead) {
      _bossDefeated(adrCtx, ctx);
    }

    if (isMonsterDead || isHeroDead) {
      if (gen.generated) {
        delete mInfo._generatedMonsters[heroPackedId][ctx.iteration];
      }
      // assume that if the hero is dead clearUsedConsumables will be called in _objectAction
      if (isMonsterDead) {
        adrCtx.statController.clearUsedConsumables(ctx.heroToken, ctx.heroTokenId);
      }
    } else {
      if (gen.generated) {
        gen.hp = fInfo.monsterHp;
        gen.turnCounter = gen.turnCounter + 1;
      } else {
        // new instance of gen is created
        gen = IGOC.GeneratedMonster({
          generated: true,
          amplifier: fInfo.monsterRarity,
          hp: fInfo.monsterHp,
          turnCounter: 1
        });
      }

      mInfo._generatedMonsters[heroPackedId][ctx.iteration] = packGeneratedMonster(gen);
    }

    if (isMonsterDead) {
      bytes32 index = _getMonsterCounterIndex(ctx.objectId);
      uint curValue = adrCtx.statController.heroCustomData(ctx.heroToken, ctx.heroTokenId, index);
      adrCtx.statController.setHeroCustomData(ctx.heroToken, ctx.heroTokenId, index, curValue + 1);
    }

    // --- generate result
    result.kill = isHeroDead;
    result.experience = isMonsterDead
      ? StatLib.expPerMonster(
        fInfo.monsterFightInfo.fighterStats.experience,
        fInfo.monsterRarity,
        fInfo.heroFightInfo.fighterStats.experience,
        fInfo.heroFightInfo.fighterStats.level,
        ctx.biome
      )
      : 0;

    result.heal = fInfo.heroLifeRegen;
    result.manaRegen = isMonsterDead ? fInfo.heroFightInfo.fighterAttributes[uint(IStatController.ATTRIBUTES.MANA_AFTER_KILL)] : int32(0);
    // result.lifeChancesRecovered = 0; // zero by default
    result.damage = fInfo.damage;
    result.manaConsumed = fInfo.manaConsumed;
    result.mintItems = isMonsterDead
      ? _mintRandomItems(fInfo, ctx, genInfo, CalcLib.nextPrng)
      : new address[](0);
    result.completed = isMonsterDead || isHeroDead;

    return result;
  }

  /// @notice Generate new {GeneratedMonster} and put it to {mInfo._generatedMonsters}
  /// @param random_ Pass _pseudoRandom here, param is required for unit tests, range [0...MAX_AMPLIFIER]
  function _initialGeneration(
    IGOC.MonsterInfo storage mInfo,
    address heroToken,
    uint heroTokenId,
    uint iteration,
    function (uint) internal view returns (uint) random_,
    uint totalSupply
  ) internal {
    IGOC.GeneratedMonster memory gen = IGOC.GeneratedMonster({
      generated: true,
      amplifier: uint32(random_(_MAX_AMPLIFIER)),
      hp: 0,
      turnCounter: 0
    });

    IGOC.MonsterGenInfo memory info = unpackMonsterInfo(mInfo);

    (int32[] memory attributes,) = generateMonsterAttributes(
      info.attributeIds,
      info.attributeValues,
      gen.amplifier,
      monsterMultiplier(totalSupply),
      info.experience
    );
    gen.hp = attributes[uint(IStatController.ATTRIBUTES.LIFE)];

    mInfo._generatedMonsters[heroToken.packNftId(heroTokenId)][iteration] = packGeneratedMonster(gen);
  }

  function _bossDefeated(AdrContext memory adrCtx, IGOC.ActionContext memory ctx) internal {
    if (ctx.objectSubType == uint8(IGOC.ObjectSubType.BOSS_3)) {
      IDungeonFactory(adrCtx.controller.dungeonFactory()).setBossCompleted(ctx.objectId, ctx.heroToken, ctx.heroTokenId, ctx.biome);
    }
  }

  function _collectHeroFighterInfo(
    IFightCalculator.AttackInfo memory attackInfo,
    AdrContext memory adrContext
  ) internal view returns (
    IFightCalculator.FighterInfo memory fInfo,
    int32 manaConsumed
  ) {
    IStatController.ChangeableStats memory heroStats = adrContext.statController.heroStats(adrContext.heroToken, adrContext.heroTokenId);

    (int32[] memory heroAttributes, int32 _manaConsumed) = _buffAndGetHeroAttributes(heroStats.level, attackInfo, adrContext);

    manaConsumed = _manaConsumed;

    if (attackInfo.attackType == IFightCalculator.AttackType.MAGIC) {
      manaConsumed += int32(adrContext.itemController.itemMeta(attackInfo.attackToken).manaCost);
    }

    fInfo = IFightCalculator.FighterInfo({
      fighterAttributes: heroAttributes,
      fighterStats: heroStats,
      attackType: attackInfo.attackType,
      attackToken: attackInfo.attackToken,
      attackTokenId: attackInfo.attackTokenId,
      race: uint(IStatController.Race.HUMAN)
    });
  }

  function _buffAndGetHeroAttributes(
    uint level,
    IFightCalculator.AttackInfo memory attackInfo,
    AdrContext memory context
  ) internal view returns (
    int32[] memory heroAttributes,
    int32 manaConsumed
  ) {
    return context.statController.buffHero(IStatController.BuffInfo({
      heroToken: context.heroToken,
      heroTokenId: context.heroTokenId,
      heroLevel: uint32(level),
      buffTokens: attackInfo.skillTokens,
      buffTokenIds: attackInfo.skillTokenIds
    }));
  }

  /// @notice Get skill tokens, ensure that they are equipped on, add skill-tokens target attributes to hero attributes
  /// @param attributes Hero attributes. These values are incremented in place
  // @param heroAttackInfo Checked attack info. Assume that all skill tokens belong either to the hero or to the helper.
  function _debuff(
    int32[] memory attributes,
    IFightCalculator.AttackInfo memory heroAttackInfo,
    AdrContext memory context
  ) internal view {
    uint length = heroAttackInfo.skillTokens.length;
    for (uint i; i < length; ++i) {
      (int32[] memory values, uint8[] memory ids) = context.itemController.targetAttributes(
        heroAttackInfo.skillTokens[i],
        heroAttackInfo.skillTokenIds[i]
      );

      StatLib.attributesAdd(attributes, StatLib.valuesToFullAttributesArray(values, ids));
    }
  }

  /// @param random_ Pass _pseudoRandom here, param is required for unit tests, range [0...MAX_AMPLIFIER]
  function _collectMonsterFighterInfo(
    IGOC.MultiplierInfo memory multiplierInfo,
    IGOC.MonsterInfo storage mInfo,
    IGOC.GeneratedMonster memory gen,
    IFightCalculator.AttackInfo memory heroAttackInfo,
    uint heroLevel,
    AdrContext memory adrCtx,
    function (uint) internal view returns (uint) random_
  ) internal view returns (
    IFightCalculator.FighterInfo memory fighterInfo,
    uint32 rarity,
    IGOC.MonsterGenInfo memory genInfo
  ) {
    IFightCalculator.AttackInfo memory attackInfo;

    rarity = gen.generated ? gen.amplifier : uint32(random_(_MAX_AMPLIFIER));
    (
      fighterInfo.fighterAttributes,
      fighterInfo.fighterStats.level,
      fighterInfo.fighterStats.experience,
      attackInfo,
      genInfo
    ) = _generateMonsterInfo(mInfo, rarity, monsterMultiplier(multiplierInfo.totalSupply), heroLevel, multiplierInfo.biome, random_);

    _debuff(fighterInfo.fighterAttributes, heroAttackInfo, adrCtx);

    fighterInfo.fighterStats.life = gen.generated
      ? uint32(gen.hp)
      : fighterInfo.fighterStats.life = uint32(CalcLib.max32(fighterInfo.fighterAttributes[uint(IStatController.ATTRIBUTES.LIFE)], int32(1)));

    fighterInfo.fighterStats.mana = uint32(fighterInfo.fighterAttributes[uint(IStatController.ATTRIBUTES.MANA)]);

    fighterInfo.attackType = attackInfo.attackType;
    fighterInfo.attackToken = attackInfo.attackToken;
    fighterInfo.attackTokenId = attackInfo.attackTokenId;
    fighterInfo.race = genInfo.race;

    return (fighterInfo, rarity, genInfo);
  }

  /// @param random_ Pass _pseudoRandom here, param is required to simplify unit testing
  /// @param fight_ Pass FightLib.fight here, param is required to simplify unit testing
  function _fight(
    IGOC.ActionContext memory ctx,
    IGOC.MonsterInfo storage mInfo,
    IGOC.GeneratedMonster memory gen,
    AdrContext memory adrCtx,
    function (uint) internal view returns (uint) random_,
    function(
      IItemController,
      IFightCalculator.FightCall memory,
      function (uint) internal view returns (uint)
    ) internal returns (IFightCalculator.FightResult memory) fight_
  ) internal returns (
    FightInternalInfo memory fInfo,
    IGOC.MonsterGenInfo memory info
  ) {
    IFightCalculator.FighterInfo memory heroFightInfo;
    IFightCalculator.FighterInfo memory monsterFightInfo;

    {
      IFightCalculator.AttackInfo memory heroAttackInfo = decodeAndCheckAttackInfo(
        adrCtx.itemController,
        IHeroController(IController(adrCtx.controller).heroController()),
        ctx.data,
        adrCtx.heroToken,
        adrCtx.heroTokenId
      );

      // use fInfo.manaConsumed and fInfo.monsterRarity to story values temporally to avoid creation of additional vars
      (heroFightInfo, fInfo.manaConsumed) = _collectHeroFighterInfo(heroAttackInfo, adrCtx);
      (monsterFightInfo, fInfo.monsterRarity, info) = _collectMonsterFighterInfo(
        IGOC.MultiplierInfo(ctx.biome, IERC20(ctx.controller.gameToken()).totalSupply()),
        mInfo,
        gen,
        heroAttackInfo,
        heroFightInfo.fighterStats.level,
        adrCtx,
        random_
      );
    }

    // >>> FIGHT!
    IFightCalculator.FightResult memory fightResult = fight_(
      adrCtx.itemController,
      IFightCalculator.FightCall({
        fighterA: heroFightInfo,
        fighterB: monsterFightInfo,
        dungeonId: ctx.dungeonId,
        objectId: ctx.objectId,
        heroAdr: adrCtx.heroToken,
        heroId: adrCtx.heroTokenId,
        stageId: ctx.stageId,
        iteration: ctx.iteration,
        turn: gen.turnCounter
      }),
      random_
    );

    fInfo = FightInternalInfo({
      manaConsumed: fInfo.manaConsumed + fightResult.manaConsumedA,
      monsterRarity: fInfo.monsterRarity,
      damage: _calcDmg(int32(heroFightInfo.fighterStats.life), fightResult.healthA),
      heroFightInfo: heroFightInfo,
      monsterFightInfo: monsterFightInfo,
      heroLifeRegen: fightResult.healthA > int32(heroFightInfo.fighterStats.life) ? fightResult.healthA - int32(heroFightInfo.fighterStats.life) : int32(0),
      heroHp: fightResult.healthA,
      monsterHp: fightResult.healthB
    });
  }

  /// @param random_ Pass _pseudoRandom here, param is required for unit tests, range [0...1e18]
  /// @return attributes Attributes amplified on amplifier and dungeonMultiplier
  /// @return level Result level in the range: [mInfo.level .. heroLevel]
  /// @return experience Experience amplified on amplifier and dungeonMultiplier
  /// @return attackInfo Attack info. For magic hero attack type monster will have melee in half hits (randomly)
  /// @return info Unpacked data from {mInfo}, some fields can be uninitialized, see comments to unpackMonsterInfo (!)
  function _generateMonsterInfo(
    IGOC.MonsterInfo storage mInfo,
    uint32 amplifier,
    int32 dungeonMultiplier,
    uint heroLevel,
    uint biome,
    function (uint) internal view returns (uint) random_
  ) internal view returns (
    int32[] memory attributes,
    uint32 level,
    uint32 experience,
    IFightCalculator.AttackInfo memory attackInfo,
    IGOC.MonsterGenInfo memory info
  ) {
    info = unpackMonsterInfo(mInfo);

    level = uint32(info.level);
    if (level < heroLevel + 1) {
      level = uint32(Math.min(level + ((heroLevel - level) * 10 / 15), biome * 5));
    }

    if (info.attackType == uint8(IFightCalculator.AttackType.MAGIC)) {
      // sometimes use melee (25% chance)
      uint rnd = random_(1e18);
      if (rnd > 0.75e18) {
        attackInfo.attackType = IFightCalculator.AttackType.MELEE;
      } else {
        attackInfo.attackType = IFightCalculator.AttackType.MAGIC;
        attackInfo.attackToken = info.attackToken;
        attackInfo.attackTokenId = info.attackTokenId;
      }
    } else {
      attackInfo.attackType = IFightCalculator.AttackType(info.attackType);
    }

    (attributes, experience) = generateMonsterAttributes(
      info.attributeIds,
      info.attributeValues,
      amplifier,
      dungeonMultiplier,
      info.experience
    );

    return (attributes, level, experience, attackInfo, info);
  }

  function _mintRandomItems(
    FightInternalInfo memory fInfo,
    IGOC.ActionContext memory ctx,
    IGOC.MonsterGenInfo memory genInfo,
    function (LibPRNG.PRNG memory, uint) internal view returns (uint) nextPrng_
  ) internal returns (
    address[] memory
  ) {
    return ItemLib._mintRandomItems(
      ItemLib.MintItemInfo({
        mintItems: genInfo.mintItems,
        mintItemsChances: genInfo.mintItemsChances,
        biome: ctx.biome,
        amplifier: fInfo.monsterRarity,
        seed: 0,
        oracle: IOracle(ctx.controller.oracle()),
        heroExp: fInfo.heroFightInfo.fighterStats.experience,
        heroCurrentLvl: uint8(fInfo.heroFightInfo.fighterStats.level),
        magicFind: fInfo.heroFightInfo.fighterAttributes[uint(IStatController.ATTRIBUTES.MAGIC_FIND)],
        destroyItems: fInfo.heroFightInfo.fighterAttributes[uint(IStatController.ATTRIBUTES.DESTROY_ITEMS)],
        maxItems: genInfo.maxDropItems
      }),
      nextPrng_
    );
  }

  //endregion ------------------------ Internal calculations

  //region ------------------------ Utils

  function _context(IGOC.ActionContext memory ctx) internal view returns (AdrContext memory context) {
    context = AdrContext({
      sender: ctx.sender,
      heroToken: ctx.heroToken,
      heroTokenId: ctx.heroTokenId,
      controller: ctx.controller,
      oracle: IOracle(ctx.controller.oracle()),
      statController: IStatController(ctx.controller.statController()),
      itemController: IItemController(ctx.controller.itemController())
    });
  }

  function unpackGeneratedMonster(bytes32 gen) internal pure returns (IGOC.GeneratedMonster memory result) {
    (bool generated, uint32 amplifier, int32 hp, uint8 turnCounter) = gen.unpackGeneratedMonster();
    result = IGOC.GeneratedMonster({
      generated: generated,
      amplifier: amplifier,
      hp: hp,
      turnCounter: turnCounter
    });
  }

  function packGeneratedMonster(IGOC.GeneratedMonster memory gen) internal pure returns (bytes32) {
    return PackingLib.packGeneratedMonster(gen.generated, gen.amplifier, gen.hp, gen.turnCounter);
  }

  function packMonsterInfo(IGOC.MonsterGenInfo memory mInfo, IGOC.MonsterInfo storage info) internal {
    info.attributes = mInfo.attributeValues.toBytes32ArrayWithIds(mInfo.attributeIds);
    info.stats = PackingLib.packMonsterStats(mInfo.level, mInfo.race, mInfo.experience, mInfo.maxDropItems);
    info.attackInfo = PackingLib.packAttackInfo(mInfo.attackToken, mInfo.attackTokenId, mInfo.attackType);

    uint len = mInfo.mintItems.length;
    bytes32[] memory mintItems = new bytes32[](len);

    for (uint i; i < len; ++i) {
      mintItems[i] = mInfo.mintItems[i].packItemMintInfo(mInfo.mintItemsChances[i]);
    }

    info.mintItems = mintItems;
  }

  /// @return Attention: Following fields are not initialized: biome, subType, monsterId
  function unpackMonsterInfo(IGOC.MonsterInfo storage mInfo) internal view returns (IGOC.MonsterGenInfo memory) {
    IGOC.MonsterGenInfo memory result;
    (result.attributeValues, result.attributeIds) = mInfo.attributes.toInt32ArrayWithIds();
    (result.level, result.race, result.experience, result.maxDropItems) = mInfo.stats.unpackMonsterStats();
    (result.attackToken, result.attackTokenId, result.attackType) = mInfo.attackInfo.unpackAttackInfo();

    uint len = mInfo.mintItems.length;
    result.mintItems = new address[](len);
    result.mintItemsChances = new uint32[](len);

    for (uint i = 0; i < len; i++) {
      (result.mintItems[i], result.mintItemsChances[i]) = mInfo.mintItems[i].unpackItemMintInfo();
    }

    // Attention: result.biome, result.subType, result.monsterId are not initialized
    return result;
  }

  /// @notice Decode attack info. Ensure that attack token belongs to the hero.
  /// Ensure that skill tokens belong to the hero OR to the current helper (SIP-001)
  function decodeAndCheckAttackInfo(
    IItemController ic,
    IHeroController heroController,
    bytes memory data,
    address heroToken,
    uint heroId
  ) internal view returns (IFightCalculator.AttackInfo memory) {
    (IFightCalculator.AttackInfo memory attackInfo) = abi.decode(data, (IFightCalculator.AttackInfo));

    if (uint(attackInfo.attackType) == 0) revert IAppErrors.UnknownAttackType(uint(attackInfo.attackType));

    if (attackInfo.attackToken != address(0)) {
      (address h, uint hId) = ic.equippedOn(attackInfo.attackToken, attackInfo.attackTokenId);
      if (heroToken != h || hId != heroId) revert IAppErrors.NotYourAttackItem();
    }

    (address helperHeroToken, uint helperHeroId) = heroController.heroReinforcementHelp(heroToken, heroId);
    for (uint i; i < attackInfo.skillTokens.length; ++i) {
      (address h, uint hId) = ic.equippedOn(attackInfo.skillTokens[i], attackInfo.skillTokenIds[i]);
      if (
        (heroToken != h || hId != heroId)
        && ((helperHeroToken == address(0)) || (helperHeroToken != h || helperHeroId != hId))
      ) revert IAppErrors.NotYourBuffItem();
    }

    return attackInfo;
  }

  /// @dev MAX_AMPLIFIER value will means +100% to all attributes
  ///      slowly increase monsters power from 10% of the target supply
  function monsterMultiplier(uint totalSupply) internal pure returns (int32) {
    if(totalSupply < _TOTAL_SUPPLY_BASE / 10) {
      return 0;
    }

    return int32(int((uint(_MAX_AMPLIFIER) * totalSupply) / _TOTAL_SUPPLY_BASE));
  }

  function getMonsterMultiplier(IController controller) internal view returns (int32) {
    uint totalSupply = IERC20(controller.gameToken()).totalSupply();
    return monsterMultiplier(totalSupply);
  }

  function amplifyMonsterAttribute(int32 value, uint32 amplifier, int32 dungeonMultiplier) internal pure returns (int32) {
    if (value == 0) {
      return 0;
    }
    return value +
      int32(
        (int(value) * int(uint(amplifier)) / int(uint(_MAX_AMPLIFIER)))
        + (int(value) * int(dungeonMultiplier) / int(uint(_MAX_AMPLIFIER)))
      );
  }

  /// @dev A wrapper around {CalcLib.pseudoRandom} to pass it as param (to be able to implement unit tests}
  function _pseudoRandom(uint max) internal view returns (uint) {
    return CalcLib.pseudoRandom(max);
  }

  /// @notice Amplify values of the attributes and of the experience
  ///         using randomly generated {amplifier} and {dungeonMultiplier}.
  ///         Attributes = amplify(ids, values), experience = amplify(baseExperience)
  function generateMonsterAttributes(
    uint8[] memory ids,
    int32[] memory values,
    uint32 amplifier,
    int32 dungeonMultiplier,
    uint32 baseExperience
  ) internal pure returns (
    int32[] memory attributes,
    uint32 experience
  ) {
    // reduce random
    amplifier = amplifier / 4;

    attributes = new int32[](uint(IStatController.ATTRIBUTES.END_SLOT));
    for (uint i; i < ids.length; ++i) {
      attributes[ids[i]] = amplifyMonsterAttribute(values[i], amplifier, dungeonMultiplier);
    }
    experience = uint32(amplifyMonsterAttribute(int32(baseExperience), amplifier, dungeonMultiplier));
  }

  function _calcDmg(int32 heroLifeBefore, int32 heroLifeAfter) internal pure returns (int32 damage) {
    return heroLifeAfter == 0
      ? heroLifeBefore
      : heroLifeBefore - CalcLib.minI32(heroLifeAfter, heroLifeBefore);
  }

  function _getMonsterCounterIndex(uint32 objectId) internal pure returns (bytes32) {
    return bytes32(abi.encodePacked("MONSTER_", StringLib._toString(uint(objectId))));
  }
  //endregion ------------------------ Utils


}

contracts/lib/PackingLib.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.23;

import "../interfaces/IItemController.sol";
import "../interfaces/IStatController.sol";

library PackingLib {

  error TooHighValue(uint value);
  error OutOfBounds(uint index, uint length);
  error UnexpectedValue(uint expected, uint actual);
  error WrongValue(uint newValue, uint actual);
  error LengthsMismatch();
  error IntOutOfRange(int value);
  error ZeroValue();
  /// @notice packCustomDataChange requires an input string with two zero bytes at the beginning
  ///         0xXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXX0000
  /// This error happens if these bytes are not zero
  error IncompatibleInputString();

  //////////////////////////
  // ---- PACKING LOGIC ----
  //////////////////////////

  //region ------------------------------------ COMMON

  function packNftId(address token, uint id) internal pure returns (bytes32 serialized) {
    if (id > uint(type(uint64).max)) revert TooHighValue(id);
    serialized = bytes32(uint(uint160(token)));
    serialized |= bytes32(uint(uint64(id))) << 160;
  }

  function unpackNftId(bytes32 data) internal pure returns (address token, uint id) {
    token = address(uint160(uint(data)));
    id = uint(data) >> 160;
  }

  function packAddressWithAmount(address token, uint amount) internal pure returns (bytes32 data) {
    if (amount > uint(type(uint96).max)) revert TooHighValue(amount);
    data = bytes32(uint(uint160(token)));
    data |= bytes32(uint(uint96(amount))) << 160;
  }

  function unpackAddressWithAmount(bytes32 data) internal pure returns (address token, uint amount) {
    token = address(uint160(uint(data)));
    amount = uint(data) >> 160;
  }

  function packItemMintInfo(address item, uint32 chance) internal pure returns (bytes32 data) {
    data = bytes32(uint(uint160(item)));
    data |= bytes32(uint(chance)) << 160;
  }

  function unpackItemMintInfo(bytes32 data) internal pure returns (address item, uint32 chance) {
    item = address(uint160(uint(data)));
    chance = uint32(uint(data) >> 160);
  }

  /// @param customDataIndex We assume, that two lowest bytes of this string are always zero
  /// So, the string looks like following: 0xXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXX0000
  /// Last 2 bytes will be used to encode {value}
  function packCustomDataChange(bytes32 customDataIndex, int16 value) internal pure returns (bytes32 data) {
    if (uint(customDataIndex) != (uint(customDataIndex) >> 16) << 16) revert IncompatibleInputString();
    data = bytes32(uint(customDataIndex));
    data |= bytes32(uint(uint16(value)));
  }

  function unpackCustomDataChange(bytes32 data) internal pure returns (bytes32 customDataIndex, int16 value) {
    customDataIndex = bytes32((uint(data) >> 16) << 16);
    value = int16(int(uint(uint16(uint(data)))));
  }

  /// @dev min(uint64) + max(uint64) + isHeroData/isMandatory(uint8)
  function packCustomDataRequirements(uint64 min, uint64 max, bool key) internal pure returns (bytes32 data) {
    data = bytes32(uint(min));
    data |= bytes32(uint(max)) << 64;
    data |= bytes32(uint(key ? uint8(1) : uint8(0))) << (64 + 64);
  }

  function unpackCustomDataRequirements(bytes32 data) internal pure returns (uint64 min, uint64 max, bool key) {
    min = uint64(uint(data));
    max = uint64(uint(data) >> 64);
    key = uint8(uint(data) >> (64 + 64)) == uint8(1);
  }

  function packStatsChange(
    uint32 experience,
    int32 heal,
    int32 manaRegen,
    int32 lifeChancesRecovered,
    int32 damage,
    int32 manaConsumed
  ) internal pure returns (bytes32 data) {
    data = bytes32(uint(experience));
    data |= bytes32(uint(uint32(heal))) << 32;
    data |= bytes32(uint(uint32(manaRegen))) << (32 + 32);
    data |= bytes32(uint(uint32(lifeChancesRecovered))) << (32 + 32 + 32);
    data |= bytes32(uint(uint32(damage))) << (32 + 32 + 32 + 32);
    data |= bytes32(uint(uint32(manaConsumed))) << (32 + 32 + 32 + 32 + 32);
  }

  function unpackStatsChange(bytes32 data) internal pure returns (
    uint32 experience,
    int32 heal,
    int32 manaRegen,
    int32 lifeChancesRecovered,
    int32 damage,
    int32 manaConsumed
  ) {
    experience = uint32(uint(data));
    heal = int32(int(uint(data) >> 32));
    manaRegen = int32(int(uint(data) >> (32 + 32)));
    lifeChancesRecovered = int32(int(uint(data) >> (32 + 32 + 32)));
    damage = int32(int(uint(data) >> (32 + 32 + 32 + 32)));
    manaConsumed = int32(int(uint(data) >> (32 + 32 + 32 + 32 + 32)));
  }
  //endregion ------------------------------------ COMMON

  //region ------------------------------------ WORLD/BATTLEFIELD MAP

  function packMapObject(address objectAddress, uint64 objectId, uint8 objectType) internal pure returns (bytes32 packedData) {
    packedData = bytes32(bytes20(objectAddress));
    packedData |= bytes32(uint(objectId) << 32);
    packedData |= bytes32(uint(objectType) << 24);
  }

  function unpackMapObject(bytes32 packedData) internal pure returns (address objectAddress, uint64 objectId, uint8 objectType) {
    objectAddress = address(bytes20(packedData));
    objectId = uint64(uint(packedData) >> 32);
    objectType = uint8(uint(packedData) >> 24);
  }

  function packCoordinate(uint128 x, uint128 y) internal pure returns (bytes32 packedData) {
    packedData = bytes32(uint(x));
    packedData |= bytes32(uint(y) << 128);
  }

  function unpackCoordinate(bytes32 packedData) internal pure returns (uint128 x, uint128 y) {
    x = uint128(uint(packedData));
    y = uint128(uint(packedData) >> 128);
  }

  /// @param x Assume x <= max uint64
  /// @param y Assume y <= max uint64
  function packBattlefieldId(uint8 biomeMapFieldId, uint8 territoryNumber, uint128 x, uint128 y) internal pure returns (bytes32 packedData) {
    // 256 => 128 + 128;
    // 1) 128 is used for biomeMapFieldId, territoryNumber and probably other fields in the future
    // 2) 128 is used to store x, y as uint64, uint64

    // we will use uint64 for coordinates assuming it is more than enough for biome map
    packedData = bytes32(uint(biomeMapFieldId));
    packedData |= bytes32(uint(territoryNumber) << (8));
    packedData |= bytes32(uint(uint64(x)) << 128);
    packedData |= bytes32(uint(uint64(y)) << (64 + 128));
  }

  function unpackBattlefieldId(bytes32 packedData) internal pure returns (uint8 biomeMapFieldId, uint8 territoryNumber, uint128 x, uint128 y) {
    biomeMapFieldId = uint8(uint(packedData));
    territoryNumber = uint8(uint(packedData) >> (8));
    x = uint128(uint64(uint(packedData) >> (128)));
    y = uint128(uint64(uint(packedData) >> (64 + 128)));
  }
  //endregion ------------------------------------ WORLD/BATTLEFIELD MAP

  //region ------------------------------------ REINFORCEMENT

  function packReinforcementHeroInfo(uint8 biome, uint128 score, uint8 fee, uint64 stakeTs) internal pure returns (bytes32 packedData) {
    packedData = bytes32(uint(biome));
    packedData |= bytes32(uint(score) << 8);
    packedData |= bytes32(uint(fee) << (8 + 128));
    packedData |= bytes32(uint(stakeTs) << (8 + 128 + 8));
  }

  function unpackReinforcementHeroInfo(bytes32 packedData) internal pure returns (uint8 biome, uint128 score, uint8 fee, uint64 stakeTs) {
    biome = uint8(uint(packedData));
    score = uint128(uint(packedData) >> 8);
    fee = uint8(uint(packedData) >> (8 + 128));
    stakeTs = uint64(uint(packedData) >> (8 + 128 + 8));
  }
  //endregion ------------------------------------ REINFORCEMENT

  //region ------------------------------------ DUNGEON

  function packDungeonKey(address heroAdr, uint80 heroId, uint16 dungLogicNum) internal pure returns (bytes32 data) {
    data = bytes32(uint(uint160(heroAdr)));
    data |= bytes32(uint(heroId)) << 160;
    data |= bytes32(uint(dungLogicNum)) << (160 + 80);
  }

  function unpackDungeonKey(bytes32 data) internal pure returns (address heroAdr, uint80 heroId, uint16 dungLogicNum) {
    heroAdr = address(uint160(uint(data)));
    heroId = uint80(uint(data) >> 160);
    dungLogicNum = uint16(uint(data) >> (160 + 80));
  }

  // --- GAME OBJECTS ---

  function packIterationKey(address heroAdr, uint64 heroId, uint32 objId) internal pure returns (bytes32 data) {
    data = bytes32(uint(uint160(heroAdr)));
    data |= bytes32(uint(heroId)) << 160;
    data |= bytes32(uint(objId)) << (160 + 64);
  }

  function unpackIterationKey(bytes32 data) internal pure returns (address heroAdr, uint64 heroId, uint32 objId) {
    heroAdr = address(uint160(uint(data)));
    heroId = uint64(uint(data) >> 160);
    objId = uint32(uint(data) >> (160 + 64));
  }

  function packMonsterStats(
    uint8 level,
    uint8 race,
    uint32 experience,
    uint8 maxDropItems
  ) internal pure returns (bytes32 data) {
    data = bytes32(uint(level));
    data |= bytes32(uint(race)) << 8;
    data |= bytes32(uint(experience)) << (8 + 8);
    data |= bytes32(uint(maxDropItems)) << (8 + 8 + 32);
  }

  function unpackMonsterStats(bytes32 data) internal pure returns (
    uint8 level,
    uint8 race,
    uint32 experience,
    uint8 maxDropItems
  ) {
    level = uint8(uint(data));
    race = uint8(uint(data) >> 8);
    experience = uint32(uint(data) >> (8 + 8));
    maxDropItems = uint8(uint(data) >> (8 + 8 + 32));
  }

  function packAttackInfo(
    address attackToken,
    uint64 attackTokenId,
    uint8 attackType
  ) internal pure returns (bytes32 data) {
    data = bytes32(uint(uint160(attackToken)));
    data |= bytes32(uint(attackTokenId)) << 160;
    data |= bytes32(uint(attackType)) << (160 + 64);
  }

  function unpackAttackInfo(bytes32 data) internal pure returns (
    address attackToken,
    uint64 attackTokenId,
    uint8 attackType
  ) {
    attackToken = address(uint160(uint(data)));
    attackTokenId = uint64(uint(data) >> 160);
    attackType = uint8(uint(data) >> (160 + 64));
  }

  function packPlayedObjKey(address heroAdr, uint64 heroId, uint8 oType, uint8 biome) internal pure returns (bytes32 data) {
    data = bytes32(uint(uint160(heroAdr)));
    data |= bytes32(uint(heroId)) << 160;
    data |= bytes32(uint(oType)) << (160 + 64);
    data |= bytes32(uint(biome)) << (160 + 64 + 8);
  }

  function unpackPlayedObjKey(bytes32 data) internal pure returns (address heroAdr, uint64 heroId, uint8 oType, uint8 biome) {
    heroAdr = address(uint160(uint(data)));
    heroId = uint64(uint(data) >> 160);
    oType = uint8(uint(data) >> (160 + 64));
    biome = uint8(uint(data) >> (160 + 64 + 8));
  }

  function packGeneratedMonster(bool generated, uint32 amplifier, int32 hp, uint8 turnCounter) internal pure returns (bytes32 data) {
    data = bytes32(uint(uint8(generated ? 1 : 0)));
    data |= bytes32(uint(amplifier)) << 8;
    data |= bytes32(uint(uint32(hp))) << (8 + 32);
    data |= bytes32(uint(turnCounter)) << (8 + 32 + 32);
  }

  function unpackGeneratedMonster(bytes32 data) internal pure returns (bool generated, uint32 amplifier, int32 hp, uint8 turnCounter) {
    generated = uint8(uint(data)) == uint8(1);
    amplifier = uint32(uint(data) >> 8);
    hp = int32(int(uint(data) >> (8 + 32)));
    turnCounter = uint8(uint(data) >> (8 + 32 + 32));
  }
  //endregion ------------------------------------ DUNGEON

  //region ------------------------------------ ITEMS

  /// @notice itemMetaType8 + itemLvl8 + itemType8 + baseDurability16 + defaultRarity8 + minAttr8 + maxAttr8 + manaCost32 + req(packed core 128)
  /// @param itemType This is ItemType enum
  function packItemMeta(
    uint8 itemMetaType,
    uint8 itemLvl,
    uint8 itemType,
    uint16 baseDurability,
    uint8 defaultRarity,
    uint8 minAttr,
    uint8 maxAttr,
    uint32 manaCost,
    IStatController.CoreAttributes memory req
  ) internal pure returns (bytes32 data) {
    data = bytes32(uint(itemMetaType));
    data |= bytes32(uint(itemLvl)) << 8;
    data |= bytes32(uint(itemType)) << (8 + 8);
    data |= bytes32(uint(baseDurability)) << (8 + 8 + 8);
    data |= bytes32(uint(defaultRarity)) << (8 + 8 + 8 + 16);
    data |= bytes32(uint(minAttr)) << (8 + 8 + 8 + 16 + 8);
    data |= bytes32(uint(maxAttr)) << (8 + 8 + 8 + 16 + 8 + 8);
    data |= bytes32(uint(manaCost)) << (8 + 8 + 8 + 16 + 8 + 8 + 8);
    data |= bytes32(uint(int(req.strength))) << (8 + 8 + 8 + 16 + 8 + 8 + 8 + 32);
    data |= bytes32(uint(int(req.dexterity))) << (8 + 8 + 8 + 16 + 8 + 8 + 8 + 32 + 32);
    data |= bytes32(uint(int(req.vitality))) << (8 + 8 + 8 + 16 + 8 + 8 + 8 + 32 + 32 + 32);
    data |= bytes32(uint(int(req.energy))) << (8 + 8 + 8 + 16 + 8 + 8 + 8 + 32 + 32 + 32 + 32);
  }

  function unpackItemMeta(bytes32 data) internal pure returns (IItemController.ItemMeta memory) {
    IItemController.ItemMeta memory result;

    result.itemMetaType = uint8(uint(data));
    result.itemLevel = uint8(uint(data) >> 8);
    result.itemType = IItemController.ItemType(uint8(uint(data) >> (8 + 8)));
    result.baseDurability = uint16(uint(data) >> (8 + 8 + 8));
    result.defaultRarity = uint8(uint(data) >> (8 + 8 + 8 + 16));
    result.minRandomAttributes = uint8(uint(data) >> (8 + 8 + 8 + 16 + 8));
    result.maxRandomAttributes = uint8(uint(data) >> (8 + 8 + 8 + 16 + 8 + 8));
    result.manaCost = uint32(uint(data) >> (8 + 8 + 8 + 16 + 8 + 8 + 8));
    result.requirements.strength = int32(int(uint(data) >> (8 + 8 + 8 + 16 + 8 + 8 + 8 + 32)));
    result.requirements.dexterity = int32(int(uint(data) >> (8 + 8 + 8 + 16 + 8 + 8 + 8 + 32 + 32)));
    result.requirements.vitality = int32(int(uint(data) >> (8 + 8 + 8 + 16 + 8 + 8 + 8 + 32 + 32 + 32)));
    result.requirements.energy = int32(int(uint(data) >> (8 + 8 + 8 + 16 + 8 + 8 + 8 + 32 + 32 + 32 + 32)));

    return result;
  }

  function packItemGenerateInfo(uint8 id, int32 min, int32 max, uint32 chance) internal pure returns (bytes32 data) {
    data = bytes32(uint(id));
    data |= bytes32(uint(uint32(min))) << 8;
    data |= bytes32(uint(uint32(max))) << (8 + 32);
    data |= bytes32(uint(chance)) << (8 + 32 + 32);
  }

  function unpackItemGenerateInfo(bytes32 data) internal pure returns (uint8 id, int32 min, int32 max, uint32 chance) {
    id = uint8(uint(data));
    min = int32(int(uint(data) >> 8));
    max = int32(int(uint(data) >> (8 + 32)));
    chance = uint32(uint(data) >> (8 + 32 + 32));
  }

  function packItemAttackInfo(
    uint8 attackType,
    int32 min,
    int32 max,
    int32 factorStr,
    int32 factorDex,
    int32 factorVit,
    int32 factorEng
  ) internal pure returns (bytes32 data) {
    data = bytes32(uint(attackType));
    data |= bytes32(uint(uint32(min))) << 8;
    data |= bytes32(uint(uint32(max))) << (8 + 32);
    data |= bytes32(uint(int(factorStr))) << (8 + 32 + 32);
    data |= bytes32(uint(int(factorDex))) << (8 + 32 + 32 + 32);
    data |= bytes32(uint(int(factorVit))) << (8 + 32 + 32 + 32 + 32);
    data |= bytes32(uint(int(factorEng))) << (8 + 32 + 32 + 32 + 32 + 32);
  }

  function unpackItemAttackInfo(bytes32 data) internal pure returns (
    uint8 attackType,
    int32 min,
    int32 max,
    int32 factorStr,
    int32 factorDex,
    int32 factorVit,
    int32 factorEng
  ) {
    attackType = uint8(uint(data));
    min = int32(int(uint(data) >> 8));
    max = int32(int(uint(data) >> (8 + 32)));
    factorStr = int32(int(uint(data) >> (8 + 32 + 32)));
    factorDex = int32(int(uint(data) >> (8 + 32 + 32 + 32)));
    factorVit = int32(int(uint(data) >> (8 + 32 + 32 + 32 + 32)));
    factorEng = int32(int(uint(data) >> (8 + 32 + 32 + 32 + 32 + 32)));
  }

  function packItemInfo(uint8 rarity, uint8 augmentationLevel, uint16 durability) internal pure returns (bytes32 data) {
    data = bytes32(uint(rarity));
    data |= bytes32(uint(augmentationLevel)) << 8;
    data |= bytes32(uint(durability)) << (8 + 8);
  }

  function unpackItemInfo(bytes32 data) internal pure returns (uint8 rarity, uint8 augmentationLevel, uint16 durability) {
    rarity = uint8(uint(data));
    augmentationLevel = uint8(uint(data) >> 8);
    durability = uint16(uint(data) >> (8 + 8));
  }
  //endregion ------------------------------------ ITEMS

  //region ------------------------------------ STORIES

  function packStoryPageId(uint16 storyId, uint16 pageId, uint8 heroClass) internal pure returns (bytes32 data) {
    data = bytes32(uint(storyId));
    data |= bytes32(uint(pageId)) << 16;
    data |= bytes32(uint(heroClass)) << (16 + 16);
  }

  function unpackStoryPageId(bytes32 data) internal pure returns (uint16 storyId, uint16 pageId, uint8 heroClass) {
    storyId = uint16(uint(data));
    pageId = uint16(uint(data) >> 16);
    heroClass = uint8(uint(data) >> (16 + 16));
  }

  function packStoryAnswerId(uint16 storyId, uint16 pageId, uint8 heroClass, uint16 answerId) internal pure returns (bytes32 data) {
    data = bytes32(uint(storyId));
    data |= bytes32(uint(pageId)) << 16;
    data |= bytes32(uint(heroClass)) << (16 + 16);
    data |= bytes32(uint(answerId)) << (16 + 16 + 8);
  }

  function unpackStoryAnswerId(bytes32 data) internal pure returns (uint16 storyId, uint16 pageId, uint8 heroClass, uint16 answerId) {
    storyId = uint16(uint(data));
    pageId = uint16(uint(data) >> 16);
    heroClass = uint8(uint(data) >> (16 + 16));
    answerId = uint16(uint(data) >> (16 + 16 + 8));
  }

  function packStoryNextPagesId(uint16 storyId, uint16 pageId, uint8 heroClass, uint16 answerId, uint8 resultId) internal pure returns (bytes32 data) {
    data = bytes32(uint(storyId));
    data |= bytes32(uint(pageId)) << 16;
    data |= bytes32(uint(heroClass)) << (16 + 16);
    data |= bytes32(uint(answerId)) << (16 + 16 + 8);
    data |= bytes32(uint(resultId)) << (16 + 16 + 8 + 16);
  }

  function unpackStoryNextPagesId(bytes32 data) internal pure returns (uint16 storyId, uint16 pageId, uint8 heroClass, uint16 answerId, uint8 resultId) {
    storyId = uint16(uint(data));
    pageId = uint16(uint(data) >> 16);
    heroClass = uint8(uint(data) >> (16 + 16));
    answerId = uint16(uint(data) >> (16 + 16 + 8));
    resultId = uint8(uint(data) >> (16 + 16 + 8 + 16));
  }

  function packStoryAttributeRequirement(uint8 attributeIndex, int32 value, bool isCore) internal pure returns (bytes32 data) {
    data = bytes32(uint(attributeIndex));
    data |= bytes32(uint(uint32(value))) << 8;
    data |= bytes32(uint(isCore ? uint8(1) : uint8(0))) << (8 + 32);
  }

  function unpackStoryAttributeRequirement(bytes32 data) internal pure returns (uint8 attributeIndex, int32 value, bool isCore) {
    attributeIndex = uint8(uint(data));
    value = int32(int(uint(data) >> 8));
    isCore = uint8(uint(data) >> (8 + 32)) == uint8(1);
  }

  function packStoryItemRequirement(address item, bool requireItemBurn, bool requireItemEquipped) internal pure returns (bytes32 data) {
    data = bytes32(uint(uint160(item)));
    data |= bytes32(uint(requireItemBurn ? uint8(1) : uint8(0))) << 160;
    data |= bytes32(uint(requireItemEquipped ? uint8(1) : uint8(0))) << (160 + 8);
  }

  function unpackStoryItemRequirement(bytes32 data) internal pure returns (address item, bool requireItemBurn, bool requireItemEquipped) {
    item = address(uint160(uint(data)));
    requireItemBurn = uint8(uint(data) >> 160) == uint8(1);
    requireItemEquipped = uint8(uint(data) >> (160 + 8)) == uint8(1);
  }

  /// @dev max amount is 309,485,009 for token with 18 decimals
  function packStoryTokenRequirement(address token, uint88 amount, bool requireTransfer) internal pure returns (bytes32 data) {
    data = bytes32(uint(uint160(token)));
    data |= bytes32(uint(amount)) << 160;
    data |= bytes32(uint(requireTransfer ? uint8(1) : uint8(0))) << (160 + 88);
  }

  function unpackStoryTokenRequirement(bytes32 data) internal pure returns (address token, uint88 amount, bool requireTransfer) {
    token = address(uint160(uint(data)));
    amount = uint88(uint(data) >> 160);
    requireTransfer = uint8(uint(data) >> (160 + 88)) == uint8(1);
  }

  function packStoryCustomDataResult(uint16 storyId, uint16 pageId, uint8 heroClass, uint16 answerId, uint8 customDataResultId) internal pure returns (bytes32 data) {
    data = bytes32(uint(storyId));
    data |= bytes32(uint(pageId)) << 16;
    data |= bytes32(uint(heroClass)) << (16 + 16);
    data |= bytes32(uint(answerId)) << (16 + 16 + 8);
    data |= bytes32(uint(customDataResultId)) << (16 + 16 + 8 + 16);
  }

  function unpackStoryCustomDataResult(bytes32 data) internal pure returns (uint16 storyId, uint16 pageId, uint8 heroClass, uint16 answerId, uint8 customDataResultId) {
    storyId = uint16(uint(data));
    pageId = uint16(uint(data) >> 16);
    heroClass = uint8(uint(data) >> (16 + 16));
    answerId = uint16(uint(data) >> (16 + 16 + 8));
    customDataResultId = uint8(uint(data) >> (16 + 16 + 8 + 16));
  }

  function packStoryHeroState(uint16 pageId, uint40 heroLastActionTS) internal pure returns (bytes32 data) {
    data = bytes32(uint(pageId));
    data |= bytes32(uint(heroLastActionTS)) << 16;
  }

  function unpackStoryHeroState(bytes32 data) internal pure returns (uint16 pageId, uint40 heroLastActionTS) {
    pageId = uint16(uint(data));
    heroLastActionTS = uint40(uint(data) >> 16);
  }

  function packStoryHeroStateId(address heroAdr, uint80 heroId, uint16 storyId) internal pure returns (bytes32 data) {
    data = bytes32(uint(uint160(heroAdr)));
    data |= bytes32(uint(heroId)) << 160;
    data |= bytes32(uint(storyId)) << (160 + 80);
  }

  function unpackStoryHeroStateId(bytes32 data) internal pure returns (address heroAdr, uint80 heroId, uint16 storyId) {
    heroAdr = address(uint160(uint(data)));
    heroId = uint80(uint(data) >> 160);
    storyId = uint16(uint(data) >> (160 + 80));
  }

  function packStorySimpleRequirement(uint32 randomRequirement, uint32 delayRequirement, bool isFinalAnswer) internal pure returns (bytes32 data) {
    data = bytes32(uint(randomRequirement));
    data |= bytes32(uint(delayRequirement)) << 32;
    data |= bytes32(uint(isFinalAnswer ? uint8(1) : uint8(0))) << (32 + 32);
  }

  function unpackStorySimpleRequirement(bytes32 data) internal pure returns (uint32 randomRequirement, uint32 delayRequirement, bool isFinalAnswer) {
    randomRequirement = uint32(uint(data));
    delayRequirement = uint32(uint(data) >> 32);
    isFinalAnswer = uint8(uint(data) >> (32 + 32)) == uint8(1);
  }

  function packBurnInfo(uint8 slot, uint64 chance, bool stopIfBurned) internal pure returns (bytes32 data) {
    data = bytes32(uint(slot));
    data |= bytes32(uint(chance)) << 8;
    data |= bytes32(uint(stopIfBurned ? uint8(1) : uint8(0))) << (8 + 64);
  }

  function unpackBurnInfo(bytes32 data) internal pure returns (uint8 slot, uint64 chance, bool stopIfBurned) {
    slot = uint8(uint(data));
    chance = uint64(uint(data) >> 8);
    stopIfBurned = uint8(uint(data) >> (8 + 64)) == uint8(1);
  }
  //endregion ------------------------------------ STORIES

  ////////////////////////////////////////////////////////////////////////////////////
  // ---- ARRAYS LOGIC ----
  ////////////////////////////////////////////////////////////////////////////////////

  //region ------------------------------------ SIMPLE ARRAYS


  function packUint8Array(uint8[] memory data) internal pure returns (bytes32) {
    uint len = data.length;
    if (len > 32) revert OutOfBounds(len, 32);
    bytes32 result;
    for (uint i = 0; i < len; i++) {
      result |= bytes32(uint(data[i])) << (i * 8);
    }
    return result;
  }

  /// @notice Simple faster version of {packUint8Array} for small number of items
  ///         It allows to exclude dynamic array creation.
  function packUint8Array3(uint8 a, uint8 b, uint8 c) internal pure returns (bytes32) {
    bytes32 result = bytes32(uint(a));
    result |= bytes32(uint(b)) << (1 * 8);
    result |= bytes32(uint(c)) << (2 * 8);
    return result;
  }


  function unpackUint8Array(bytes32 data) internal pure returns (uint8[] memory) {
    uint8[] memory result = new uint8[](32);
    for (uint i = 0; i < 32; i++) {
      result[i] = uint8(uint(data) >> (i * 8));
    }
    return result;
  }

  /// @notice Simple faster version of {unpackUint8Array} for small number of items
  ///         It allows to exclude only first 3 values
  function unpackUint8Array3(bytes32 data) internal pure returns (uint8 a, uint8 b, uint8 c) {
    a = uint8(uint(data));
    b = uint8(uint(data) >> (1 * 8));
    c = uint8(uint(data) >> (2 * 8));
  }

  function changeUnit8ArrayWithCheck(bytes32 data, uint index, uint8 value, uint8 expectedPrevValue) internal pure returns (bytes32 newData) {
    uint8[] memory arr = unpackUint8Array(data);
    if (arr[index] != expectedPrevValue) revert UnexpectedValue(uint(expectedPrevValue), uint(arr[index]));
    arr[index] = value;
    return packUint8Array(arr);
  }

  function packInt32Array(int32[] memory data) internal pure returns (bytes32) {
    uint len = data.length;
    if (len > 8) revert OutOfBounds(len, 8);
    bytes32 result;
    for (uint i; i < len; i++) {
      result |= bytes32(uint(uint32(data[i]))) << (i * 32);
    }
    return result;
  }

  function unpackInt32Array(bytes32 data) internal pure returns (int32[] memory) {
    int32[] memory result = new int32[](8);
    for (uint i = 0; i < 8; i++) {
      result[i] = int32(int(uint(data) >> (i * 32)));
    }
    return result;
  }

  function packUint32Array(uint32[] memory data) internal pure returns (bytes32) {
    uint len = data.length;
    if (len > 8) revert OutOfBounds(len, 8);
    bytes32 result;
    for (uint i = 0; i < len; i++) {
      result |= bytes32(uint(data[i])) << (i * 32);
    }
    return result;
  }

  function unpackUint32Array(bytes32 data) internal pure returns (uint32[] memory) {
    uint32[] memory result = new uint32[](8);
    for (uint i = 0; i < 8; i++) {
      result[i] = uint32(uint(data) >> (i * 32));
    }
    return result;
  }
  //endregion ------------------------------------ SIMPLE ARRAYS

  //region ------------------------------------ COMPLEX ARRAYS

  // We should represent arrays without concrete size.
  // For this reason we must not revert on out of bounds but return zero value instead.

  // we need it for properly unpack packed arrays with ids
//  function getInt32AsInt24(bytes32[] memory arr, uint idx) internal pure returns (int32) {
//    if (idx / 8 >= arr.length) {
//      return int32(0);
//    }
//    return int32(int24(int(uint(arr[idx / 8]) >> ((idx % 8) * 32))));
//  }

  // we need it for properly unpack packed arrays with ids
//  function getUnit8From32Step(bytes32[] memory arr, uint idx) internal pure returns (uint8) {
//    if (idx / 8 >= arr.length) {
//      return uint8(0);
//    }
//    return uint8(uint(arr[idx / 8]) >> ((idx % 8) * 32 + 24));
//  }

  function getInt32Memory(bytes32[] memory arr, uint idx) internal pure returns (int32) {
    if (idx / 8 >= arr.length) {
      return int32(0);
    }
    return int32(int(uint(arr[idx / 8]) >> ((idx % 8) * 32)));
  }

  function getInt32(bytes32[] storage arr, uint idx) internal view returns (int32) {
    // additional gas usage, but we should not revert on out of bounds
    if (idx / 8 >= arr.length) {
      return int32(0);
    }
    return int32(int(uint(arr[idx / 8]) >> ((idx % 8) * 32)));
  }

  function setInt32(bytes32[] storage arr, uint idx, int32 value) internal {
    uint pos = idx / 8;
    uint shift = (idx % 8) * 32;

    uint curLength = arr.length;
    if (pos >= curLength) {
      arr.push(0);
      for (uint i = curLength; i < pos; ++i) {
        arr.push(0);
      }
    }

    arr[pos] = bytes32(uint(arr[pos]) & ~(uint(0xffffffff) << shift) | (uint(uint32(value)) & 0xffffffff) << shift);
  }

  /// @notice Increment {idx}-th item on {value}
  function changeInt32(bytes32[] storage arr, uint idx, int32 value) internal returns (int32 newValue, int32 change) {
    int32 cur = int32(int(getInt32(arr, idx)));
    int newValueI = int(cur) + int(value);
    newValue = int32(newValueI);
    change = int32(newValueI - int(cur));

    setInt32(arr, idx, newValue);
  }

  function toInt32Array(bytes32[] memory arr, uint size) internal pure returns (int32[] memory) {
    int32[] memory result = new int32[](size);
    for (uint i = 0; i < arr.length; i++) {
      for (uint j; j < 8; ++j) {
        uint idx = i * 8 + j;
        if (idx >= size) break;
        result[idx] = getInt32Memory(arr, idx);
      }
    }
    return result;
  }

  /// @dev pack int32 array into bytes32 array
  function toBytes32Array(int32[] memory arr) internal pure returns (bytes32[] memory) {
    uint size = arr.length / 8 + 1;
    bytes32[] memory result = new bytes32[](size);
    for (uint i; i < size; ++i) {
      for (uint j; j < 8; ++j) {
        uint idx = i * 8 + j;
        if (idx >= arr.length) break;
        result[i] |= bytes32(uint(uint32(arr[idx]))) << (j * 32);
      }
    }
    return result;
  }

  /// @dev pack int32 array into bytes32 array using last 8bytes for ids
  ///      we can not use zero values coz will not able to properly unpack it later
  function toBytes32ArrayWithIds(int32[] memory arr, uint8[] memory ids) internal pure returns (bytes32[] memory) {
    if (arr.length != ids.length) revert LengthsMismatch();

    uint size = arr.length / 8 + 1;
    bytes32[] memory result = new bytes32[](size);
    for (uint i; i < size; ++i) {
      for (uint j; j < 8; ++j) {
        uint idx = i * 8 + j;
        if (idx >= arr.length) break;

//        console.log("toBytes32ArrayWithIds id: %s, value: %s%s", ids[idx], arr[idx] >= 0 ? '' : '-', arr[idx] >= 0 ? uint(int(arr[idx])) : uint(int(-arr[idx])));

        if (arr[idx] > type(int24).max || arr[idx] < type(int24).min) revert IntOutOfRange(int(arr[idx]));
        if (arr[idx] == 0) revert ZeroValue();
        result[i] |= bytes32(uint(uint24(int24(arr[idx])))) << (j * 32);
        result[i] |= bytes32(uint(ids[idx])) << (j * 32 + 24);
      }
    }
    return result;
  }

  /// @dev we do not know exact size of array, assume zero values is not acceptable for this array
  function toInt32ArrayWithIds(bytes32[] memory arr) internal pure returns (int32[] memory values, uint8[] memory ids) {
    uint len = arr.length;
    uint size = len * 8;
    int32[] memory valuesTmp = new int32[](size);
    uint8[] memory idsTmp = new uint8[](size);
    uint counter;
    for (uint i = 0; i < len; i++) {
      for (uint j; j < 8; ++j) {
        uint idx = i * 8 + j;
        // if (idx >= size) break;  // it looks like a useless check
        valuesTmp[idx] = int32(int24(int(uint(arr[i]) >> (j * 32)))); // getInt32AsInt24(arr, idx);
        idsTmp[idx] = uint8(uint(arr[i]) >> (j * 32 + 24)); // getUnit8From32Step(arr, idx);
        if (valuesTmp[idx] == 0) {
          break;
        }
        counter++;
      }
    }

    values = new int32[](counter);
    ids = new uint8[](counter);
    for (uint i; i < counter; ++i) {
      values[i] = valuesTmp[i];
      ids[i] = idsTmp[i];
    }
  }
  //endregion ------------------------------------ COMPLEX ARRAYS

}

contracts/lib/ReinforcementControllerLib.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.23;

import "../interfaces/IReinforcementController.sol";
import "../interfaces/IOracle.sol";
import "../interfaces/IERC20.sol";
import "../interfaces/IERC721.sol";
import "../interfaces/IApplicationEvents.sol";
import "../interfaces/IHeroController.sol";
import "../interfaces/IDungeonFactory.sol";
import "../lib/CalcLib.sol";
import "../lib/PackingLib.sol";

library ReinforcementControllerLib {
  using EnumerableSet for EnumerableSet.Bytes32Set;
  using EnumerableMap for EnumerableMap.AddressToUintMap;
  using PackingLib for bytes32;
  using PackingLib for address;
  using PackingLib for uint8[];

  //region ------------------------ CONSTANTS

  /// @dev keccak256(abi.encode(uint256(keccak256("reinforcement.controller.main")) - 1)) & ~bytes32(uint256(0xff))
  bytes32 internal constant MAIN_STORAGE_LOCATION = 0x5a053c541e08c6bd7dfc3042a100e83af246544a23ecda1a47bf22b441b00c00;
  uint internal constant _SEARCH_LOOPS = 10;
  int32 internal constant _ATTRIBUTES_RATIO = 20;
  uint internal constant _FEE_MIN = 10;
  uint internal constant _TO_HELPER_RATIO_MAX = 50;
  uint internal constant _STAKE_REDUCE_DELAY = 7 days;
  uint internal constant _DELAY_FACTOR = 2;
  uint internal constant _SIP001_COUNT_REQUIRED_SKILLS = 3;
  //endregion ------------------------ CONSTANTS

  //region ------------------------ VIEWS

  function _S() internal pure returns (IReinforcementController.MainState storage s) {
    assembly {
      s.slot := MAIN_STORAGE_LOCATION
    }
    return s;
  }

  function minLevel() internal view returns (uint8 _minLevel) {
    (_minLevel,) = unpackConfig(_S().config);
    return _minLevel;
  }

  function minLifeChances() internal view returns (uint8 _minLifeChances) {
    (, _minLifeChances) = unpackConfig(_S().config);
    return _minLifeChances;
  }

  function toHelperRatio(address heroToken, uint heroId) internal view returns (uint) {
    return heroInfo(heroToken, heroId).fee;
  }

  function heroInfo(address heroToken, uint heroId) internal view returns (IReinforcementController.HeroInfo memory) {
    return unpackHeroInfo(_S()._stakedHeroes[heroToken.packNftId(heroId)]);
  }

  function isStaked(address heroToken, uint heroId) internal view returns (bool) {
    return heroInfo(heroToken, heroId).biome != 0;
  }

  function maxScore(uint biome) internal view returns (uint) {
    return _S().maxScore[biome];
  }

  function earned(address heroToken, uint heroId) internal view returns (
    address[] memory tokens,
    uint[] memory amounts,
    address[] memory nfts,
    uint[] memory ids
  ){
    EnumerableMap.AddressToUintMap storage erc20Rewards = _S()._heroTokenRewards[heroToken.packNftId(heroId)];
    uint length = erc20Rewards.length();
    tokens = new address[](length);
    amounts = new uint[](length);
    for (uint i; i < length; ++i) {
      (tokens[i], amounts[i]) = erc20Rewards.at(i);
    }

    bytes32[] storage nftRewards = _S()._heroNftRewards[heroToken.packNftId(heroId)];
    length = nftRewards.length;
    nfts = new address[](length);
    ids = new uint[](length);
    for (uint i; i < length; ++i) {
      (nfts[i], ids[i]) = PackingLib.unpackNftId(nftRewards[i]);
    }
  }

  function heroScoreAdjusted(address heroToken, uint heroId) internal view returns (uint) {
    IReinforcementController.HeroInfo memory info = unpackHeroInfo(_S()._stakedHeroes[heroToken.packNftId(heroId)]);
    if (info.stakeTs > block.timestamp) {
      return 0;
    }
    uint time = block.timestamp - info.stakeTs;
    if (time > _STAKE_REDUCE_DELAY) {
      time -= _STAKE_REDUCE_DELAY;
    } else {
      // if staked less then delay ago hero has 100% scores
      return info.score;
    }
    if (time > _STAKE_REDUCE_DELAY * _DELAY_FACTOR) {
      // if hero staked more than delay*2 days return zero
      return 0;
    }
    return info.score * (_STAKE_REDUCE_DELAY * _DELAY_FACTOR - time) / (_STAKE_REDUCE_DELAY * _DELAY_FACTOR);
  }
  //endregion ------------------------ VIEWS

  //region ------------------------ GOV ACTIONS

  function setMinLevel(bool isGovernance, uint8 value) internal {
    if (!isGovernance) revert IAppErrors.NotGovernance(msg.sender);

    (, uint8 _minLifeChances) = unpackConfig(_S().config);
    _S().config = packConfig(value, _minLifeChances);

    emit IApplicationEvents.MinLevelChanged(value);
  }

  function setMinLifeChances(bool isGovernance, uint8 value) internal {
    if (!isGovernance) revert IAppErrors.NotGovernance(msg.sender);

    (uint8 _minLevel,) = unpackConfig(_S().config);
    _S().config = packConfig(_minLevel, value);

    emit IApplicationEvents.MinLifeChancesChanged(value);
  }

  //endregion ------------------------ GOV ACTIONS

  //region ------------------------ USER ACTIONS

  /// @notice Mark the hero as staked in _stakedHeroes and _internalIdsByBiomes, update maxScore for biome if necessary
  /// @param fee [0..._TO_HELPER_RATIO_MAX], higher fee => less score
  function stakeHero(bool isEoa, IController controller, address msgSender, address heroToken, uint heroId, uint8 fee) internal {
    IReinforcementController.MainState storage s = _S();
    if (!isEoa) revert IAppErrors.ErrorOnlyEoa();
    if (controller.onPause()) revert IAppErrors.ErrorPaused();
    if (IERC721(heroToken).ownerOf(heroId) != msgSender) revert IAppErrors.ErrorNotHeroOwner(heroToken, msgSender);

    IHeroController hc = IHeroController(controller.heroController());
    if (hc.heroClass(heroToken) == 0) revert IAppErrors.ErrorHeroIsNotRegistered(heroToken);

    if (IDungeonFactory(controller.dungeonFactory()).currentDungeon(heroToken, heroId) != 0) revert IAppErrors.HeroInDungeon();
    if (isStaked(heroToken, heroId)) revert IAppErrors.AlreadyStaked();
    if (fee > _TO_HELPER_RATIO_MAX) revert IAppErrors.MaxFee(fee);
    if (fee < _FEE_MIN) revert IAppErrors.MinFee(fee);

    IStatController statController = IStatController(controller.statController());

    {
      IStatController.ChangeableStats memory stats = statController.heroStats(heroToken, heroId);
      (uint8 _minLevel, uint8 _minLifeChances) = unpackConfig(s.config);
      if (stats.level < _minLevel || stats.lifeChances < _minLifeChances) revert IAppErrors.StakeHeroNotStats();
      if (stats.lifeChances == 0) revert IAppErrors.ErrorHeroIsDead(heroToken, heroId); // for the case _minLifeChances == 0
    }

    { // Reinforcement requires 3 skills, see https://discord.com/channels/1134537718039318608/1236334562024620092
      uint8[] memory busySlots = statController.heroItemSlots(heroToken, heroId);
      uint counter = 0;
      if (busySlots.length >= _SIP001_COUNT_REQUIRED_SKILLS) {
        for (uint i; i < busySlots.length; ++i) {
          if (
            (busySlots[i] == uint8(IStatController.ItemSlots.SKILL_1))
            || (busySlots[i] == uint8(IStatController.ItemSlots.SKILL_2))
            || (busySlots[i] == uint8(IStatController.ItemSlots.SKILL_3))
          ) counter++;
        }
      }
      if (counter < _SIP001_COUNT_REQUIRED_SKILLS) revert IAppErrors.ErrorReinforcementRequiresThreeSkills();
    }

    uint8 biome = hc.heroBiome(heroToken, heroId);
    uint score = hc.score(heroToken, heroId);

    // x10 for each % discount
    score += score * (_TO_HELPER_RATIO_MAX - fee) / 10;
    EnumerableSet.Bytes32Set storage internalIds = s._internalIdsByBiomes[biome];

    uint _maxScore = s.maxScore[biome];
    if (score > _maxScore) {
      s.maxScore[biome] = score;
    }

    internalIds.add(heroToken.packNftId(heroId));

    s._stakedHeroes[heroToken.packNftId(heroId)] = packHeroInfo(IReinforcementController.HeroInfo({
      biome: biome,
      score: score,
      fee: fee,
      stakeTs: uint64(block.timestamp)
    }));

    emit IApplicationEvents.HeroStaked(heroToken, heroId, biome, score);
  }

  /// @notice Reverse operation for {stakeHero}
  function withdrawHero(bool isEoa, IController controller, address msgSender, address heroToken, uint heroId) internal {
    IReinforcementController.MainState storage s = _S();

    if (!isEoa) revert IAppErrors.ErrorOnlyEoa();
    if (controller.onPause()) revert IAppErrors.ErrorPaused();
    if (IERC721(heroToken).ownerOf(heroId) != msgSender) revert IAppErrors.ErrorNotHeroOwner(heroToken, msgSender);
    if (IHeroController(controller.heroController()).heroClass(heroToken) == 0) revert IAppErrors.ErrorHeroIsNotRegistered(heroToken);

    IReinforcementController.HeroInfo memory _heroInfo = unpackHeroInfo(s._stakedHeroes[heroToken.packNftId(heroId)]);
    if (_heroInfo.biome == 0) revert IAppErrors.NotStaked();

    s._internalIdsByBiomes[_heroInfo.biome].remove(heroToken.packNftId(heroId));
    delete s._stakedHeroes[heroToken.packNftId(heroId)];

    emit IApplicationEvents.HeroWithdraw(heroToken, heroId);
  }

  /// @dev It's view like function but we need to touch slots in oracle function.
  function askHero(IController controller, uint biome) internal returns (
    address heroToken,
    uint heroId,
    int32[] memory attributes
  ) {
    heroToken = address(0);
    heroId = 0;
    {
      IOracle oracle = IOracle(controller.oracle());
      EnumerableSet.Bytes32Set storage internalIds = _S()._internalIdsByBiomes[biome];
      uint length = internalIds.length();
      if (length == 0) revert IAppErrors.NoStakedHeroes();
      uint _maxScore = _S().maxScore[biome];
      uint maxScoreSqrt = CalcLib.sqrt(_maxScore);

      for (uint i; i < _SEARCH_LOOPS; ++i) {
        uint random = oracle.getRandomNumber(1e18, length);
        uint b = (random * maxScoreSqrt / 1e18) ** 2;
        (heroToken, heroId) = internalIds.at(random % length).unpackNftId();

        if (
          heroScoreAdjusted(heroToken, heroId)
          >=
          (b < _maxScore ? _maxScore - b : 0)
        ) break;
      }
    }
    {
      IStatController sc = IStatController(controller.statController());
      uint[] memory indexes = new uint[](12);

      indexes[0] = uint(IStatController.ATTRIBUTES.STRENGTH);
      indexes[1] = uint(IStatController.ATTRIBUTES.DEXTERITY);
      indexes[2] = uint(IStatController.ATTRIBUTES.VITALITY);
      indexes[3] = uint(IStatController.ATTRIBUTES.ENERGY);
      indexes[4] = uint(IStatController.ATTRIBUTES.DAMAGE_MIN);
      indexes[5] = uint(IStatController.ATTRIBUTES.DAMAGE_MAX);
      indexes[6] = uint(IStatController.ATTRIBUTES.ATTACK_RATING);
      indexes[7] = uint(IStatController.ATTRIBUTES.DEFENSE);
      indexes[8] = uint(IStatController.ATTRIBUTES.BLOCK_RATING);
      indexes[9] = uint(IStatController.ATTRIBUTES.FIRE_RESISTANCE);
      indexes[10] = uint(IStatController.ATTRIBUTES.COLD_RESISTANCE);
      indexes[11] = uint(IStatController.ATTRIBUTES.LIGHTNING_RESISTANCE);

      attributes = _generateReinforcementAttributes(sc, indexes, heroToken, heroId);
    }
    emit IApplicationEvents.HeroAsk(heroToken, heroId);
  }

  /// @dev Only for dungeon. Assume the tokens already sent to this contract.
  function registerTokenReward(IController controller, address heroToken, uint heroId, address token, uint amount) internal {
    if (controller.dungeonFactory() != msg.sender) revert IAppErrors.ErrorNotDungeonFactory(msg.sender);

    EnumerableMap.AddressToUintMap storage rewards = _S()._heroTokenRewards[heroToken.packNftId(heroId)];

    (,uint existAmount) = rewards.tryGet(token);
    rewards.set(token, existAmount + amount);

    emit IApplicationEvents.TokenRewardRegistered(heroToken, heroId, token, amount, existAmount + amount);
  }

  /// @dev Only for dungeon. Assume the NFT already sent to this contract.
  function registerNftReward(IController controller, address heroToken, uint heroId, address token, uint tokenId) internal {
    if (controller.dungeonFactory() != msg.sender) revert IAppErrors.ErrorNotDungeonFactory(msg.sender);

    _S()._heroNftRewards[heroToken.packNftId(heroId)].push(token.packNftId(tokenId));

    emit IApplicationEvents.NftRewardRegistered(heroToken, heroId, token, tokenId);
  }

  function claimAll(bool isEoa, IController controller, address msgSender, address heroToken, uint heroId) internal {
    if (IERC721(heroToken).ownerOf(heroId) != msgSender) revert IAppErrors.ErrorNotHeroOwner(heroToken, msgSender);
    if (controller.onPause()) revert IAppErrors.ErrorPaused();
    if (!isEoa) revert IAppErrors.ErrorOnlyEoa();

    _claimAllTokenRewards(heroToken, heroId, msgSender);
    _claimAllNftRewards(heroToken, heroId, msgSender);
  }
  //endregion ------------------------ USER ACTIONS

  //region ------------------------ Internal logic
  /// @notice Claim all rewards from {_heroTokenRewards} to {recipient}, remove data from {_heroTokenRewards}
  function _claimAllTokenRewards(address heroToken, uint heroId, address recipient) internal {
    EnumerableMap.AddressToUintMap storage rewards = _S()._heroTokenRewards[heroToken.packNftId(heroId)];
    uint length = rewards.length();
    address[] memory tokens = new address[](length);
    for (uint i; i < length; ++i) {
      (address token, uint amount) = rewards.at(i);
      IERC20(token).transfer(recipient, amount);
      emit IApplicationEvents.ClaimedToken(heroToken, heroId, token, amount, recipient);

      tokens[i] = token;
    }

    // need to remove after the ordered reading for handle all elements, just remove the struct will not work coz contains mapping inside
    for (uint i; i < length; ++i) {
      rewards.remove(tokens[i]);
    }
  }

  function _claimAllNftRewards(address heroToken, uint heroId, address recipient) internal {
    bytes32[] storage rewards = _S()._heroNftRewards[heroToken.packNftId(heroId)];
    uint length = rewards.length;
    for (uint i; i < length; ++i) {
      (address token, uint id) = rewards[i].unpackNftId();
      IERC721(token).safeTransferFrom(address(this), recipient, id);
      emit IApplicationEvents.ClaimedItem(heroToken, heroId, token, id, recipient);
    }
    // a simple array can be just deleted
    delete _S()._heroNftRewards[heroToken.packNftId(heroId)];
  }

  function _generateReinforcementAttributes(IStatController sc, uint[] memory indexes, address heroToken, uint heroId)
  internal view returns (int32[] memory attributes) {
    attributes = new int32[](uint(IStatController.ATTRIBUTES.END_SLOT));
    for (uint i; i < indexes.length; ++i) {
      attributes[indexes[i]] = CalcLib.max32(sc.heroAttribute(heroToken, heroId, indexes[i]) * _ATTRIBUTES_RATIO / 100, 1);
    }
  }
  //endregion ------------------------ Internal logic

  //region ------------------------ Packing utils

  function packHeroInfo(IReinforcementController.HeroInfo memory info) internal pure returns (bytes32) {
    return PackingLib.packReinforcementHeroInfo(info.biome, uint128(info.score), info.fee, info.stakeTs);
  }

  function unpackHeroInfo(bytes32 packed) internal pure returns (IReinforcementController.HeroInfo memory info) {
    (info.biome, info.score, info.fee, info.stakeTs) = PackingLib.unpackReinforcementHeroInfo(packed);
    return info;
  }

  function packConfig(uint8 minLevel_, uint8 minLifeChances_) internal pure returns (bytes32) {
    return PackingLib.packUint8Array3(minLevel_, minLifeChances_, 0);
  }

  function unpackConfig(bytes32 packed) internal pure returns (uint8 minLevel_, uint8 minLifeChances_) {
    (minLevel_, minLifeChances_,) = PackingLib.unpackUint8Array3(packed);
  }

  //endregion ------------------------ Packing utils

}

contracts/lib/ScoreLib.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.23;

import "../openzeppelin/Math.sol";
import "../interfaces/IStatController.sol";
import "./CalcLib.sol";

library ScoreLib {
  using CalcLib for int32;

  // core
  uint public constant STRENGTH = 100;
  uint public constant DEXTERITY = 100;
  uint public constant VITALITY = 100;
  uint public constant ENERGY = 100;

  // attributes
  uint public constant MELEE_DAMAGE = 10;
  uint public constant ATTACK_RATING = 3;
  uint public constant DEFENCE = 10;
  uint public constant BLOCK_RATING = 500;
  uint public constant LIFE = 10;
  uint public constant MANA = 10;

  uint public constant LIFE_CHANCES = 10_000;
  uint public constant MAGIC_FIND = 300;
  uint public constant CRITICAL_HIT = 150;
  uint public constant DMG_FACTOR = 200;

  uint public constant AR_FACTOR = 200;
  uint public constant LIFE_STOLEN_PER_HIT = 1000;
  uint public constant MANA_AFTER_KILL = 1000;
  uint public constant DAMAGE_REDUCTION = 500;
  uint public constant REFLECT_DAMAGE = 250;
  uint public constant RESIST_TO_STATUSES = 70;

  // resistance
  uint public constant ELEMENT_RESIST = 100;

  // race specific attributes
  uint public constant RACE_SPECIFIC = 20;

  // statuses
  uint public constant STATUSES = 100;

  // items
  uint public constant DURABILITY_SCORE = 1;

  // hero
  uint public constant HERO_LEVEL_SCORE = 1000;

  /// @param isForReinforcement If true calculate score using 12 main attributes only. Otherwise use all attributes.
  function attributesScore(int32[] memory attributes, bool isForReinforcement) internal pure returns (uint) {
    uint result;
    {
      result += (attributes[uint(IStatController.ATTRIBUTES.STRENGTH)]).toUint() * STRENGTH
        + (attributes[uint(IStatController.ATTRIBUTES.DEXTERITY)]).toUint() * DEXTERITY
        + (attributes[uint(IStatController.ATTRIBUTES.VITALITY)]).toUint() * VITALITY
        + (attributes[uint(IStatController.ATTRIBUTES.ENERGY)]).toUint() * ENERGY
        + (attributes[uint(IStatController.ATTRIBUTES.ATTACK_RATING)]).toUint() * ATTACK_RATING
        + (attributes[uint(IStatController.ATTRIBUTES.DEFENSE)]).toUint() * DEFENCE
        + (attributes[uint(IStatController.ATTRIBUTES.BLOCK_RATING)]).toUint() * BLOCK_RATING
        + Math.average(attributes[uint(IStatController.ATTRIBUTES.DAMAGE_MIN)].toUint(), attributes[uint(IStatController.ATTRIBUTES.DAMAGE_MAX)].toUint()) * MELEE_DAMAGE
      ;
    }
    {
      result +=
        (attributes[uint(IStatController.ATTRIBUTES.FIRE_RESISTANCE)]).toUint() * ELEMENT_RESIST
        + (attributes[uint(IStatController.ATTRIBUTES.COLD_RESISTANCE)]).toUint() * ELEMENT_RESIST
        + (attributes[uint(IStatController.ATTRIBUTES.LIGHTNING_RESISTANCE)]).toUint() * ELEMENT_RESIST;
    }

    if (! isForReinforcement) {
      {
        result +=
          (attributes[uint(IStatController.ATTRIBUTES.LIFE)]).toUint() * LIFE
          + (attributes[uint(IStatController.ATTRIBUTES.MANA)]).toUint() * MANA;
      }
      {
        result +=
          (attributes[uint(IStatController.ATTRIBUTES.DMG_AGAINST_HUMAN)]).toUint() * RACE_SPECIFIC
          + (attributes[uint(IStatController.ATTRIBUTES.DMG_AGAINST_UNDEAD)]).toUint() * RACE_SPECIFIC
          + (attributes[uint(IStatController.ATTRIBUTES.DMG_AGAINST_DAEMON)]).toUint() * RACE_SPECIFIC
          + (attributes[uint(IStatController.ATTRIBUTES.DMG_AGAINST_BEAST)]).toUint() * RACE_SPECIFIC
          + (attributes[uint(IStatController.ATTRIBUTES.DEF_AGAINST_HUMAN)]).toUint() * RACE_SPECIFIC
          + (attributes[uint(IStatController.ATTRIBUTES.DEF_AGAINST_UNDEAD)]).toUint() * RACE_SPECIFIC
          + (attributes[uint(IStatController.ATTRIBUTES.DEF_AGAINST_DAEMON)]).toUint() * RACE_SPECIFIC
          + (attributes[uint(IStatController.ATTRIBUTES.DEF_AGAINST_BEAST)]).toUint() * RACE_SPECIFIC;
      }
      {
        result +=
          (attributes[uint(IStatController.ATTRIBUTES.STUN)]).toUint() * STATUSES
          + (attributes[uint(IStatController.ATTRIBUTES.BURN)]).toUint() * STATUSES
          + (attributes[uint(IStatController.ATTRIBUTES.FREEZE)]).toUint() * STATUSES
          + (attributes[uint(IStatController.ATTRIBUTES.CONFUSE)]).toUint() * STATUSES
          + (attributes[uint(IStatController.ATTRIBUTES.CURSE)]).toUint() * STATUSES
          + (attributes[uint(IStatController.ATTRIBUTES.POISON)]).toUint() * STATUSES;
      }
      {
        result +=
          (attributes[uint(IStatController.ATTRIBUTES.LIFE_CHANCES)]).toUint() * LIFE_CHANCES
          + (attributes[uint(IStatController.ATTRIBUTES.MAGIC_FIND)]).toUint() * MAGIC_FIND
          + (attributes[uint(IStatController.ATTRIBUTES.CRITICAL_HIT)]).toUint() * CRITICAL_HIT
          + (attributes[uint(IStatController.ATTRIBUTES.MELEE_DMG_FACTOR)]).toUint() * DMG_FACTOR
          + (attributes[uint(IStatController.ATTRIBUTES.FIRE_DMG_FACTOR)]).toUint() * DMG_FACTOR
          + (attributes[uint(IStatController.ATTRIBUTES.COLD_DMG_FACTOR)]).toUint() * DMG_FACTOR
          + (attributes[uint(IStatController.ATTRIBUTES.LIGHTNING_DMG_FACTOR)]).toUint() * DMG_FACTOR;
      }
      {
        result +=
          (attributes[uint(IStatController.ATTRIBUTES.AR_FACTOR)]).toUint() * AR_FACTOR
          + (attributes[uint(IStatController.ATTRIBUTES.LIFE_STOLEN_PER_HIT)]).toUint() * LIFE_STOLEN_PER_HIT
          + (attributes[uint(IStatController.ATTRIBUTES.MANA_AFTER_KILL)]).toUint() * MANA_AFTER_KILL
          + (attributes[uint(IStatController.ATTRIBUTES.DAMAGE_REDUCTION)]).toUint() * DAMAGE_REDUCTION
          + (attributes[uint(IStatController.ATTRIBUTES.REFLECT_DAMAGE_MELEE)]).toUint() * REFLECT_DAMAGE
          + (attributes[uint(IStatController.ATTRIBUTES.REFLECT_DAMAGE_MAGIC)]).toUint() * REFLECT_DAMAGE
          + (attributes[uint(IStatController.ATTRIBUTES.RESIST_TO_STATUSES)]).toUint() * RESIST_TO_STATUSES;
      }
    }
    return result;
  }

  function itemScore(int32[] memory attributes, uint16 baseDurability) internal pure returns (uint) {
    return attributesScore(attributes, false) + baseDurability * DURABILITY_SCORE;
  }

  function heroScore(int32[] memory attributes, uint level) internal pure returns (uint) {
    return attributesScore(attributes, true) + level * HERO_LEVEL_SCORE;
  }

}

contracts/lib/SlotsLib.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.23;

/// @title Library for setting / getting slot variables (used in upgradable proxy contracts)
/// @author bogdoslav
library SlotsLib {

  /// @notice Version of the contract
  /// @dev Should be incremented when contract changed
  string public constant SLOT_LIB_VERSION = "1.0.0";

  // ************* GETTERS *******************

  /// @dev Gets a slot as bytes32
  function getBytes32(bytes32 slot) internal view returns (bytes32 result) {
    assembly {
      result := sload(slot)
    }
  }

  /// @dev Gets a slot as an address
  function getAddress(bytes32 slot) internal view returns (address result) {
    assembly {
      result := sload(slot)
    }
  }

  /// @dev Gets a slot as uint256
  function getUint(bytes32 slot) internal view returns (uint result) {
    assembly {
      result := sload(slot)
    }
  }

  // ************* ARRAY GETTERS *******************

  /// @dev Gets an array length
  function arrayLength(bytes32 slot) internal view returns (uint result) {
    assembly {
      result := sload(slot)
    }
  }

  /// @dev Gets a slot array by index as address
  /// @notice First slot is array length, elements ordered backward in memory
  /// @notice This is unsafe, without checking array length.
  function addressAt(bytes32 slot, uint index) internal view returns (address result) {
    bytes32 pointer = bytes32(uint(slot) - 1 - index);
    assembly {
      result := sload(pointer)
    }
  }

  // ************* SETTERS *******************

  /// @dev Sets a slot with bytes32
  /// @notice Check address for 0 at the setter
  function set(bytes32 slot, bytes32 value) internal {
    assembly {
      sstore(slot, value)
    }
  }

  /// @dev Sets a slot with address
  /// @notice Check address for 0 at the setter
  function set(bytes32 slot, address value) internal {
    assembly {
      sstore(slot, value)
    }
  }

  /// @dev Sets a slot with uint
  function set(bytes32 slot, uint value) internal {
    assembly {
      sstore(slot, value)
    }
  }

}

contracts/lib/StatControllerLib.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.23;

import "../openzeppelin/Math.sol";
import "../openzeppelin/EnumerableSet.sol";
import "../interfaces/IItemController.sol";
import "../interfaces/IStatController.sol";
import "../interfaces/IAppErrors.sol";
import "../interfaces/IApplicationEvents.sol";
import "../lib/StatLib.sol";

/// @notice Implementation of StatController
library StatControllerLib {
  using StatLib for uint;
  using StatLib for uint[];
  using StatLib for uint32;
  using StatLib for int32;
  using StatLib for int32;
  using CalcLib for uint;
  using CalcLib for int;
  using CalcLib for int32;
  using EnumerableSet for EnumerableSet.AddressSet;
  using PackingLib for bytes32[];
  using PackingLib for bytes32;
  using PackingLib for int32;
  using PackingLib for uint32;

  //region ------------------------ Constants
  /// @dev keccak256(abi.encode(uint256(keccak256("stat.controller.main")) - 1)) & ~bytes32(uint256(0xff))
  bytes32 internal constant MAIN_STORAGE_LOCATION = 0xca9e8235a410bd2656fc43f888ab589425034944963c2881072ee821e700e600;

  int32 public constant LEVEL_UP_SUM = 5;
  bytes32 public constant KARMA_HASH = bytes32("KARMA");
  bytes32 public constant HERO_CLASS_HASH = bytes32("HERO_CLASS");

  /// @notice Custom data of the hero. Value is incremented on every life-chance lost
  bytes32 public constant DEATH_COUNT_HASH = bytes32("DEATH_COUNT");
  //endregion ------------------------ Constants

  //region ------------------------ RESTRICTIONS

  function onlyRegisteredContract(IController controller_) internal view {
    address sender = msg.sender;
    if (
      controller_.heroController() != sender
      && controller_.itemController() != sender
      && controller_.dungeonFactory() != sender
      && controller_.storyController() != sender
      && controller_.gameObjectController() != sender
    ) revert IAppErrors.ErrorForbidden(sender);
  }

  function onlyItemController(IController controller_) internal view {
    if (controller_.itemController() != msg.sender) revert IAppErrors.ErrorNotItemController(msg.sender);
  }

  function onlyHeroController(IController controller_) internal view {
    if (controller_.heroController() != msg.sender) revert IAppErrors.ErrorNotHeroController(msg.sender);
  }
  //endregion ------------------------ RESTRICTIONS

  //region ------------------------ VIEWS
  function _S() internal pure returns (IStatController.MainState storage s) {
    assembly {
      s.slot := MAIN_STORAGE_LOCATION
    }
    return s;
  }

  function heroAttributes(IStatController.MainState storage s, address token, uint tokenId) internal view returns (int32[] memory) {
    return PackingLib.toInt32Array(s.heroTotalAttributes[PackingLib.packNftId(token, tokenId)], uint(IStatController.ATTRIBUTES.END_SLOT));
  }

  function heroBonusAttributes(IStatController.MainState storage s, address token, uint tokenId) internal view returns (int32[] memory) {
    return PackingLib.toInt32Array(s.heroBonusAttributes[PackingLib.packNftId(token, tokenId)], uint(IStatController.ATTRIBUTES.END_SLOT));
  }

  function heroTemporallyAttributes(IStatController.MainState storage s, address token, uint tokenId) internal view returns (int32[] memory) {
    return PackingLib.toInt32Array(s.heroTemporallyAttributes[PackingLib.packNftId(token, tokenId)], uint(IStatController.ATTRIBUTES.END_SLOT));
  }


  function heroAttributesLength(address /*token*/, uint /*tokenId*/) internal pure returns (uint) {
    return uint(IStatController.ATTRIBUTES.END_SLOT);
  }

  function heroAttribute(IStatController.MainState storage s, address token, uint tokenId, uint index) internal view returns (int32) {
    return PackingLib.getInt32(s.heroTotalAttributes[PackingLib.packNftId(token, tokenId)], index);
  }

  function heroBaseAttributes(IStatController.MainState storage s, address token, uint tokenId) internal view returns (
    IStatController.CoreAttributes memory core
  ) {
    int32[] memory data = PackingLib.unpackInt32Array(s._heroCore[PackingLib.packNftId(token, tokenId)]);
    core = IStatController.CoreAttributes({
      strength: int32(data[0]),
      dexterity: int32(data[1]),
      vitality: int32(data[2]),
      energy: int32(data[3])
    });
  }

  function heroCustomData(IStatController.MainState storage s, address token, uint tokenId, bytes32 index) internal view returns (uint) {
    return s.heroCustomData[PackingLib.packNftId(token, tokenId)][index];
  }

  function globalCustomData(IStatController.MainState storage s, bytes32 index) internal view returns (uint) {
    return s.globalCustomData[index];
  }

  function heroStats(IStatController.MainState storage s, address token, uint tokenId) internal view returns (
    IStatController.ChangeableStats memory result
  ) {
    uint32[] memory data = PackingLib.unpackUint32Array(s.heroStats[PackingLib.packNftId(token, tokenId)]);
    result = IStatController.ChangeableStats({
      level: uint32(data[0]),
      experience: uint32(data[1]),
      life: uint32(data[2]),
      mana: uint32(data[3]),
      lifeChances: uint32(data[4])
    });
  }

  function heroItemSlot(IStatController.MainState storage s, address heroToken, uint64 heroTokenId, uint8 itemSlot) internal view returns (
    bytes32 nftPacked
  ) {
    return s.heroSlots[PackingLib.packMapObject(heroToken, heroTokenId, itemSlot)];
  }

  /// @return Return list of indices of the busy item slots for the given hero
  function heroItemSlots(IStatController.MainState storage s, address heroToken, uint heroTokenId) internal view returns (
    uint8[] memory
  ) {
    uint8[] memory slots = PackingLib.unpackUint8Array(s.heroBusySlots[PackingLib.packNftId(heroToken, heroTokenId)]);

    uint8[] memory busySlotsNumbers = new uint8[](slots.length);
    uint counter;

    for (uint8 i; i < uint8(slots.length); ++i) {
      if (slots[i] != 0) {
        busySlotsNumbers[counter] = i;
        counter++;
      }
    }

    uint8[] memory result = new uint8[](counter);

    for (uint i; i < counter; ++i) {
      result[i] = busySlotsNumbers[i];
    }

    return result;
  }

  function isHeroAlive(IStatController.MainState storage s, address heroToken, uint heroTokenId) internal view returns (bool) {
    return heroStats(s, heroToken, heroTokenId).lifeChances != 0;
  }

  function isConsumableUsed(IStatController.MainState storage s, address heroToken, uint heroTokenId, address item) internal view returns (bool) {
    return s.usedConsumables[PackingLib.packNftId(heroToken, heroTokenId)].contains(item);
  }

  /// @notice Calculate totalAttributes + all attributes of the items specified in {info}
  function buffHero(
    IStatController.MainState storage s,
    IController c,
    IStatController.BuffInfo memory info
  ) internal view returns (
    int32[] memory dest,
    int32 manaSum
  ) {
    uint length = info.buffTokens.length;
    if (length == 0) {
      return (heroAttributes(s, info.heroToken, info.heroTokenId), 0);
    }

    IItemController ic = IItemController(c.itemController());

    int32[] memory buffAttributes = new int32[](uint(IStatController.ATTRIBUTES.END_SLOT));
    address[] memory usedTokens = new address[](length);

    for (uint i; i < length; ++i) {

      // we should ignore the same skills
      bool used;
      for(uint j; j < i; ++j) {
        if (usedTokens[j] == info.buffTokens[i]) {
          used = true;
          break;
        }
      }
      if(used) {
        continue;
      }


      manaSum += int32(ic.itemMeta(info.buffTokens[i]).manaCost);
      (int32[] memory values, uint8[] memory ids) = ic.casterAttributes(info.buffTokens[i], info.buffTokenIds[i]);
      StatLib.attributesAdd(buffAttributes, StatLib.valuesToFullAttributesArray(values, ids));
      usedTokens[i] = info.buffTokens[i];
    }

    int32[] memory totalAttributes = StatLib.attributesAdd(heroAttributes(s, info.heroToken, info.heroTokenId), buffAttributes);

    StatLib.attributesAdd(buffAttributes, heroBonusAttributes(s, info.heroToken, info.heroTokenId));
    StatLib.attributesAdd(buffAttributes, heroTemporallyAttributes(s, info.heroToken, info.heroTokenId));

    return (
      StatLib.updateCoreDependAttributesInMemory(
        totalAttributes,
        buffAttributes,
        IHeroController(c.heroController()).heroClass(info.heroToken),
        info.heroLevel
      ),
      manaSum
    );
  }

  //endregion ------------------------ VIEWS

  //region ------------------------ PURE

  function isItemTypeEligibleToItemSlot(uint itemType, uint itemSlot) internal pure returns (bool) {
    // Consumable items not eligible
    if (itemType == 0 || itemSlot == 0) {
      return false;
    }
    // items with type before 5 mapped 1 to 1
    if (itemType <= uint(IItemController.ItemType.AMULET)) {
      return itemSlot == itemType;
    }
    if (itemType == uint(IItemController.ItemType.RING)) {
      return itemSlot == uint(IStatController.ItemSlots.RIGHT_RING)
        || itemSlot == uint(IStatController.ItemSlots.LEFT_RING);
    }
    if (itemType == uint(IItemController.ItemType.BOOTS)) {
      return itemSlot == uint(IStatController.ItemSlots.BOOTS);
    }
    if (itemType == uint(IItemController.ItemType.ONE_HAND)) {
      return itemSlot == uint(IStatController.ItemSlots.RIGHT_HAND);
    }
    if (itemType == uint(IItemController.ItemType.OFF_HAND)) {
      return itemSlot == uint(IStatController.ItemSlots.LEFT_HAND);
    }
    if (itemType == uint(IItemController.ItemType.TWO_HAND)) {
      return itemSlot == uint(IStatController.ItemSlots.TWO_HAND);
    }
    if (itemType == uint(IItemController.ItemType.SKILL)) {
      return itemSlot == uint(IStatController.ItemSlots.SKILL_1)
      || itemSlot == uint(IStatController.ItemSlots.SKILL_2)
        || itemSlot == uint(IStatController.ItemSlots.SKILL_3);
    }
    // unknown types
    return false;
  }

  /// @notice How much experience is required to go from the {level} to the next level
  function levelUpExperienceRequired(uint32 level) internal pure returns (uint) {
    if (level == 0 || level >= StatLib.MAX_LEVEL) return 0;
    return level == uint32(1)
      ? StatLib.levelExperience(level)
      : StatLib.levelExperience(level) - StatLib.levelExperience(level - uint32(1));
  }

  //endregion ------------------------ PURE

  //region ------------------------ ACTIONS

  /// @notice Initialize new hero, set up custom data, core data, changeable stats by default value
  /// @param heroClass [1..6], see StatLib.initHeroXXX
  function initNewHero(
    IStatController.MainState storage s,
    IController c,
    address heroToken,
    uint heroTokenId,
    uint heroClass
  ) internal {
    StatControllerLib.onlyHeroController(c);

    bytes32 heroPackedId = PackingLib.packNftId(heroToken, heroTokenId);
    _initNewHeroCore(s, heroPackedId, heroClass);

    bytes32[] storage totalAttributes = s.heroTotalAttributes[heroPackedId];
    uint32[] memory baseStats = StatLib.initAttributes(totalAttributes, heroClass, 1, heroClass.initialHero().core);

    _initChangeableStats(s, heroPackedId, baseStats);
    emit IApplicationEvents.NewHeroInited(heroToken, heroTokenId, IStatController.ChangeableStats({
      level: 1,
      experience: 0,
      life: baseStats[0],
      mana: baseStats[1],
      lifeChances: baseStats[2]
    }));

    // --- init predefined custom hero data

    mapping(bytes32 => uint) storage customData = s.heroCustomData[heroPackedId];

    // set initial karma
    customData[KARMA_HASH] = 1000;
    emit IApplicationEvents.HeroCustomDataChanged(heroToken, heroTokenId, KARMA_HASH, 1000);

    // set hero class as parameter for stories
    customData[HERO_CLASS_HASH] = heroClass;
    emit IApplicationEvents.HeroCustomDataChanged(heroToken, heroTokenId, HERO_CLASS_HASH, heroClass);

    // set death count value
    // customData[DEATH_COUNT_HASH] is initialized by 0 by default
    emit IApplicationEvents.HeroCustomDataChanged(heroToken, heroTokenId, DEATH_COUNT_HASH, 0);
  }

  function _initNewHeroCore(IStatController.MainState storage s, bytes32 heroPackedId, uint heroClass) internal {
    IStatController.CoreAttributes memory initialCore = heroClass.initialHero().core;
    int32[] memory arr = new int32[](4);

    arr[0] = int32(initialCore.strength);
    arr[1] = int32(initialCore.dexterity);
    arr[2] = int32(initialCore.vitality);
    arr[3] = int32(initialCore.energy);

    s._heroCore[heroPackedId] = PackingLib.packInt32Array(arr);
  }

  function _initChangeableStats(IStatController.MainState storage s, bytes32 heroPackedId, uint32[] memory baseStats) internal {
    _changeChangeableStats(s, heroPackedId, 1, 0, baseStats[0], baseStats[1], baseStats[2]);
  }

  function _changeChangeableStats(
    IStatController.MainState storage s,
    bytes32 heroPackedId,
    uint32 level,
    uint32 experience,
    uint32 life,
    uint32 mana,
    uint32 lifeChances
  ) internal {
    if(lifeChances != 0 && life == 0) {
      life = 1;
    }
    uint32[] memory data = new uint32[](5);
    data[0] = level;
    data[1] = experience;
    data[2] = life;
    data[3] = mana;
    data[4] = lifeChances;

    s.heroStats[heroPackedId] = PackingLib.packUint32Array(data);
  }

  /// @notice Add/remove the item to/from the hero
  function changeHeroItemSlot(
    IStatController.MainState storage s,
    IController controller,
    address heroToken,
    uint64 heroTokenId,
    uint itemType,
    uint8 itemSlot,
    address itemToken,
    uint itemTokenId,
    bool equip
  ) internal {
    StatControllerLib.onlyItemController(controller);
    if (!StatControllerLib.isItemTypeEligibleToItemSlot(itemType, itemSlot)) revert IAppErrors.ErrorItemNotEligibleForTheSlot(itemType, itemSlot);

    // if we are going to take an item by two hands, we need both hands free.
    // if we are going to use only one hand, we shouldn't keep anything by two hands
    if (itemSlot == uint(IStatController.ItemSlots.TWO_HAND)) {
      if (heroItemSlot(s, heroToken, heroTokenId, uint8(IStatController.ItemSlots.RIGHT_HAND)) != bytes32(0)
        || heroItemSlot(s, heroToken, heroTokenId, uint8(IStatController.ItemSlots.LEFT_HAND)) != bytes32(0)) {
        revert IAppErrors.ErrorItemSlotBusyHand(itemSlot);
      }
    }
    if (itemSlot == uint(IStatController.ItemSlots.RIGHT_HAND) || itemSlot == uint(IStatController.ItemSlots.LEFT_HAND)) {
      if (heroItemSlot(s, heroToken, heroTokenId, uint8(IStatController.ItemSlots.TWO_HAND)) != bytes32(0)) {
        revert IAppErrors.ErrorItemSlotBusyHand(itemSlot);
      }
    }

    bytes32 heroPackedId = PackingLib.packNftId(heroToken, heroTokenId);
    (address equippedItem, uint equippedItemId) = PackingLib.unpackNftId(heroItemSlot(s, heroToken, heroTokenId, itemSlot));
    if (equip) {
      if (equippedItem != address(0)) revert IAppErrors.ErrorItemSlotBusy();

      s.heroSlots[PackingLib.packMapObject(heroToken, uint64(heroTokenId), itemSlot)] = PackingLib.packNftId(itemToken, itemTokenId);
      s.heroBusySlots[heroPackedId] = PackingLib.changeUnit8ArrayWithCheck(s.heroBusySlots[heroPackedId], itemSlot, 1, 0);
    } else {
      if (equippedItem != itemToken || equippedItemId != itemTokenId) revert IAppErrors.ErrorItemNotInSlot();

      delete s.heroSlots[PackingLib.packMapObject(heroToken, uint64(heroTokenId), itemSlot)];
      s.heroBusySlots[heroPackedId] = PackingLib.changeUnit8ArrayWithCheck(s.heroBusySlots[heroPackedId], itemSlot, 0, 1);
    }

    emit IApplicationEvents.HeroItemSlotChanged(heroToken, heroTokenId, itemType, itemSlot, itemToken, itemTokenId, equip, msg.sender);
  }

  /// @notice Increase or decrease stats (life, mana, lifeChances). Experience can be increased only.
  function changeCurrentStats(
    IStatController.MainState storage s,
    IController c,
    address heroToken,
    uint heroTokenId,
    IStatController.ChangeableStats memory change,
    bool increase
  ) internal {
    StatControllerLib.onlyRegisteredContract(c);

    bytes32 heroPackedId = PackingLib.packNftId(heroToken, heroTokenId);
    IStatController.ChangeableStats memory currentStats = heroStats(s, heroToken, heroTokenId);

    uint32 life = currentStats.life;
    uint32 mana = currentStats.mana;
    uint32 lifeChances = currentStats.lifeChances;

    if (increase) {
      bytes32[] storage attrs = s.heroTotalAttributes[heroPackedId];
      int32 maxLife = attrs.getInt32(uint(IStatController.ATTRIBUTES.LIFE));
      int32 maxMana = attrs.getInt32(uint(IStatController.ATTRIBUTES.MANA));
      int32 maxLC = attrs.getInt32(uint(IStatController.ATTRIBUTES.LIFE_CHANCES));

      currentStats.experience += change.experience;
      life = uint32(Math.min(maxLife.toUint(), uint(life + change.life)));
      mana = uint32(Math.min(maxMana.toUint(), uint(mana + change.mana)));
      lifeChances = uint32(Math.min(maxLC.toUint(), uint(lifeChances + change.lifeChances)));
    } else {
      if (change.experience != 0) revert IAppErrors.ErrorExperienceMustNotDecrease();
      life = life > change.life ? life - change.life : 0;
      lifeChances = lifeChances > change.lifeChances ? lifeChances - change.lifeChances : 0;
      mana = mana > change.mana ? mana - change.mana : 0;
    }

    _changeChangeableStats(s, heroPackedId, currentStats.level, currentStats.experience, life, mana, lifeChances);
    emit IApplicationEvents.CurrentStatsChanged(heroToken, heroTokenId, change, increase, msg.sender);
  }

  /// @notice Mark consumable {item} as used
  function registerConsumableUsage(
    IStatController.MainState storage s,
    IController c,
    address heroToken,
    uint heroTokenId,
    address item
  ) internal {
    StatControllerLib.onlyRegisteredContract(c);

    if (!s.usedConsumables[PackingLib.packNftId(heroToken, heroTokenId)].add(item)) revert IAppErrors.ErrorConsumableItemIsUsed(item);
    emit IApplicationEvents.ConsumableUsed(heroToken, heroTokenId, item);
  }

  /// @notice Clear all consumable items of the given hero
  function clearUsedConsumables(
    IStatController.MainState storage s,
    IController c,
    address heroToken,
    uint heroTokenId
  ) internal {
    StatControllerLib.onlyRegisteredContract(c);

    EnumerableSet.AddressSet storage items = s.usedConsumables[PackingLib.packNftId(heroToken, heroTokenId)];

    uint length = items.length();

    for (uint i; i < length; ++i) {
      // we are removing the first element, so it's safe to use in cycle
      address item = items.at(0);
      if (!items.remove(item)) revert IAppErrors.ErrorCannotRemoveItemFromMap();
      emit IApplicationEvents.RemoveConsumableUsage(heroToken, heroTokenId, item);
    }
  }

  /// @notice Increase or decrease values of the given attributes, any attributes are allowed.
  /// @dev If a core attribute is changed than depended attributes are recalculated
  function changeBonusAttributes(
    IStatController.MainState storage s,
    IController c,
    IStatController.ChangeAttributesInfo memory info
  ) internal {
    StatControllerLib.onlyRegisteredContract(c);
    bytes32 heroPackedId = PackingLib.packNftId(info.heroToken, info.heroTokenId);

    IStatController.ChangeableStats memory stats = heroStats(s, info.heroToken, info.heroTokenId);
    bytes32[] storage totalAttributes = s.heroTotalAttributes[heroPackedId];
    (bytes32[] storage bonusMain, bytes32[] storage bonusExtra) = info.temporally
      ? (s.heroTemporallyAttributes[heroPackedId], s.heroBonusAttributes[heroPackedId])
      : (s.heroBonusAttributes[heroPackedId], s.heroTemporallyAttributes[heroPackedId]);

    int32[] memory cachedTotalAttrChanged = new int32[](info.changeAttributes.length);
    for (uint i; i < info.changeAttributes.length; ++i) {
      int32 change = info.changeAttributes[i];
      if (change != 0) {
        int32 newTotalValue;

        if (info.add) {
          bonusMain.changeInt32(i, change);
          newTotalValue = totalAttributes.getInt32(i) + change;
        } else {
          bonusMain.changeInt32(i, - change);
          newTotalValue = totalAttributes.getInt32(i) - change;
        }

        // todo in some cases value stored here to totalAttributes will be overwritten below by updateCoreDependAttributes
        // it happens if core attribute is changed AND it's depend attribute is change too
        // values of the depend attribute will be overwritten by updateCoreDependAttributes
        // fix it together with PACKED WRITING
        totalAttributes.setInt32(i, newTotalValue);
        cachedTotalAttrChanged[i] = newTotalValue;
      }
    }

    _updateCoreDependAttributes(c, totalAttributes, bonusMain, bonusExtra, stats, info.heroToken, cachedTotalAttrChanged, info.changeAttributes);
    _compareStatsWithAttributes(s, heroPackedId, totalAttributes, stats);

    emit IApplicationEvents.BonusAttributesChanged(info.heroToken, info.heroTokenId, info.add, info.temporally, msg.sender);
  }

  /// @dev Make sure we don't have life/mana more than total attributes after decreasing
  function _compareStatsWithAttributes(
    IStatController.MainState storage s,
    bytes32 heroPackedId,
    bytes32[] storage totalAttributes,
    IStatController.ChangeableStats memory curStats
  ) internal {
    uint life = totalAttributes.getInt32(uint(IStatController.ATTRIBUTES.LIFE)).toUint();
    uint mana = totalAttributes.getInt32(uint(IStatController.ATTRIBUTES.MANA)).toUint();
    bool changed;
    if (life < curStats.life) {
      curStats.life = uint32(Math.min(life, curStats.life));
      changed = true;
    }
    if (mana < curStats.mana) {
      curStats.mana = uint32(Math.min(mana, curStats.mana));
      changed = true;
    }
    if (changed) {
      _changeChangeableStats(s,
        heroPackedId,
        curStats.level,
        curStats.experience,
        curStats.life,
        curStats.mana,
        curStats.lifeChances
      );
    }
  }

  function clearTemporallyAttributes(
    IStatController.MainState storage s,
    IController c,
    address heroToken,
    uint heroTokenId
  ) internal {
    StatControllerLib.onlyRegisteredContract(c);
    bytes32 heroPackedId = PackingLib.packNftId(heroToken, heroTokenId);

    bytes32[] memory tmpBonuses = s.heroTemporallyAttributes[heroPackedId];

    IStatController.ChangeableStats memory stats = heroStats(s, heroToken, heroTokenId);
    bytes32[] storage bonus = s.heroBonusAttributes[heroPackedId];
    bytes32[] storage totalAttributes = s.heroTotalAttributes[heroPackedId];

    int32[] memory baseValues = new int32[](uint(IStatController.ATTRIBUTES.END_SLOT));
    int32[] memory tmpBonusesUnpacked = new int32[](uint(IStatController.ATTRIBUTES.END_SLOT));
    for (uint i; i < uint(IStatController.ATTRIBUTES.END_SLOT); ++i) {
      int32 value = tmpBonuses.getInt32Memory(i);
      if (value != int32(0)) {
        (baseValues[i],) = totalAttributes.changeInt32(i, - int32(uint32(value)));
        tmpBonusesUnpacked[i] = value;
      }
    }

    delete s.heroTemporallyAttributes[heroPackedId];

    bytes32[] storage tmpBonusesStorage = s.heroTemporallyAttributes[heroPackedId];

    _updateCoreDependAttributes(c, totalAttributes, bonus, tmpBonusesStorage, stats, heroToken, baseValues, tmpBonusesUnpacked);
    _compareStatsWithAttributes(s, heroPackedId, totalAttributes, stats);

    emit IApplicationEvents.TemporallyAttributesCleared(heroToken, heroTokenId, msg.sender);
  }

  /// @dev Update depend-values for all changed attributes
  function _updateCoreDependAttributes(
    IController c,
    bytes32[] storage totalAttributes,
    bytes32[] storage bonusMain,
    bytes32[] storage bonusExtra,
    IStatController.ChangeableStats memory stats,
    address heroToken,
    int32[] memory baseValues,
    int32[] memory changed
  ) internal {
    // handle core depend attributes in the second loop, totalAttributes should be updated together
    uint len = changed.length;
    for (uint i; i < len; ++i) {
      // depend-values should be recalculated if corresponded core value is changed (even if it's equal to 0 now)
      if (changed[i] != 0) {
        StatLib.updateCoreDependAttributes(c, totalAttributes, bonusMain, bonusExtra, stats, i, heroToken, baseValues[i]);
      }
    }
  }

  function levelUp(
    IStatController.MainState storage s,
    IController c,
    address heroToken,
    uint heroTokenId,
    uint heroClass,
    IStatController.CoreAttributes memory change
  ) internal returns (uint newLvl) {
    StatControllerLib.onlyHeroController(c);

    bytes32 heroPackedId = PackingLib.packNftId(heroToken, heroTokenId);
    if (change.strength + change.dexterity + change.vitality + change.energy != LEVEL_UP_SUM) revert IAppErrors.ErrorWrongLevelUpSum();

    IStatController.ChangeableStats memory currentStats = heroStats(s, heroToken, heroTokenId);

    if (currentStats.level >= StatLib.MAX_LEVEL) revert IAppErrors.ErrorMaxLevel();
    if (currentStats.level.levelExperience() > currentStats.experience) revert IAppErrors.ErrorNotEnoughExperience();
    currentStats.level++;

    {
      int32[] memory data = PackingLib.unpackInt32Array(s._heroCore[heroPackedId]);

      data[0] += change.strength;
      data[1] += change.dexterity;
      data[2] += change.vitality;
      data[3] += change.energy;

      s._heroCore[heroPackedId] = PackingLib.packInt32Array(data);
    }

    bytes32[] storage totalAttributes = s.heroTotalAttributes[heroPackedId];
    {
      bytes32[] storage bonus = s.heroBonusAttributes[heroPackedId];
      bytes32[] storage bonusTmp = s.heroTemporallyAttributes[heroPackedId];

      // update
      _addCoreToTotal(
        c,
        totalAttributes,
        bonus,
        bonusTmp,
        currentStats,
        heroToken,
        change.strength,
        uint(IStatController.ATTRIBUTES.STRENGTH)
      );
      _addCoreToTotal(
        c,
        totalAttributes,
        bonus,
        bonusTmp,
        currentStats,
        heroToken,
        change.dexterity,
        uint(IStatController.ATTRIBUTES.DEXTERITY)
      );
      _addCoreToTotal(
        c,
        totalAttributes,
        bonus,
        bonusTmp,
        currentStats,
        heroToken,
        change.vitality,
        uint(IStatController.ATTRIBUTES.VITALITY)
      );
      _addCoreToTotal(
        c,
        totalAttributes,
        bonus,
        bonusTmp,
        currentStats,
        heroToken,
        change.energy,
        uint(IStatController.ATTRIBUTES.ENERGY)
      );
    }

    // setup new level and restore life/mana
    currentStats.life = uint32(totalAttributes.getInt32(uint(IStatController.ATTRIBUTES.LIFE)).toUint());
    currentStats.mana = uint32(totalAttributes.getInt32(uint(IStatController.ATTRIBUTES.MANA)).toUint());

    _changeChangeableStats(
      s,
      heroPackedId,
      currentStats.level,
      currentStats.experience,
      currentStats.life,
      currentStats.mana,
      currentStats.lifeChances
    );

    emit IApplicationEvents.LevelUp(heroToken, heroTokenId, heroClass, change);

    return currentStats.level;
  }

  /// @notice Restore life and mana to default values stored in total attributes
  function restoreLifeAndMana(
    IStatController.MainState storage s,
    IController c,
    address heroToken,
    uint heroTokenId
  ) internal {
    StatControllerLib.onlyHeroController(c);

    IStatController.ChangeableStats memory currentStats = heroStats(s, heroToken, heroTokenId);
    bytes32 heroPackedId = PackingLib.packNftId(heroToken, heroTokenId);
    bytes32[] storage totalAttributes = s.heroTotalAttributes[heroPackedId];

    currentStats.life = uint32(totalAttributes.getInt32(uint(IStatController.ATTRIBUTES.LIFE)).toUint());
    currentStats.mana = uint32(totalAttributes.getInt32(uint(IStatController.ATTRIBUTES.MANA)).toUint());

    _changeChangeableStats(
      s,
      heroPackedId,
      currentStats.level,
      currentStats.experience,
      currentStats.life,
      currentStats.mana,
      currentStats.lifeChances
    );
  }

  function _addCoreToTotal(
    IController c,
    bytes32[] storage totalAttributes,
    bytes32[] storage bonus,
    bytes32[] storage bonusTmp,
    IStatController.ChangeableStats memory stats,
    address heroToken,
    int32 changeValue,
    uint attrIndex
  ) internal {
    if (changeValue != 0) {
      (int32 newValue,) = totalAttributes.changeInt32(attrIndex, int32(uint32(changeValue)));
      StatLib.updateCoreDependAttributes(c, totalAttributes, bonus, bonusTmp, stats, attrIndex, heroToken, newValue);
    }
  }

  function setHeroCustomData(
    IStatController.MainState storage s,
    IController c,
    address token,
    uint tokenId,
    bytes32 index,
    uint value
  ) internal {
    StatControllerLib.onlyRegisteredContract(c);

    if (index == KARMA_HASH && value == 0) {
      revert IAppErrors.ErrorZeroKarmaNotAllowed();
    }

    s.heroCustomData[PackingLib.packNftId(token, tokenId)][index] = value;

    emit IApplicationEvents.HeroCustomDataChanged(token, tokenId, index, value);
  }

  function setGlobalCustomData(
    IStatController.MainState storage s,
    IController c,
    bytes32 index,
    uint value
  ) internal {
    StatControllerLib.onlyRegisteredContract(c);

    s.globalCustomData[index] = value;

    emit IApplicationEvents.GlobalCustomDataChanged(index, value);
  }
  //endregion ------------------------ ACTIONS
}

contracts/lib/StatLib.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.23;

import "../interfaces/IStatController.sol";
import "../interfaces/IHeroController.sol";
import "../interfaces/IAppErrors.sol";
import "../openzeppelin/Math.sol";
import "./CalcLib.sol";
import "./PackingLib.sol";

library StatLib {
  using PackingLib for bytes32[];
  using PackingLib for bytes32;
  using PackingLib for uint32[];
  using PackingLib for int32[];
  using CalcLib for int32;

  //region --------------------------- Constants

  /// @notice Version of the contract
  /// @dev Should be incremented when contract changed
  string public constant STAT_LIB_VERSION = "1.0.0";
  uint32 public constant MAX_LEVEL = 99;

  uint public constant BASE_EXPERIENCE = 100_000;
  uint public constant BIOME_LEVEL_STEP = 5;
  uint internal constant _MAX_AMPLIFIER = 1e18;
  uint private constant _PRECISION = 1e18;

  /// @dev Assume MAX_BIOME * BIOME_LEVEL_STEP < MAX_LEVEL + 1, see dungeonTreasuryReward
  uint public constant MAX_POSSIBLE_BIOME = 19;
  //endregion --------------------------- Constants

  //region --------------------------- Data types

  struct BaseMultiplier {
    uint minDamage;
    uint maxDamage;
    uint attackRating;
    uint defense;
    uint blockRating;
    uint life;
    uint mana;
  }

  struct LevelUp {
    uint life;
    uint mana;
  }

  struct InitialHero {
    IStatController.CoreAttributes core;
    BaseMultiplier multiplier;
    LevelUp levelUp;
    int32 baseLifeChances;
  }

  enum HeroClasses {
    UNKNOWN,
    THRALL,
    SAVAGE,
    MAGE,
    ASSASSIN,
    GHOST,
    HAMMERGINA,
    END_SLOT
  }
  //endregion --------------------------- Data types

  //region --------------------------- BASE

  // --- HERO 1 (Slave) ---

  function initialHero1() internal pure returns (InitialHero memory) {
    return InitialHero({
      core: IStatController.CoreAttributes({
      strength: 15,
      dexterity: 15,
      vitality: 30,
      energy: 10
    }),

      multiplier: BaseMultiplier({
      minDamage: 0.1e18,
      maxDamage: 0.2e18,
      attackRating: 2e18,
      defense: 2e18,
      blockRating: 0.1e18,
      life: 1.5e18,
      mana: 0.5e18
    }),

      levelUp: LevelUp({
      life: 2e18,
      mana: 1e18
    }),

      baseLifeChances: 5
    });
  }

  // --- HERO 2 (Spata) ---

  function initialHero2() internal pure returns (InitialHero memory) {
    return InitialHero({
      core: IStatController.CoreAttributes({
      strength: 30,
      dexterity: 5,
      vitality: 25,
      energy: 10
    }),

      multiplier: BaseMultiplier({
      minDamage: 0.15e18,
      maxDamage: 0.25e18,
      attackRating: 2e18,
      defense: 1e18,
      blockRating: 0.08e18,
      life: 1.3e18,
      mana: 0.5e18
    }),

      levelUp: LevelUp({
      life: 1.8e18,
      mana: 1e18
    }),

      baseLifeChances: 5
    });
  }

  // --- HERO 3 (Decidia) ---

  function initialHero3() internal pure returns (InitialHero memory) {
    return InitialHero({
      core: IStatController.CoreAttributes({
      strength: 10,
      dexterity: 15,
      vitality: 20,
      energy: 25
    }),

      multiplier: BaseMultiplier({
      minDamage: 0.1e18,
      maxDamage: 0.2e18,
      attackRating: 2e18,
      defense: 1e18,
      blockRating: 0.1e18,
      life: 1e18,
      mana: 2e18
    }),

      levelUp: LevelUp({
      life: 1.3e18,
      mana: 2e18
    }),

      baseLifeChances: 5
    });
  }

  // --- HERO 4 (Innatus) ---

  function initialHero4() internal pure returns (InitialHero memory) {
    return InitialHero({
      core: IStatController.CoreAttributes({
      strength: 15,
      dexterity: 25,
      vitality: 15,
      energy: 15
    }),

      multiplier: BaseMultiplier({
      minDamage: 0.1e18,
      maxDamage: 0.2e18,
      attackRating: 4e18,
      defense: 3e18,
      blockRating: 0.2e18,
      life: 1.2e18,
      mana: 1e18
    }),

      levelUp: LevelUp({
      life: 1.7e18,
      mana: 1.5e18
    }),

      baseLifeChances: 5
    });
  }

  // --- HERO 5 (F2P) ---

  function initialHero5() internal pure returns (InitialHero memory) {
    return InitialHero({
      core: IStatController.CoreAttributes({
      strength: 20,
      dexterity: 20,
      vitality: 20,
      energy: 10
    }),

      multiplier: BaseMultiplier({
      minDamage: 0.15e18,
      maxDamage: 0.25e18,
      attackRating: 3e18,
      defense: 2.5e18,
      blockRating: 0.15e18,
      life: 1.5e18,
      mana: 1.5e18
    }),

      levelUp: LevelUp({
      life: 1.5e18,
      mana: 1.5e18
    }),

      baseLifeChances: 1
    });
  }

  // --- HERO 6 (F2P) HAMMERGINA ---

  function initialHero6() internal pure returns (InitialHero memory) {
    return InitialHero({
      core: IStatController.CoreAttributes({
      strength: 50,
      dexterity: 30,
      vitality: 50,
      energy: 15
    }),

      multiplier: BaseMultiplier({
      minDamage: 0.2e18,
      maxDamage: 0.3e18,
      attackRating: 5e18,
      defense: 3e18,
      blockRating: 0.15e18,
      life: 2e18,
      mana: 2e18
    }),

      levelUp: LevelUp({
      life: 1.7e18,
      mana: 1.5e18
    }),

      baseLifeChances: 1
    });
  }

  // ------

  function initialHero(uint heroClass) internal pure returns (InitialHero memory) {
    if (heroClass == 1) {
      return initialHero1();
    } else if (heroClass == 2) {
      return initialHero2();
    } else if (heroClass == 3) {
      return initialHero3();
    } else if (heroClass == 4) {
      return initialHero4();
    } else if (heroClass == 5) {
      return initialHero5();
    } else if (heroClass == 6) {
      return initialHero6();
    } else {
      revert IAppErrors.UnknownHeroClass(heroClass);
    }
  }
  //endregion --------------------------- BASE

  //region --------------------------- CALCULATIONS

  function minDamage(int32 strength, uint heroClass) internal pure returns (int32) {
    return int32(int(strength.toUint() * initialHero(heroClass).multiplier.minDamage / _PRECISION));
  }

  function maxDamage(int32 strength, uint heroClass) internal pure returns (int32){
    return int32(int(strength.toUint() * initialHero(heroClass).multiplier.maxDamage / _PRECISION));
  }

  function attackRating(int32 dexterity, uint heroClass) internal pure returns (int32){
    return int32(int(dexterity.toUint() * initialHero(heroClass).multiplier.attackRating / _PRECISION));
  }

  function defense(int32 dexterity, uint heroClass) internal pure returns (int32){
    return int32(int(dexterity.toUint() * initialHero(heroClass).multiplier.defense / _PRECISION));
  }

  function blockRating(int32 dexterity, uint heroClass) internal pure returns (int32){
    return int32(int(Math.min((dexterity.toUint() * initialHero(heroClass).multiplier.blockRating / _PRECISION), 75)));
  }

  function life(int32 vitality, uint heroClass, uint32 level) internal pure returns (int32){
    return int32(int(
      (vitality.toUint() * initialHero(heroClass).multiplier.life / _PRECISION)
      + (uint(level) * initialHero(heroClass).levelUp.life / _PRECISION)
    ));
  }

  function mana(int32 energy, uint heroClass, uint32 level) internal pure returns (int32){
    return int32(int(
      (energy.toUint() * initialHero(heroClass).multiplier.mana / _PRECISION)
      + (uint(level) * initialHero(heroClass).levelUp.mana / _PRECISION)
    ));
  }

  function lifeChances(uint heroClass, uint32 /*level*/) internal pure returns (int32){
    return initialHero(heroClass).baseLifeChances;
  }

  function levelExperience(uint32 level) internal pure returns (uint32) {
    if (level == 0 || level >= MAX_LEVEL) {
      return 0;
    }
    return uint32(uint(level) * BASE_EXPERIENCE * (67e17 - CalcLib.log2((uint(MAX_LEVEL - level + 2)) * 1e18)) / 1e18);
  }

  function chanceToHit(
    uint attackersAttackRating,
    uint defendersDefenceRating,
    uint attackersLevel,
    uint defendersLevel,
    uint arFactor
  ) internal pure returns (uint) {
    attackersAttackRating += attackersAttackRating * arFactor / 100;
    uint x = Math.max(attackersAttackRating, 1);
    uint y = Math.max(attackersAttackRating + defendersDefenceRating, 1);
    uint z = attackersLevel;
    uint k = defendersLevel / 2;
    uint xy = x * 1e18 / y;
    uint zk = z * 1e18 / (attackersLevel + k);
    uint base = 2 * xy * zk / 1e18;
    return Math.max(Math.min(base, 0.95e18), 0.2e18);
  }

  function experienceToLvl(uint experience, uint startFromLevel) internal pure returns (uint level) {
    level = startFromLevel;
    for (; level < MAX_LEVEL;) {
      if (levelExperience(uint32(level)) >= experience) {
        break;
      }
      unchecked{++level;}
    }
  }

  function expPerMonster(uint32 monsterExp, uint monsterRarity, uint32 heroExp, uint32 heroCurrentLvl, uint monsterBiome) internal pure returns (uint32) {
    uint heroLvl = experienceToLvl(uint(heroExp), uint(heroCurrentLvl));
    uint heroBiome = heroLvl / StatLib.BIOME_LEVEL_STEP + 1;
    uint base = uint(monsterExp) + uint(monsterExp) * monsterRarity / _MAX_AMPLIFIER;

    // reduce exp if hero not in his biome
    if (heroBiome > monsterBiome) {
      base = base / (2 ** (heroBiome - monsterBiome));
    }
    return uint32(base);
  }

  /// @notice Allow to calculate delta param for {mintDropChance}
  function mintDropChanceDelta(uint experience, uint startFromLevel, uint monsterBiome) internal pure returns (uint) {
    uint heroBiome = StatLib.experienceToLvl(experience, startFromLevel) / StatLib.BIOME_LEVEL_STEP + 1;
    return heroBiome > monsterBiome ? 2**(heroBiome - monsterBiome) : 0;
  }

  /// @param delta 2 ** (heroBiome - monsterBiome) or zero if heroBiome < monsterBiome, see {mintDropChanceDelta}
  function mintDropChance(uint baseChance, uint monsterRarity, uint delta) internal pure returns (uint) {
    uint chance = baseChance + baseChance * monsterRarity / _MAX_AMPLIFIER;

    // reduce chance if hero not in his biome
    return delta == 0
      ? chance
      : chance / delta;
  }

  function initAttributes(
    bytes32[] storage attributes,
    uint heroClass,
    uint32 level,
    IStatController.CoreAttributes memory base
  ) internal returns (uint32[] memory result) {

    attributes.setInt32(uint(IStatController.ATTRIBUTES.STRENGTH), base.strength);
    attributes.setInt32(uint(IStatController.ATTRIBUTES.DEXTERITY), base.dexterity);
    attributes.setInt32(uint(IStatController.ATTRIBUTES.VITALITY), base.vitality);
    attributes.setInt32(uint(IStatController.ATTRIBUTES.ENERGY), base.energy);

    attributes.setInt32(uint(IStatController.ATTRIBUTES.DAMAGE_MIN), minDamage(base.strength, heroClass));
    attributes.setInt32(uint(IStatController.ATTRIBUTES.DAMAGE_MAX), maxDamage(base.strength, heroClass));
    attributes.setInt32(uint(IStatController.ATTRIBUTES.ATTACK_RATING), attackRating(base.dexterity, heroClass));
    attributes.setInt32(uint(IStatController.ATTRIBUTES.DEFENSE), defense(base.dexterity, heroClass));
    attributes.setInt32(uint(IStatController.ATTRIBUTES.BLOCK_RATING), blockRating(base.dexterity, heroClass));
    attributes.setInt32(uint(IStatController.ATTRIBUTES.LIFE), life(base.vitality, heroClass, level));
    attributes.setInt32(uint(IStatController.ATTRIBUTES.MANA), mana(base.energy, heroClass, level));
    attributes.setInt32(uint(IStatController.ATTRIBUTES.LIFE_CHANCES), lifeChances(heroClass, level));

    result = new uint32[](3);
    result[0] = uint32(life(base.vitality, heroClass, level).toUint());
    result[1] = uint32(mana(base.energy, heroClass, level).toUint());
    result[2] = uint32(lifeChances(heroClass, uint32(level)).toUint());
  }

  function updateCoreDependAttributesInMemory(
    int32[] memory attributes,
    int32[] memory bonus,
    uint heroClass,
    uint32 level
  ) internal pure returns (int32[] memory) {
    int32 strength = attributes[uint(IStatController.ATTRIBUTES.STRENGTH)];
    int32 dexterity = attributes[uint(IStatController.ATTRIBUTES.DEXTERITY)];
    int32 vitality = attributes[uint(IStatController.ATTRIBUTES.VITALITY)];
    int32 energy = attributes[uint(IStatController.ATTRIBUTES.ENERGY)];

    attributes[uint(IStatController.ATTRIBUTES.DAMAGE_MIN)] = minDamage(strength, heroClass) + bonus[uint(IStatController.ATTRIBUTES.DAMAGE_MIN)];
    attributes[uint(IStatController.ATTRIBUTES.DAMAGE_MAX)] = maxDamage(strength, heroClass) + bonus[uint(IStatController.ATTRIBUTES.DAMAGE_MAX)];
    attributes[uint(IStatController.ATTRIBUTES.ATTACK_RATING)] = attackRating(dexterity, heroClass) + bonus[uint(IStatController.ATTRIBUTES.ATTACK_RATING)];
    attributes[uint(IStatController.ATTRIBUTES.DEFENSE)] = defense(dexterity, heroClass) + bonus[uint(IStatController.ATTRIBUTES.DEFENSE)];
    attributes[uint(IStatController.ATTRIBUTES.BLOCK_RATING)] = blockRating(dexterity, heroClass) + bonus[uint(IStatController.ATTRIBUTES.BLOCK_RATING)];
    attributes[uint(IStatController.ATTRIBUTES.LIFE)] = life(vitality, heroClass, level) + bonus[uint(IStatController.ATTRIBUTES.LIFE)];
    attributes[uint(IStatController.ATTRIBUTES.MANA)] = mana(energy, heroClass, level) + bonus[uint(IStatController.ATTRIBUTES.MANA)];
    return attributes;
  }

  function updateCoreDependAttributes(
    IController controller,
    bytes32[] storage attributes,
    bytes32[] storage bonusMain,
    bytes32[] storage bonusExtra,
    IStatController.ChangeableStats memory _heroStats,
    uint index,
    address heroToken,
    int32 base
  ) internal {
    uint heroClass = IHeroController(controller.heroController()).heroClass(heroToken);
    if (index == uint(IStatController.ATTRIBUTES.STRENGTH)) {

      attributes.setInt32(uint(IStatController.ATTRIBUTES.DAMAGE_MIN),
        StatLib.minDamage(base, heroClass)
        + bonusMain.getInt32(uint(IStatController.ATTRIBUTES.DAMAGE_MIN))
        + bonusExtra.getInt32(uint(IStatController.ATTRIBUTES.DAMAGE_MIN))
      );
      attributes.setInt32(uint(IStatController.ATTRIBUTES.DAMAGE_MAX),
        StatLib.maxDamage(base, heroClass)
        + bonusMain.getInt32(uint(IStatController.ATTRIBUTES.DAMAGE_MAX))
        + bonusExtra.getInt32(uint(IStatController.ATTRIBUTES.DAMAGE_MAX))
      );
    } else if (index == uint(IStatController.ATTRIBUTES.DEXTERITY)) {

      attributes.setInt32(uint(IStatController.ATTRIBUTES.ATTACK_RATING),
        StatLib.attackRating(base, heroClass)
        + bonusMain.getInt32(uint(IStatController.ATTRIBUTES.ATTACK_RATING))
        + bonusExtra.getInt32(uint(IStatController.ATTRIBUTES.ATTACK_RATING))
      );

      attributes.setInt32(uint(IStatController.ATTRIBUTES.DEFENSE),
        StatLib.defense(base, heroClass)
        + bonusMain.getInt32(uint(IStatController.ATTRIBUTES.DEFENSE))
        + bonusExtra.getInt32(uint(IStatController.ATTRIBUTES.DEFENSE))
      );

      attributes.setInt32(uint(IStatController.ATTRIBUTES.BLOCK_RATING),
        StatLib.blockRating(base, heroClass)
        + bonusMain.getInt32(uint(IStatController.ATTRIBUTES.BLOCK_RATING))
        + bonusExtra.getInt32(uint(IStatController.ATTRIBUTES.BLOCK_RATING))
      );
    } else if (index == uint(IStatController.ATTRIBUTES.VITALITY)) {

      attributes.setInt32(uint(IStatController.ATTRIBUTES.LIFE),
        StatLib.life(base, heroClass, _heroStats.level)
        + bonusMain.getInt32(uint(IStatController.ATTRIBUTES.LIFE))
        + bonusExtra.getInt32(uint(IStatController.ATTRIBUTES.LIFE))
      );
    } else if (index == uint(IStatController.ATTRIBUTES.ENERGY)) {

      attributes.setInt32(uint(IStatController.ATTRIBUTES.MANA),
        StatLib.mana(base, heroClass, _heroStats.level)
        + bonusMain.getInt32(uint(IStatController.ATTRIBUTES.MANA))
        + bonusExtra.getInt32(uint(IStatController.ATTRIBUTES.MANA))
      );
    }
  }

  function attributesAdd(int32[] memory base, int32[] memory add) internal pure returns (int32[] memory) {
    unchecked{
      for (uint i; i < base.length; ++i) {
        base[i] += add[i];
      }
    }
    return base;
  }

// Currently this function is not used
//  function attributesRemove(int32[] memory base, int32[] memory remove) internal pure returns (int32[] memory) {
//    unchecked{
//      for (uint i; i < base.length; ++i) {
//        base[i] = CalcLib.minusWithMinFloorI32(base[i], remove[i]);
//      }
//    }
//    return base;
//  }

  function packChangeableStats(IStatController.ChangeableStats memory stats) internal pure returns (bytes32) {
    uint32[] memory cData = new uint32[](5);
    cData[0] = stats.level;
    cData[1] = stats.experience;
    cData[2] = stats.life;
    cData[3] = stats.mana;
    cData[4] = stats.lifeChances;

    return cData.packUint32Array();
  }

  function unpackChangeableStats(bytes32 data) internal pure returns (IStatController.ChangeableStats memory result) {
    uint32[] memory cData = data.unpackUint32Array();
    return IStatController.ChangeableStats({
      level: cData[0],
      experience: cData[1],
      life: cData[2],
      mana: cData[3],
      lifeChances: cData[4]
    });
  }

  function bytesToFullAttributesArray(bytes32[] memory attributes) internal pure returns (int32[] memory result) {
    (int32[] memory values, uint8[] memory ids) = attributes.toInt32ArrayWithIds();
    return valuesToFullAttributesArray(values, ids);
  }

  function valuesToFullAttributesArray(int32[] memory values, uint8[] memory ids) internal pure returns (int32[] memory result) {
    result = new int32[](uint(IStatController.ATTRIBUTES.END_SLOT));
    for (uint i; i < values.length; ++i) {
      int32 value = values[i];
      if (value != 0) {
        result[ids[i]] = value;
      }
    }
  }
  //endregion --------------------------- CALCULATIONS

}

contracts/lib/StoryControllerLib.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.23;
import "../proxy/Controllable.sol";
import "../interfaces/IStoryController.sol";
import "../interfaces/IStatController.sol";
import "../interfaces/IOracle.sol";
import "../interfaces/ITreasury.sol";
import "../interfaces/IGOC.sol";
import "../interfaces/IHeroController.sol";
import "../lib/StoryLib.sol";
import "../lib/PackingLib.sol";
import "../lib/StorySetupLib.sol";


library StoryControllerLib {
  using EnumerableSet for EnumerableSet.UintSet;
  using EnumerableSet for EnumerableSet.Bytes32Set;
  using CalcLib for uint;
  using PackingLib for bytes32;
  using PackingLib for uint16;
  using PackingLib for uint8;
  using PackingLib for address;
  using PackingLib for uint32[];
  using PackingLib for uint32;
  using PackingLib for uint64;
  using PackingLib for int32[];
  using PackingLib for int32;


  //region ------------------------ CONSTANTS
  /// @dev keccak256(abi.encode(uint256(keccak256("story.controller.main")) - 1)) & ~bytes32(uint256(0xff))
  bytes32 internal constant MAIN_STORAGE_LOCATION = 0x1fbca2ab9841348cca3f2687c48325e9989a76ad929b9970d1c11e233677cf00;
  //endregion ------------------------ CONSTANTS


  //region ------------------------ Restrictions
  function onlyDeployer(IController controller) internal view {
    if (!controller.isDeployer(msg.sender)) revert IAppErrors.ErrorNotDeployer(msg.sender);
  }
  //endregion ------------------------ Restrictions

  //region ------------------------ VIEWS


  function _S() internal pure returns (IStoryController.MainState storage s) {
    assembly {
      s.slot := MAIN_STORAGE_LOCATION
    }
    return s;
  }

  function idToStory(uint16 storyId) internal view returns (uint32) {
    return _S().idToStory[storyId];
  }

  function storyBuildHash(uint16 storyId) internal view returns (uint) {
    return _S().storyBuildHash[storyId];
  }

  function heroPage(address hero, uint80 heroId, uint16 storyId) internal view returns (uint16 pageId) {
    (pageId,) = _S().heroState[hero.packStoryHeroStateId(heroId, storyId)].unpackStoryHeroState();
  }

  function storyIds(uint32 objectId) internal view returns (uint16) {
    return _S().storyIds[objectId];
  }

  function registeredStories(uint32 objectId) internal view returns (bool) {
    return _S().registeredStories[objectId];
  }

  function allStoryPagesLength(uint16 storyId) internal view returns (uint) {
    return _S().allStoryPages[storyId].length();
  }

  function allStoryPages(uint16 storyId, uint index) internal view returns (uint) {
    return _S().allStoryPages[storyId].at(index);
  }

  function allStoryAnswersLength(uint16 storyId) internal view returns (uint) {
    return _S().allStoryAnswers[storyId].length();
  }

  function allStoryAnswers(uint16 storyId, uint index) internal view returns (bytes32) {
    return _S().allStoryAnswers[storyId].at(index);
  }

  /// @notice Get list of answers for the current page stored in the hero state
  /// @return List of answers for the (page, heroClass). If the list is empty return default answers for (page, 0)
  function currentHeroAnswers(IController controller, uint16 storyId, address hero, uint80 heroId) internal view returns (
    bytes32[] memory
  ) {
    IHeroController hc = IHeroController(controller.heroController());

    (uint16 pageId,) = _S().heroState[hero.packStoryHeroStateId(heroId, storyId)].unpackStoryHeroState();
    bytes32[] memory heroAnswers = _S().answers[storyId.packStoryPageId(pageId, hc.heroClass(hero))];

    if (heroAnswers.length == 0) {
      heroAnswers = _S().answers[storyId.packStoryPageId(pageId, 0)];
    }

    if (heroAnswers.length != 0) {
      // shuffle answers using Fisher–Yates shuffle algorithm
      for (uint i; i < heroAnswers.length - 1; i++) {
        uint randomIndex = CalcLib.pseudoRandomInRange(i, heroAnswers.length - 1);
        (heroAnswers[randomIndex], heroAnswers[i]) = (heroAnswers[i], heroAnswers[randomIndex]);
      }
    }

    return heroAnswers;
  }

  function isStoryAvailableForHero(IController controller, uint32 objectId, address heroToken, uint heroTokenId) internal view returns (bool) {
    return StoryLib.isStoryAvailableForHero(_S(), _S().storyIds[objectId], heroToken, heroTokenId, controller.statController());
  }

  //endregion ------------------------ VIEWS

  //region ------------------------ SETTERS

  function setAllStoryFields(IController controller, IStoryController.StoryMetaInfo calldata meta) internal {
    onlyDeployer(controller);
    StorySetupLib.setAllStoryFields(_S(), meta);
  }

  function setBurnItemsMeta(
    IController controller,
    uint16 storyId,
    IStoryController.AnswerBurnRandomItemMeta calldata meta
  ) internal {
    onlyDeployer(controller);
    StorySetupLib.setBurnItemsMeta(_S(), storyId, meta);
  }

  function setNextObjRewriteMeta(IController controller, uint16 storyId, IStoryController.NextObjRewriteMeta calldata meta) internal {
    onlyDeployer(controller);
    StorySetupLib.setNextObjRewriteMeta(_S(), storyId, meta);
  }

  function setAnswersMeta(
    IController controller,
    uint16 storyId,
    uint16[] calldata answerPageIds,
    uint8[] calldata answerHeroClasses,
    uint16[] calldata answerIds
  ) internal {
    onlyDeployer(controller);
    StorySetupLib.setAnswersMeta(_S(), storyId, answerPageIds, answerHeroClasses, answerIds);
  }

  function setAnswerNextPageMeta(IController controller, uint16 storyId, IStoryController.AnswerNextPageMeta calldata meta) internal {
    onlyDeployer(controller);
    StorySetupLib.setAnswerNextPageMeta(_S(), storyId, meta);
  }

  function setAnswerAttributeRequirements(IController controller, uint16 storyId, IStoryController.AnswerAttributeRequirementsMeta calldata meta) internal {
    onlyDeployer(controller);
    StorySetupLib.setAnswerAttributeRequirements(_S(), storyId, meta);
  }

  function setAnswerItemRequirements(IController controller, uint16 storyId, IStoryController.AnswerItemRequirementsMeta calldata meta) internal {
    onlyDeployer(controller);
    StorySetupLib.setAnswerItemRequirements(_S(), storyId, meta);
  }

  function setAnswerTokenRequirementsMeta(IController controller, uint16 storyId, IStoryController.AnswerTokenRequirementsMeta calldata meta) internal {
    onlyDeployer(controller);
    StorySetupLib.setAnswerTokenRequirementsMeta(_S(), storyId, meta);
  }

  function setAnswerAttributes(IController controller, uint16 storyId, IStoryController.AnswerAttributesMeta calldata meta) internal {
    onlyDeployer(controller);
    StorySetupLib.setAnswerAttributes(_S(), storyId, meta);
  }

  function setAnswerHeroCustomDataRequirementMeta(IController controller, uint16 storyId, IStoryController.AnswerCustomDataMeta calldata meta) internal {
    onlyDeployer(controller);
    StorySetupLib.setAnswerHeroCustomDataRequirementMeta(_S(), storyId, meta);
  }

  function setAnswerGlobalCustomDataRequirementMeta(IController controller, uint16 storyId, IStoryController.AnswerCustomDataMeta calldata meta) internal {
    onlyDeployer(controller);
    StorySetupLib.setAnswerGlobalCustomDataRequirementMeta(_S(), storyId, meta);

  }

  function setSuccessInfo(IController controller, uint16 storyId, IStoryController.AnswerResultMeta calldata meta) internal {
    onlyDeployer(controller);
    StorySetupLib.setSuccessInfo(_S(), storyId, meta);
  }

  function setFailInfo(IController controller, uint16 storyId, IStoryController.AnswerResultMeta calldata meta) internal {
    onlyDeployer(controller);
    StorySetupLib.setFailInfo(_S(), storyId, meta);
  }

  function setCustomDataResult(
    IController controller,
    uint16 storyId,
    IStoryController.AnswerCustomDataResultMeta calldata meta,
    IStoryController.CustomDataResult type_
  ) internal {
    onlyDeployer(controller);
    StorySetupLib.setCustomDataResult(_S(), storyId, meta, type_);
  }

  function setStoryCustomDataRequirements(
    IController controller,
    uint16 storyId,
    bytes32[] calldata requiredCustomDataIndex,
    uint64[] calldata requiredCustomDataMinValue,
    uint64[] calldata requiredCustomDataMaxValue,
    bool[] calldata requiredCustomDataIsHero,
    uint minLevel
  ) internal {
    onlyDeployer(controller);
    StorySetupLib.setStoryCustomDataRequirements(_S(), storyId, requiredCustomDataIndex, requiredCustomDataMinValue, requiredCustomDataMaxValue, requiredCustomDataIsHero, minLevel);
  }

  function finalizeStoryRegistration(IController controller, uint16 storyId, uint32 objectId, uint buildHash) internal {
    onlyDeployer(controller);
    StorySetupLib.finalizeStoryRegistration(_S(), storyId, objectId, buildHash);
  }
  //endregion ------------------------ SETTERS

  //region ------------------------ CHANGE META

  function removeStory(IController controller, uint32 objectId) internal {
    onlyDeployer(controller);
    StorySetupLib.removeStory(_S(), objectId);
  }

  function removeStoryPagesMeta(IController controller, uint16 storyId, uint maxIterations) internal {
    onlyDeployer(controller);
    StorySetupLib.removeStoryPagesMeta(_S(), storyId, maxIterations);
  }

  function removeStoryAnswersMeta(IController controller, uint16 storyId, uint maxIterations) internal {
    onlyDeployer(controller);
    StorySetupLib.removeStoryAnswersMeta(_S(), storyId, maxIterations);
  }
  //endregion ------------------------ CHANGE META

  //region ------------------------ MAIN LOGIC

  function storyAction(
    IController controller,
    address sender,
    uint64 dungeonId,
    uint32 objectId,
    uint stageId,
    address heroToken,
    uint heroTokenId,
    uint8 biome,
    uint iteration,
    bytes memory data
  ) internal returns (IGOC.ActionResult memory result) {
    if (controller.gameObjectController() != msg.sender) revert IAppErrors.ErrorNotObjectController(msg.sender);

    IStatController statController = IStatController(controller.statController());
    IStoryController.StoryActionContext memory context = IStoryController.StoryActionContext({
      sender: sender,
      dungeonId: dungeonId,
      objectId: objectId,
      storyId: _S().storyIds[objectId],
      stageId: stageId,
      controller: controller,
      statController: statController,
      heroToken: heroToken,
      heroTokenId: uint80(heroTokenId),
      heroClass: 0,
      storyIdFromAnswerHash: 0,
      pageIdFromAnswerHash: 0,
      heroClassFromAnswerHash: 0,
      answerNumber: 0,
      answerIdHash: _decodeAnswerId(data),
      pageId: 0,
      heroLastActionTS: 0,
      answerAttributes: bytes32(0),
      heroStats: statController.heroStats(heroToken, heroTokenId),
      biome: biome,
      oracle: IOracle(controller.oracle()),
      iteration: iteration,
      heroController: IHeroController(controller.heroController()),
      itemController: IItemController(controller.itemController())
    });

    if (context.storyId == 0) revert IAppErrors.ZeroStoryIdStoryAction();

    context.heroClass = context.heroController.heroClass(heroToken);
    context.answerAttributes = _S().answerAttributes[context.answerIdHash];

    (context.pageId, context.heroLastActionTS) = _S().heroState[heroToken.packStoryHeroStateId(uint80(heroTokenId), context.storyId)].unpackStoryHeroState();

    (context.storyIdFromAnswerHash,
      context.pageIdFromAnswerHash,
      context.heroClassFromAnswerHash,
      context.answerNumber
    ) = context.answerIdHash.unpackStoryAnswerId();

    result = _handleAnswer(context, currentHeroAnswers(controller, context.storyId, heroToken, uint80(heroTokenId)));
  }

  /// @param heroAnswers Full list of possible answers (to be able to check that the answer belongs to the list)
  function _handleAnswer(IStoryController.StoryActionContext memory context, bytes32[] memory heroAnswers) internal returns (
    IGOC.ActionResult memory results
  ) {
    IStoryController.MainState storage s = _S();

    if (heroAnswers.length == 0) {
      results.objectId = context.objectId;
      results.heroTokenId = context.heroTokenId;
      results.heroToken = context.heroToken;
      results.completed = true;
    } else {
      // check ids only if answer exists, for empty answers we can accept empty answer hash from user
      if (context.storyId != context.storyIdFromAnswerHash) revert IAppErrors.AnswerStoryIdMismatch(context.storyId, context.storyIdFromAnswerHash);
      if (context.pageId != context.pageIdFromAnswerHash) revert IAppErrors.AnswerPageIdMismatch(context.pageId, context.pageIdFromAnswerHash);

      // ensure that the given answer belongs to the list of the available answers
      StoryLib.checkAnswerIndexValid(heroAnswers, context.answerIdHash);
      (,, bool finalAnswer) = context.answerAttributes.unpackStorySimpleRequirement();

      // check answer requirements, burn items, transfer tokens and so on
      IStoryController.AnswerResultId answerResult = StoryLib.checkAnswer(context, s);

      // burn randomly selected items
      StoryLib.burn(context, s);

      // handle answer - refresh states
      uint16 nextPage;
      uint16[] memory nextPages;
      (results, nextPage, nextPages) = StoryLib.handleAnswer(answerResult, s, context);

      if (finalAnswer || nextPages.length == 0) {
        results.completed = true;
      } else {
        s.heroState[context.heroToken.packStoryHeroStateId(context.heroTokenId, context.storyId)] = nextPage.packStoryHeroState(uint40(block.timestamp));
      }
    }

    if (results.completed) {
      results.rewriteNextObject = StoryLib.finishStory(context, s);
    }

    return results;
  }
  //endregion ------------------------ MAIN LOGIC

  //region ------------------------ Utils
  function _decodeAnswerId(bytes memory data) internal pure returns (bytes32 answerId) {
    (answerId) = abi.decode(data, (bytes32));
  }
  //endregion ------------------------ Utils

}

contracts/lib/StoryLib.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.23;

import "../interfaces/IStoryController.sol";
import "../interfaces/IOracle.sol";
import "../interfaces/ITreasury.sol";
import "../interfaces/IGOC.sol";
import "../interfaces/IERC20.sol";
import "../interfaces/IERC721Enumerable.sol";
import "../interfaces/IAppErrors.sol";
import "../interfaces/IApplicationEvents.sol";
import "../lib/CalcLib.sol";
import "../lib/PackingLib.sol";
import "../lib/ItemLib.sol";
import "../lib/StringLib.sol";

library StoryLib {
  using CalcLib for uint;
  using PackingLib for address;
  using PackingLib for uint16;
  using PackingLib for bytes32;
  using PackingLib for bytes32[];

  //region ------------------------ Story logic

  /// @notice Make action, increment STORY_XXX hero custom data if the dungeon is completed / hero is killed
  function action(IGOC.ActionContext memory ctx, uint16 storyId) internal returns (IGOC.ActionResult memory result) {
    if (storyId == 0) revert IAppErrors.ZeroStoryIdAction();

    result = IStoryController(ctx.controller.storyController()).storyAction(
      ctx.sender,
      ctx.dungeonId,
      ctx.objectId,
      ctx.stageId,
      ctx.heroToken,
      ctx.heroTokenId,
      ctx.biome,
      ctx.iteration,
      ctx.data
    );

    if (result.completed || result.kill) {
      IStatController statController = IStatController(ctx.controller.statController());
      bytes32 index = _getStoryIndex(storyId);
      uint curValue = statController.heroCustomData(ctx.heroToken, ctx.heroTokenId, index);
      statController.setHeroCustomData(ctx.heroToken, ctx.heroTokenId, index, curValue + 1);
    }
  }

  /// @notice Check if the story is available for the hero
  /// The story is available if hero level fits to requirements
  /// and if the hero/global custom data requirements are met (current value is inside of [min, max])
  function isStoryAvailableForHero(
    IStoryController.MainState storage s,
    uint16 storyId,
    address heroToken,
    uint heroTokenId,
    address statController
  ) internal view returns (bool) {
    uint reqLvl = s.storyRequiredLevel[storyId];
    if (reqLvl != 0 && IStatController(statController).heroStats(heroToken, heroTokenId).level < reqLvl) {
      return false;
    }

    IStoryController.CustomDataRequirementRangePacked[] storage allData = s.storyRequiredHeroData[storyId];
    uint len = allData.length;
    for (uint i; i < len; ++i) {
      IStoryController.CustomDataRequirementRangePacked memory data = allData[i];

      if (data.index == bytes32(0)) continue;

      (uint64 min, uint64 max, bool isHeroData) = data.data.unpackCustomDataRequirements();

      uint value = isHeroData
        ? IStatController(statController).heroCustomData(heroToken, heroTokenId, data.index)
        : IStatController(statController).globalCustomData(data.index);

      if (value < uint(min) || value > uint(max)) {
        return false;
      }
    }

    return true;
  }

  /// @notice Update bonus attributes, refresh hero states, initialize and return results
  /// @param mintItemsData Source for _mintRandomItems, random item (max 1, probably 0) is selected and put to results
  /// @param mintItems_ Function _mintRandomItems is passed here. Parameter is required to make unit tests.
  function handleResult(
    IStoryController.StoryActionContext memory context,
    bytes32[] memory attributesChanges,
    bytes32 statsChanges,
    bytes32[] memory mintItemsData,
    function (IStoryController.StoryActionContext memory, bytes32[] memory) internal returns (address[] memory) mintItems_
  ) internal returns (IGOC.ActionResult memory result) {
    result.heroToken = context.heroToken;
    result.heroTokenId = context.heroTokenId;
    result.objectId = context.objectId;

    int32[] memory attributes = _generateAttributes(attributesChanges);

    if (attributes.length != 0) {
      context.statController.changeBonusAttributes(IStatController.ChangeAttributesInfo({
        heroToken: context.heroToken,
        heroTokenId: context.heroTokenId,
        changeAttributes: attributes,
        add: true,
        temporally: true
      }));
      // changeBonusAttributes can change life and mana, so we need to refresh hero stats. It's safer to do it always
      context.heroStats = context.statController.heroStats(context.heroToken, context.heroTokenId);
      emit IApplicationEvents.StoryChangeAttributes(
        context.objectId,
        context.heroToken,
        context.heroTokenId,
        context.dungeonId,
        context.storyId,
        context.stageId,
        context.iteration,
        attributes
      );
    }

    IStoryController.StatsChange memory statsToChange = _generateStats(statsChanges);

    if (statsToChange.heal != 0) {
      int32 max = context.statController.heroAttribute(context.heroToken, context.heroTokenId, uint(IStatController.ATTRIBUTES.LIFE));
      result.heal = max * statsToChange.heal / 100;
    }

    if (statsToChange.manaRegen != 0) {
      int32 max = context.statController.heroAttribute(context.heroToken, context.heroTokenId, uint(IStatController.ATTRIBUTES.MANA));
      result.manaRegen = max * statsToChange.manaRegen / 100;
    }

    if (statsToChange.damage != 0) {
      int32 max = context.statController.heroAttribute(context.heroToken, context.heroTokenId, uint(IStatController.ATTRIBUTES.LIFE));
      result.damage = max * statsToChange.damage / 100;

      if (int32(context.heroStats.life) <= result.damage) {
        result.kill = true;
      }
    }

    if (statsToChange.manaConsumed != 0) {
      int32 max = context.statController.heroAttribute(context.heroToken, context.heroTokenId, uint(IStatController.ATTRIBUTES.MANA));
      result.manaConsumed = CalcLib.minI32(max * statsToChange.manaConsumed / 100, int32(context.heroStats.mana));
    }

    result.experience = statsToChange.experience;
    result.lifeChancesRecovered = statsToChange.lifeChancesRecovered;

    if (mintItemsData.length != 0) {
      result.mintItems = mintItems_(context, mintItemsData);
    }
    return result;
  }

  /// @notice Put data from {heroCustomDatas} and {globalCustomDatas} to {statController}
  function handleCustomDataResult(
    IStoryController.StoryActionContext memory context,
    bytes32[] memory heroCustomDatas,
    bytes32[] memory globalCustomDatas
  ) internal {
    uint len = heroCustomDatas.length;
    for (uint i; i < len; ++i) {

      (bytes32 customDataIndex, int16 value) = heroCustomDatas[i].unpackCustomDataChange();

      if (customDataIndex != 0) {
        uint curValue = context.statController.heroCustomData(context.heroToken, context.heroTokenId, customDataIndex);
        context.statController.setHeroCustomData(
          context.heroToken,
          context.heroTokenId,
          customDataIndex,
          value == 0
            ? 0
            : value > 0
              ? curValue + uint(int(value))
              : curValue.minusWithZeroFloor(uint(int(- value)))
        );
      }
    }

    len = globalCustomDatas.length;
    for (uint i; i < len; ++i) {

      (bytes32 customDataIndex, int16 value) = globalCustomDatas[i].unpackCustomDataChange();

      if (customDataIndex != 0) {
        uint curValue = context.statController.globalCustomData(customDataIndex);
        context.statController.setGlobalCustomData(
          customDataIndex,
          value == 0
            ? 0
            : value > 0
              ? curValue + uint(int(value))
              : curValue.minusWithZeroFloor(uint(int(- value)))
        );
      }
    }
  }

  /// @notice Randomly select one or several burnItems and burn them
  function burn(IStoryController.StoryActionContext memory context, IStoryController.MainState storage s) internal {

    bytes32[] storage burnInfos = s.burnItem[context.answerIdHash];
    uint length = burnInfos.length;

    for (uint i; i < length; ++i) {
      (uint8 slot, uint64 chance, bool stopIfBurned) = burnInfos[i].unpackBurnInfo();

      if (chance != 0 && context.oracle.getRandomNumberInRange(0, 100, 0) <= uint(chance)) {
        uint8[] memory busySlots = context.statController.heroItemSlots(context.heroToken, context.heroTokenId);

        uint lenBusySlots = busySlots.length;
        if (lenBusySlots != 0) {
          uint busySlotIndex;
          bool itemExist;
          if (slot == 0) {
            busySlotIndex = context.oracle.getRandomNumberInRange(0, lenBusySlots - 1, 0);
            itemExist = true;
          } else {
            for (uint j; j < lenBusySlots; ++j) {
              if (busySlots[j] == slot) {
                busySlotIndex = j;
                itemExist = true;
                break;
              }
            }
          }

          if (itemExist) {
            _burnItemInHeroSlot(context, busySlots[busySlotIndex]);
            if (stopIfBurned) {
              break;
            }
          }
        }
      }
    }
  }

  /// @notice Update internal hero state, generate {result}
  /// @param context We update some fields in place, so memory, not calldata here
  function handleAnswer(
    IStoryController.AnswerResultId answerResultId,
    IStoryController.MainState storage s,
    IStoryController.StoryActionContext memory context
  ) external returns (
    IGOC.ActionResult memory result,
    uint16 nextPage,
    uint16[] memory nextPages
  ) {
    result.objectId = context.objectId;
    result.heroTokenId = context.heroTokenId;
    result.heroToken = context.heroToken;

    nextPages = s.nextPageIds[context.storyId.packStoryNextPagesId(
      context.pageId,
      context.heroClassFromAnswerHash,
      context.answerNumber,
      uint8(answerResultId)
    )];
    nextPage = _getNextPage(context.oracle, nextPages);

    if (answerResultId == IStoryController.AnswerResultId.SUCCESS) {
      result = handleResult(
        context,
        s.successInfoAttributes[context.answerIdHash],
        s.successInfoStats[context.answerIdHash],
        s.successInfoMintItems[context.answerIdHash],
        _mintRandomItems
      );

      handleCustomDataResult(
        context,
        s.customDataResult[context.storyId.packStoryCustomDataResult(
          context.pageId,
          context.heroClassFromAnswerHash,
          context.answerNumber,
          uint8(IStoryController.CustomDataResult.HERO_SUCCESS)
        )],
        s.customDataResult[context.storyId.packStoryCustomDataResult(
          context.pageId,
          context.heroClassFromAnswerHash,
          context.answerNumber,
          uint8(IStoryController.CustomDataResult.GLOBAL_SUCCESS)
        )]
      );
    } else {
      result = handleResult(
        context,
        s.failInfoAttributes[context.answerIdHash],
        s.failInfoStats[context.answerIdHash],
        s.failInfoMintItems[context.answerIdHash],
        _mintRandomItems
      );

      handleCustomDataResult(
        context,
        s.customDataResult[context.storyId.packStoryCustomDataResult(
          context.pageId,
          context.heroClassFromAnswerHash,
          context.answerNumber,
          uint8(IStoryController.CustomDataResult.HERO_FAIL)
        )],
        s.customDataResult[context.storyId.packStoryCustomDataResult(
          context.pageId,
          context.heroClassFromAnswerHash,
          context.answerNumber,
          uint8(IStoryController.CustomDataResult.GLOBAL_FAIL)
        )]
      );
    }
  }

  /// @notice Revert if {heroAnswers} doesn't contain {answerIdHash}
  function checkAnswerIndexValid(bytes32[] memory heroAnswers, bytes32 answerIdHash) internal pure {
    uint len = heroAnswers.length;
    for (uint i; i < len; ++i) {
      if (heroAnswers[i] == answerIdHash) return;
    }
    revert IAppErrors.NotAnswer();
  }

  /// @notice Clear heroState for the give current story
  /// @return nextObjs Default nextObjectsRewrite for the current page (values for 0 hero class)
  function finishStory(IStoryController.StoryActionContext memory ctx, IStoryController.MainState storage s) internal returns (
    uint32[] memory nextObjs
  ) {
    delete s.heroState[ctx.heroToken.packStoryHeroStateId(ctx.heroTokenId, ctx.storyId)];
    return s.nextObjectsRewrite[ctx.storyId.packStoryPageId(ctx.pageId, 0)];
  }

  //endregion ------------------------ Story logic

  //region ------------------------ Internal utils for story logic

  /// @dev This function is made separate to simplify unit testing
  function _mintRandomItems(IStoryController.StoryActionContext memory context, bytes32[] memory mintItemsData) internal returns (
    address[] memory
  ) {
    uint len = mintItemsData.length;
    address[] memory mintItems = new address[](len);
    uint32[] memory mintItemsChances = new uint32[](len);
    for (uint i; i < len; ++i) {
      (mintItems[i], mintItemsChances[i]) = mintItemsData[i].unpackItemMintInfo();
    }

    return ItemLib.mintRandomItems(ItemLib.MintItemInfo({
      mintItems: mintItems,
      mintItemsChances: mintItemsChances,
      biome: context.biome,
      amplifier: 0,
      seed: 0,
      oracle: context.oracle,
      heroExp: context.heroStats.experience,
      heroCurrentLvl: uint8(context.heroStats.level),
      magicFind: 0,
      destroyItems: 0,
      maxItems: 1 // MINT ONLY 1 ITEM!
    }));
  }

  /// @param attributesChanges Values+ids packed using toBytes32ArrayWithIds
  function _generateAttributes(bytes32[] memory attributesChanges) internal pure returns (int32[] memory attributes) {
    if (attributesChanges.length != 0) {
      (int32[] memory values, uint8[] memory ids) = attributesChanges.toInt32ArrayWithIds();
      uint len = ids.length;
      if (len != 0) {
        attributes = new int32[](uint(IStatController.ATTRIBUTES.END_SLOT));
        for (uint i; i < len; ++i) {
          int32 value = values[i];
          attributes[ids[i]] = value;
        }
      }
    }

    return attributes;
  }

  function _generateStats(bytes32 statsChanges) internal pure returns (IStoryController.StatsChange memory change) {
    (
      change.experience,
      change.heal,
      change.manaRegen,
      change.lifeChancesRecovered,
      change.damage,
      change.manaConsumed
    ) = statsChanges.unpackStatsChange();

    return change;
  }

  /// @notice Take off and destroy the item from the given {slot}
  function _burnItemInHeroSlot(IStoryController.StoryActionContext memory ctx, uint8 slot) internal {
    (address itemAdr, uint itemId) = ctx.statController.heroItemSlot(ctx.heroToken, uint64(ctx.heroTokenId), slot).unpackNftId();

    ctx.itemController.takeOffDirectly(itemAdr, itemId, ctx.heroToken, ctx.heroTokenId, slot, address(this), false);

    ctx.itemController.destroy(itemAdr, itemId);

    emit IApplicationEvents.ItemBurned(
      ctx.heroToken,
      ctx.heroTokenId,
      ctx.dungeonId,
      ctx.objectId,
      itemAdr,
      itemId,
      ctx.stageId,
      ctx.iteration
    );
  }

  function _getNextPage(IOracle oracle, uint16[] memory pages) internal returns (uint16) {
    if (pages.length == 0) {
      return 0;
    }
    if (pages.length == 1) {
      return pages[0];
    }
    return pages[oracle.getRandomNumberInRange(0, pages.length - 1, 0)];
  }

  function _getStoryIndex(uint16 storyId) internal pure returns (bytes32) {
    return bytes32(abi.encodePacked("STORY_", StringLib._toString(storyId)));
  }
  //endregion ------------------------ Internal utils for story logic

  //region ------------------------ Check answers

  function checkAnswer(
    IStoryController.StoryActionContext memory context,
    IStoryController.MainState storage s
  ) external returns (IStoryController.AnswerResultId result) {
    result = checkAnswerAttributes(context, context.answerIdHash, s);
    if (result == IStoryController.AnswerResultId.SUCCESS) {
      result = checkAnswerItems(context, context.answerIdHash, s);
    }
    if (result == IStoryController.AnswerResultId.SUCCESS) {
      result = checkAnswerTokens(context, context.answerIdHash, s);
    }
    if (result == IStoryController.AnswerResultId.SUCCESS) {
      result = checkAnswerDelay(context);
    }
    if (result == IStoryController.AnswerResultId.SUCCESS) {
      result = checkAnswerHeroCustomData(context, context.answerIdHash, s);
    }
    if (result == IStoryController.AnswerResultId.SUCCESS) {
      result = checkAnswerGlobalCustomData(context, context.answerIdHash, s);
    }
    if (result == IStoryController.AnswerResultId.SUCCESS) {
      result = checkAnswerRandom(context);
    }
  }

  /// @notice Check if hero attribute values meet attribute requirements for the given answer
  function checkAnswerAttributes(
    IStoryController.StoryActionContext memory context,
    bytes32 answerIndex,
    IStoryController.MainState storage s
  ) internal view returns (IStoryController.AnswerResultId) {
    bytes32[] storage reqs = s.attributeRequirements[answerIndex];
    uint length = reqs.length;

    for (uint i; i < length; ++i) {
      (uint8 attributeIndex, int32 value, bool isCore) = reqs[i].unpackStoryAttributeRequirement();
      if (isCore) {
        IStatController.CoreAttributes memory base = context.statController.heroBaseAttributes(context.heroToken, context.heroTokenId);
        if (attributeIndex == uint8(IStatController.ATTRIBUTES.STRENGTH) && base.strength < value) {
          return IStoryController.AnswerResultId.ATTRIBUTE_FAIL;
        }
        if (attributeIndex == uint8(IStatController.ATTRIBUTES.DEXTERITY) && base.dexterity < value) {
          return IStoryController.AnswerResultId.ATTRIBUTE_FAIL;
        }
        if (attributeIndex == uint8(IStatController.ATTRIBUTES.VITALITY) && base.vitality < value) {
          return IStoryController.AnswerResultId.ATTRIBUTE_FAIL;
        }
        if (attributeIndex == uint8(IStatController.ATTRIBUTES.ENERGY) && base.energy < value) {
          return IStoryController.AnswerResultId.ATTRIBUTE_FAIL;
        }
      } else {
        int32 attr = context.statController.heroAttribute(context.heroToken, context.heroTokenId, attributeIndex);
        if (attr < value) {
          return IStoryController.AnswerResultId.ATTRIBUTE_FAIL;
        }
      }
    }

    return IStoryController.AnswerResultId.SUCCESS;
  }

  /// @notice Check item requirements for the given answer, check following issues:
  /// 1) For equipped item: check if it is on balance
  /// 2) For not equipped item: burn first owned item if requireItemBurn OR check that not equipped item is on balance
  function checkAnswerItems(
    IStoryController.StoryActionContext memory context,
    bytes32 answerIndex,
    IStoryController.MainState storage s
  ) internal returns (IStoryController.AnswerResultId) {

    bytes32[] storage reqs = s.itemRequirements[answerIndex];
    uint length = reqs.length;

    for (uint i; i < length; ++i) {
      (address item, bool requireItemBurn, bool requireItemEquipped) = reqs[i].unpackStoryItemRequirement();

      // equipped item is on balance of the heroToken, not on balance of the sender
      if (requireItemEquipped && IERC721Enumerable(item).balanceOf(context.heroToken) == 0) {
        revert IAppErrors.NotItem1();
      }

      if (requireItemBurn) {
        _burnFirstOwnedItem(context, item);
      }

      if (!requireItemEquipped && !requireItemBurn) {
        if (IERC721Enumerable(item).balanceOf(context.sender) == 0) revert IAppErrors.NotItem2();
      }

    }
    return IStoryController.AnswerResultId.SUCCESS;
  }

  /// @notice burn first owned item and generate event
  /// @dev Use separate function to workaround stack too deep
  function _burnFirstOwnedItem(IStoryController.StoryActionContext memory context, address item) internal {
    uint itemId = IERC721Enumerable(item).tokenOfOwnerByIndex(context.sender, 0);
    context.itemController.destroy(item, itemId); // destroy reverts if the item is equipped

    emit IApplicationEvents.NotEquippedItemBurned(
      context.heroToken,
      context.heroTokenId,
      context.dungeonId,
      context.storyId,
      item,
      itemId,
      context.stageId,
      context.iteration
    );
  }

  /// @notice Ensure that the sender has enough amounts of the required tokens, send fees to the treasury
  function checkAnswerTokens(
    IStoryController.StoryActionContext memory context,
    bytes32 answerIndex,
    IStoryController.MainState storage s
  ) internal returns (IStoryController.AnswerResultId) {
    bytes32[] memory reqs = s.tokenRequirements[answerIndex];
    uint length = reqs.length;
    for (uint i; i < length; ++i) {
      (address token, uint88 amount, bool requireTransfer) = reqs[i].unpackStoryTokenRequirement();

      if (amount != 0) {
        uint balance = IERC20(token).balanceOf(context.sender);
        if (balance < uint(amount)) revert IAppErrors.NotEnoughAmount(balance, uint(amount));

        if (requireTransfer) {
          address treasury = context.controller.treasury();
          IERC20(token).transferFrom(context.sender, address(this), uint(amount));
          IERC20(token).approve(treasury, type(uint).max);
          ITreasury(treasury).sendFee(token, uint(amount), IItemController.FeeType.STORY);
        }
      }
    }
    return IStoryController.AnswerResultId.SUCCESS;
  }

  /// @notice Generate error randomly
  function checkAnswerRandom(IStoryController.StoryActionContext memory context) internal returns (IStoryController.AnswerResultId) {
    (uint32 random,,) = context.answerAttributes.unpackStorySimpleRequirement();

    if (random != 0 && random < 100) {
      if (context.oracle.getRandomNumber(100, 0) > uint(random)) {
        return IStoryController.AnswerResultId.RANDOM_FAIL;
      }
    } else if (random > 100) {
      revert IAppErrors.NotRandom(random);
    }

    return IStoryController.AnswerResultId.SUCCESS;
  }

  /// @notice Ensure that the answer was given fast enough
  function checkAnswerDelay(IStoryController.StoryActionContext memory context) internal view returns (IStoryController.AnswerResultId) {

    (,uint32 delay,) = context.answerAttributes.unpackStorySimpleRequirement();

    if (delay != 0) {
      uint lastCall = uint(context.heroLastActionTS);
      if (lastCall != 0 && lastCall < block.timestamp && block.timestamp - lastCall > uint(delay)) {
        return IStoryController.AnswerResultId.DELAY_FAIL;
      }
    }

    return IStoryController.AnswerResultId.SUCCESS;
  }

  function checkAnswerHeroCustomData(
    IStoryController.StoryActionContext memory context,
    bytes32 answerIndex,
    IStoryController.MainState storage s
  ) internal view returns (IStoryController.AnswerResultId) {
    return _checkAnswerCustomData(context, s.heroCustomDataRequirement[answerIndex], true);
  }

  function checkAnswerGlobalCustomData(
    IStoryController.StoryActionContext memory context,
    bytes32 answerIndex,
    IStoryController.MainState storage s
  ) internal view returns (IStoryController.AnswerResultId) {
    return _checkAnswerCustomData(context, s.globalCustomDataRequirement[answerIndex], false);
  }

  function _checkAnswerCustomData(
    IStoryController.StoryActionContext memory context,
    IStoryController.CustomDataRequirementPacked[] memory datas,
    bool heroCustomData
  ) internal view returns (IStoryController.AnswerResultId) {
    uint len = datas.length;
    for (uint i; i < len; ++i) {
      IStoryController.CustomDataRequirementPacked memory data = datas[i];

      if (data.index != 0) {
        (uint valueMin, uint valueMax, bool mandatory) = data.data.unpackCustomDataRequirements();
        uint heroValue = heroCustomData
          ? context.statController.heroCustomData(context.heroToken, context.heroTokenId, data.index)
          : context.statController.globalCustomData(data.index);

        if (heroValue < valueMin || heroValue > valueMax) {
          if (mandatory) {
            if (heroCustomData) {
              revert IAppErrors.NotHeroData();
            } else {
              revert IAppErrors.NotGlobalData();
            }
          } else {
            return heroCustomData
              ? IStoryController.AnswerResultId.HERO_CUSTOM_DATA_FAIL
              : IStoryController.AnswerResultId.GLOBAL_CUSTOM_DATA_FAIL;
          }
        }
      }
    }

    return IStoryController.AnswerResultId.SUCCESS;
  }

  //endregion ------------------------ Check answers
}

contracts/lib/StorySetupLib.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.23;

import "../interfaces/IStoryController.sol";
import "../interfaces/IAppErrors.sol";
import "../interfaces/IApplicationEvents.sol";
import "../lib/PackingLib.sol";
import "../lib/StatLib.sol";

library StorySetupLib {
  using EnumerableSet for EnumerableSet.UintSet;
  using EnumerableSet for EnumerableSet.Bytes32Set;
  using PackingLib for bytes32;
  using PackingLib for uint16;
  using PackingLib for uint8;
  using PackingLib for address;
  using PackingLib for uint32[];
  using PackingLib for uint32;
  using PackingLib for uint64;
  using PackingLib for int32[];
  using PackingLib for int32;

  //region ------------------ Data types
  struct RemoveStoryContext {
    uint8 heroClass;
    uint8 answerResultId;
    uint8 customDataResultId;
    uint16 storyId;
    uint16 pageId;
    uint16 answerNum;
    uint len;
    uint[] tmpPages;
    bytes32 answerId;
    bytes32[] tmpAnswers;
  }
  //endregion ------------------ Data types

  //region ------------------ Set story fields

  function setAllStoryFields(IStoryController.MainState storage s, IStoryController.StoryMetaInfo memory meta) external {
    setBurnItemsMeta(s, meta.storyId, meta.answerBurnRandomItemMeta);
    setNextObjRewriteMeta(s, meta.storyId, meta.nextObjRewriteMeta);
    setAnswersMeta(
      s,
      meta.storyId,
      meta.answersMeta.answerPageIds,
      meta.answersMeta.answerHeroClasses,
      meta.answersMeta.answerIds
    );
    setAnswerNextPageMeta(s, meta.storyId, meta.answerNextPage);
    setAnswerAttributeRequirements(s, meta.storyId, meta.answerAttributeRequirements);
    setAnswerItemRequirements(s, meta.storyId, meta.answerItemRequirements);
    setAnswerTokenRequirementsMeta(s, meta.storyId, meta.answerTokenRequirements);
    setAnswerAttributes(s, meta.storyId, meta.answerAttributes);
    setAnswerHeroCustomDataRequirementMeta(s, meta.storyId, meta.answerHeroCustomDataRequirement);
    setAnswerGlobalCustomDataRequirementMeta(s, meta.storyId, meta.answerGlobalCustomDataRequirement);

    setSuccessInfo(s, meta.storyId, meta.successInfo);
    setFailInfo(s, meta.storyId, meta.failInfo);

    setCustomDataResult(s, meta.storyId, meta.successHeroCustomData, IStoryController.CustomDataResult.HERO_SUCCESS);
    setCustomDataResult(s, meta.storyId, meta.failHeroCustomData, IStoryController.CustomDataResult.HERO_FAIL);
    setCustomDataResult(s, meta.storyId, meta.successGlobalCustomData, IStoryController.CustomDataResult.GLOBAL_SUCCESS);
    setCustomDataResult(s, meta.storyId, meta.failGlobalCustomData, IStoryController.CustomDataResult.GLOBAL_FAIL);

    setStoryCustomDataRequirements(
      s,
      meta.storyId,
      meta.requiredCustomDataIndex,
      meta.requiredCustomDataMinValue,
      meta.requiredCustomDataMaxValue,
      meta.requiredCustomDataIsHero,
      meta.minLevel
    );

  }

  function setBurnItemsMeta(
    IStoryController.MainState storage s,
    uint16 storyId,
    IStoryController.AnswerBurnRandomItemMeta memory meta
  ) public {
    unchecked {
      uint len = meta.pageId.length;
      for (uint i; i < len; ++i) {
        bytes32 answerPackedId = _registerAnswer(s, storyId, meta.pageId[i], meta.heroClass[i], meta.answerId[i]);
        if (answerPackedId != bytes32(0)) {

          bytes32[] storage answersBurn = s.burnItem[answerPackedId];

          for (uint j; j < meta.slots[i].length; ++j) {
            bytes32 d = meta.slots[i][j].packBurnInfo(meta.chances[i][j], meta.isStopIfBurnt[i][j]);
            if (d != bytes32(0)) {
              answersBurn.push(d);
            }
          }
        }
      }
    }

    emit IApplicationEvents.SetBurnItemsMeta(storyId, meta);
  }

  function setNextObjRewriteMeta(IStoryController.MainState storage s, uint16 storyId, IStoryController.NextObjRewriteMeta memory meta) public {
    unchecked {
      uint len = meta.nextObjPageIds.length;
      for (uint i; i < len; ++i) {
        registerPage(s, storyId, meta.nextObjPageIds[i]);
        bytes32 id = storyId.packStoryPageId(meta.nextObjPageIds[i], meta.nextObjHeroClasses[i]);
        s.nextObjectsRewrite[id] = meta.nextObjIds[i];
      }
    }

    emit IApplicationEvents.SetNextObjRewriteMeta(storyId, meta);
  }

  function setAnswersMeta(
    IStoryController.MainState storage s,
    uint16 storyId,
    uint16[] memory answerPageIds,
    uint8[] memory answerHeroClasses,
    uint16[] memory answerIds
  ) public {
    unchecked {
      uint len = answerPageIds.length;
      for (uint i; i < len; ++i) {
        registerPage(s, storyId, answerPageIds[i]);

        bytes32[] storage answersHashes = s.answers[storyId.packStoryPageId(answerPageIds[i], answerHeroClasses[i])];

        bytes32 answerPackedId = _registerAnswer(s, storyId, answerPageIds[i], answerHeroClasses[i], answerIds[i]);
        if (answerPackedId != bytes32(0)) {
          answersHashes.push(answerPackedId);
        }
      }
    }

    emit IApplicationEvents.SetAnswersMeta(storyId, answerPageIds, answerHeroClasses, answerIds);
  }

  function setAnswerNextPageMeta(IStoryController.MainState storage s, uint16 storyId, IStoryController.AnswerNextPageMeta memory meta) public {
    unchecked {
      uint len = meta.pageId.length;
      for (uint i; i < len; ++i) {
        bytes32 answerPackedId = _registerAnswer(s, storyId, meta.pageId[i], meta.heroClass[i], meta.answerId[i]);
        if (answerPackedId != bytes32(0)) {
          bytes32 pagePackedId = storyId.packStoryNextPagesId(
            meta.pageId[i],
            meta.heroClass[i],
            meta.answerId[i],
            meta.answerResultIds[i]
          );
          // pagePackedId cannot be 0 here because answerPackedId is not 0
          s.nextPageIds[pagePackedId] = meta.answerNextPageIds[i];
        }
      }
    }

    emit IApplicationEvents.SetAnswerNextPageMeta(storyId, meta);
  }

  function setAnswerAttributeRequirements(IStoryController.MainState storage s, uint16 storyId, IStoryController.AnswerAttributeRequirementsMeta memory meta) public {
    unchecked {
      uint len = meta.pageId.length;
      for (uint i; i < len; ++i) {

        bytes32 answerPackedId = _registerAnswer(s, storyId, meta.pageId[i], meta.heroClass[i], meta.answerId[i]);
        if (answerPackedId != bytes32(0)) {
          bytes32[] storage attrs = s.attributeRequirements[answerPackedId];

          for (uint j; j < meta.cores[i].length; ++j) {
            bytes32 attributeRequirementsPacked = meta.ids[i][j].packStoryAttributeRequirement(
              meta.values[i][j],
              meta.cores[i][j]
            );

            if (attributeRequirementsPacked != bytes32(0)) {
              attrs.push(attributeRequirementsPacked);
            }
          }
        }
      }
    }

    emit IApplicationEvents.SetAnswerAttributeRequirements(storyId, meta);
  }

  function setAnswerItemRequirements(IStoryController.MainState storage s, uint16 storyId, IStoryController.AnswerItemRequirementsMeta memory meta) public {
    unchecked {
      uint len = meta.pageId.length;
      for (uint i; i < len; ++i) {

        bytes32 answerPackedId = _registerAnswer(s, storyId, meta.pageId[i], meta.heroClass[i], meta.answerId[i]);
        if (answerPackedId != bytes32(0)) {
          bytes32[] storage attrs = s.itemRequirements[answerPackedId];

          for (uint j; j < meta.requireItems[i].length; ++j) {
            bytes32 d = meta.requireItems[i][j].packStoryItemRequirement(
              meta.requireItemBurn[i][j],
              meta.requireItemEquipped[i][j]);

            if (d != bytes32(0)) {
              attrs.push(d);
            }
          }
        }
      }
    }

    emit IApplicationEvents.SetAnswerItemRequirements(storyId, meta);
  }

  function setAnswerTokenRequirementsMeta(IStoryController.MainState storage s, uint16 storyId, IStoryController.AnswerTokenRequirementsMeta memory meta) public {
    unchecked {
      uint len = meta.pageId.length;
      for (uint i; i < len; ++i) {

        bytes32 answerPackedId = _registerAnswer(s, storyId, meta.pageId[i], meta.heroClass[i], meta.answerId[i]);
        if (answerPackedId != bytes32(0)) {
          bytes32[] storage attrs = s.tokenRequirements[answerPackedId];

          for (uint j; j < meta.requireToken[i].length; ++j) {
            bytes32 d = meta.requireToken[i][j].packStoryTokenRequirement(
              meta.requireAmount[i][j],
              meta.requireTransfer[i][j]
            );
            if (d != bytes32(0)) {
              attrs.push(d);
            }
          }
        }
      }
    }

    emit IApplicationEvents.SetAnswerTokenRequirementsMeta(storyId, meta);
  }

  function setAnswerAttributes(IStoryController.MainState storage s, uint16 storyId, IStoryController.AnswerAttributesMeta memory meta) public {
    unchecked {
      uint len = meta.pageId.length;
      for (uint i; i < len; ++i) {
        bytes32 answerPackedId = _registerAnswer(s, storyId, meta.pageId[i], meta.heroClass[i], meta.answerId[i]);
        if (answerPackedId != bytes32(0)) {
          bytes32 data = meta.randomRequirements[i].packStorySimpleRequirement(
            meta.delayRequirements[i],
            meta.isFinalAnswer[i]
          );

          if (data != bytes32(0)) {
            s.answerAttributes[answerPackedId] = data;
          }
        }
      }
    }

    emit IApplicationEvents.SetAnswerAttributes(storyId, meta);
  }

  function setAnswerHeroCustomDataRequirementMeta(IStoryController.MainState storage s, uint16 storyId, IStoryController.AnswerCustomDataMeta memory meta) public {
    _setCustomDataRequirementMeta(s, storyId, meta, s.heroCustomDataRequirement);
    emit IApplicationEvents.SetAnswerHeroCustomDataRequirementMeta(storyId, meta);
  }

  function setAnswerGlobalCustomDataRequirementMeta(IStoryController.MainState storage s, uint16 storyId, IStoryController.AnswerCustomDataMeta memory meta) public {
    _setCustomDataRequirementMeta(s, storyId, meta, s.globalCustomDataRequirement);
    emit IApplicationEvents.SetAnswerGlobalCustomDataRequirementMeta(storyId, meta);
  }

  function setStoryCustomDataRequirements(
    IStoryController.MainState storage s,
    uint16 storyId,
    bytes32[] memory requiredCustomDataIndex,
    uint64[] memory requiredCustomDataMinValue,
    uint64[] memory requiredCustomDataMaxValue,
    bool[] memory requiredCustomDataIsHero,
    uint minLevel
  ) public {
    s.storyRequiredLevel[storyId] = minLevel;
    emit IApplicationEvents.StoryRequiredLevel(storyId, minLevel);

    IStoryController.CustomDataRequirementRangePacked[] storage allData = s.storyRequiredHeroData[storyId];

    for (uint i; i < requiredCustomDataIndex.length; ++i) {
      allData.push(IStoryController.CustomDataRequirementRangePacked({
        index: requiredCustomDataIndex[i],
        data: requiredCustomDataMinValue[i].packCustomDataRequirements(
          requiredCustomDataMaxValue[i],
          requiredCustomDataIsHero[i]
        )
      }));

      emit IApplicationEvents.StoryCustomDataRequirements(storyId, requiredCustomDataIndex[i], requiredCustomDataMinValue[i], requiredCustomDataMaxValue[i], requiredCustomDataIsHero[i]);
    }
  }

  function setSuccessInfo(IStoryController.MainState storage s, uint16 storyId, IStoryController.AnswerResultMeta memory meta) public {
    _setInfo(s, storyId, meta, s.successInfoAttributes, s.successInfoStats, s.successInfoMintItems);
    emit IApplicationEvents.SetSuccessInfo(storyId, meta);
  }

  function setFailInfo(IStoryController.MainState storage s, uint16 storyId, IStoryController.AnswerResultMeta memory meta) public {
    _setInfo(s, storyId, meta, s.failInfoAttributes, s.failInfoStats, s.failInfoMintItems);
    emit IApplicationEvents.SetFailInfo(storyId, meta);
  }

  function setCustomDataResult(
    IStoryController.MainState storage s,
    uint16 storyId,
    IStoryController.AnswerCustomDataResultMeta memory meta,
    IStoryController.CustomDataResult type_
  ) public {
    unchecked {
      uint len = meta.pageId.length;
      for (uint i; i < len; ++i) {
        if (_registerAnswer(s, storyId, meta.pageId[i], meta.heroClass[i], meta.answerId[i]) != bytes32(0)) {
          bytes32 answerPackedIdWithType = storyId.packStoryCustomDataResult(
            meta.pageId[i],
            meta.heroClass[i],
            meta.answerId[i],
            uint8(type_)
          );

          bytes32[] storage arr = s.customDataResult[answerPackedIdWithType];
          for (uint j; j < meta.dataIndexes[i].length; ++j) {
            arr.push(meta.dataIndexes[i][j].packCustomDataChange(meta.dataValues[i][j]));
          }
        }
      }
    }

    emit IApplicationEvents.SetCustomDataResult(storyId, meta, type_);
  }

  function finalizeStoryRegistration(
    IStoryController.MainState storage s,
    uint16 storyId,
    uint32 objectId,
    uint buildHash
  ) external {
    // it's not necessary to remove previously stored data here
    // we assume, that old data is already removed completely before registering new data

    s.registeredStories[objectId] = true;
    // store new used id
    s._usedStoryIds[storyId] = true;
    // register new id for story
    s.storyIds[objectId] = storyId;
    s.idToStory[storyId] = objectId;
    s.storyBuildHash[storyId] = buildHash;

    emit IApplicationEvents.StoryFinalized(objectId, storyId);
  }
  //endregion ------------------ Set story fields

  //region ------------------ Utils to set story fields
  function _registerAnswer(IStoryController.MainState storage s,  uint16 storyId, uint16 pageId, uint8 heroClass, uint16 answerId)
  internal returns (bytes32 answerPackedId) {
    answerPackedId = storyId.packStoryAnswerId(pageId, heroClass, answerId);
    if (answerPackedId != bytes32(0)) {
      registerAnswer(s, storyId, answerPackedId);
    }
  }

  /// @param map Either heroCustomDataRequirement or globalCustomDataRequirement
  function _setCustomDataRequirementMeta(
    IStoryController.MainState storage s,
    uint16 storyId,
    IStoryController.AnswerCustomDataMeta memory meta,
    mapping(bytes32 => IStoryController.CustomDataRequirementPacked[]) storage map
  ) internal {
    unchecked {
      uint len = meta.pageId.length;
      for (uint i; i < len; ++i) {

        bytes32 answerPackedId = _registerAnswer(s, storyId, meta.pageId[i], meta.heroClass[i], meta.answerId[i]);
        if (answerPackedId != bytes32(0)) {
          IStoryController.CustomDataRequirementPacked[] storage arr = map[answerPackedId];

          bytes32[] memory dataIndexes = meta.dataIndexes[i];
          bool[] memory mandatory = meta.mandatory[i];
          uint64[] memory dataValuesMin = meta.dataValuesMin[i];
          uint64[] memory dataValuesMax = meta.dataValuesMax[i];

          for (uint j; j < dataIndexes.length; ++j) {
            arr.push(
              IStoryController.CustomDataRequirementPacked({
                index: dataIndexes[j],
                data: dataValuesMin[j].packCustomDataRequirements(dataValuesMax[j], mandatory[j])
              })
            );
          }
        }
      }
    }
  }

  function _setInfo(
    IStoryController.MainState storage s,
    uint16 storyId,
    IStoryController.AnswerResultMeta memory meta,
    mapping(bytes32 => bytes32[]) storage infoAttributes,
    mapping(bytes32 => bytes32) storage infoStats,
    mapping(bytes32 => bytes32[]) storage infoMintItems
  ) public {
    unchecked {
      uint len = meta.pageId.length;
      for (uint i; i < len; ++i) {

        bytes32 answerPackedId = _registerAnswer(s, storyId, meta.pageId[i], meta.heroClass[i], meta.answerId[i]);
        if (answerPackedId != bytes32(0)) {
          if (meta.attributeIds[i].length != 0) {
            infoAttributes[answerPackedId] = meta.attributeValues[i].toBytes32ArrayWithIds(meta.attributeIds[i]);
          }

          bytes32 stats = PackingLib.packStatsChange(
            meta.experience[i],
            meta.heal[i],
            meta.manaRegen[i],
            meta.lifeChancesRecovered[i],
            meta.damage[i],
            meta.manaConsumed[i]
          );
          if (stats != bytes32(0)) {
            infoStats[answerPackedId] = stats;
          }

          uint lenItems = meta.mintItems[i].length;
          if (lenItems != 0) {
            bytes32[] memory items = new bytes32[](lenItems);
            for (uint j; j < lenItems; ++j) {
              items[j] = meta.mintItems[i][j].packItemMintInfo(meta.mintItemsChances[i][j]);
            }
            infoMintItems[answerPackedId] = items;
          }
        }
      }
    }
  }
  //endregion ------------------ Utils to set story fields

  //region ------------------ Remove logic
  // WE MUST REMOVE ALL EXIST META!
  // otherwise we will still have meta for story id and will totally mess data
  function removeStory(IStoryController.MainState storage s, uint32 objectId) external {
    if (s.storyIds[objectId] == 0 || !s.registeredStories[objectId]) revert IAppErrors.ZeroStoryIdRemoveStory();

    uint16 storyId = s.storyIds[objectId];
    delete s._usedStoryIds[storyId];
    delete s.storyIds[objectId];
    delete s.idToStory[storyId];
    delete s.registeredStories[objectId];
    delete s.storyBuildHash[storyId];

    delete s.storyRequiredHeroData[storyId];
    delete s.storyRequiredLevel[storyId];


    emit IApplicationEvents.StoryRemoved(objectId, storyId);
  }

  function removeStoryPagesMeta(IStoryController.MainState storage s, uint16 storyId, uint maxIterations) external {
    RemoveStoryContext memory ctx;
    ctx.storyId = storyId;

    // --- clean all data related to pages ---

    EnumerableSet.UintSet storage allPages = s.allStoryPages[ctx.storyId];
    ctx.len = allPages.length();
    if (ctx.len > maxIterations) {
      ctx.len = maxIterations;
    }
    ctx.tmpPages = new uint[](ctx.len);

    for (uint i; i < ctx.len; ++i) {
      ctx.tmpPages[i] = allPages.at(i);
      ctx.pageId = uint16(ctx.tmpPages[i]);

      // zero hero class means all classes
      for (ctx.heroClass = 0; ctx.heroClass < uint(StatLib.HeroClasses.END_SLOT); ++ctx.heroClass) {
        delete s.answers[ctx.storyId.packStoryPageId(ctx.pageId, ctx.heroClass)];
        delete s.nextObjectsRewrite[ctx.storyId.packStoryPageId(ctx.pageId, ctx.heroClass)];
      }
    }

    // remove all pages
    for (uint i; i < ctx.tmpPages.length; ++i) {
      if (!allPages.remove(ctx.tmpPages[i])) {
        revert IAppErrors.PageNotRemovedError(ctx.tmpPages[i]);
      }
    }
  }

  function removeStoryAnswersMeta(IStoryController.MainState storage s, uint16 storyId, uint maxIterations) external {
    RemoveStoryContext memory ctx;
    ctx.storyId = storyId;

    // --- clean all data related to answers ---

    EnumerableSet.Bytes32Set storage allAnswers = s.allStoryAnswers[ctx.storyId];
    ctx.len = allAnswers.length();
    if (ctx.len > maxIterations) {
      ctx.len = maxIterations;
    }
    ctx.tmpAnswers = new bytes32[](ctx.len);

    for (uint i; i < ctx.len; ++i) {
      ctx.answerId = allAnswers.at(i);
      ctx.tmpAnswers[i] = ctx.answerId;

      (, ctx.pageId, ctx.heroClass, ctx.answerNum) = ctx.answerId.unpackStoryAnswerId();

      delete s.answerAttributes[ctx.answerId];
      delete s.attributeRequirements[ctx.answerId];
      delete s.itemRequirements[ctx.answerId];
      delete s.tokenRequirements[ctx.answerId];
      delete s.heroCustomDataRequirement[ctx.answerId];
      delete s.globalCustomDataRequirement[ctx.answerId];
      delete s.successInfoAttributes[ctx.answerId];
      delete s.successInfoStats[ctx.answerId];
      delete s.successInfoMintItems[ctx.answerId];
      delete s.failInfoAttributes[ctx.answerId];
      delete s.failInfoStats[ctx.answerId];
      delete s.failInfoMintItems[ctx.answerId];
      delete s.burnItem[ctx.answerId];

      for (ctx.answerResultId = 0; ctx.answerResultId < uint(IStoryController.AnswerResultId.END_SLOT); ++ctx.answerResultId) {
        delete s.nextPageIds[ctx.storyId.packStoryNextPagesId(
          ctx.pageId,
          ctx.heroClass,
          ctx.answerNum,
          ctx.answerResultId
        )];
      }

      // we assume here, that CustomDataResultId.UNKNOWN = 0 shouldn't be used, so we can skip delete for it
      for (ctx.customDataResultId = 1; ctx.customDataResultId < uint(IStoryController.CustomDataResult.END_SLOT); ++ctx.customDataResultId) {
        delete s.customDataResult[ctx.storyId.packStoryCustomDataResult(
          ctx.pageId,
          ctx.heroClass,
          ctx.answerNum,
          ctx.customDataResultId
        )];
      }
    }

    // ATTENTION! need to remove items one by one from sets

    // remove all answers
    for (uint i; i < ctx.tmpAnswers.length; ++i) {
      allAnswers.remove(ctx.tmpAnswers[i]);
    }
  }
  //endregion ------------------ Remove logic

  //region ------------------ Utils
  function registerAnswer(IStoryController.MainState storage s, uint16 storyId, bytes32 answerId) internal {
    s.allStoryAnswers[storyId].add(answerId);
  }

  function registerPage(IStoryController.MainState storage s, uint16 storyId, uint16 pageId) internal {
    s.allStoryPages[storyId].add(pageId);
  }
  //endregion ------------------ Utils
}

