pragma solidity 0.8.23;

import "../../lib/CalcLib.sol";

contract FacadeCalcLib {
  mapping(uint => uint) internal _maxToRandom;

  //region ------------------------ For tests
  function setRandom(uint maxValue, uint value) external {
    _maxToRandom[maxValue] = value;
  }

  function randomForTests(uint maxValue) internal view returns (uint) {
    return _maxToRandom[maxValue];
  }

  //endregion ------------------------ For tests


  //region ------------------------ Math
  function minI32(int32 a, int32 b) external pure returns (int32) {
    return CalcLib.minI32(a, b);
  }

  function max32(int32 a, int32 b) external pure returns (int32) {
    return CalcLib.max32(a, b);
  }

  function absDiff(int32 a, int32 b) external pure returns (uint32) {
    return CalcLib.absDiff(a, b);
  }

  function sqrt(uint x) external pure returns (uint z) {
    return CalcLib.sqrt(x);
  }

  function log2(uint256 x) external pure returns (uint256 result) {
    return CalcLib.log2(x);
  }

  function mostSignificantBit(uint256 x) external pure returns (uint256 msb) {
    return CalcLib.mostSignificantBit(x);
  }

  //endregion ------------------------ Math


  //region ------------------------ To XXX
  function toUint(int32 a) external pure returns (uint) {
    return CalcLib.toUint(a);
  }

  function toInt32(uint a) external pure returns (int32) {
    return CalcLib.toInt32(a);
  }
  //endregion ------------------------ To XXX


  //region ------------------------ Random
  /// @dev Simplified pseudo-random for minor functionality
  function pseudoRandom(uint maxValue) external view returns (uint) {
    return CalcLib.pseudoRandom(maxValue);
  }

  function pseudoRandomUint32(uint32 maxValue) external view returns (uint32) {
    return CalcLib.pseudoRandomUint32(maxValue);
  }

  /// @notice pseudoRandomUint32 with customizable pseudoRandom()
  function pseudoRandomUint32Flex(uint32 maxValue) external view returns (uint32) {
    return CalcLib.pseudoRandomUint32Flex(maxValue, randomForTests);
  }

  function pseudoRandomInt32(int32 maxValue) external view returns (int32) {
    return CalcLib.pseudoRandomInt32(maxValue);
  }

  /// @dev Simplified pseudo-random for minor functionality
  function pseudoRandomWithSeed(uint maxValue, uint seed) external view returns (uint) {
    return CalcLib.pseudoRandomWithSeed(maxValue, seed);
  }

  /// @dev Simplified pseudo-random for minor functionality, in range
  function pseudoRandomInRange(uint min, uint max) external view returns (uint) {
    return CalcLib.pseudoRandomInRange(min, max);
  }

  /// @dev Simplified pseudo-random for minor functionality, in range
  ///      Equal to pseudoRandomInRange(min, max, pseudoRandom)
  function pseudoRandomInRangeFlex(uint min, uint max) external view returns (uint) {
    return CalcLib.pseudoRandomInRangeFlex(min, max, randomForTests);
  }
  //endregion ------------------------ Random


  //region ------------------------ Minus, plus
  function minusWithZeroFloor(uint a, uint b) external pure returns (uint) {
    return CalcLib.minusWithZeroFloor(a, b);
  }

  function minusWithMinFloorI32(int32 a, int32 b) external pure returns (int32) {
    return CalcLib.minusWithMinFloorI32(a, b);
  }

  function plusWithMaxFloor32(int32 a, int32 b) external pure returns (int32) {
    return CalcLib.plusWithMaxFloor32(a, b);
  }
  //endregion ------------------------ Minus, plus
}

