// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.23;

import "../../lib/DungeonFactoryLib.sol";
import "../../openzeppelin/EnumerableSet.sol";
import {DungeonFactory} from "../../core/DungeonFactory.sol";

/// @notice Lightweight wrapper around DungeonFactory to improve coverage of the core contract
contract FacadeDungeonFactory is DungeonFactory {
  using EnumerableSet for EnumerableSet.UintSet;

  /// @notice Two functions were combine to single one to reduce size
  function setMaxBiomeCompletedAddFreeDungeons(bytes32 packedHeroId, uint8 biome, uint value) external {
    DungeonFactoryLib._S().maxBiomeCompleted[packedHeroId] = biome;
    DungeonFactoryLib._S().freeDungeons[biome].add(value);
  }

  /// @param packedMapObject hero + heroId + biome (packMapObject)
  function setIsBiomeBoss(bytes32 packedMapObject) external {
    DungeonFactoryLib._S().bossCompleted[packedMapObject] = true;
  }

  /// @notice Two functions were combine to single one to reduce size
  function initEnterOpenObjectTest(
    IDungeonFactory.DungeonAttributes calldata value,
    uint64 dungeonId,
    uint16 dungNum,
    uint8 stages,
    uint32[] memory uniqObjects,
    address heroToken,
    uint heroTokenId
  ) external {
    DungeonFactoryLib._S().dungeonAttributes[dungNum] = value;
    DungeonFactoryLib._S().dungeonStatuses[dungeonId].dungeonId = dungeonId;
    DungeonFactoryLib._S().dungeonStatuses[dungeonId].dungNum = dungNum;
    DungeonFactoryLib._S().dungeonStatuses[dungeonId].stages = stages;
    DungeonFactoryLib._S().dungeonStatuses[dungeonId].uniqObjects = uniqObjects;
    DungeonFactoryLib._S().dungeonStatuses[dungeonId].heroToken = heroToken;
    DungeonFactoryLib._S().dungeonStatuses[dungeonId].heroTokenId = heroTokenId;

    if (heroTokenId != 0) {
      // we need following data for test openObject only
      DungeonFactoryLib._S().heroCurrentDungeon[PackingLib.packNftId(heroToken, heroTokenId)] = dungeonId;
    }
  }
}

// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.23;

import "../../lib/PackingLib.sol";
import "../../lib/DungeonFactoryLib.sol";
import "../../interfaces/IItemController.sol";
import "../../interfaces/IAppErrors.sol";

contract FacadeDungeonFactoryLib is IAppErrors {
  using EnumerableSet for EnumerableSet.UintSet;
  using EnumerableMap for EnumerableMap.AddressToUintMap;

  IController internal _controller;

  function _s() internal pure returns (IDungeonFactory.MainState storage) {
    return DungeonLib._S();
  }

  //region ------------------------ IControllable (all contracts allow to receive controller through this)
  function controller() external view returns (address) {
    return address(_controller);
  }
  function setController(address controller_) external {
    _controller = IController(controller_);
  }
  //endregion ------------------------ IControllable (all contracts allow to receive controller through this)

  //region ------------------------ Initialization of _s()

  function initFreeDungeons(uint key, uint[] memory values) external {
    for (uint i = 0; i < values.length; ++i) {
      _s().freeDungeons[key].add(values[i]);
    }
  }

  function extractFreeDungeons(uint8 dungBiome) external view returns (uint[] memory dungIds) {
    dungIds = new uint[](_s().freeDungeons[dungBiome].length());
    for (uint i = 0; i < _s().freeDungeons[dungBiome].length(); ++i) {
      dungIds[i] = _s().freeDungeons[dungBiome].at(i);
    }
  }

  function initBossCompleted(address heroToken, uint64 heroTokenId, uint8 heroBiome, bool completed) external {
    bytes32 key = PackingLib.packMapObject(heroToken, uint64(heroTokenId), heroBiome);
    _s().bossCompleted[key] = completed;
  }

  function initMaxBiomeCompleted(address heroToken, uint64 heroTokenId, uint8 biome) external {
    bytes32 key = PackingLib.packNftId(heroToken, uint64(heroTokenId));
    _s().maxBiomeCompleted[key] = biome;
  }

  function initAllSpecificDungeons(uint[] memory values) external {
    for (uint i = 0; i < values.length; ++i) {
      _s().allSpecificDungeons.add(values[i]);
    }
  }

  function initAllSpecificDungeonsForBiome(uint8 biome, uint[] memory values) external {
    for (uint i = 0; i < values.length; ++i) {
      _s().dungeonsLogicByBiome[biome].add(values[i]);
    }
  }

  function initDungeonCounter(uint64 dungeonCounter_) external {
    _s().dungeonCounter = dungeonCounter_;
  }

  function initDungeonAttributes(uint16 dungNum, IDungeonFactory.DungeonAttributes memory value) external {
    _s().dungeonAttributes[dungNum] = value;
  }

  function initDungeonStatuses(
    uint64 dungeonId,
    uint16 dungNum,
    bool isCompleted,
    address heroToken,
    uint heroTokenId,
    uint32 currentObject,
    uint8 currentStage,
    bytes32[] memory treasuryItems,
    uint8 stages,
    uint32[] memory uniqObjects
  ) external {
    _s().dungeonStatuses[dungeonId].dungeonId = dungeonId;
    _s().dungeonStatuses[dungeonId].dungNum = dungNum;
    _s().dungeonStatuses[dungeonId].isCompleted = isCompleted;
    _s().dungeonStatuses[dungeonId].heroToken = heroToken;
    _s().dungeonStatuses[dungeonId].heroTokenId = heroTokenId;
    _s().dungeonStatuses[dungeonId].currentObject = currentObject;
    _s().dungeonStatuses[dungeonId].currentStage = currentStage;
    _s().dungeonStatuses[dungeonId].treasuryItems = treasuryItems;
    _s().dungeonStatuses[dungeonId].stages = stages;
    _s().dungeonStatuses[dungeonId].uniqObjects = uniqObjects;
  }

  function extractDungAttributes(uint16 dungNum) external view returns (
    uint8 stages,
    uint8 biome,
    uint32[] memory uniqObjects,
    bytes32 minMaxLevel,
    bytes32[] memory requiredCustomDataIndex,
    bytes32[] memory requiredCustomDataValue,
    bytes32[] memory objTypesByStages,
    uint32[][] memory objChancesByStages
  ) {
    IDungeonFactory.DungeonAttributes memory da = _s().dungeonAttributes[dungNum];
    return (
      da.stages,
      da.biome,
      da.uniqObjects,
      da.minMaxLevel,
      da.requiredCustomDataIndex,
      da.requiredCustomDataValue,
      da.info.objTypesByStages,
      da.info.objChancesByStages
    );
  }

  function extractMaxBiome() external view returns (uint) {
    return _s().maxBiome;
  }

  function extractDungeonsLogicByBiome(uint8 biome, uint16 dungNum) external view returns (bool) {
    return _s().dungeonsLogicByBiome[biome].contains(dungNum);
  }

  function extractDungeonSpecific(uint8 specReqBiome, uint8 specReqHeroClass) external view returns (uint16) {
    return _s().dungeonSpecific[DungeonLib._toUint8PackedArray(specReqBiome, specReqHeroClass)];
  }

  function extractAllSpecificDungeons(uint16 dungNum) external view returns (bool) {
    return _s().allSpecificDungeons.contains(dungNum);
  }

  function extractMinLevelForTreasury(address token) external view returns (uint) {
    return _s().minLevelForTreasury[token];
  }

  function isBossCompleted(address heroToken, uint64 heroTokenId, uint8 heroBiome) external view returns (bool) {
    bytes32 key = PackingLib.packMapObject(heroToken, uint64(heroTokenId), heroBiome);
    return _s().bossCompleted[key];
  }

  function setHeroCurrentDungeon(address heroToken, uint heroTokenId, uint64 dungId) external {
    bytes32 heroPackedId = PackingLib.packNftId(heroToken, heroTokenId);
    _s().heroCurrentDungeon[heroPackedId] = dungId;
  }
  function extractHeroCurrentDungeon(address heroToken, uint heroTokenId) external view returns (uint64) {
    bytes32 heroPackedId = PackingLib.packNftId(heroToken, heroTokenId);
    return _s().heroCurrentDungeon[heroPackedId];
  }

  function extractTreasureTokens(uint64 dungId, address treasureToken) external view returns (uint treasureAmount) {
    if (_s().dungeonStatuses[dungId].treasuryTokens.contains(treasureToken)) {
      return _s().dungeonStatuses[dungId].treasuryTokens.get(treasureToken);
    } else {
      return type(uint).max;
    }
  }

  function initDungeonSpecific(uint8 specReqBiome, uint8 specReqHeroClass, uint16 specificDungeon) external {
    _s().dungeonSpecific[PackingLib.packUint8Array3(specReqBiome, specReqHeroClass, 0)] = specificDungeon;
  }

  //endregion ------------------------ Initialization of _s()

  //region ------------------------ VIEWS

  function dungeonAttributes(uint16 dungLogicNum) external view returns (IDungeonFactory.DungeonAttributes memory) {
    return DungeonFactoryLib.dungeonAttributes(dungLogicNum);
  }
  function dungeonStatus(uint64 dungeonId) external view returns (
    uint16 dungNum,
    bool isCompleted,
    address heroToken,
    uint heroTokenId,
    uint32 currentObject,
    uint8 currentObjIndex,
    address[] memory treasuryTokens,
    uint[] memory treasuryTokensAmounts,
    bytes32[] memory treasuryItems,
    uint8 stages,
    uint32[] memory uniqObjects
  ) {
    return DungeonFactoryLib.dungeonStatus(dungeonId);
  }

  function dungeonCounter() external view returns (uint64) {
    return DungeonFactoryLib.dungeonCounter();
  }

  function maxBiomeCompleted(address heroToken, uint heroTokenId) external view returns (uint8) {
    return DungeonFactoryLib.maxBiomeCompleted(heroToken, heroTokenId);
  }

  function currentDungeon(address heroToken, uint heroTokenId) external view returns (uint64) {
    return DungeonFactoryLib.currentDungeon(heroToken, heroTokenId);
  }

  function skillSlotsForDurabilityReduction(address heroToken, uint heroTokenId) external view returns (uint8[] memory result) {
    return DungeonFactoryLib.skillSlotsForDurabilityReduction(heroToken, heroTokenId);
  }

  function freeDungeonsByLevelLength(uint biome) external view returns (uint) {
    return DungeonFactoryLib.freeDungeonsByLevelLength(biome);
  }

  function freeDungeonsByLevel(uint index, uint biome) external view returns (uint64) {
    return DungeonFactoryLib.freeDungeonsByLevel(index, biome);
  }

  function getDungeonTreasuryAmount(address token, uint heroLevel, uint biome) public view returns (
    uint totalAmount,
    uint amountForDungeon,
    uint mintAmount
  ) {
    return DungeonFactoryLib.getDungeonTreasuryAmount(_controller, token, heroLevel, biome);
  }

  /// @dev Easily get info should given hero fight with boss in the current biome or not.
  function isBiomeBoss(address heroToken, uint heroTokenId) external view returns (bool) {
    return DungeonFactoryLib.isBiomeBoss(_controller, heroToken, heroTokenId);
  }

  function _getBossCompletedIndex(uint8 heroBiome) external pure returns (bytes32) {
    return DungeonFactoryLib._getBossCompletedIndex(heroBiome);
  }

  //endregion ------------------------ VIEWS

  //region ------------------------ ACTIONS

  function launch(bool isNotSmartContract_, address msgSender, address heroToken, uint heroTokenId, address treasuryToken) external returns (uint64 dungeonId) {
    return DungeonFactoryLib.launch(isNotSmartContract_, _controller, msgSender, heroToken, heroTokenId, treasuryToken);
  }

  function launchForNewHero(address msgSender, address heroToken, uint heroTokenId) external returns (uint64 dungeonId) {
    return DungeonFactoryLib.launchForNewHero(_controller, msgSender, heroToken, heroTokenId);
  }

  function setBossCompleted(uint32 objectId, address heroToken, uint heroTokenId, uint8 heroBiome) external {
    DungeonFactoryLib.setBossCompleted(_controller, objectId, heroToken, heroTokenId, heroBiome);
  }
  //endregion ------------------------ ACTIONS

  //region ------------------------ USER ACTIONS
  function enter(bool isNotSmartContract_, address msgSender_, uint64 dungId, address heroToken_, uint heroTokenId_) external {
    DungeonFactoryLib.enter(isNotSmartContract_, _controller, msgSender_, dungId, heroToken_, heroTokenId_);
  }

  function openObject(bool isNotSmartContract_, address msgSender_, uint64 dungId) external {
    DungeonFactoryLib.openObject(isNotSmartContract_, _controller, msgSender_, dungId);
  }

  function objectAction(bool isNotSmartContract_, address msgSender_, uint64 dungId, bytes memory data) external {
    DungeonFactoryLib.objectAction(isNotSmartContract_, _controller, msgSender_, dungId, data);
  }

  function exit(bool isNotSmartContract_, address msgSender_, uint64 dungId, bool claim) external {
    DungeonFactoryLib.exit(isNotSmartContract_, _controller, msgSender_, dungId, claim);
  }
  //endregion ------------------------ USER ACTIONS

  //region ------------------------ DUNGEON LOGIC - INTERNAL LOGIC

  function _generateObject(uint64 dungeonId, uint stage, uint16 dungNum, IGOC goc, address heroToken, uint heroTokenId)
  external returns (uint32 objectId) {
    IDungeonFactory.DungeonStatus storage dungStatus = _s().dungeonStatuses[dungeonId];
    IDungeonFactory.DungeonAttributes storage da = _s().dungeonAttributes[dungNum];
    return DungeonFactoryLib._generateObject(da, dungStatus, stage, goc, heroToken, heroTokenId);
  }

  function _clear(uint8 biome, uint64 dungId) external {
    DungeonFactoryLib._clear(_s().dungeonStatuses[dungId], biome, dungId);
  }

  /// @notice Check: hero is registered, not dead, in the dungeon, sender is the owner, the dungeon is not completed
  /// @return heroToken Token of the hero who is in the dungeon
  /// @return heroTokenId Token ID of the hero who is in the dungeon
  function _checkCurrentHero(uint64 dungeonId, address msgSender) external view returns (address heroToken, uint heroTokenId) {
    ControllerContextLib.ControllerContext memory cc = ControllerContextLib.init(_controller);
    return DungeonFactoryLib._checkCurrentHero(_s().dungeonStatuses[dungeonId], msgSender, cc);
  }

  function _addFreeDungeon(uint8 biome, uint64 dungeonId) external {
    DungeonFactoryLib._addFreeDungeon(biome, dungeonId);
  }
  //endregion ------------------------ DUNGEON LOGIC - INTERNAL LOGIC

  /// @dev Set eligible hero level for treasury tokens
  function setMinLevelForTreasury(address token, uint heroLevel) external {
    DungeonFactoryLib.setMinLevelForTreasury(_controller, token, heroLevel);
  }
}

