// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.23;

import "../../openzeppelin/EnumerableMap.sol";
import "../../interfaces/IDungeonFactory.sol";
import "../../interfaces/IController.sol";
import "../../interfaces/IHeroController.sol";
import "../../interfaces/IStatController.sol";
import "../../interfaces/IAppErrors.sol";
import "../../lib/DungeonLib.sol";
import "../../lib/DungeonFactoryLib.sol";

/// @dev We need IAppErrors to correctly handle custom errors in the tests
contract FacadeDungeonLib is IAppErrors {
  using EnumerableMap for EnumerableMap.AddressToUintMap;
  using EnumerableSet for EnumerableSet.UintSet;

  IController internal _controller;

  function _s() internal pure returns (IDungeonFactory.MainState storage) {
    return DungeonLib._S();
  }

  //region ------------------------ IControllable (all contracts allow to receive controller through this)
  function controller() external view returns (address) {
    return address(_controller);
  }
  function setController(address controller_) external {
    _controller = IController(controller_);
  }
  //endregion ------------------------ IControllable (all contracts allow to receive controller through this)


  //region ------------------------ Init
  function setTreasuryTokenValue(uint64 dungId, address token, uint value) external {
    DungeonLib._S().dungeonStatuses[dungId].treasuryTokens.set(token, value);
  }
  function getTreasuryTokenValue(uint64 dungId, address token) external view returns (uint) {
    if (DungeonLib._S().dungeonStatuses[dungId].treasuryTokens.contains(token)) {
      return DungeonLib._S().dungeonStatuses[dungId].treasuryTokens.get(token);
    } else {
      return type(uint).max;
    }
  }
  function setTreasureItem(uint64 dungId, address item, uint itemId) external {
    DungeonLib._S().dungeonStatuses[dungId].treasuryItems.push(PackingLib.packNftId(item, itemId));
  }

  function getTreasuryItems(uint64 dungId) external view returns (
    address[] memory tokens,
    uint[] memory ids
  ) {
    bytes32[] storage treasuryItems = DungeonLib._S().dungeonStatuses[dungId].treasuryItems;
    tokens = new address[](treasuryItems.length);
    ids = new uint[](treasuryItems.length);

    for (uint i = 0; i < treasuryItems.length; ++i) {
      (address token, uint id) = PackingLib.unpackNftId(treasuryItems[i]);
      tokens[i] = token;
      ids[i] = id;
    }
  }

  function getTreasuryTokens(uint64 dungId) external view returns (
    address[] memory tokens,
    uint[] memory values
  ) {
    EnumerableMap.AddressToUintMap storage treasuryTokens = DungeonLib._S().dungeonStatuses[dungId].treasuryTokens;
    tokens = new address[](treasuryTokens.length());
    values = new uint[](treasuryTokens.length());

    for (uint i = 0; i < tokens.length; ++i) {
      (address token, uint value) = treasuryTokens.at(i);
      tokens[i] = token;
      values[i] = value;
    }
  }

  function setAllSpecificDungeons(uint16 dungNum) external {
    DungeonLib._S().allSpecificDungeons.add(dungNum);
  }

  function getSpecificDungeonCompleted(address heroToken, uint64 heroTokenId, uint16 dungNum) external view returns (bool) {
    return DungeonLib._S().specificDungeonCompleted[PackingLib.packDungeonKey(heroToken, heroTokenId, dungNum)];
  }

  function extractSkillSlotsForDurabilityReduction(address heroToken, uint heroTokenId) external view returns (uint8[] memory) {
    bytes32 heroPackedId = PackingLib.packNftId(heroToken, heroTokenId);
    bytes32 out = _s().skillSlotsForDurabilityReduction[heroPackedId];

    return PackingLib.unpackUint8Array(out);
  }

  function initDungeonAttributes(uint16 dungNum, IDungeonFactory.DungeonAttributes memory value) external {
    _s().dungeonAttributes[dungNum] = value;
  }

  function initDungeonStatuses(
    uint64 dungeonId,
    uint16 dungNum,
    bool isCompleted,
    address heroToken,
    uint heroTokenId,
    uint32 currentObject,
    uint8 currentStage,
    bytes32[] memory treasuryItems,
    uint8 stages,
    uint32[] memory uniqObjects
  ) external {
    IDungeonFactory.DungeonStatus storage __dungStatus = _s().dungeonStatuses[dungeonId];
    __dungStatus.dungeonId = dungeonId;
    __dungStatus.dungNum = dungNum;
    __dungStatus.isCompleted = isCompleted;
    __dungStatus.heroToken = heroToken;
    __dungStatus.heroTokenId = heroTokenId;
    __dungStatus.currentObject = currentObject;
    __dungStatus.currentStage = currentStage;
    __dungStatus.treasuryItems = treasuryItems;
    __dungStatus.stages = stages;
    __dungStatus.uniqObjects = uniqObjects;
  }

  function extractDungeonStatus(uint64 dungeonId) external view returns (
    uint16 dungNum,
    bool isCompleted,
    address heroToken,
    uint heroTokenId,
    uint32 currentObject,
    uint8 currentStage,
    address[] memory treasuryTokens,
    uint[] memory treasuryTokensAmounts,
    bytes32[] memory treasuryItems,
    uint8 stages,
    uint32[] memory uniqObjects
  ) {
    return DungeonFactoryLib.dungeonStatus(dungeonId);
  }

  function initFreeDungeons(uint8 dungBiome, uint dungId) external {
    _s().freeDungeons[dungBiome].add(dungId);
  }

  function extractFreeDungeons(uint8 dungBiome) external view returns (uint[] memory dungIds) {
    dungIds = new uint[](_s().freeDungeons[dungBiome].length());
    for (uint i = 0; i < _s().freeDungeons[dungBiome].length(); ++i) {
      dungIds[i] = _s().freeDungeons[dungBiome].at(i);
    }
  }

  function setHeroCurrentDungeon(address heroToken, uint heroTokenId, uint64 dungId) external {
    bytes32 heroPackedId = PackingLib.packNftId(heroToken, heroTokenId);
    _s().heroCurrentDungeon[heroPackedId] = dungId;
  }
  function extractHeroCurrentDungeon(address heroToken, uint heroTokenId) external view returns (uint64) {
    bytes32 heroPackedId = PackingLib.packNftId(heroToken, heroTokenId);
    return _s().heroCurrentDungeon[heroPackedId];
  }

  function initDungeonSpecific(uint8 heroLevel, uint8 heroClass, uint16 specificDungeon) external {
    uint8 level = heroLevel == 0
      ? 0
      : heroLevel / uint8(StatLib.BIOME_LEVEL_STEP) + 1;
    _s().dungeonSpecific[PackingLib.packUint8Array3(level, heroClass, 0)] = specificDungeon;
  }

  function initSpecificDungeonCompleted(address heroToken, uint heroTokenId, uint16 dungNum, bool completed) external {
    bytes32 key = PackingLib.packDungeonKey(heroToken, uint80(heroTokenId), dungNum);
    _s().specificDungeonCompleted[key] = completed;
  }

  function initDungeonsLogicByBiome(uint8 biome, uint[] memory dungNums) external {
    for (uint i = 0; i < dungNums.length; ++i) {
      _s().dungeonsLogicByBiome[biome].add(dungNums[i]);
    }
  }

  function initMinLevelForTreasury(address token, uint8 level) external {
    _s().minLevelForTreasury[token] = level;
  }

  function initMaxBiome(uint8 maxBiome) external {
    _s().maxBiome = maxBiome;
  }

  function minLevelForTreasury(address token) external view returns (uint level) {
    return _s().minLevelForTreasury[token];
  }
  //endregion ------------------------ Init


  //region ------------------------ Common
  function dungeonTreasuryReward(address token, uint maxAvailableBiome, uint treasuryBalance, uint heroLevel, uint dungeonBiome) external view returns (uint) {
    return DungeonLib.dungeonTreasuryReward(token, maxAvailableBiome, treasuryBalance, heroLevel, dungeonBiome);
  }
  //endregion ------------------------ Common

  //region ------------------------ Main logic

  //!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!
  // Main logic is moved to FacadeDungeonLibMain
  //!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!

  //endregion ------------------------ Main logic

  //region ------------------------ ENTER/EXIT

  function _enter(uint64 dungId, address heroToken_, uint heroTokenId_) external {
    IDungeonFactory.DungeonStatus storage dungStatus = _s().dungeonStatuses[dungId];
    uint16 dungNum = dungStatus.dungNum;
    ControllerContextLib.ControllerContext memory cc = ControllerContextLib.init(_controller);
    DungeonLib._enter(cc, dungStatus, _s().dungeonAttributes[dungNum], dungNum, dungId, heroToken_, heroTokenId_);
  }

  function isDungeonEligibleForHero(
    uint16 dungNum,
    uint8 heroLevel,
    address heroToken,
    uint heroTokenId
  ) external view returns (bool) {
    return DungeonLib.isDungeonEligibleForHero(_s(), IStatController(_controller.statController()), dungNum, heroLevel, heroToken, heroTokenId);
  }

  function getDungeonLogic(uint8 heroLevel, address heroToken, uint heroTokenId, uint random) external view returns (uint16) {
    ControllerContextLib.ControllerContext memory cc = ControllerContextLib.init(_controller);
    return DungeonLib.getDungeonLogic(_s(), cc, heroLevel, heroToken, heroTokenId, random);
  }

  function exitDungeon(uint64 dungId, bool claim, address msgSender) external {
    DungeonLib.exitDungeon(_controller, dungId, claim, msgSender);
  }

  function emergencyExit(uint64 dungId) external {
    DungeonLib.emergencyExit(_controller, dungId);
  }

  function _getSkillSlotsForHero(address heroToken, uint heroTokenId) external view returns (
    address[3] memory skillSlotAdr,
    uint[3] memory skillSlotIds
  ) {
    return DungeonLib._getSkillSlotsForHero(IStatController(_controller.statController()), heroToken, heroTokenId);
  }

  /// @dev mark skill slots for durability reduction
  function markSkillSlotsForDurabilityReduction(bytes memory data, address heroToken, uint heroTokenId) external {
    return DungeonLib._markSkillSlotsForDurabilityReduction(
      _s(),
      IStatController(_controller.statController()),
      IItemController(_controller.itemController()),
      data,
      heroToken,
      heroTokenId
    );
  }

  /// @dev clear all skill slots marks
  function releaseSkillSlotsForDurabilityReduction(address heroToken, uint heroTokenId) external {
    return DungeonLib._releaseSkillSlotsForDurabilityReduction(_s(), heroToken, heroTokenId);
  }
  //endregion ------------------------ ENTER/EXIT

  //region ------------------------ CLAIM

  function _claimAll(
    address msgSender,
    uint64 dungId,
    address heroToken,
    uint heroTokenId,
    address heroPayToken
  ) external {
    IDungeonFactory.DungeonStatus storage dungStatus = _s().dungeonStatuses[dungId];
    ControllerContextLib.ControllerContext memory cc = ControllerContextLib.init(_controller);
    DungeonLib._claimAll(cc, msgSender, dungId, dungStatus, heroToken, heroTokenId, heroPayToken);
  }

  function _claimToken(
    uint64 dungId,
    DungeonLib.ClaimContext memory context,
    address token,
    uint amount
  ) external returns (
    address contextGameToken,
    address contextReinforcementController,
    address contextOracle
  ) {
    ControllerContextLib.ControllerContext memory cc = ControllerContextLib.init(_controller);
    DungeonLib._claimToken(
      _s().dungeonStatuses[dungId].treasuryTokens,
      context,
      cc,
      token,
      amount
    );
    return (address(cc.gameToken), address(cc.reinforcementController), address(cc.oracle));
  }

  function _claimItem(
    DungeonLib.ClaimContext memory context,
    address token,
    uint tokenId
  ) external returns (
    address contextReinforcementController,
    address contextOracle
  ) {
    ControllerContextLib.ControllerContext memory cc = ControllerContextLib.init(_controller);
    DungeonLib._claimItem(context, cc, token, tokenId);
    return (address(cc.reinforcementController), address(cc.oracle));
  }
  //endregion ------------------------ CLAIM

  //region ------------------------ Utils
  function _toUint8PackedArray(uint8 val0, uint8 val1) external pure returns (bytes32 key) {
    return DungeonLib._toUint8PackedArray(val0, val1);
  }

  function _toUint8ArrayWithoutZeroes(bytes32 data) external pure returns (uint8[] memory result) {
    return DungeonLib._toUint8ArrayWithoutZeroes(data);
  }
  //endregion ------------------------ Utils

}

