pragma solidity 0.8.23;

import "../../interfaces/IStatController.sol";
import "../../interfaces/IItemController.sol";
import "../../interfaces/IFightCalculator.sol";
import "../../interfaces/IApplicationEvents.sol";
import "../../lib/FightLib.sol";
import "../../lib/PackingLib.sol";

contract FacadeFightLib is IApplicationEvents {
  IController private _controller;
  mapping(uint => uint) private _pseudoRandomValue;

  //region ------------------------ IControllable (all contracts allow to receive controller through this)
  function controller() external view returns (address) {
    return address(_controller);
  }
  function setController(address controller_) external {
    _controller = IController(controller_);
  }
  //endregion ------------------------ IControllable (all contracts allow to receive controller through this)

  //region ------------------------ For tests
  function setPseudoRandomForTests(uint max, uint value) external {
    _pseudoRandomValue[max] = value;
  }

  function _pseudoRandomForTests(uint max) internal view returns (uint) {
    return _pseudoRandomValue[max];
  }
  //endregion ------------------------ For tests

  //region ------------------------ Main logic
  function fight(IFightCalculator.FightCall memory callData) external returns (
    IFightCalculator.FightResult memory
  ) {
    return FightLib.fight(IItemController(_controller.itemController()), callData, _pseudoRandomForTests);
  }
  //endregion ------------------------ Main logic

  //region ------------------------ High level of internal logic
  function fightProcessing(IFightCalculator.FightInfoInternal memory fResult) external view returns (
    IFightCalculator.FightInfoInternal memory
  ) {
    FightLib.fightProcessing(fResult, _pseudoRandomForTests);
    return fResult;
  }

  function processAttack(
    IFightCalculator.FightInfoInternal memory fResult,
    bool isA
  ) external view returns (FightLib.AttackResult memory attackResult) {
    return FightLib.processAttack(fResult, isA, _pseudoRandomForTests);
  }
  //endregion ------------------------ High level of internal logic

  //region ------------------------ Internal logic

  function prepareFightInternalInfo(
    IFightCalculator.FighterInfo memory fighterA,
    IFightCalculator.FighterInfo memory fighterB
  ) external view returns (IFightCalculator.FightInfoInternal memory fInfo) {
    return FightLib.prepareFightInternalInfo(IItemController(_controller.itemController()), fighterA, fighterB);
  }

  function statusChance(
    IFightCalculator.FighterInfo memory attackerInfo,
    IItemController.AttackInfo memory attackerMA,
    IStatController.ATTRIBUTES index,
    int32 resist
  ) external view returns (bool) {
    return FightLib.statusChance(attackerInfo, attackerMA, index, resist, _pseudoRandomForTests);
  }

  function magicDamageCalculation(
    IFightCalculator.FighterInfo memory attackerInfo,
    IFightCalculator.FighterInfo memory defenderInfo,
    IItemController.AttackInfo memory magicAttack,
    int32 defenderHealth
  ) external view returns (FightLib.AttackResult memory attackResult) {
    return FightLib.magicDamageCalculation(attackerInfo, defenderInfo, magicAttack, defenderHealth, _pseudoRandomForTests);
  }

  function meleeDamageCalculation(
    IFightCalculator.FighterInfo memory attackerInfo,
    IFightCalculator.FighterInfo memory defenderInfo,
    int32 defenderHealth
  ) external view returns (FightLib.AttackResult memory attackResult) {
    return FightLib.meleeDamageCalculation(attackerInfo, defenderInfo, defenderHealth, _pseudoRandomForTests);
  }


  //endregion ------------------------ Internal logic

  //region ------------------------ Pure utils
  function reduceAttributesByStatuses(
    int32[] memory targetAttributes,
    IFightCalculator.Statuses memory statuses,
    int32[] memory casterAttributes
  ) external pure returns (
    int32[] memory targetAttributesOut,
    int32[] memory casterAttributesOut
  ) {
    FightLib.reduceAttributesByStatuses(targetAttributes, statuses, casterAttributes);
    return (targetAttributes, casterAttributes);
  }

  function increaseRaceDmg(int32 dmg, IFightCalculator.FighterInfo memory attackerInfo, uint defenderRace) external pure returns (int32){
    return FightLib.increaseRaceDmg(dmg, attackerInfo, defenderRace);
  }

  function increaseMeleeDmgByFactor(int32 dmg, IFightCalculator.FighterInfo memory attackerInfo) external pure returns (int32){
    return FightLib.increaseMeleeDmgByFactor(dmg, attackerInfo);
  }

  function increaseMagicDmgByFactor(int32 dmg, IFightCalculator.FighterInfo memory attackerInfo, IItemController.AttackType aType) external pure returns (int32) {
    return FightLib.increaseMagicDmgByFactor(dmg, attackerInfo, aType);
  }

  function decreaseRaceDmg(int32 dmg, IFightCalculator.FighterInfo memory defenderInfo, uint attackerRace) external pure returns (int32) {
    return FightLib.decreaseRaceDmg(dmg, defenderInfo, attackerRace);
  }

  function decreaseDmgByDmgReduction(int32 dmg, IFightCalculator.FighterInfo memory defenderInfo) external pure returns (int32) {
    return FightLib.decreaseDmgByDmgReduction(dmg, defenderInfo);
  }

  function reduceHp(
    FightLib.AttackResult memory firstAttack,
    FightLib.AttackResult memory secondAttack,
    IFightCalculator.Fighter memory firstFighter,
    IFightCalculator.Fighter memory secondFighter
  ) external pure returns (
    IFightCalculator.Fighter memory firstFighterOut,
    IFightCalculator.Fighter memory secondFighterOut
  ) {
    FightLib.reduceHp(firstAttack, secondAttack, firstFighter, secondFighter);
    return (firstFighter, secondFighter);
  }

  function stealLife(
    IFightCalculator.Fighter memory fighter,
    FightLib.AttackResult memory attackResult
  ) external pure returns (
    IFightCalculator.Fighter memory
  ) {
    FightLib.stealLife(fighter, attackResult);
    return fighter;
  }

  function setStatuses(IFightCalculator.FightInfoInternal memory fResult, bool isA) external view returns (
    IFightCalculator.FightInfoInternal memory
  ){
    FightLib.setStatuses(fResult, isA, _pseudoRandomForTests);
    return fResult;
  }

  function calcFirstHit(IFightCalculator.FightInfoInternal memory fInfo) external pure returns (bool aFirst){
    return FightLib.calcFirstHit(fInfo);
  }

  function skipTurn(IFightCalculator.FightInfoInternal memory fResult, bool isA) external pure returns (bool) {
    return FightLib.skipTurn(fResult, isA);
  }

  function poisonDmg(int32 health, IFightCalculator.Statuses memory statuses) external pure returns (int32){
    return FightLib.poisonDmg(health, statuses);
  }

  function isAttackerFaster(
    IFightCalculator.FighterInfo memory fighterAInfo,
    IFightCalculator.FighterInfo memory fighterBInfo
  ) external pure returns (bool) {
    return FightLib.isAttackerFaster(fighterAInfo, fighterBInfo);
  }

  function lifeStolenPerHit(int32 dmg, IFightCalculator.FighterInfo memory attackerInfo) external pure returns (int32) {
    return FightLib.lifeStolenPerHit(dmg, attackerInfo);
  }

  function reflectMeleeDmg(int32 dmg, IFightCalculator.FighterInfo memory defenderInfo) external pure returns (int32){
    return FightLib.reflectMeleeDmg(dmg, defenderInfo);
  }

  function reflectMagicDmg(int32 dmg, IFightCalculator.FighterInfo memory defenderInfo) external pure returns (int32){
    return FightLib.reflectMagicDmg(dmg, defenderInfo);
  }

  function _getChance(
    IFightCalculator.FighterInfo memory attackerInfo,
    IItemController.AttackType aType,
    IStatController.ATTRIBUTES index,
    int32 resist
  ) external pure returns (int32 chance) {
    return FightLib._getChance(attackerInfo, aType, index, resist);
  }
  //endregion ------------------------ Pure utils

}

