  }
  //endregion ------------------------ IControllable (all contracts allow to receive controller through this)


  //region ------------------------ For tests
  function setPseudoRandomForTests(uint max, uint value) external {
    _pseudoRandomValue[max] = value;
  }

  function _pseudoRandomForTests(uint max) internal view returns (uint) {
    return _pseudoRandomValue[max];
  }
  //endregion ------------------------ For tests

  //region --------------------------------- Access
  function _getMapItems(EnumerableSet.UintSet storage map) internal view returns (uint[] memory values) {
    values = new uint[](map.length());

    for (uint i = 0; i < map.length(); ++i) {
      values[i] = map.at(i);
    }
  }

  function _setMapItems(EnumerableSet.UintSet storage map, uint[] memory values) internal {
    for (uint i = 0; i < values.length; ++i) {
      map.add(values[i]);
    }
  }

  function getPlayed(address heroToken, uint64 heroTokenId, uint8 cType, uint8 biome) external view returns (uint[] memory values) {
    EnumerableSet.UintSet storage _played = _s().playedObjects[PackingLib.packPlayedObjKey(heroToken, heroTokenId, cType, biome)];
    return _getMapItems(_played);
  }
  function setPlayed(address heroToken, uint64 heroTokenId, uint8 cType, uint8 biome, uint[] memory values) external {
    EnumerableSet.UintSet storage _played = _s().playedObjects[PackingLib.packPlayedObjKey(heroToken, heroTokenId, cType, biome)];
    return _setMapItems(_played, values);
  }

  function getObjects(uint8 cType, uint8 biome) external view returns (uint[] memory values) {
    EnumerableSet.UintSet storage _objects = _s().objectIds[GOCLib.packObjectMeta(biome, cType)];
    return _getMapItems(_objects);
  }
  function setObjects(uint8 cType, uint8 biome, uint[] memory values) external {
    EnumerableSet.UintSet storage _objects = _s().objectIds[GOCLib.packObjectMeta(biome, cType)];
    return _setMapItems(_objects, values);
  }
//endregion --------------------------------- Access

//region --------------------------------- GOCLib
  function _s() internal pure returns (IGOC.MainState storage) {
    return GameObjectControllerLib._S();
  }

  /// @dev Chances in range 0-1e9
  function getRandomObject(
    uint8[] memory cTypes,
    uint32[] memory chances,
    uint8 biome,
    address heroToken,
    uint heroTokenId
  ) external returns (uint32 objectId) {
    return GOCLib.getRandomObject(_s(), IStoryController(_controller.storyController()), cTypes, chances, biome, heroToken, heroTokenId);
  }

  function _getObjectType(uint8[] memory cTypes, uint32[] memory chances) external view returns (uint8 cType) {
    return GOCLib._getObjectType(cTypes, chances, _pseudoRandomForTests);
  }

  function _searchObject(
    uint cLength,
    uint objArrayIdx,
    bool skipPlayed,
    address heroToken,
    uint heroTokenId,
    uint8 cType,
    uint8 biome
  ) external returns (uint32 objectId) {
    EnumerableSet.UintSet storage _played = _s().playedObjects[PackingLib.packPlayedObjKey(heroToken, uint64(heroTokenId), cType, biome)];
    EnumerableSet.UintSet storage _objects = _s().objectIds[GOCLib.packObjectMeta(biome, cType)];
    return GOCLib._searchObject(IStoryController(_controller.storyController()), cLength, _objects, _played, objArrayIdx, skipPlayed, heroToken, heroTokenId, cType);
  }

  function _clearPlayedObjects(address heroToken, uint64 heroTokenId, uint8 cType, uint8 biome) external {
    EnumerableSet.UintSet storage _played = _s().playedObjects[PackingLib.packPlayedObjKey(heroToken, heroTokenId, cType, biome)];
    return GOCLib._clearPlayedObjects(_played);
  }

  function isAvailableForHero(uint32 objId, uint8 objectSubType, address hero, uint heroId) public view returns (bool) {
    return GOCLib.isAvailableForHero(IStoryController(_controller.storyController()), objId, objectSubType, hero, heroId);
  }

  function packObjectMeta(uint8 biome, uint8 oType) external pure returns (bytes32){
    return GOCLib.packObjectMeta(biome, oType);
  }

  function unpackObjectMeta(bytes32 data) external pure returns (uint8 biome, uint8 oType){
    return GOCLib.unpackObjectMeta(data);
  }

  function getObjectTypeBySubType(IGOC.ObjectSubType subType) public pure returns (IGOC.ObjectType) {
    return GOCLib.getObjectTypeBySubType(subType);
  }
//endregion --------------------------------- GOCLib
}

