pragma solidity 0.8.23;
import "../../lib/PackingLib.sol";
import "../../lib/HeroLib.sol";
import "../../lib/HeroControllerLib.sol";
import "../../core/HeroController.sol";

/// @notice Provide access to HeroControllerLib and HeroLib
contract FacadeHeroControllerLib is HeroController {
  //region ------------------------ For tests
  function _S() internal pure returns (IHeroController.MainState storage) {
    return HeroLib._S();
  }

  function setNameToHero(string memory heroName_, address heroToken, uint heroId) external {
    _S().nameToHero[heroName_] = PackingLib.packNftId(heroToken, heroId);
  }

  function setReinforcementHero(address hero, uint heroId, address helpHero, uint helpHeroId) external {
    _S().reinforcementHero[PackingLib.packNftId(hero, heroId)] = PackingLib.packNftId(helpHero, helpHeroId);
  }

  function getReinforcementHero(address hero, uint heroId) external view returns(
    address helpHero,
    uint helpHeroId
  ) {
    return PackingLib.unpackNftId(_S().reinforcementHero[PackingLib.packNftId(hero, heroId)]);
  }

  function setHeroBiome(address hero, uint heroId, uint8 biome) external {
    _S().heroBiome[PackingLib.packNftId(hero, heroId)] = biome;
  }

  function getReinforcementHeroAttributes(address hero, uint heroId) external view returns(int32[] memory) {
    return PackingLib.toInt32Array(
      _S().reinforcementHeroAttributes[PackingLib.packNftId(hero, heroId)],
      uint(IStatController.ATTRIBUTES.END_SLOT)
    );
  }

  function setReinforcementHeroAttributes(address hero, uint heroId, int32[] memory helpAttributes) external {
    _S().reinforcementHeroAttributes[PackingLib.packNftId(hero, heroId)] = PackingLib.toBytes32Array(helpAttributes);
  }
  //endregion ------------------------ For tests

  //region ------------------------ User actions: create, setBiome, levelUp

  function __create(address msgSender, address hero, string calldata heroName_, bool enter) external returns (uint) {
    return HeroControllerLib.create(IController(controller()), msgSender, hero, heroName_, enter);
  }

  function __createWithRefCode(bool isEoa, address msgSender, address hero, string calldata heroName_, string memory refCode, bool enter) external returns (uint) {
    return HeroControllerLib.createWithRefCode(isEoa, IController(controller()), msgSender, hero, heroName_, refCode, enter);
  }

  function __setBiome(bool isEoa, address msgSender, address hero, uint heroId, uint8 biome) external {
    return HeroControllerLib.setBiome(isEoa, IController(controller()), msgSender, hero, heroId, biome);
  }

  function __levelUp(bool isEoa, address msgSender, address hero, uint heroId, IStatController.CoreAttributes memory change) external {
    return HeroControllerLib.levelUp(isEoa, IController(controller()), msgSender, hero, heroId, change);
  }
  //endregion ------------------------ User actions: create, setBiome, levelUp

  //region ------------------------ User actions: reinforcement
  function __askReinforcement(bool isEoa, address msgSender, address hero, uint heroId) external {
    return HeroControllerLib.askReinforcement(isEoa, IController(controller()), msgSender, hero, heroId);
  }

  function __releaseReinforcement(address msgSender, address hero, uint heroId) external returns (address helperToken, uint helperId) {
    return HeroControllerLib.releaseReinforcement(IController(controller()), msgSender, hero, heroId);
  }
  //endregion ------------------------ User actions: reinforcement

  //region ------------------------ Kill
  /// @return dropItems List of items (packed: item NFT address + item id)
  function __kill(address msgSender, address hero, uint heroId) external returns (bytes32[] memory dropItems) {
    return HeroControllerLib.kill(IController(controller()), msgSender, hero, heroId);
  }
  //endregion ------------------------ Kill

  //region ------------------------ HeroLib
  function _takeOffAll(address hero, uint heroId, address recipient, bool broken)
  external returns (bytes32[] memory items) {
    return HeroLib._takeOffAll(
      IItemController(IController(controller()).itemController()),
      IStatController(IController(controller()).statController()),
      hero,
      heroId,
      recipient,
      broken
    );
  }

  function isASCIILettersOnly(string memory str) external pure returns (bool) {
    return HeroLib.isASCIILettersOnly(str);
  }
  //endregion ------------------------ HeroLib

}

