// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.23;

import "../../interfaces/IItemController.sol";
import "../../lib/ItemLib.sol";
import "../../lib/ItemStatsLib.sol";
import "hardhat/console.sol";

contract FacadeItemLib is IAppErrors {
  using EnumerableSet for EnumerableSet.AddressSet;

  IController internal _controller;
  mapping(uint => uint) private _pseudoRandomValue;
  mapping(uint => mapping(uint => uint)) private _pseudoRandomValueWithSeed;
  bool private _useRealPseudoRandom;
  bool private _useRealPrng;

  //region ------------------------ IControllable (all contracts allow to receive controller through this)
  function controller() external view returns (address) {
    return address(_controller);
  }
  function setController(address controller_) external {
    _controller = IController(controller_);
  }
  //endregion ------------------------ IControllable (all contracts allow to receive controller through this)


  //region ------------------------ For tests
  function _S() internal pure returns (IItemController.MainState storage s) {
    return ItemStatsLib._S();
  }

  function setPseudoRandomForTests(uint max, uint value) external {
    _pseudoRandomValue[max] = value;
  }

  function _pseudoNextPrng(LibPRNG.PRNG memory prng, uint max) internal view returns (uint) {
    if (_useRealPrng) {
      return LibPRNG.next(prng);
    } else {
      return _pseudoRandomValue[max];
    }
  }

  function _pseudoRandomForTests(uint max) internal view returns (uint) {
    return _useRealPseudoRandom
      ? CalcLib.pseudoRandom(max)
      : _pseudoRandomValue[max];
  }

  function setUseRealPseudoRandom() external {
    _useRealPseudoRandom = true;
    _useRealPrng = true;
  }

  function setPseudoRandomWithSeedForTests(uint max, uint seed, uint value) external {
    _pseudoRandomValueWithSeed[max][seed] = value;
  }

  function _pseudoRandomWithSeedForTests(uint max, uint seed) internal view returns (uint) {
    return _pseudoRandomValueWithSeed[max][seed];
  }

  function setItemMeta(address item, IItemController.ItemMeta memory meta) external {
    _S().itemMeta[item] = ItemLib.packItemMeta(meta);
  }

  function setGenerateInfoAttributes(address item, IItemController.ItemGenerateInfo memory info) external {
    _S().generateInfoAttributes[item] = ItemLib.packItemGenerateInfo(info);
  }

  function getItemAttributes(address item, uint itemId) external view returns (
    int32[] memory values,
    uint8[] memory ids
  ) {
    return PackingLib.toInt32ArrayWithIds(_S()._itemAttributes[PackingLib.packNftId(item, itemId)]);
  }

  function getItemInfo(address item, uint itemId) external view returns (
    uint8 itemRarity,
    uint8 augmentationLevel,
    uint16 durability
  ) {
    return PackingLib.unpackItemInfo(_S().itemInfo[PackingLib.packNftId(item, itemId)]);
  }

  function setGenerateInfoAttack(address item, IItemController.AttackInfo memory attackInfo) external {
    _S().generateInfoAttack[item] = ItemLib.packItemAttackInfo(attackInfo);
  }

  function setGenerateAttributes(address item, IItemController.ItemGenerateInfo memory data, bool caster) external {
    (caster ? _S().generateInfoCasterAttributes : _S().generateInfoTargetAttributes)[item] = ItemLib.packItemGenerateInfo(data);
  }

  function itemAttackInfo(address item, uint itemId) external view returns (IItemController.AttackInfo memory info) {
    return ItemLib.unpackItemAttackInfo(_S()._itemAttackInfo[PackingLib.packNftId(item, itemId)]);
  }

  function getItemAttributesCasterTarget(address item, uint itemId, bool caster) external view returns (
    uint8[] memory ids,
    int32[] memory values
  ) {
    (values, ids) = PackingLib.toInt32ArrayWithIds((caster ? _S()._itemCasterAttributes : _S()._itemTargetAttributes)[PackingLib.packNftId(item, itemId)]);
  }

  function addItemToItems(address item) external {
    _S().items.add(item);
  }

  function isItemInItems(address item) external view returns (bool) {
    return _S().items.contains(item);
  }
  //endregion ------------------------ For tests

  //region ------------------------ Main logic
  function mintNewItem(address sender, address item, address recipient) external returns (uint itemId) {
    return ItemLib.mintNewItem(_S(), _controller, sender, item, recipient);
  }

  function mintRandomItems(ItemLib.MintItemInfo memory info) external returns (address[] memory) {
    return ItemLib.mintRandomItems(info);
  }
  //endregion ------------------------ Main logic

  //region ------------------------ Internal logic
  function _mintRandomItems(
    ItemLib.MintItemInfo memory info
  ) external returns (address[] memory) {
    return ItemLib._mintRandomItems(info, _pseudoNextPrng);
  }

  function _generateAttributes(
    IItemController.ItemGenerateInfo memory info,
    IItemController.ItemMeta memory meta
  ) external view returns (
    uint8[] memory ids,
    int32[] memory values,
    IItemController.ItemRarity itemRarity
  ) {
    return ItemLib._generateAttributes(info, meta, _pseudoRandomForTests);
  }

  function generateSimpleAttributes(IItemController.ItemGenerateInfo memory info, bool maxChance) external view returns (
    uint8[] memory ids,
    int32[] memory values
  ) {
    return ItemLib._generateSimpleAttributes(info, maxChance, _pseudoRandomForTests);
  }

  function _generateMissingRandomAttributes(
    IItemController.ItemGenerateInfo memory info,
    uint8 minRandomAttributes,
    ItemLib.GenerateAttributesContext memory ctx
  ) external view returns (
    ItemLib.GenerateAttributesContext memory
  ) {
    ItemLib._generateMissingRandomAttributes(info, minRandomAttributes, ctx, _pseudoRandomForTests);
    return ctx;
  }

  function _prepareAttributes(
    IItemController.ItemGenerateInfo memory info,
    uint8 maxRandomAttributes,
    ItemLib.GenerateAttributesContext memory ctx
  ) external view returns (ItemLib.GenerateAttributesContext memory) {
    ctx.ids = new uint8[](info.ids.length);
    ctx.values = new int32[](info.ids.length);
    ctx.usedIndexes = new bool[](info.ids.length);
    ItemLib._prepareAttributes(info, maxRandomAttributes, ctx, _pseudoRandomForTests);
    return ctx;
  }

  function _setupNewAttributes(address item, uint itemId) external returns (
    IItemController.ItemMeta memory meta,
    uint8[] memory ids,
    int32[] memory values,
    IItemController.ItemRarity itemRarity
  ){
    return ItemLib._setupNewAttributes(_S(), item, itemId, _pseudoRandomForTests);
  }

  function _setupNewAttackItem(address item, uint itemId) external returns (bytes32 attackInfo) {
    return ItemLib._setupNewAttackItem(_S(), item, itemId);
  }

  function _setupNewBuffItem(address item, uint itemId) external returns (
    uint8[] memory casterIds,
    int32[] memory casterValues,
    uint8[] memory targetIds,
    int32[] memory targetValues
  ){
    return ItemLib._setupNewBuffItem(_S(), item, itemId, _pseudoRandomForTests);
  }

  //endregion ------------------------ Internal logic

  //region ------------------------ Internal utils
  function _generateAttribute(int32 min, int32 max, uint32 chance) external view returns (int32 attr, uint rnd) {
    return ItemLib._generateAttribute(min, max, chance, _pseudoRandomForTests);
  }

  function _calculateRarity(uint randomSum, uint chancesSum, uint attrCounter, uint maxAttr) external pure returns (
    IItemController.ItemRarity
  ) {
    return ItemLib._calculateRarity(randomSum, chancesSum, attrCounter, maxAttr);
  }

  function _shuffleInfo(IItemController.ItemGenerateInfo memory info) external view returns (
    IItemController.ItemGenerateInfo memory infoOut
  ) {
    ItemLib._shuffleInfo(info, _pseudoRandomForTests);
    return info;
  }

  function _shuffleIndices(uint countItems) external view returns (uint[] memory indices) {
    return ItemLib._shuffleIndices(countItems, _pseudoRandomForTests);
  }
  //endregion ------------------------ Internal utils

  //region ------------------------ PACKING

  function packItemGenerateInfo(IItemController.ItemGenerateInfo memory info) external pure returns (bytes32[] memory result) {
    return ItemLib.packItemGenerateInfo(info);
  }

  function unpackItemGenerateInfo(
    bytes32[] memory gen
  ) external pure returns (IItemController.ItemGenerateInfo memory) {
    return ItemLib.unpackItemGenerateInfo(gen);
  }

  function packItemMeta(IItemController.ItemMeta memory meta) external pure returns (bytes32) {
    return ItemLib.packItemMeta(meta);
  }

  function unpackedItemMeta(bytes32 meta) external pure returns (IItemController.ItemMeta memory result) {
    return ItemLib.unpackedItemMeta(meta);
  }

  function packItemInfo(IItemController.ItemInfo memory info) external pure returns (bytes32) {
    return ItemLib.packItemInfo(info);
  }

  function unpackedItemInfo(bytes32 info) external pure returns (IItemController.ItemInfo memory result) {
    return ItemLib.unpackedItemInfo(info);
  }

  function packItemAttackInfo(IItemController.AttackInfo memory info) external pure returns (bytes32) {
    return ItemLib.packItemAttackInfo(info);
  }

  function unpackItemAttackInfo(bytes32 info) external pure returns (IItemController.AttackInfo memory result) {
    return ItemLib.unpackItemAttackInfo(info);
  }
  //endregion ------------------------ PACKING
}

