pragma solidity 0.8.23;
import "../../lib/PackingLib.sol";
import "../../lib/ItemStatsLib.sol";

contract FacadeItemStatsLib is IAppErrors {
  IController internal _controller;

  //region ------------------------ IControllable (all contracts allow to receive controller through this)
  function controller() external view returns (address) {
    return address(_controller);
  }
  function setController(address controller_) external {
    _controller = IController(controller_);
  }
  //endregion ------------------------ IControllable (all contracts allow to receive controller through this)


  //region ------------------------ For tests
  function _S() internal pure returns (IItemController.MainState storage s) {
    return ItemStatsLib._S();
  }

  function setGenerateAttributes(address item, bytes32[] memory data, bool caster) external {
    (caster ? _S().generateInfoCasterAttributes : _S().generateInfoTargetAttributes)[item] = data;
  }

  function setItemMeta(address item, IItemController.ItemMeta memory meta) external {
    _S().itemMeta[item] = ItemLib.packItemMeta(meta);
  }

  function setItemInfo(address item, uint itemId, IItemController.ItemInfo memory info) external {
    _S().itemInfo[PackingLib.packNftId(item, itemId)] = ItemLib.packItemInfo(info);
  }

  function setEquipped(address item, uint itemId, bytes32 value) external {
    _S().equippedOn[PackingLib.packNftId(item, itemId)] = value;
  }

  function setItemAttributes(address item, uint itemId, int32[] memory values, uint8[] memory ids) external {
    _S()._itemAttributes[PackingLib.packNftId(item, itemId)] = PackingLib.toBytes32ArrayWithIds(values, ids);
  }

  function setAugmentInfo(address item, address token, uint amount) external {
    _S().augmentInfo[item] = PackingLib.packAddressWithAmount(token, amount);
  }

  function getItemAttributesCasterTarget(address item, uint itemId, bool caster) external view returns (
    uint8[] memory ids,
    int32[] memory values
  ) {
    (values, ids) = PackingLib.toInt32ArrayWithIds((caster ? _S()._itemCasterAttributes : _S()._itemTargetAttributes)[PackingLib.packNftId(item, itemId)]);
  }

  function setItemAttributesCasterTarget(address item, uint itemId, int32[] memory values, uint8[] memory ids, bool caster) external {
    (
      caster ? _S()._itemCasterAttributes : _S()._itemTargetAttributes)[PackingLib.packNftId(item, itemId)
    ] = PackingLib.toBytes32ArrayWithIds(values, ids);
  }


  function getItemAttributes(address item, uint itemId) external view returns (
    uint8[] memory ids,
    int32[] memory values
  ) {
    (values, ids) = ItemStatsLib.itemAttributes(item, itemId);
  }

  function setItemAttackInfo(address item, uint itemId, IItemController.AttackInfo memory info) external {
    _S()._itemAttackInfo[PackingLib.packNftId(item, itemId)] = ItemLib.packItemAttackInfo(info);
  }

  function setItemConsumableStats(address item, IStatController.ChangeableStats memory data) external {
    _S().itemConsumableStats[item] = StatLib.packChangeableStats(data);
  }
  function setItemConsumableAttributes(address item, uint8[] memory ids, int32[] memory values) external {
    _S()._itemConsumableAttributes[item] = PackingLib.toBytes32ArrayWithIds(values, ids);
  }

  //endregion ------------------------ For tests

  //region ------------------------ REGISTER

  // Following functions were moved to FacadItemLib to fix too-big-size of FacadeItemStatsLib

//  function registerItem(address msgSender, address item, IItemController.RegisterItemParams memory info) external {
//    return ItemStatsLib.registerItem(_controller, msgSender, item, info);
//  }

//  function removeItem(address msgSender, address item) external {
//    return ItemStatsLib.removeItem(_controller, msgSender, item);
//  }
  //endregion ------------------------ REGISTER

  //region ------------------------ EQUIP

  function isItemEquipped(address item, uint itemId) external view returns (bool) {
    return ItemStatsLib.isItemEquipped(_S(), item, itemId);
  }

  function equipMany(
    bool isEoa,
    address msgSender,
    address heroToken,
    uint heroTokenId,
    address[] calldata items,
    uint[] calldata tokenIds,
    uint8[] calldata itemSlots
  ) external {
    return ItemStatsLib.equipMany(isEoa, _controller, msgSender, heroToken, heroTokenId, items, tokenIds, itemSlots);
  }

  function _checkRequirements(address heroToken, uint heroTokenId, IStatController.CoreAttributes memory requirements) external view {
    return ItemStatsLib._checkRequirements(IStatController(_controller.statController()), heroToken, heroTokenId, requirements);
  }
  //endregion ------------------------ EQUIP

  //region ------------------------ TAKE OFF

  function takeOffMany(
    bool isEoa,
    address msgSender,
    address heroToken,
    uint heroTokenId,
    address[] calldata items,
    uint[] calldata tokenIds,
    uint8[] calldata itemSlots
  ) external {
    ItemStatsLib.takeOffMany(isEoa, _controller, msgSender, heroToken, heroTokenId, items, tokenIds, itemSlots);
  }

  function takeOffDirectly(
    address item,
    uint itemId,
    address hero,
    uint heroId,
    uint8 itemSlot,
    address destination,
    bool broken
  ) external {
    ItemStatsLib.takeOffDirectly(_controller, item, itemId, hero, heroId, itemSlot, destination, broken);
  }

  function _takeOff(
    address item,
    uint itemId,
    address heroToken,
    uint heroTokenId,
    uint8 itemSlot,
    address destination,
    bool broken
  ) external {
    ItemStatsLib._takeOff(_S(), IStatController(_controller.statController()), item, itemId, heroToken, heroTokenId, itemSlot, destination, broken);
  }
  //endregion ------------------------ TAKE OFF

  //region ------------------------ REPAIR and AUGMENT

  function repairDurability(bool isEoa, address msgSender, address item, uint itemId, uint consumedItemId) external {
    return ItemStatsLib.repairDurability(isEoa, _controller, msgSender, item, itemId, consumedItemId);
  }

  function augment(bool isEoa, address msgSender, address item, uint itemId, uint consumedItemId) external {
    return ItemStatsLib.augment(isEoa, _controller, msgSender, item, itemId, consumedItemId);
  }

  function augmentAttributes(bytes32[] memory packedAttr, bool ignoreNegative) external pure returns (int32[] memory values, uint8[] memory ids){
    return ItemStatsLib._augmentAttributes(packedAttr, ignoreNegative);
  }

  function augmentAttribute(int32 value) external pure returns (int32) {
    return ItemStatsLib._augmentAttribute(value);
  }
  //endregion ------------------------ REPAIR and AUGMENT

  //region ------------------------ REDUCE DURABILITY

  function reduceEquippedItemsDurability(address msgSender, address hero, uint heroId, uint8 biome) external {
    return ItemStatsLib.reduceEquippedItemsDurability(_controller, msgSender, hero, heroId, biome);
  }

  function _reduceDurabilityForItem(address item, uint itemId, uint biome) external returns (uint16 newDurability) {
    return ItemStatsLib._reduceDurabilityForItem(_S(), item, itemId, biome);
  }

  function calcReduceDurability(uint biome, uint currentDurability, uint8 itemLevel_, IItemController.ItemType itemType_)
  external pure returns (uint) {
    return ItemStatsLib._calcReduceDurability(biome, currentDurability, itemLevel_, itemType_);
  }
  //endregion ------------------------ REDUCE DURABILITY

  //region ------------------------ DESTROY

  function destroy(address msgSender, address item, uint itemId) external {
    ItemStatsLib.destroy(_controller, msgSender, item, itemId);
  }

  function _destroy(address item, uint itemId) external {
    ItemStatsLib._destroy(item, itemId);
  }
  //endregion ------------------------ DESTROY

  //region ------------------------ FEE

  function _sendFee(address item, address msgSender, IItemController.FeeType feeType) external {
    ItemStatsLib._sendFee(_S(), _controller, item, msgSender, feeType);
  }
  //endregion ------------------------ FEE

  //region ------------------------ USE

  function use(bool isEoa, address msgSender, address item, uint itemId, address heroToken, uint heroTokenId) external {
