// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.23;

import "../../interfaces/IStatController.sol";
import "../../interfaces/IItemController.sol";
import "../../interfaces/IFightCalculator.sol";
import "../../interfaces/IApplicationEvents.sol";
import "../../interfaces/IGOC.sol";
import "../../lib/FightLib.sol";
import "../../lib/PackingLib.sol";
import "../../lib/MonsterLib.sol";
import "hardhat/console.sol";

contract FacadeMonsterLib is IApplicationEvents {
  IController private _controller;
  IGOC.MonsterInfo internal _mInfo;
  mapping(uint => uint) private _pseudoRandomValue;
  mapping(uint => mapping(uint => uint)) private _pseudoRandomValueWithSeed;

  //region ------------------------ IControllable (all contracts allow to receive controller through this)
  function controller() external view returns (address) {
    return address(_controller);
  }
  function setController(address controller_) external {
    _controller = IController(controller_);
  }
  //endregion ------------------------ IControllable (all contracts allow to receive controller through this)

  //region ------------------------ For tests
  function setPseudoRandomForTests(uint max, uint value) external {
    _pseudoRandomValue[max] = value;
  }

  function _pseudoRandomForTests(uint max) internal view returns (uint) {
    return _pseudoRandomValue[max];
  }

  function _pseudoNextPrng(LibPRNG.PRNG memory, uint max) internal view returns (uint) {
    return _pseudoRandomValue[max];
  }

  function setPseudoRandomWithSeedForTests(uint max, uint seed, uint value) external {
    _pseudoRandomValueWithSeed[max][seed] = value;
  }

  function _pseudoRandomWithSeedForTests(uint max, uint seed) internal view returns (uint) {
    return _pseudoRandomValueWithSeed[max][seed];
  }

  function setGeneratedMonsters(address heroToken, uint heroTokenId, uint iteration, IGOC.GeneratedMonster memory gen) external {
    bytes32 data = PackingLib.packGeneratedMonster(gen.generated, gen.amplifier, gen.hp, gen.turnCounter);
    _mInfo._generatedMonsters[PackingLib.packNftId(heroToken, heroTokenId)][iteration] = data;
  }

  function getGeneratedMonsters(address heroToken, uint heroTokenId, uint iteration) external view returns (IGOC.GeneratedMonster memory gen) {
    bytes32 data = _mInfo._generatedMonsters[PackingLib.packNftId(heroToken, heroTokenId)][iteration];
    (gen.generated, gen.amplifier, gen.hp, gen.turnCounter) = PackingLib.unpackGeneratedMonster(data);
    return gen;
  }

  //endregion ------------------------ For tests

  //region ------------------------ Main logic
  function initialGeneration(address heroToken, uint heroTokenId, uint iteration, uint totalSupply) external {
    return MonsterLib.initialGeneration(_mInfo, heroToken, heroTokenId, iteration, totalSupply);
  }

  function action(IGOC.ActionContext memory ctx) external returns (IGOC.ActionResult memory result, uint8 turn) {
    return MonsterLib.action(ctx, _mInfo);
  }

  //endregion ------------------------ Main logic

  //region ------------------------ Internal calculations

  function _postFight(
    IGOC.ActionContext memory ctx,
    MonsterLib.AdrContext memory adrCtx,
    MonsterLib.FightInternalInfo memory fInfo,
    IGOC.MonsterGenInfo memory genInfo,
    IGOC.GeneratedMonster memory gen
  ) external returns (
    IGOC.ActionResult memory result,
    IGOC.GeneratedMonster memory genOut
  ) {
    result = MonsterLib._postFight(_mInfo, ctx, adrCtx, fInfo, genInfo, gen);
    return (result, gen);
  }

  function _initialGeneration(address heroToken, uint heroTokenId, uint iteration, uint totalSupply) external {
    return MonsterLib._initialGeneration(_mInfo, heroToken, heroTokenId, iteration, _pseudoRandomForTests, totalSupply);
  }

  function _bossDefeated(MonsterLib.AdrContext memory adrCtx, IGOC.ActionContext memory ctx) external {
    return MonsterLib._bossDefeated(adrCtx, ctx);
  }

  function _collectHeroFighterInfo(
    IFightCalculator.AttackInfo memory attackInfo,
    MonsterLib.AdrContext memory adrContext
  ) external view returns (IFightCalculator.FighterInfo memory fInfo, int32 manaConsumed) {
    return MonsterLib._collectHeroFighterInfo(attackInfo, adrContext);
  }

  function _buffAndGetHeroAttributes(
    uint level,
    IFightCalculator.AttackInfo memory attackInfo,
    MonsterLib.AdrContext memory context
  ) external view returns (int32[] memory heroAttributes, int32 manaConsumed) {
    return MonsterLib._buffAndGetHeroAttributes(level, attackInfo, context);
  }

  function _debuff(
    int32[] memory attributes,
    IFightCalculator.AttackInfo memory heroAttackInfo,
    MonsterLib.AdrContext memory context
  ) external view returns (
    int32[] memory
  ) {
    MonsterLib._debuff(attributes, heroAttackInfo, context);
    return attributes;
  }

  function _collectMonsterFighterInfo(
    uint8 biome,
    uint totalSupply,
    IGOC.GeneratedMonster memory gen,
    IFightCalculator.AttackInfo memory heroAttackInfo,
    uint heroLevel,
    MonsterLib.AdrContext memory adrCtx
  ) external view returns (
    IFightCalculator.FighterInfo memory fighterInfo,
    uint32 rarity,
    IGOC.MonsterGenInfo memory genInfo
  ) {
    return MonsterLib._collectMonsterFighterInfo(IGOC.MultiplierInfo(biome, totalSupply), _mInfo, gen, heroAttackInfo, heroLevel, adrCtx, _pseudoRandomForTests);
  }

  /////////////////////////////////////////////////////////////////
  ///
  /// function _fight is moved to FacadeMonsterLibFight because of size limitations
  ///
  /////////////////////////////////////////////////////////////////

  function _calcDmg(int32 heroLifeBefore, int32 heroLifeAfter) external pure returns (int32 damage) {
    return MonsterLib._calcDmg(heroLifeBefore, heroLifeAfter);
  }

  function _generateMonsterInfo(
    uint32 amplifier,
    int32 dungeonMultiplier,
    uint heroLevel,
    uint biome
  ) external view returns (
    int32[] memory attributes,
    uint32 level,
    uint32 experience,
    IFightCalculator.AttackInfo memory attackInfo,
    IGOC.MonsterGenInfo memory info
  ) {
    return MonsterLib._generateMonsterInfo(_mInfo, amplifier, dungeonMultiplier, heroLevel, biome, _pseudoRandomForTests);
  }

  function _mintRandomItems(
    MonsterLib.FightInternalInfo memory fInfo,
    IGOC.ActionContext memory ctx,
    IGOC.MonsterGenInfo memory genInfo
  ) external returns (address[] memory) {
    return MonsterLib._mintRandomItems(fInfo, ctx, genInfo, _pseudoNextPrng);
  }

  function generateMonsterAttributes(
    uint8[] memory ids,
    int32[] memory values,
    uint32 amplifier,
    int32 dungeonMultiplier,
    uint32 baseExperience
  ) external pure returns (int32[] memory attributes, uint32 experience) {
    return MonsterLib.generateMonsterAttributes(ids, values, amplifier, dungeonMultiplier, baseExperience);
  }

  //endregion ------------------------ Internal calculations

  //region ------------------------ Utils

  function _context(IGOC.ActionContext memory ctx) external view returns (MonsterLib.AdrContext memory context) {
    return MonsterLib._context(ctx);
  }

  function packGeneratedMonster(IGOC.GeneratedMonster memory gen) external pure returns (bytes32) {
    return MonsterLib.packGeneratedMonster(gen);
  }

  function unpackGeneratedMonster(bytes32 gen) external pure returns (IGOC.GeneratedMonster memory result) {
    return MonsterLib.unpackGeneratedMonster(gen);
  }

  function packMonsterInfo(IGOC.MonsterGenInfo calldata mInfo) external {
    MonsterLib.packMonsterInfo(mInfo, _mInfo);
  }

  function unpackMonsterInfo() external view returns (IGOC.MonsterGenInfo memory result) {
    return MonsterLib.unpackMonsterInfo(_mInfo);
  }

  function decodeAndCheckAttackInfo(bytes memory data, address heroToken, uint heroId) external view returns (IFightCalculator.AttackInfo memory) {
    return MonsterLib.decodeAndCheckAttackInfo(
      IItemController(_controller.itemController()),
      IHeroController(_controller.heroController()),
      data,
      heroToken,
      heroId
    );
  }

  function amplifyMonsterAttribute(int32 value, uint32 amplifier, int32 dungeonMultiplier) external pure returns (int32) {
    return MonsterLib.amplifyMonsterAttribute(value, amplifier, dungeonMultiplier);
  }

  function _getMonsterCounterIndex(uint32 objectId) external pure returns (bytes32) {
    return MonsterLib._getMonsterCounterIndex(objectId);
  }

  function monsterMultiplier(uint totalSupply) external pure returns (int32) {
    return MonsterLib.monsterMultiplier(totalSupply);
  }
  //endregion ------------------------ Utils

}

