// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.23;

import "../../interfaces/IItemController.sol";
import "../../interfaces/IFightCalculator.sol";
import "../../interfaces/IApplicationEvents.sol";
import "../../interfaces/IGOC.sol";
import "../../lib/FightLib.sol";
import "../../lib/PackingLib.sol";
import "../../lib/MonsterLib.sol";

/// @dev Access to _fight function (it was moved away from FacadeMonsterLib because of size limitations)
contract FacadeMonsterLibFight is IApplicationEvents {
  struct FightForTestDataCalled {
    IItemController ic;
    IFightCalculator.FightCall callData;
    uint randomResult;
  }

  struct FightForTestDataSettings {
    uint maxRandom;
    IFightCalculator.FightResult results;
  }

  IGOC.MonsterInfo internal _mInfo;
  mapping(uint => uint) private _pseudoRandomValue;
  IController private _controller;

  FightForTestDataSettings private _settings;
  FightForTestDataCalled private _callResults;

  //region ------------------------ IControllable (all contracts allow to receive controller through this)
  function controller() external view returns (address) {
    return address(_controller);
  }
  function setController(address controller_) external {
    _controller = IController(controller_);
  }
  //endregion ------------------------ IControllable (all contracts allow to receive controller through this)


  //region ------------------------ For tests
  function setPseudoRandomForTests(uint max, uint value) external {
    _pseudoRandomValue[max] = value;
  }

  function _pseudoRandomForTests(uint max) internal view returns (uint) {
    return _pseudoRandomValue[max];
  }

  function setFightForTest(uint maxRandom, IFightCalculator.FightResult memory results) external {
    _settings = FightForTestDataSettings({maxRandom: maxRandom, results: results});
  }

  function fightForTests(
    IItemController ic,
    IFightCalculator.FightCall memory callData,
    function (uint) internal view returns (uint) random_
  ) internal returns (
    IFightCalculator.FightResult memory
  ) {
    _callResults = FightForTestDataCalled({
      ic: ic,
      callData: IFightCalculator.FightCall({
        fighterA: callData.fighterA,
        fighterB: callData.fighterB,
        heroId: callData.heroId,
        dungeonId: callData.dungeonId,
        objectId: callData.objectId,
        heroAdr: callData.heroAdr,
        stageId: callData.stageId,
        iteration: callData.iteration,
        turn: callData.turn
      }),
      randomResult: random_(_settings.maxRandom)
    });
    return _settings.results;
  }

  function getFightCallResults() external view returns (
    IItemController ic,
    IFightCalculator.FightCall memory callData,
    uint randomResult
  ) {
    return (_callResults.ic, _callResults.callData, _callResults.randomResult);
  }

  function setGeneratedMonsters(address heroToken, uint heroTokenId, uint iteration, IGOC.GeneratedMonster memory gen) external {
    bytes32 data = PackingLib.packGeneratedMonster(gen.generated, gen.amplifier, gen.hp, gen.turnCounter);
    _mInfo._generatedMonsters[PackingLib.packNftId(heroToken, heroTokenId)][iteration] = data;
  }

  //endregion ------------------------ For tests

  function _fight(
    IGOC.ActionContext memory ctx,
    IGOC.GeneratedMonster memory gen,
    MonsterLib.AdrContext memory adrCtx
  ) external returns (MonsterLib.FightInternalInfo memory fInfo, IGOC.MonsterGenInfo memory info){
    return MonsterLib._fight(ctx, _mInfo, gen, adrCtx, _pseudoRandomForTests, fightForTests);
  }

  function _action(IGOC.ActionContext memory ctx) external returns (IGOC.ActionResult memory result, uint8 turn) {
    return MonsterLib._action(ctx, _mInfo, _pseudoRandomForTests, fightForTests);
  }

  function packMonsterInfo(IGOC.MonsterGenInfo calldata mInfo) external {
    MonsterLib.packMonsterInfo(mInfo, _mInfo);
  }
}

