// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.23;

import "../../interfaces/IAppErrors.sol";
import "../../interfaces/IReinforcementController.sol";
import "../../lib/ReinforcementControllerLib.sol";
import "../../lib/PackingLib.sol";
import {ReinforcementController} from "../../core/ReinforcementController.sol";

/// @dev We need IAppErrors to correctly handle custom errors in the tests
contract FacadeReinforcementControllerLib is ReinforcementController {
  using EnumerableSet for EnumerableSet.Bytes32Set;
  using EnumerableMap for EnumerableMap.AddressToUintMap;

  //region ------------------------ For tests
  function _S() internal pure returns (IReinforcementController.MainState storage s) {
    return ReinforcementControllerLib._S();
  }
  function setHeroTokenRewards(address heroToken, uint heroId, address token, uint amount) external {
    _S()._heroTokenRewards[PackingLib.packNftId(heroToken, heroId)].set(token, amount);
  }
  function getHeroTokenRewards(address heroToken, uint heroId, uint index) external view returns (address, uint) {
    return _S()._heroTokenRewards[PackingLib.packNftId(heroToken, heroId)].at(index);
  }
  function getHeroTokenRewardsLength(address heroToken, uint heroId) external view returns (uint) {
    return _S()._heroTokenRewards[PackingLib.packNftId(heroToken, heroId)].length();
  }
  function setHeroNftRewards(address heroToken, uint heroId, address token, uint amount) external {
    _S()._heroNftRewards[PackingLib.packNftId(heroToken, heroId)].push(PackingLib.packNftId(token, amount));
  }
  function getHeroNftRewards(address heroToken, uint heroId) external view returns (bytes32[] memory) {
    return _S()._heroNftRewards[PackingLib.packNftId(heroToken, heroId)];
  }
  function setConfig(uint8 minLevel_, uint8 minLifeChances_) external {
    _S().config = ReinforcementControllerLib.packConfig(minLevel_, minLifeChances_);
  }
  function getConfig() external view returns (uint8 minLevel_, uint8 minLifeChances_) {
    return ReinforcementControllerLib.unpackConfig(_S().config);
  }
  function setStaked(address heroToken, uint heroId, uint8 biome, uint score, uint8 fee, uint64 blockTimestamp) external {
    _S()._stakedHeroes[PackingLib.packNftId(heroToken, heroId)] = ReinforcementControllerLib.packHeroInfo(
      IReinforcementController.HeroInfo({
        biome: biome,
        score: score,
        fee: fee,
        stakeTs: blockTimestamp == 0 ? uint64(block.timestamp) : blockTimestamp
      })
    );
  }
  function addToInternalIdsByBiomes(address heroToken, uint heroId, uint8 biome) external {
    _S()._internalIdsByBiomes[biome].add(PackingLib.packNftId(heroToken, heroId));
  }
  function getInternalIdsByBiomes(address heroToken, uint heroId, uint8 biome) external view returns (bool) {
    return _S()._internalIdsByBiomes[biome].contains(PackingLib.packNftId(heroToken, heroId));
  }
  function setMaxScore(uint8 biome, uint score) external {
    _S().maxScore[biome] = score;
  }
  //endregion ------------------------ For tests

  //region ------------------------ Internal logic
  function _claimAllTokenRewards(address heroToken, uint heroId, address recipient) external {
    return ReinforcementControllerLib._claimAllTokenRewards(heroToken, heroId, recipient);
  }

  function _claimAllNftRewards(address heroToken, uint heroId, address recipient) external {
    return ReinforcementControllerLib._claimAllNftRewards(heroToken, heroId, recipient);
  }

  function _generateReinforcementAttributes(uint[] memory indexes, address heroToken, uint heroId) external view returns (
    int32[] memory attributes
  ) {
    return ReinforcementControllerLib._generateReinforcementAttributes(
      IStatController(IController(controller()).statController()),
      indexes,
      heroToken,
      heroId
    );
  }
  //endregion ------------------------ Internal logic

  //region ------------------------ Packing utils

  function packHeroInfo(IReinforcementController.HeroInfo memory info) external pure returns (bytes32) {
    return ReinforcementControllerLib.packHeroInfo(info);
  }

  function unpackHeroInfo(bytes32 packed) external pure returns (IReinforcementController.HeroInfo memory info) {
    return ReinforcementControllerLib.unpackHeroInfo(packed);
  }

  function packConfig(uint8 minLevel_, uint8 minLifeChances_) external pure returns (bytes32) {
    return ReinforcementControllerLib.packConfig(minLevel_, minLifeChances_);
  }

  function unpackConfig(bytes32 packed) external pure returns (uint8 minLevel_, uint8 minLifeChances_) {
    return ReinforcementControllerLib.unpackConfig(packed);
  }

  //endregion ------------------------ Packing utils

  //region ------------------------ USER ACTIONS for tests

  function __stakeHero(bool isEoa, address msgSender, address heroToken, uint heroId, uint8 fee) external {
    ReinforcementControllerLib.stakeHero(isEoa, IController(controller()), msgSender, heroToken, heroId, fee);
  }

  function __withdrawHero(bool isEoa, address msgSender, address heroToken, uint heroId) external {
    ReinforcementControllerLib.withdrawHero(isEoa, IController(controller()), msgSender, heroToken, heroId);
  }

  function __claimAll(bool isEoa, address msgSender, address heroToken, uint heroId) external {
    ReinforcementControllerLib.claimAll(isEoa, IController(controller()), msgSender, heroToken, heroId);
  }
  //endregion ------------------------ USER ACTIONS for tests
}

