pragma solidity 0.8.23;

import "../../openzeppelin/EnumerableSet.sol";
import "../../lib/StatControllerLib.sol";
import "../../core/StatController.sol";

/// @notice Lightweight wrapper around StatController to be able to test some function in core StatController contract
contract FacadeStatController is StatController {
  using EnumerableSet for EnumerableSet.AddressSet;

  /// @param packedHeroId packNftId(heroToken, heroTokenId)
  function addIsConsumableUsed(bytes32 packedHeroId, address item) external {
    StatControllerLib._S().usedConsumables[packedHeroId].add(item);
  }
}

// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.23;

import "../../lib/StatControllerLib.sol";
import "../../interfaces/IStatController.sol";
import "../../openzeppelin/EnumerableSet.sol";

contract FacadeStatControllerLib {
  using EnumerableSet for EnumerableSet.AddressSet;

  IController internal _controller;
  /// @dev To test _compareStatsWithAttributes
  bytes32[] internal totalAttributes;

  function _S() internal pure returns (IStatController.MainState storage s) {
    return StatControllerLib._S();
  }

  //region ------------------------ For tests
  function setHeroTotalAttributes(bytes32 key, bytes32[] memory value) external {
    _S().heroTotalAttributes[key] = value;
  }
  //endregion ------------------------ For tests

  //region ------------------------ Constants
  function getLevelUpSum() external pure returns (int32) {
    return StatControllerLib.LEVEL_UP_SUM;
  }
  function getKarmaHash() external pure returns (bytes32) {
    return StatControllerLib.KARMA_HASH;
  }
  function getClassHash() external pure returns (bytes32) {
    return StatControllerLib.HERO_CLASS_HASH;
  }
  //endregion ------------------------ Constants

  //region ------------------------ IControllable (all contracts allow to receive controller through this)
  function controller() public view returns (address) {
    return address(_controller);
  }
  function setController(address controller_) external {
    _controller = IController(controller_);
  }
  //endregion ------------------------ IControllable (all contracts allow to receive controller through this)

  //region ------------------------ Initialization of _s
  function initHeroStats(bytes32 key, bytes32 value) external {
    _S().heroStats[key] = value;
  }
  function setHeroItemSlot(address heroToken, uint64 heroTokenId, uint8 itemSlot, address itemToken, uint itemTokenId) external {
    _S().heroSlots[PackingLib.packMapObject(heroToken, heroTokenId, itemSlot)] = PackingLib.packNftId(itemToken, itemTokenId);
  }
  //endregion ------------------------ Initialization of _s

  //region ------------------------ Get values of _s
  function getHeroTotalAttributes(bytes32 key) external view returns (bytes32[] memory) {
    return _S().heroTotalAttributes[key];
  }
  function getHeroCore(bytes32 key) external view returns (int32[] memory) {
    return PackingLib.unpackInt32Array(_S()._heroCore[key]);
  }
  function getHeroItemSlot(bytes32 key) external view returns (address itemToken, uint itemTokenId) {
    return PackingLib.unpackNftId(_S().heroSlots[key]);
  }
  function getUsedConsumables(bytes32 key) external view returns (address[] memory dest) {
    uint len = _S().usedConsumables[key].length();
    dest = new address[](len);
    for (uint i = 0; i < len; ++i) {
      dest[i] = _S().usedConsumables[key].at(i);
    }
    return dest;
  }
  function getHeroCustomData(bytes32 key1, bytes32 key2) external view returns (uint) {
    return _S().heroCustomData[key1][key2];
  }
  //endregion ------------------------ Get values of _s

  //region ------------------------ RESTRICTIONS

  function onlyRegisteredContract() external view {
    StatControllerLib.onlyRegisteredContract(_controller);
  }

  function onlyItemController() external view {
    StatControllerLib.onlyItemController(_controller);
  }

  function onlyHeroController() external view {
    StatControllerLib.onlyHeroController(_controller);
  }
  //endregion ------------------------ RESTRICTIONS


  //region ------------------------ PURE
  function isItemTypeEligibleToItemSlot(uint itemType, uint itemSlot) external pure returns (bool) {
    return StatControllerLib.isItemTypeEligibleToItemSlot(itemType, itemSlot);
  }

  function levelUpExperienceRequired(uint32 level) external pure returns (uint) {
    return StatControllerLib.levelUpExperienceRequired(level);
  }

  /// @dev Required to test _levelUpExperienceRequired
  function levelExperience(uint32 level) external pure returns (uint32) {
    return StatLib.levelExperience(level);
  }
  //endregion ------------------------ PURE

  //region ------------------------ VIEWS
  function heroAttributes(address token, uint tokenId) external view returns (int32[] memory) {
    return StatControllerLib.heroAttributes(_S(), token, tokenId);
  }

  function heroBonusAttributes(address token, uint tokenId) external view returns (int32[] memory) {
    return StatControllerLib.heroBonusAttributes(_S(), token, tokenId);
  }

  function heroTemporallyAttributes(address token, uint tokenId) external view returns (int32[] memory) {
    return StatControllerLib.heroTemporallyAttributes(_S(), token, tokenId);
  }

  function heroAttributesLength(address token, uint tokenId) external pure returns (uint) {
    return StatControllerLib.heroAttributesLength(token, tokenId);
  }

  function heroAttribute(address token, uint tokenId, uint index) external view returns (int32) {
    return StatControllerLib.heroAttribute(_S(), token, tokenId, index);
  }

  function heroBaseAttributes(address token, uint tokenId) external view returns (IStatController.CoreAttributes memory core) {
    return StatControllerLib.heroBaseAttributes(_S(), token, tokenId);
  }

  function heroCustomData(address token, uint tokenId, bytes32 index) external view returns (uint) {
    return StatControllerLib.heroCustomData(_S(), token, tokenId, index);
  }

  function globalCustomData(bytes32 index) external view returns (uint) {
    return StatControllerLib.globalCustomData(_S(), index);
  }

  function heroStats(address token, uint tokenId) external view returns (IStatController.ChangeableStats memory result) {
    return StatControllerLib.heroStats(_S(), token, tokenId);
  }

  function heroItemSlot(address heroToken, uint64 heroTokenId, uint8 itemSlot) external view returns (bytes32 nftPacked) {
    return StatControllerLib.heroItemSlot(_S(), heroToken, heroTokenId, itemSlot);
  }

  function heroItemSlots(address heroToken, uint heroTokenId) external view returns (uint8[] memory) {
    return StatControllerLib.heroItemSlots(_S(), heroToken, heroTokenId);
  }

  function isHeroAlive(address heroToken, uint heroTokenId) external view returns (bool) {
    return StatControllerLib.isHeroAlive(_S(), heroToken, heroTokenId);
  }

  function isConsumableUsed(address heroToken, uint heroTokenId, address item) external view returns (bool) {
    return StatControllerLib.isConsumableUsed(_S(), heroToken, heroTokenId, item);
  }

  function buffHero(IStatController.BuffInfo memory info) external view returns (int32[] memory, int32) {
    return StatControllerLib.buffHero(_S(), IController(_controller), info);
  }
  //endregion ------------------------ VIEWS

  //region ------------------------ ACTIONS

  function initNewHero(address heroToken, uint heroTokenId, uint heroClass) external {
    return StatControllerLib.initNewHero(_S(), IController(_controller), heroToken, heroTokenId, heroClass);
  }

  function changeHeroItemSlot(
    address heroToken,
    uint64 heroTokenId,
    uint itemType,
    uint8 itemSlot,
    address itemToken,
    uint itemTokenId,
    bool equip
  ) external {
    return StatControllerLib.changeHeroItemSlot(
      _S(),
      IController(_controller),
      heroToken,
      heroTokenId,
      itemType,
      itemSlot,
      itemToken,
      itemTokenId,
      equip
    );
  }

  function changeCurrentStats(
    address heroToken,
    uint heroTokenId,
    IStatController.ChangeableStats memory change,
    bool increase
  ) external {
    return StatControllerLib.changeCurrentStats(
      _S(),
      IController(_controller),
      heroToken,
      heroTokenId,
      change,
      increase
    );
  }

  function registerConsumableUsage(address heroToken, uint heroTokenId, address item) external {
    return StatControllerLib.registerConsumableUsage(_S(), IController(controller()), heroToken, heroTokenId, item);
  }

  function clearUsedConsumables(address heroToken, uint heroTokenId) external {
    return StatControllerLib.clearUsedConsumables(_S(), IController(controller()), heroToken, heroTokenId);
  }

  function changeBonusAttributes(IStatController.ChangeAttributesInfo memory info) external {
    return StatControllerLib.changeBonusAttributes(_S(), IController(controller()), info);
  }

  function clearTemporallyAttributes(address heroToken, uint heroTokenId) external {
    return StatControllerLib.clearTemporallyAttributes(_S(), IController(controller()), heroToken, heroTokenId);
  }

  function levelUp(
    address heroToken,
    uint heroTokenId,
    uint heroClass,
    IStatController.CoreAttributes memory change
  ) external returns (uint newLvl) {
    return StatControllerLib.levelUp(_S(), IController(controller()), heroToken, heroTokenId, heroClass, change);
  }

  function setHeroCustomData(address token, uint tokenId, bytes32 index, uint value) external {
    return StatControllerLib.setHeroCustomData(_S(), IController(controller()), token, tokenId, index, value);
  }

  function setGlobalCustomData(bytes32 index, uint value) external {
    return StatControllerLib.setGlobalCustomData(_S(), IController(controller()), index, value);
  }

  function restoreLifeAndMana(address heroToken, uint heroTokenId) external {
    StatControllerLib.restoreLifeAndMana(_S(), IController(controller()), heroToken, heroTokenId);
  }
  //endregion ------------------------ ACTIONS

  //region ------------------------ _compareStatsWithAttributes
  function _setTotalAttributeValue(uint attributeIndex, int32 value) external {
    PackingLib.setInt32(totalAttributes, attributeIndex, value);
  }
  function _compareStatsWithAttributes(
    address heroToken,
    uint heroTokenId,
    IStatController.ChangeableStats memory curStats
  ) external {
    bytes32 heroPackedId = PackingLib.packNftId(heroToken, heroTokenId);
    StatControllerLib._compareStatsWithAttributes(_S(), heroPackedId, totalAttributes, curStats);
  }

  //endregion ------------------------ _compareStatsWithAttributes

  function _changeChangeableStats(
    bytes32 heroPackedId,
    uint32 level,
    uint32 experience,
    uint32 life,
    uint32 mana,
    uint32 lifeChances
  ) external {
    StatControllerLib._changeChangeableStats(_S(), heroPackedId, level, experience, life, mana, lifeChances);
  }
}

