// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.23;

import "../../interfaces/IStoryController.sol";
import "../../interfaces/IGOC.sol";
import "../../lib/StoryControllerLib.sol";

contract FacadeStoryControllerLib is IAppErrors {
  using EnumerableSet for EnumerableSet.UintSet;
  using EnumerableSet for EnumerableSet.Bytes32Set;
  IController internal _controller;

  //region ------------------------ IControllable (all contracts allow to receive controller through this)
  function controller() external view returns (address) {
    return address(_controller);
  }
  function setController(address controller_) external {
    _controller = IController(controller_);
  }
  //endregion ------------------------ IControllable (all contracts allow to receive controller through this)


  //region ------------------------ For tests
  function _S() internal pure returns (IStoryController.MainState storage s) {
    return StoryControllerLib._S();
  }

  function setStoryRequiredLevel(uint16 storyId, uint requiredLevel) external {
    _S().storyRequiredLevel[storyId] = requiredLevel;
  }

  function setStoryRequiredHeroData(uint16 storyId, bytes32 index, uint64 min, uint64 max, bool key) external {
    bytes32 data = PackingLib.packCustomDataRequirements(min, max, key);
    _S().storyRequiredHeroData[storyId].push(IStoryController.CustomDataRequirementRangePacked({
      index: index,
      data: data
    }));
  }

  function addBurnItem(bytes32 answerIdHash, uint8 slot, uint64 chance, bool stopIfBurned) external {
    _S().burnItem[answerIdHash].push(PackingLib.packBurnInfo(slot, chance, stopIfBurned));
  }

  function setNextPageIds(bytes32 pagePackedId, uint16[] memory values) external {
    _S().nextPageIds[pagePackedId] = values;
  }

  function setInfoAttributesAndItems(
    uint16 storyId,
    uint16 pageId,
    uint8 heroClass,
    uint16 answerId,
    bool success,
    uint8[] memory attributeIds,
    int32[] memory attributeValues,
    address[] memory mintItems,
    uint32[] memory mintItemsChances
  ) external {
    bytes32 answerPackedId = PackingLib.packStoryAnswerId(storyId, pageId, heroClass, answerId);

    (success
      ? _S().successInfoAttributes
      : _S().failInfoAttributes
    )[answerPackedId] = PackingLib.toBytes32ArrayWithIds(attributeValues, attributeIds);

    bytes32[] storage infoMintItems = success
      ? _S().successInfoMintItems[answerPackedId]
      : _S().failInfoMintItems[answerPackedId];

    uint len = mintItems.length;
    for (uint i = 0; i < len; ++i) {
      infoMintItems.push(PackingLib.packItemMintInfo(mintItems[i], mintItemsChances[i]));
    }
  }

  function setInfoStats(
    bytes32 answerPackedId,
    uint32 experience,
    int32 heal,
    int32 manaRegen,
    int32 lifeChancesRecovered,
    int32 damage,
    int32 manaConsumed,
    bool success
  ) external {
    (success ? _S().successInfoStats : _S().failInfoStats)[answerPackedId] = PackingLib.packStatsChange(
      experience, heal, manaRegen, lifeChancesRecovered, damage, manaConsumed
    );
  }

  function setAttributeRequirements(bytes32 answerPackedId, uint8 attributeIndex, int32 value, bool isCore) external {
    bytes32 data = PackingLib.packStoryAttributeRequirement(attributeIndex, value, isCore);
    _S().attributeRequirements[answerPackedId].push(data);
  }

  function setStoryTokenRequirements(bytes32 answerPackedId, address token, uint88 amount, bool requireTransfer) external {
    bytes32 data = PackingLib.packStoryTokenRequirement(token, amount, requireTransfer);
    _S().tokenRequirements[answerPackedId].push(data);
  }

  function setItemRequirements(bytes32 answerPackedId, address item, bool requireItemBurn, bool requireItemEquipped) external {
    bytes32 data = PackingLib.packStoryItemRequirement(item, requireItemBurn, requireItemEquipped);
    _S().itemRequirements[answerPackedId].push(data);
  }

  function setHeroCustomDataRequirement(
    bytes32 answerPackedId,
    bool globalCustomData,
    bytes32 index,
    uint64 min,
    uint64 max,
    bool mandatory
  ) external {
    IStoryController.CustomDataRequirementPacked[] storage arr = globalCustomData
      ? _S().globalCustomDataRequirement[answerPackedId]
      : _S().heroCustomDataRequirement[answerPackedId];

    arr.push(IStoryController.CustomDataRequirementPacked({
      data: PackingLib.packCustomDataRequirements(min, max, mandatory),
      index: index
    }));
  }

  function setNextObjectsRewrite(uint16 storyId, uint16 pageId, uint8 heroClass, uint32[] memory values) external {
    bytes32 key = PackingLib.packStoryPageId(storyId, pageId, heroClass);
    _S().nextObjectsRewrite[key] = values;
  }

  function setHeroState(address heroToken, uint80 heroTokenId, uint16 storyId, bytes32 value) external {
    bytes32 key = PackingLib.packStoryHeroStateId(heroToken, heroTokenId, storyId);
    _S().heroState[key] = value;
  }

  function getHeroState(address heroToken, uint80 heroTokenId, uint16 storyId) external view returns (bytes32 data) {
    bytes32 key = PackingLib.packStoryHeroStateId(heroToken, heroTokenId, storyId);
    return _S().heroState[key];
  }

  function setStoryIds(uint32 objectId, uint16 storyId) external {
    _S().storyIds[objectId] = storyId;
  }

  function setIdToStory(uint16 storyId, uint32 objectId) external {
    _S().idToStory[storyId] = objectId;
  }

  function addAnswer(uint16 storyId, uint16 pageId, uint8 heroClass, bytes32 answer) external {
    _S().answers[PackingLib.packStoryPageId(storyId, pageId, heroClass)].push(answer);
  }

  function setStoryRegistrationInfo(uint32 objectId, uint16 storyIds_, bool registered) external {
    _S().storyIds[objectId] = storyIds_;
    _S().registeredStories[objectId] = registered;
  }

  function registerPage(uint16 storyId, uint16 pageId) external {
    StorySetupLib.registerPage(_S(), storyId, pageId);
  }

  function registerAnswer(uint16 storyId, bytes32 answerId) external {
    StorySetupLib.registerAnswer(_S(), storyId, answerId);
  }

  function getCountStoryPages(uint16 storyId) external view returns (uint) {
    return _S().allStoryPages[storyId].length();
  }

  function getCountStoryAnswers(uint16 storyId) external view returns (uint) {
    return _S().allStoryAnswers[storyId].length();
  }

  function addAllStoryPages(uint16 storyId, uint page) external {
    _S().allStoryPages[storyId].add(page);
  }

  function addAllStoryAnswers(uint16 storyId, bytes32 answer) external {
    _S().allStoryAnswers[storyId].add(answer);
  }

  function setCustomDataResult(
    uint16 storyId,
    IStoryController.AnswerCustomDataResultMeta calldata meta,
    IStoryController.CustomDataResult type_
  ) external {
    StorySetupLib.setCustomDataResult(_S(), storyId, meta, type_);
  }
  //endregion ------------------------ For tests

  //region ------------------------ VIEWS
  function idToStory(uint16 storyId) external view returns (uint32) {
    return StoryControllerLib.idToStory(storyId);
  }

  function heroPage(address hero, uint80 heroId, uint16 storyId) external view returns (uint16 pageId) {
    return StoryControllerLib.heroPage(hero, heroId, storyId);
  }

  function storyIds(uint32 objectId) external view returns (uint16) {
    return StoryControllerLib.storyIds(objectId);
  }

  function registeredStories(uint32 objectId) external view returns (bool) {
    return StoryControllerLib.registeredStories(objectId);
  }

  function allStoryPagesLength(uint16 storyId) external view returns (uint) {
    return StoryControllerLib.allStoryPagesLength(storyId);
  }

  function allStoryPages(uint16 storyId, uint index) external view returns (uint) {
    return StoryControllerLib.allStoryPages(storyId, index);
  }

  function allStoryAnswersLength(uint16 storyId) external view returns (uint) {
    return StoryControllerLib.allStoryAnswersLength(storyId);
  }

  function allStoryAnswers(uint16 storyId, uint index) external view returns (bytes32) {
    return StoryControllerLib.allStoryAnswers(storyId, index);
  }

  function currentHeroAnswers(uint16 storyId, address hero, uint80 heroId) public view returns (bytes32[] memory) {
    return StoryControllerLib.currentHeroAnswers(_controller, storyId, hero, heroId);
  }

  function isStoryAvailableForHero(uint32 objectId, address heroToken, uint heroTokenId) external view returns (bool) {
    return StoryControllerLib.isStoryAvailableForHero(_controller, objectId, heroToken, heroTokenId);
  }

  //endregion ------------------------ VIEWS

  //region ------------------------ CHANGE META

  function removeStory(uint32 objectId) external {
    StoryControllerLib.removeStory(_controller, objectId);
  }

  function removeStoryPagesMeta(uint16 storyId, uint maxIterations) external {
    StoryControllerLib.removeStoryPagesMeta(_controller, storyId, maxIterations);
  }

  function removeStoryAnswersMeta(uint16 storyId, uint maxIterations) external {
    StoryControllerLib.removeStoryAnswersMeta(_controller, storyId, maxIterations);
  }
  //endregion ------------------------ CHANGE META

  //region ------------------------ MAIN LOGIC

  function storyAction(
    address sender,
    uint64 dungeonId,
    uint32 objectId,
    uint stageId,
    address heroToken,
    uint heroTokenId,
    uint8 biome,
    uint iteration,
    bytes memory data
  ) external returns (
    IGOC.ActionResult memory result
  ) {
    return StoryControllerLib.storyAction(
      _controller,
      sender,
      dungeonId,
      objectId,
      stageId,
      heroToken,
      heroTokenId,
      biome,
      iteration,
      data
    );
  }

  function _handleAnswer(
    IStoryController.StoryActionContext memory context,
    bytes32[] memory heroAnswers
  ) external returns (
    IGOC.ActionResult memory returnResult
  ) {
    return StoryControllerLib._handleAnswer(context, heroAnswers);
  }

  //endregion ------------------------ MAIN LOGIC

  //region ------------------------ SETTERS
  // !!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!
  // Setters section is implemented in FacadeStoryControllerLibSetters
  // !!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!
  //endregion ------------------------ SETTERS


}

