  function setItemRequirements(bytes32 answerPackedId, address item, bool requireItemBurn, bool requireItemEquipped) external {
    bytes32 data = PackingLib.packStoryItemRequirement(item, requireItemBurn, requireItemEquipped);
    _S().itemRequirements[answerPackedId].push(data);
  }

  function setHeroCustomDataRequirement(
    bytes32 answerPackedId,
    bool globalCustomData,
    bytes32 index,
    uint64 min,
    uint64 max,
    bool mandatory
  ) external {
    IStoryController.CustomDataRequirementPacked[] storage arr = globalCustomData
      ? _S().globalCustomDataRequirement[answerPackedId]
      : _S().heroCustomDataRequirement[answerPackedId];

    arr.push(IStoryController.CustomDataRequirementPacked({
      data: PackingLib.packCustomDataRequirements(min, max, mandatory),
      index: index
    }));
  }

  function setHeroState(address heroToken, uint80 heroTokenId, uint16 storyId, bytes32 value) external {
    bytes32 key = PackingLib.packStoryHeroStateId(heroToken, heroTokenId, storyId);
    _S().heroState[key] = value;
  }

  function getHeroState(address heroToken, uint80 heroTokenId, uint16 storyId) external view returns (bytes32 data) {
    bytes32 key = PackingLib.packStoryHeroStateId(heroToken, heroTokenId, storyId);
    return _S().heroState[key];
  }


  function setNextObjectsRewrite(uint16 storyId, uint16 pageId, uint8 heroClass, uint32[] memory values) external {
    bytes32 key = PackingLib.packStoryPageId(storyId, pageId, heroClass);
    _S().nextObjectsRewrite[key] = values;
  }
  //endregion ------------------------ For tests


  //region ------------------------ Story logic

  function action(IGOC.ActionContext memory ctx, uint16 storyId) external returns (IGOC.ActionResult memory result) {
    return StoryLib.action(ctx, storyId);
  }

  function isStoryAvailableForHero(uint16 storyId, address heroToken, uint heroTokenId) external view returns (bool) {
    return StoryLib.isStoryAvailableForHero(_S(), storyId, heroToken, heroTokenId, _controller.statController());
  }

  function handleResult(
    IStoryController.StoryActionContext memory context,
    bytes32[] memory attributesChanges,
    bytes32 statsChanges,
    bytes32[] memory mintItemsData
  ) external returns (
    IGOC.ActionResult memory results,
    IStatController.ChangeableStats memory updatedHeroStats
  ) {
    results = StoryLib.handleResult(context, attributesChanges, statsChanges, mintItemsData, _mintRandomItemsMock);
    updatedHeroStats = context.heroStats;
  }

  function _mintRandomItemsMock(
    IStoryController.StoryActionContext memory context,
    bytes32[] memory mintItemsData
  ) internal returns (
    address[] memory mintItems
  ){
    _mintRandomItemsMockLastCallResults = MintRandomItemsMockLastCallResults({
      biome: context.biome,
      oracle: address(context.oracle),
      experience : context.heroStats.experience,
      level: context.heroStats.level,
      mintItemsData: mintItemsData
    });
    return _mintRandomItemsMockReturn;
  }

  function setMintRandomItemsMockReturn(address[] memory mintItems) external {
    _mintRandomItemsMockReturn = mintItems;
  }

  function getMintRandomItemsMockLastCallResults() external view returns(
    uint8 biome,
    address oracle,
    uint32 experience,
    uint32 level,
    bytes32[] memory mintItemsData
  ) {
    MintRandomItemsMockLastCallResults memory r = _mintRandomItemsMockLastCallResults;
    return (r.biome, r.oracle, r.experience, r.level, r.mintItemsData);
  }

  function _generateAttributes(bytes32[] memory attributesChanges) external pure returns (int32[] memory attributes) {
    return StoryLib._generateAttributes(attributesChanges);
  }

  function _generateStats(bytes32 statsChanges) external pure returns (IStoryController.StatsChange memory change) {
    return StoryLib._generateStats(statsChanges);
  }

  function handleCustomDataResult(
    IStoryController.StoryActionContext memory context,
    bytes32[] memory heroCustomDatas,
    bytes32[] memory globalCustomDatas
  ) external {
    return StoryLib.handleCustomDataResult(context, heroCustomDatas, globalCustomDatas);
  }

  function burn(IStoryController.StoryActionContext memory context) external {
    return StoryLib.burn(context, _S());
  }

  function _burnItemInHeroSlot(IStoryController.StoryActionContext memory context, uint8 slot) external {
    return StoryLib._burnItemInHeroSlot(context, slot);
  }

  function finishStory(IStoryController.StoryActionContext memory context) external returns (
    uint32[] memory nextObjs
  ) {
    return StoryLib.finishStory(context, _S());
  }

  function handleAnswer(
    IStoryController.AnswerResultId answerResultId,
    IStoryController.StoryActionContext memory context
  ) external returns (IGOC.ActionResult memory returnResult, uint16 nextPage, uint16[] memory nextPages) {
    return StoryLib.handleAnswer(answerResultId, _S(), context);
  }

  function getNextPage(IOracle oracle, uint16[] memory pages) external returns (uint16) {
    return StoryLib._getNextPage(oracle, pages);
  }

  function checkAnswerIndexValid(bytes32[] memory heroAnswers, bytes32 answerIdHash) external pure {
    return StoryLib.checkAnswerIndexValid(heroAnswers, answerIdHash);
  }

  //endregion ------------------------ Story logic

//region ------------------------ Check answers

//!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!
// see FacadeStoryLibCheckAnswers
//!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!

//endregion ------------------------ Check answers

  //region ------------------------ Utils
  function _getStoryIndex(uint16 storyId) external pure returns (bytes32) {
    return StoryLib._getStoryIndex(storyId);
  }

  //endregion ------------------------ Utils
}

