pragma solidity 0.8.23;

import "../../interfaces/IStoryController.sol";
import "../../interfaces/IGOC.sol";
import "../../interfaces/IOracle.sol";
import "../../interfaces/IAppErrors.sol";
import "../../lib/StoryLib.sol";
import "../../lib/StoryControllerLib.sol";
import "../../lib/PackingLib.sol";

contract FacadeStoryLibCheckAnswers is IAppErrors {
  struct MintRandomItemsMockLastCallResults {
    uint8 biome;
    address oracle;
    uint32 experience;
    uint32 level;
    bytes32[] mintItemsData;
  }
  MintRandomItemsMockLastCallResults private _mintRandomItemsMockLastCallResults;
  address[] private _mintRandomItemsMockReturn;
  IController private _controller;

  //region ------------------------ IControllable (all contracts allow to receive controller through this)
  function controller() external view returns (address) {
    return address(_controller);
  }
  function setController(address controller_) external {
    _controller = IController(controller_);
  }
  //endregion ------------------------ IControllable (all contracts allow to receive controller through this)


  //region ------------------------ For tests
  function _S() internal pure returns (IStoryController.MainState storage s) {
    return StoryControllerLib._S();
  }

  function setStoryRequiredLevel(uint16 storyId, uint requiredLevel) external {
    _S().storyRequiredLevel[storyId] = requiredLevel;
  }

  function setStoryRequiredHeroData(uint16 storyId, bytes32 index, uint64 min, uint64 max, bool key) external {
    bytes32 data = PackingLib.packCustomDataRequirements(min, max, key);
    _S().storyRequiredHeroData[storyId].push(IStoryController.CustomDataRequirementRangePacked({
      index: index,
      data: data
    }));
  }

  function addBurnItem(bytes32 answerIdHash, uint8 slot, uint64 chance, bool stopIfBurned) external {
    _S().burnItem[answerIdHash].push(PackingLib.packBurnInfo(slot, chance, stopIfBurned));
  }

  function setNextPageIds(bytes32 pagePackedId, uint16[] memory values) external {
    _S().nextPageIds[pagePackedId] = values;
  }

  function setInfoAttributesAndItems(
    uint16 storyId,
    uint16 pageId,
    uint8 heroClass,
    uint16 answerId,
    bool success,
    uint8[] memory attributeIds,
    int32[] memory attributeValues,
    address[] memory mintItems,
    uint32[] memory mintItemsChances
  ) external {
    bytes32 answerPackedId = PackingLib.packStoryAnswerId(storyId, pageId, heroClass, answerId);

    (success
      ? _S().successInfoAttributes
      : _S().failInfoAttributes
    )[answerPackedId] = PackingLib.toBytes32ArrayWithIds(attributeValues, attributeIds);

    bytes32[] storage infoMintItems = success
      ? _S().successInfoMintItems[answerPackedId]
      : _S().failInfoMintItems[answerPackedId];

      uint len = mintItems.length;
      for (uint i = 0; i < len; ++i) {
        infoMintItems.push(PackingLib.packItemMintInfo(mintItems[i], mintItemsChances[i]));
      }
  }

  function setInfoStats(
    bytes32 answerPackedId,
    uint32 experience,
    int32 heal,
    int32 manaRegen,
    int32 lifeChancesRecovered,
    int32 damage,
    int32 manaConsumed,
    bool success
  ) external {
    (success ? _S().successInfoStats : _S().failInfoStats)[answerPackedId] = PackingLib.packStatsChange(
      experience, heal, manaRegen, lifeChancesRecovered, damage, manaConsumed
    );
  }

  function setCustomDataResult(
    uint16 storyId,
    IStoryController.AnswerCustomDataResultMeta calldata meta,
    IStoryController.CustomDataResult type_
  ) external {
    StorySetupLib.setCustomDataResult(_S(), storyId, meta, type_);
  }

  function setAttributeRequirements(bytes32 answerPackedId, uint8 attributeIndex, int32 value, bool isCore) external {
    bytes32 data = PackingLib.packStoryAttributeRequirement(attributeIndex, value, isCore);
    _S().attributeRequirements[answerPackedId].push(data);
  }

  function setStoryTokenRequirements(bytes32 answerPackedId, address token, uint88 amount, bool requireTransfer) external {
    bytes32 data = PackingLib.packStoryTokenRequirement(token, amount, requireTransfer);
    _S().tokenRequirements[answerPackedId].push(data);
  }

  function setItemRequirements(bytes32 answerPackedId, address item, bool requireItemBurn, bool requireItemEquipped) external {
    bytes32 data = PackingLib.packStoryItemRequirement(item, requireItemBurn, requireItemEquipped);
    _S().itemRequirements[answerPackedId].push(data);
  }

  function setHeroCustomDataRequirement(
    bytes32 answerPackedId,
    bool globalCustomData,
    bytes32 index,
    uint64 min,
    uint64 max,
    bool mandatory
  ) external {
    IStoryController.CustomDataRequirementPacked[] storage arr = globalCustomData
      ? _S().globalCustomDataRequirement[answerPackedId]
      : _S().heroCustomDataRequirement[answerPackedId];

    arr.push(IStoryController.CustomDataRequirementPacked({
      data: PackingLib.packCustomDataRequirements(min, max, mandatory),
      index: index
    }));
  }

  function setHeroState(address heroToken, uint80 heroTokenId, uint16 storyId, bytes32 value) external {
    bytes32 key = PackingLib.packStoryHeroStateId(heroToken, heroTokenId, storyId);
    _S().heroState[key] = value;
  }

  function getHeroState(address heroToken, uint80 heroTokenId, uint16 storyId) external view returns (bytes32 data) {
    bytes32 key = PackingLib.packStoryHeroStateId(heroToken, heroTokenId, storyId);
    return _S().heroState[key];
  }


  function setNextObjectsRewrite(uint16 storyId, uint16 pageId, uint8 heroClass, uint32[] memory values) external {
    bytes32 key = PackingLib.packStoryPageId(storyId, pageId, heroClass);
    _S().nextObjectsRewrite[key] = values;
  }
  //endregion ------------------------ For tests


  //region ------------------------ Check answers

  function checkAnswer(IStoryController.StoryActionContext memory context) external returns (IStoryController.AnswerResultId result) {
    return StoryLib.checkAnswer(context, _S());
  }

  function checkAnswerAttributes(IStoryController.StoryActionContext memory context, bytes32 answerIndex) external view returns (
    IStoryController.AnswerResultId
  ) {
    return StoryLib.checkAnswerAttributes(context, answerIndex, _S());
  }

  function checkAnswerItems(IStoryController.StoryActionContext memory context, bytes32 answerIndex) external returns (
    IStoryController.AnswerResultId
  ) {
    return StoryLib.checkAnswerItems(context, answerIndex, _S());
  }

  function checkAnswerTokens(IStoryController.StoryActionContext memory context, bytes32 answerIndex) external returns (
    IStoryController.AnswerResultId
  ) {
    return StoryLib.checkAnswerTokens(context, answerIndex, _S());
  }

  function checkAnswerRandom(IStoryController.StoryActionContext memory context) external returns (IStoryController.AnswerResultId) {
    return StoryLib.checkAnswerRandom(context);
  }

  function checkAnswerDelay(IStoryController.StoryActionContext memory context) external view returns (IStoryController.AnswerResultId) {
    return StoryLib.checkAnswerDelay(context);
  }

  function checkAnswerHeroCustomData(IStoryController.StoryActionContext memory context, bytes32 answerIndex) external view returns (
    IStoryController.AnswerResultId
  ) {
    return StoryLib.checkAnswerHeroCustomData(context, answerIndex, _S());
  }

  function checkAnswerGlobalCustomData(IStoryController.StoryActionContext memory context, bytes32 answerIndex) external view returns (
    IStoryController.AnswerResultId
  ) {
    return StoryLib.checkAnswerGlobalCustomData(context, answerIndex, _S());
  }

  //endregion ------------------------ Check answers
}

contracts/test/facades/FacadeStorySetupB.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.23;

import "../../interfaces/IStoryController.sol";
import "../../interfaces/IGOC.sol";
import "../../interfaces/IOracle.sol";
import "../../interfaces/IAppErrors.sol";
import "../../lib/StorySetupLib.sol";
import "../../lib/StoryControllerLib.sol";
import "../../lib/PackingLib.sol";

abstract contract FacadeStorySetupBase is IAppErrors {
  using EnumerableSet for EnumerableSet.Bytes32Set;
  using EnumerableSet for EnumerableSet.UintSet;

  //region ------------------------ For tests
  function _S() internal pure returns (IStoryController.MainState storage s) {
    return StoryControllerLib._S();
  }

  function _getMapItems(EnumerableSet.Bytes32Set storage map) internal view returns (bytes32[] memory values) {
    values = new bytes32[](map.length());

    for (uint i = 0; i < map.length(); ++i) {
      values[i] = map.at(i);
    }
  }

  function _getMapItems(EnumerableSet.UintSet storage map) internal view returns (uint[] memory values) {
    values = new uint[](map.length());

    for (uint i = 0; i < map.length(); ++i) {
      values[i] = map.at(i);
    }
  }

  function getBurnItems(uint16 storyId, uint16 pageId, uint8 heroClass, uint16 answerId) external view returns (
    uint8[] memory slots,
    uint64[] memory chances,
    bool[] memory stopIfBurned
  ) {
    bytes32 answerPackedId = PackingLib.packStoryAnswerId(storyId, pageId, heroClass, answerId);
    bytes32[] memory answersBurn = _S().burnItem[answerPackedId];

    slots = new uint8[](answersBurn.length);
    chances = new uint64[](answersBurn.length);
    stopIfBurned = new bool[](answersBurn.length);

    for (uint i = 0; i < answersBurn.length; ++i) {
      (slots[i], chances[i], stopIfBurned[i]) = PackingLib.unpackBurnInfo(answersBurn[i]);
    }
  }

  function getAllStoryAnswers(uint16 storyId) external view returns (bytes32[] memory answers) {
    return _getMapItems(_S().allStoryAnswers[storyId]);
  }

  function getAllStoryPages(uint16 storyId) external view returns (uint[] memory answers) {
    return _getMapItems(_S().allStoryPages[storyId]);
  }

  function getAnswers(uint16 storyId, uint16 pageId, uint8 heroClass) external view returns (bytes32[] memory answers) {
    bytes32 id = PackingLib.packStoryPageId(storyId, pageId, heroClass);
    return _S().answers[id];
  }

  function getNextObjectsRewrite(uint16 storyId, uint16 pageId, uint8 heroClass) external view returns (uint32[] memory nextObjIds) {
    bytes32 id = PackingLib.packStoryPageId(storyId, pageId, heroClass);
    return _S().nextObjectsRewrite[id];
  }

  function getNextPageIds(bytes32 pagePackedId) external view returns (uint16[] memory) {
    return _S().nextPageIds[pagePackedId];
  }

  function getAttributeRequirements(uint16 storyId, uint16 pageId, uint8 heroClass, uint16 answerId) external view returns (
    uint8[] memory attributeIndices,
    int32[] memory values,
    bool[] memory isCores
  ) {
    bytes32 answerPackedId = PackingLib.packStoryAnswerId(storyId, pageId, heroClass, answerId);
    bytes32[] storage attrs = _S().attributeRequirements[answerPackedId];

    attributeIndices = new uint8[](attrs.length);
    values = new int32[](attrs.length);
    isCores = new bool[](attrs.length);

    for (uint i = 0; i < attrs.length; ++i) {
      (attributeIndices[i], values[i], isCores[i]) = PackingLib.unpackStoryAttributeRequirement(attrs[i]);
    }
  }

  function getAnswerItemRequirements(uint16 storyId, uint16 pageId, uint8 heroClass, uint16 answerId) external view returns (
    address[] memory items,
    bool[] memory requireItemBurn,
    bool[] memory requireItemEquipped
  ) {
    bytes32 answerPackedId = PackingLib.packStoryAnswerId(storyId, pageId, heroClass, answerId);
    bytes32[] storage attrs = _S().itemRequirements[answerPackedId];

    items = new address[](attrs.length);
    requireItemBurn = new bool[](attrs.length);
    requireItemEquipped = new bool[](attrs.length);

    for (uint i = 0; i < attrs.length; ++i) {
      (items[i], requireItemBurn[i], requireItemEquipped[i]) = PackingLib.unpackStoryItemRequirement(attrs[i]);
    }
  }

  function getAnswerTokenRequirements(uint16 storyId, uint16 pageId, uint8 heroClass, uint16 answerId) external view returns (
    address[] memory tokens,
    uint88[] memory amounts,
    bool[] memory requireTransfers
  ) {
    bytes32 answerPackedId = PackingLib.packStoryAnswerId(storyId, pageId, heroClass, answerId);
    bytes32[] storage attrs = _S().tokenRequirements[answerPackedId];

    tokens = new address[](attrs.length);
    amounts = new uint88[](attrs.length);
    requireTransfers = new bool[](attrs.length);

    for (uint i = 0; i < attrs.length; ++i) {
      (tokens[i], amounts[i], requireTransfers[i]) = PackingLib.unpackStoryTokenRequirement(attrs[i]);
    }
  }

  function getAnswerAttributes(uint16 storyId, uint16 pageId, uint8 heroClass, uint16 answerId) external view returns (
    uint32 randomRequirements,
    uint32 delayRequirements,
    bool isFinalAnswer
  ) {
    bytes32 answerPackedId = PackingLib.packStoryAnswerId(storyId, pageId, heroClass, answerId);
    bytes32 data = _S().answerAttributes[answerPackedId];

    (randomRequirements, delayRequirements, isFinalAnswer) = PackingLib.unpackStorySimpleRequirement(data);
  }

  function getHeroCustomDataRequirement(uint16 storyId, uint16 pageId, uint8 heroClass, uint16 answerId, bool globalCustomData) external view returns (
    bytes32[] memory indices,
    bool[] memory mandatory,
    uint64[] memory min,
    uint64[] memory max
  ) {
    bytes32 answerPackedId = PackingLib.packStoryAnswerId(storyId, pageId, heroClass, answerId);
    IStoryController.CustomDataRequirementPacked[] storage arr = globalCustomData
      ? _S().globalCustomDataRequirement[answerPackedId]
      : _S().heroCustomDataRequirement[answerPackedId];

    indices = new bytes32[](arr.length);
    mandatory = new bool[](arr.length);
    max = new uint64[](arr.length);
    min = new uint64[](arr.length);

    for (uint i = 0; i < arr.length; ++i) {
      indices[i] = arr[i].index;
      (min[i], max[i], mandatory[i]) = PackingLib.unpackCustomDataRequirements(arr[i].data);
    }
  }

  function getInfoAttributesAndItems(uint16 storyId, uint16 pageId, uint8 heroClass, uint16 answerId, bool success) external view returns (
    uint8[] memory attributeIds,
    int32[] memory attributeValues,
    address[] memory mintItems,
    uint32[] memory mintItemsChances
  ) {
    bytes32 answerPackedId = PackingLib.packStoryAnswerId(storyId, pageId, heroClass, answerId);

    {
      bytes32[] storage infoAttributes = success
        ? _S().successInfoAttributes[answerPackedId]
        : _S().failInfoAttributes[answerPackedId];
      attributeIds = new uint8[](infoAttributes.length);
      (attributeValues, attributeIds) = PackingLib.toInt32ArrayWithIds(infoAttributes);
    }

    {
      bytes32[] storage infoMintItems = success
        ? _S().successInfoMintItems[answerPackedId]
        : _S().failInfoMintItems[answerPackedId];
      uint len = infoMintItems.length;
      mintItems = new address[](len);
      mintItemsChances = new uint32[](len);
      for (uint i = 0; i < len; ++i) {
        (mintItems[i], mintItemsChances[i]) = PackingLib.unpackItemMintInfo(infoMintItems[i]);
      }
    }
  }

  function getInfoStats(uint16 storyId, uint16 pageId, uint8 heroClass, uint16 answerId, bool success) external view returns (
    uint32 experience,
    int32 heal,
    int32 manaRegen,
    int32 lifeChancesRecovered,
    int32 damage,
    int32 manaConsumed
  ) {
    bytes32 answerPackedId = PackingLib.packStoryAnswerId(storyId, pageId, heroClass, answerId);
    (experience, heal, manaRegen, lifeChancesRecovered, damage, manaConsumed) = PackingLib.unpackStatsChange(
      success
        ? _S().successInfoStats[answerPackedId]
        : _S().failInfoStats[answerPackedId]
    );
  }

  function getCustomDataResult(uint16 storyId, uint16 pageId, uint8 heroClass, uint16 answerId, uint8 type_) external view returns (
    bytes32[] memory dataIndices,
    int16[] memory dataValues
  ) {
    bytes32 answerPackedIdWithType = PackingLib.packStoryCustomDataResult(storyId, pageId, heroClass, answerId, type_);
    bytes32[] storage arr = _S().customDataResult[answerPackedIdWithType];

    dataIndices = new bytes32[](arr.length);
    dataValues = new int16[](arr.length);

    for (uint i; i < arr.length; ++i) {
      (dataIndices[i], dataValues[i]) = PackingLib.unpackCustomDataChange(arr[i]);
    }
  }

  function getStoryRequiredHeroData(uint16 storyId) external view returns (
    bytes32[] memory indices,
    bool[] memory mandatory,
    uint64[] memory min,
    uint64[] memory max,
    uint storyRequiredLevel
  ) {
    IStoryController.CustomDataRequirementRangePacked[] storage arr = _S().storyRequiredHeroData[storyId];

    indices = new bytes32[](arr.length);
    mandatory = new bool[](arr.length);
    max = new uint64[](arr.length);
    min = new uint64[](arr.length);

    for (uint i = 0; i < arr.length; ++i) {
      indices[i] = arr[i].index;
      (min[i], max[i], mandatory[i]) = PackingLib.unpackCustomDataRequirements(arr[i].data);
    }

    storyRequiredLevel = _S().storyRequiredLevel[storyId];
  }

  function getStoryRegistrationInfo(uint16 storyId, uint32 objectId) external view returns (
    uint32 idToStory,
    uint16 storyIds
  ) {
    idToStory = _S().idToStory[storyId]; // object id
    storyIds = _S().storyIds[objectId]; // story id by object id
  }

  function setStoryRegistrationInfo(uint32 objectId, uint16 storyIds, bool registered) external {
    _S().storyIds[objectId] = storyIds;
    _S().registeredStories[objectId] = registered;
  }

  //endregion ------------------------ For tests

}

