    return s.nextObjectsRewrite[id];
  }

  function getNextPageIds(IStoryController.MainState storage s, bytes32 pagePackedId) internal view returns (uint16[] memory) {
    return s.nextPageIds[pagePackedId];
  }

  function getAttributeRequirements(IStoryController.MainState storage s, uint16 storyId, uint16 pageId, uint8 heroClass, uint16 answerId) external view returns (
    uint8[] memory attributeIndices,
    int32[] memory values,
    bool[] memory isCores
  ) {
    bytes32 answerPackedId = PackingLib.packStoryAnswerId(storyId, pageId, heroClass, answerId);
    bytes32[] storage attrs = s.attributeRequirements[answerPackedId];

    attributeIndices = new uint8[](attrs.length);
    values = new int32[](attrs.length);
    isCores = new bool[](attrs.length);

    for (uint i = 0; i < attrs.length; ++i) {
      (attributeIndices[i], values[i], isCores[i]) = PackingLib.unpackStoryAttributeRequirement(attrs[i]);
    }
  }

  function getAnswerItemRequirements(IStoryController.MainState storage s, uint16 storyId, uint16 pageId, uint8 heroClass, uint16 answerId) external view returns (
    address[] memory items,
    bool[] memory requireItemBurn,
    bool[] memory requireItemEquipped
  ) {
    bytes32 answerPackedId = PackingLib.packStoryAnswerId(storyId, pageId, heroClass, answerId);
    bytes32[] storage attrs = s.itemRequirements[answerPackedId];

    items = new address[](attrs.length);
    requireItemBurn = new bool[](attrs.length);
    requireItemEquipped = new bool[](attrs.length);

    for (uint i = 0; i < attrs.length; ++i) {
      (items[i], requireItemBurn[i], requireItemEquipped[i]) = PackingLib.unpackStoryItemRequirement(attrs[i]);
    }
  }

  function getAnswerTokenRequirements(IStoryController.MainState storage s, uint16 storyId, uint16 pageId, uint8 heroClass, uint16 answerId) external view returns (
    address[] memory tokens,
    uint88[] memory amounts,
    bool[] memory requireTransfers
  ) {
    bytes32 answerPackedId = PackingLib.packStoryAnswerId(storyId, pageId, heroClass, answerId);
    bytes32[] storage attrs = s.tokenRequirements[answerPackedId];

    tokens = new address[](attrs.length);
    amounts = new uint88[](attrs.length);
    requireTransfers = new bool[](attrs.length);

    for (uint i = 0; i < attrs.length; ++i) {
      (tokens[i], amounts[i], requireTransfers[i]) = PackingLib.unpackStoryTokenRequirement(attrs[i]);
    }
  }

  function getAnswerAttributes(IStoryController.MainState storage s, uint16 storyId, uint16 pageId, uint8 heroClass, uint16 answerId) external view returns (
    uint32 randomRequirements,
    uint32 delayRequirements,
    bool isFinalAnswer
  ) {
    bytes32 answerPackedId = PackingLib.packStoryAnswerId(storyId, pageId, heroClass, answerId);
    bytes32 data = s.answerAttributes[answerPackedId];

    (randomRequirements, delayRequirements, isFinalAnswer) = PackingLib.unpackStorySimpleRequirement(data);
  }

  function getHeroCustomDataRequirement(IStoryController.MainState storage s, uint16 storyId, uint16 pageId, uint8 heroClass, uint16 answerId, bool globalCustomData) external view returns (
    bytes32[] memory indices,
    bool[] memory mandatory,
    uint64[] memory min,
    uint64[] memory max
  ) {
    bytes32 answerPackedId = PackingLib.packStoryAnswerId(storyId, pageId, heroClass, answerId);
    IStoryController.CustomDataRequirementPacked[] storage arr = globalCustomData
      ? s.globalCustomDataRequirement[answerPackedId]
      : s.heroCustomDataRequirement[answerPackedId];

    indices = new bytes32[](arr.length);
    mandatory = new bool[](arr.length);
    max = new uint64[](arr.length);
    min = new uint64[](arr.length);

    for (uint i = 0; i < arr.length; ++i) {
      indices[i] = arr[i].index;
      (min[i], max[i], mandatory[i]) = PackingLib.unpackCustomDataRequirements(arr[i].data);
    }
  }

  function getInfoAttributesAndItems(IStoryController.MainState storage s, uint16 storyId, uint16 pageId, uint8 heroClass, uint16 answerId, bool success) external view returns (
    uint8[] memory attributeIds,
    int32[] memory attributeValues,
    address[] memory mintItems,
    uint32[] memory mintItemsChances
  ) {
    bytes32 answerPackedId = PackingLib.packStoryAnswerId(storyId, pageId, heroClass, answerId);

    {
      bytes32[] storage infoAttributes = success
        ? s.successInfoAttributes[answerPackedId]
        : s.failInfoAttributes[answerPackedId];
      attributeIds = new uint8[](infoAttributes.length);
      (attributeValues, attributeIds) = PackingLib.toInt32ArrayWithIds(infoAttributes);
    }

    {
      bytes32[] storage infoMintItems = success
        ? s.successInfoMintItems[answerPackedId]
        : s.failInfoMintItems[answerPackedId];
      uint len = infoMintItems.length;
      mintItems = new address[](len);
      mintItemsChances = new uint32[](len);
      for (uint i = 0; i < len; ++i) {
        (mintItems[i], mintItemsChances[i]) = PackingLib.unpackItemMintInfo(infoMintItems[i]);
      }
    }
  }

  function getInfoStats(IStoryController.MainState storage s, uint16 storyId, uint16 pageId, uint8 heroClass, uint16 answerId, bool success) external view returns (
    uint32 experience,
    int32 heal,
    int32 manaRegen,
    int32 lifeChancesRecovered,
    int32 damage,
    int32 manaConsumed
  ) {
    bytes32 answerPackedId = PackingLib.packStoryAnswerId(storyId, pageId, heroClass, answerId);
    (experience, heal, manaRegen, lifeChancesRecovered, damage, manaConsumed) = PackingLib.unpackStatsChange(
      success
        ? s.successInfoStats[answerPackedId]
        : s.failInfoStats[answerPackedId]
    );
  }

  function getCustomDataResult(IStoryController.MainState storage s, uint16 storyId, uint16 pageId, uint8 heroClass, uint16 answerId, uint8 type_) external view returns (
    bytes32[] memory dataIndices,
    int16[] memory dataValues
  ) {
    bytes32 answerPackedIdWithType = PackingLib.packStoryCustomDataResult(storyId, pageId, heroClass, answerId, type_);
    bytes32[] storage arr = s.customDataResult[answerPackedIdWithType];

    dataIndices = new bytes32[](arr.length);
    dataValues = new int16[](arr.length);

    for (uint i; i < arr.length; ++i) {
      (dataIndices[i], dataValues[i]) = PackingLib.unpackCustomDataChange(arr[i]);
    }
  }

  function getStoryRequiredHeroData(IStoryController.MainState storage s, uint16 storyId) external view returns (
    bytes32[] memory indices,
    bool[] memory mandatory,
    uint64[] memory min,
    uint64[] memory max,
    uint storyRequiredLevel
  ) {
    IStoryController.CustomDataRequirementRangePacked[] storage arr = s.storyRequiredHeroData[storyId];

    indices = new bytes32[](arr.length);
    mandatory = new bool[](arr.length);
    max = new uint64[](arr.length);
    min = new uint64[](arr.length);

    for (uint i = 0; i < arr.length; ++i) {
      indices[i] = arr[i].index;
      (min[i], max[i], mandatory[i]) = PackingLib.unpackCustomDataRequirements(arr[i].data);
    }

    storyRequiredLevel = s.storyRequiredLevel[storyId];
  }

  function getStoryRegistrationInfo(IStoryController.MainState storage s, uint16 storyId, uint32 objectId) internal view returns (
    uint32 idToStory,
    uint16 storyIds
  ) {
    idToStory = s.idToStory[storyId]; // object id
    storyIds = s.storyIds[objectId]; // story id by object id
  }

  function setStoryRegistrationInfo(IStoryController.MainState storage s, uint32 objectId, uint16 storyIds, bool registered) internal {
    s.storyIds[objectId] = storyIds;
    s.registeredStories[objectId] = registered;
  }

  //endregion ------------------------ For tests

}

