// SPDX-License-Identifier: BUSL-1.1

pragma solidity 0.8.23;

import "../interfaces/IERC20.sol";
import "../relay/ERC2771Context.sol";

contract Faucet is ERC2771Context{

  uint public constant BASE_ETH_VALUE = 1e18;
  uint public constant TARGET_ETH_BALANCE = 1000e18;

  address public immutable token;

  constructor(address token_) {
    token = token_;
  }

  function getToken() external payable {
    require(msg.value != 0, 'zero value');

    address _token = token;
    uint ethBalance = address(this).balance;
    if (ethBalance == 0) {
      ethBalance = 1;
    }
    uint tokenBalance = IERC20(_token).balanceOf(address(this));
    if (tokenBalance == 0) {
      tokenBalance = 1;
    }

    uint out = getAmountOut(msg.value, ethBalance - msg.value, tokenBalance);
    require(out != 0, '!out');
    require(tokenBalance >= out, '!balance');
    IERC20(_token).transfer(_msgSender(), out);
  }

  function refuel(uint amount) external {
    address _token = token;
    uint ethBalance = address(this).balance;
    if (ethBalance == 0) {
      ethBalance = 1;
    }
    uint tokenBalance = IERC20(_token).balanceOf(address(this));
    if (tokenBalance == 0) {
      tokenBalance = 1;
    }

    uint out = getAmountOut(amount, tokenBalance, ethBalance);

    require(out != 0, '!out');
    require(ethBalance >= out, '!balance');

    IERC20(_token).transferFrom(_msgSender(), address(this), amount);

    payable(_msgSender()).transfer(out);
  }

  function priceTokenToEth(uint ethersIn) public view returns (uint) {
    uint ethBalance = address(this).balance;
    if (ethBalance == 0) {
      ethBalance = 1;
    }
    uint tokenBalance = IERC20(token).balanceOf(address(this));
    if (tokenBalance == 0) {
      tokenBalance = 1;
    }
    return getAmountOut(ethersIn, ethBalance, tokenBalance);
  }

  function priceEthToToken(uint tokenIn) public view returns (uint) {
    uint ethBalance = address(this).balance;
    if (ethBalance == 0) {
      ethBalance = 1;
    }
    uint tokenBalance = IERC20(token).balanceOf(address(this));
    if (tokenBalance == 0) {
      tokenBalance = 1;
    }
    return getAmountOut(tokenIn, tokenBalance, ethBalance);
  }

  function getAmountOut(uint amountIn, uint reserveIn, uint reserveOut) public pure returns (uint amountOut) {
    require(amountIn > 0, 'INSUFFICIENT_INPUT_AMOUNT');
    require(reserveIn > 0 && reserveOut > 0, 'INSUFFICIENT_LIQUIDITY');
    amountOut = (amountIn * reserveOut) / (reserveIn + amountIn);
  }

  receive() external payable {}

}

