pragma solidity 0.8.23;

import "../openzeppelin/ERC721Upgradeable.sol";
import {ERC721EnumerableUpgradeable} from "../openzeppelin/ERC721EnumerableUpgradeable.sol";


contract MockERC721 is ERC721EnumerableUpgradeable {
  uint internal _counter;
  address public immutable owner;

  constructor(string memory name_, string memory symbol_) initializer {
    __ERC721_init(name_, symbol_);
    owner = msg.sender;
    _counter = 4000;
  }

  function mint(address receiver) public returns (uint256) {
    uint counter = _counter;
    _counter++;

    _mint(receiver, counter);
    _update(receiver, counter, address(0));
    return counter;
  }
}

