// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.23;
import "../../lib/PackingLib.sol";


contract HeroControllerMock {
  struct KillData {
    bytes32[] dropItems;
    uint dropTokenAmount;
    address dropToken;
  }

  mapping(address => uint8) private _heroClasses;
  mapping(bytes32 => KillData) private _kills;
  mapping(address => address) private _payTokens;
  mapping(bytes32 => uint8) private _heroBioms;
  mapping(bytes32 => bytes32) private _HeroReinforcementHelps;
  mapping(bytes32 => bool) private _releaseReinforcementCalled;
  mapping(bytes32 => bool) private _killIsCalled;
  mapping(bytes32 => uint) private _scores;
  mapping(uint => bool) internal _allowedToTransfer;
  address private _heroTokensVault;
  mapping(bytes32 => uint) private _create;

  function setHeroClass(address hero, uint8 heroClass_) external {
    _heroClasses[hero] = heroClass_;
  }

  function heroClass(address hero) external view returns (uint8) {
    return _heroClasses[hero];
  }

  function setKill(address hero, uint heroId, bytes32[] memory dropItems, uint dropTokenAmount, address dropToken) external {
    bytes32 key = PackingLib.packNftId(hero, heroId);
    _kills[key] = KillData({dropItems: dropItems, dropTokenAmount: dropTokenAmount, dropToken: dropToken});
  }

  function isKillCalled(address hero, uint heroId) external view returns (bool) {
    bytes32 key = PackingLib.packNftId(hero, heroId);
    return _killIsCalled[key];
  }

  function kill(address hero, uint heroId) external returns (
    bytes32[] memory dropItems,
    uint dropTokenAmount,
    address dropToken
  ) {
