    return _owner[tokenId];
  }

  function controlledTransfer(address from, address to, uint tokenId) external {
    bytes32 key = keccak256(abi.encodePacked(from, to, tokenId, _checker.isItemEquipped(address(this), tokenId)));
    _controlledTransferCalled[key] = true;
  }

  function setItemEquippedChecker(IsItemEquippedChecker checker) external {
    _checker = checker;
  }

  function isControlledTransferCalled(
