// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.23;
import "../../lib/PackingLib.sol";

/// @notice Contract called by SacraRelay
contract SacraRelayTarget {
  error ErrorF2();

  struct F1Data {
    uint value1;
    bool value2;
    address sender;
    address msgSender;
  }
  F1Data public f1data;
  address public trustedForwarder;

  constructor(address trustedForwarder_) {
    trustedForwarder = trustedForwarder_;
  }

  function f1(uint value1_, bool value2_) external {
    f1data.value1 = value1_;
    f1data.value2 = value2_;
    f1data.sender = msg.sender;
    f1data.msgSender = _msgSender();
  }

  function fWithReturn(uint value1_, bool value2_) external returns (uint) {
    f1data.value1 = value1_;
    f1data.value2 = value2_;
    f1data.sender = msg.sender;
    f1data.msgSender = _msgSender();
    return value1_;
  }

  function funcRevertPanic(uint) external pure {
    assert(false);
  }

  function funcRevertCustomError(uint) external pure {
    revert ErrorF2();
  }

  function funcRevert(uint) external pure {
    revert("SacraRelayTargetError");
  }

  function _msgSender() internal view virtual returns (address sender) {
    if (msg.sender == trustedForwarder) {
      // The assembly code is more direct than the Solidity version using `abi.decode`.
      /// @solidity memory-safe-assembly
      assembly {
        sender := shr(96, calldataload(sub(calldatasize(), 20)))
      }
      return sender;
    } else {
      return msg.sender;
    }
  }

}

