    _currentStatChangesForDirection[key][increase] = CurrentStatsChanges({change: change, increase: increase});
  }

  function getCurrentStatsChanges(address token, uint tokenId) external view returns (IStatController.ChangeableStats memory change, bool increase) {
    bytes32 key = PackingLib.packNftId(token, tokenId);
    return (_currentStatChanges[key].change, _currentStatChanges[key].increase);
  }

  function getCurrentStatsChangesForDirection(address token, uint tokenId, bool increase) external view returns (IStatController.ChangeableStats memory change) {
    bytes32 key = PackingLib.packNftId(token, tokenId);
    return _currentStatChangesForDirection[key][increase].change;
  }

  function setHeroCustomData(address token, uint tokenId, bytes32 index, uint value) external {
    bytes32 key = PackingLib.packNftId(token, tokenId);
    _heroCustomData[key][index] = value;
  }
  function heroCustomData(address token, uint tokenId, bytes32 index) external view returns (uint) {
    bytes32 key = PackingLib.packNftId(token, tokenId);
    return _heroCustomData[key][index];
  }

  function setGlobalCustomData(bytes32 index, uint value) external {
    _heroGlobalData[index] = value;
  }
  function globalCustomData(bytes32 index) external view returns (uint) {
    return _heroGlobalData[index];
  }

  function heroStats(address token, uint tokenId) external view returns (IStatController.ChangeableStats memory) {
    bytes32 key = PackingLib.packNftId(token, tokenId);
    return _heroStats[key];
  }

  function setHeroStats(address token, uint tokenId, IStatController.ChangeableStats memory data) external {
    bytes32 key = PackingLib.packNftId(token, tokenId);
    _heroStats[key] = data;
  }

  function clearTemporallyAttributes(address heroToken, uint heroTokenId) external {
    bytes32 key = PackingLib.packNftId(heroToken, heroTokenId);
    _clearTemporallyAttributesCalled[key] = true;
  }

  function isClearTemporallyAttributesCalled(address heroToken, uint heroTokenId) external view returns (bool) {
    bytes32 key = PackingLib.packNftId(heroToken, heroTokenId);
    return _clearTemporallyAttributesCalled[key];
  }

  function isHeroAlive(address heroToken, uint heroTokenId) external view returns (bool) {
    bytes32 key = PackingLib.packNftId(heroToken, heroTokenId);
    return _isHeroAlive[key];
  }

  function setIsHeroAlive(address heroToken, uint heroTokenId, bool alive) external {
    bytes32 key = PackingLib.packNftId(heroToken, heroTokenId);
    _isHeroAlive[key] = alive;
  }

  function setBuffHero(
    address heroToken,
    uint heroTokenId,
    uint level,
    address[] memory skillTokens,
    uint[] memory skillTokenIds,
    int32[] memory attributes,
    int32 manaConsumed
  ) external {
    bytes32 key = bytes32(abi.encodePacked(heroToken, heroTokenId, level));
    for (uint i = 0; i < skillTokens.length; ++i) {
      key = bytes32(abi.encodePacked(skillTokens[i], skillTokenIds[i], key));
    }

    _buffHeroData[key] = BuffHeroData({attributes: attributes, manaConsumed: manaConsumed});
  }

  function buffHero(IStatController.BuffInfo memory info) external view returns (int32[] memory attributes, int32 manaConsumed) {
    bytes32 key = bytes32(abi.encodePacked(info.heroToken, info.heroTokenId, info.heroLevel));
    for (uint i = 0; i < info.buffTokens.length; ++i) {
      key = bytes32(abi.encodePacked(info.buffTokens[i], info.buffTokenIds[i], key));
    }

    BuffHeroData memory ret = _buffHeroData[key];
    return (ret.attributes, ret.manaConsumed);
  }

  function _getKeyForInfo(IStatController.ChangeAttributesInfo memory info) internal pure returns (bytes32) {
    bytes memory key = abi.encodePacked(info.heroToken, info.heroTokenId, info.add, info.temporally, info.changeAttributes);
//    for (uint i = 0; i < info.changeAttributes.length; ++i) {
//      key = abi.encodePacked(key, info.changeAttributes[i]);
//      console.log("_getKeyForInfo.keccak256", uint(keccak256(key)), i);
//    }
    return keccak256(key);
  }
  function setChangeBonusAttributesChangeStats(IStatController.ChangeAttributesInfo memory info, IStatController.ChangeableStats memory heroStats_) external {
    bytes32 key = _getKeyForInfo(info);
    _changeBonusAttributes[key] = DataChangeBonusAttributes({
      changeStats: true,
      heroStats: heroStats_
    });
  }

  function changeBonusAttributes(IStatController.ChangeAttributesInfo memory info) external {
    _changeBonusAttributesLastCall = info;
    bytes32 key = _getKeyForInfo(info);
    DataChangeBonusAttributes memory data = _changeBonusAttributes[key];
    if (data.changeStats) {
      bytes32 key0 = PackingLib.packNftId(info.heroToken, info.heroTokenId);
      _heroStats[key0] = data.heroStats;
    }
    _isChangeBonusAttributesCalled[key] = true;
  }

  function isChangeBonusAttributesCalled(IStatController.ChangeAttributesInfo memory info) external view returns (bool) {
    return _isChangeBonusAttributesCalled[_getKeyForInfo(info)];
  }

  function initNewHero(address token, uint tokenId, uint heroClass) external {
    initNewHeroLastCall = InitNewHeroData({
      token: token,
      tokenId: tokenId,
      heroClass: heroClass
    });
  }


  function setLevelUp(address token, uint tokenId, uint heroClass, IStatController.CoreAttributes memory change, uint newLvl) external {
    bytes32 key = keccak256(abi.encodePacked(token, tokenId, heroClass, change.vitality, change.strength, change.energy, change.dexterity));
    _levelUp[key] = newLvl;
  }

  function levelUp(address token, uint tokenId, uint heroClass, IStatController.CoreAttributes memory change) external view returns (
    uint newLvl
  ) {
    bytes32 key = keccak256(abi.encodePacked(token, tokenId, heroClass, change.vitality, change.strength, change.energy, change.dexterity));
    return _levelUp[key];
  }

  function changeBonusAttributesLastCall() external view returns(
    address heroToken,
    uint heroTokenId,
    int32[] memory changeAttributes,
    bool add,
    bool temporally
  ) {
    IStatController.ChangeAttributesInfo memory d = _changeBonusAttributesLastCall;
    return (d.heroToken, d.heroTokenId, d.changeAttributes, d.add, d.temporally);
  }

  function clearUsedConsumables(address heroToken, uint heroTokenId) external {
    bytes32 key = PackingLib.packNftId(heroToken, heroTokenId);
    _isClearUsedConsumablesCalled[key] = true;
  }

  function isClearUsedConsumablesCalled(address heroToken, uint heroTokenId) external view returns (bool) {
    return _isClearUsedConsumablesCalled[PackingLib.packNftId(heroToken, heroTokenId)];
  }

  function changeHeroItemSlot(
    address heroToken,
    uint64 heroTokenId,
    uint itemType,
    uint8 itemSlot,
    address itemToken,
    uint itemTokenId,
    bool equip
  ) external {
    bytes32 key = keccak256(abi.encodePacked(heroToken, heroTokenId, itemType, itemSlot, itemToken, itemTokenId, equip));
    _isChangeHeroItemSlotCalled[key] = true;
  }

  function isChangeHeroItemSlotCalled(
    address heroToken,
    uint64 heroTokenId,
    uint itemType,
    uint8 itemSlot,
    address itemToken,
    uint itemTokenId,
    bool equip
  ) external view returns (bool) {
    bytes32 key = keccak256(abi.encodePacked(heroToken, heroTokenId, itemType, itemSlot, itemToken, itemTokenId, equip));
    return _isChangeHeroItemSlotCalled[key];
  }

  function registerConsumableUsage(address heroToken, uint heroTokenId, address item) external {
    bytes32 key = keccak256(abi.encodePacked(heroToken, heroTokenId, item));
    _isRegisterConsumableUsageCalled[key] = true;
  }

  function isRegisterConsumableUsageCalled(address heroToken, uint heroTokenId, address item) external view returns (bool) {
    bytes32 key = keccak256(abi.encodePacked(heroToken, heroTokenId, item));
    return _isRegisterConsumableUsageCalled[key];
  }

  function restoreLifeAndMana(address heroToken, uint heroTokenId) external {
    bytes32 key = keccak256(abi.encodePacked(heroToken, heroTokenId));
    _isRestoreLifeAndManaCalled[key] = true;
  }

  function isRestoreLifeAndManaCalled(address heroToken, uint heroTokenId) external view returns (bool) {
    bytes32 key = keccak256(abi.encodePacked(heroToken, heroTokenId));
    return _isRestoreLifeAndManaCalled[key];
  }
}

