// SPDX-License-Identifier: BUSL-1.1

pragma solidity 0.8.23;

contract TestLargeCall {

  struct CallData {
    bytes32[] indexes;
    uint[] resIds;
    uint[][] nextIds;
  }

  mapping(address => mapping(bytes32 => mapping(uint => uint[]))) public testMap;

  function largeCall(CallData memory data) external {
    unchecked {
      for (uint i; i < data.indexes.length; ++i) {
        testMap[msg.sender][data.indexes[i]][data.resIds[i]] = data.nextIds[i];
      }
    }
  }

}

