  function packUint8Array(uint8[] memory data) internal pure returns (bytes32) {
    uint len = data.length;
    if (len > 32) revert IAppErrors.OutOfBounds(len, 32);
    bytes32 result;
    for (uint i = 0; i < len; i++) {
      result |= bytes32(uint(data[i])) << (i * 8);
    }
    return result;
  }

  /// @notice Simple faster version of {packUint8Array} for small number of items
  ///         It allows to exclude dynamic array creation.
  function packUint8Array3(uint8 a, uint8 b, uint8 c) internal pure returns (bytes32) {
    bytes32 result = bytes32(uint(a));
    result |= bytes32(uint(b)) << (1 * 8);
    result |= bytes32(uint(c)) << (2 * 8);
    return result;
  }


  function unpackUint8Array(bytes32 data) internal pure returns (uint8[] memory) {
    uint8[] memory result = new uint8[](32);
    for (uint i = 0; i < 32; i++) {
      result[i] = uint8(uint(data) >> (i * 8));
    }
    return result;
  }

  /// @notice Simple faster version of {unpackUint8Array} for small number of items
  ///         It allows to exclude only first 3 values
  function unpackUint8Array3(bytes32 data) internal pure returns (uint8 a, uint8 b, uint8 c) {
    a = uint8(uint(data));
    b = uint8(uint(data) >> (1 * 8));
    c = uint8(uint(data) >> (2 * 8));
  }

  function changeUnit8ArrayWithCheck(bytes32 data, uint index, uint8 value, uint8 expectedPrevValue) internal pure returns (bytes32 newData) {
    uint8[] memory arr = unpackUint8Array(data);
    if (arr[index] != expectedPrevValue) revert IAppErrors.UnexpectedValue(uint(expectedPrevValue), uint(arr[index]));
    arr[index] = value;
    return packUint8Array(arr);
  }

  function packInt32Array(int32[] memory data) internal pure returns (bytes32) {
    uint len = data.length;
    if (len > 8) revert IAppErrors.OutOfBounds(len, 8);
    bytes32 result;
    for (uint i; i < len; i++) {
      result |= bytes32(uint(uint32(data[i]))) << (i * 32);
    }
    return result;
  }

  function unpackInt32Array(bytes32 data) internal pure returns (int32[] memory) {
    int32[] memory result = new int32[](8);
    for (uint i = 0; i < 8; i++) {
      result[i] = int32(int(uint(data) >> (i * 32)));
    }
    return result;
  }

  function packUint32Array(uint32[] memory data) internal pure returns (bytes32) {
    uint len = data.length;
    if (len > 8) revert IAppErrors.OutOfBounds(len, 8);
    bytes32 result;
    for (uint i = 0; i < len; i++) {
      result |= bytes32(uint(data[i])) << (i * 32);
    }
    return result;
  }

  function unpackUint32Array(bytes32 data) internal pure returns (uint32[] memory) {
    uint32[] memory result = new uint32[](8);
    for (uint i = 0; i < 8; i++) {
      result[i] = uint32(uint(data) >> (i * 32));
    }
    return result;
  }
  //endregion ------------------------------------ SIMPLE ARRAYS

  //region ------------------------------------ COMPLEX ARRAYS

  // We should represent arrays without concrete size.
  // For this reason we must not revert IAppErrors.on out of bounds but return zero value instead.

  // we need it for properly unpack packed arrays with ids
//  function getInt32AsInt24(bytes32[] memory arr, uint idx) internal pure returns (int32) {
//    if (idx / 8 >= arr.length) {
//      return int32(0);
//    }
//    return int32(int24(int(uint(arr[idx / 8]) >> ((idx % 8) * 32))));
//  }

  // we need it for properly unpack packed arrays with ids
//  function getUnit8From32Step(bytes32[] memory arr, uint idx) internal pure returns (uint8) {
//    if (idx / 8 >= arr.length) {
//      return uint8(0);
//    }
//    return uint8(uint(arr[idx / 8]) >> ((idx % 8) * 32 + 24));
//  }

  function getInt32Memory(bytes32[] memory arr, uint idx) internal pure returns (int32) {
    if (idx / 8 >= arr.length) {
      return int32(0);
    }
    return int32(int(uint(arr[idx / 8]) >> ((idx % 8) * 32)));
  }

  function getInt32(bytes32[] storage arr, uint idx) internal view returns (int32) {
    // additional gas usage, but we should not revert IAppErrors.on out of bounds
    if (idx / 8 >= arr.length) {
      return int32(0);
    }
    return int32(int(uint(arr[idx / 8]) >> ((idx % 8) * 32)));
  }

  function setInt32(bytes32[] storage arr, uint idx, int32 value) internal {
    uint pos = idx / 8;
    uint shift = (idx % 8) * 32;

    uint curLength = arr.length;
    if (pos >= curLength) {
      arr.push(0);
      for (uint i = curLength; i < pos; ++i) {
        arr.push(0);
      }
    }

    arr[pos] = bytes32(uint(arr[pos]) & ~(uint(0xffffffff) << shift) | (uint(uint32(value)) & 0xffffffff) << shift);
  }

  /// @notice Increment {idx}-th item on {value}
  function changeInt32(bytes32[] storage arr, uint idx, int32 value) internal returns (int32 newValue, int32 change) {
    int32 cur = int32(int(getInt32(arr, idx)));
    int newValueI = int(cur) + int(value);
    newValue = int32(newValueI);
    change = int32(newValueI - int(cur));

    setInt32(arr, idx, newValue);
  }

  function toInt32Array(bytes32[] memory arr, uint size) internal pure returns (int32[] memory) {
    int32[] memory result = new int32[](size);
    for (uint i = 0; i < arr.length; i++) {
      for (uint j; j < 8; ++j) {
        uint idx = i * 8 + j;
        if (idx >= size) break;
        result[idx] = getInt32Memory(arr, idx);
      }
    }
    return result;
  }

  /// @dev pack int32 array into bytes32 array
  function toBytes32Array(int32[] memory arr) internal pure returns (bytes32[] memory) {
    uint size = arr.length / 8 + 1;
    bytes32[] memory result = new bytes32[](size);
    for (uint i; i < size; ++i) {
      for (uint j; j < 8; ++j) {
        uint idx = i * 8 + j;
        if (idx >= arr.length) break;
        result[i] |= bytes32(uint(uint32(arr[idx]))) << (j * 32);
      }
    }
    return result;
  }

  /// @dev pack int32 array into bytes32 array using last 8bytes for ids
  ///      we can not use zero values coz will not able to properly unpack it later
  function toBytes32ArrayWithIds(int32[] memory arr, uint8[] memory ids) internal pure returns (bytes32[] memory) {
    if (arr.length != ids.length) revert IAppErrors.LengthsMismatch();

    uint size = arr.length / 8 + 1;
    bytes32[] memory result = new bytes32[](size);
    for (uint i; i < size; ++i) {
      for (uint j; j < 8; ++j) {
        uint idx = i * 8 + j;
        if (idx >= arr.length) break;

        if (arr[idx] > type(int24).max || arr[idx] < type(int24).min) revert IAppErrors.IntOutOfRange(int(arr[idx]));
        if (arr[idx] == 0) revert IAppErrors.ZeroValue();
        result[i] |= bytes32(uint(uint24(int24(arr[idx])))) << (j * 32);
        result[i] |= bytes32(uint(ids[idx])) << (j * 32 + 24);
      }
    }
    return result;
  }

  /// @dev we do not know exact size of array, assume zero values is not acceptable for this array
  function toInt32ArrayWithIds(bytes32[] memory arr) internal pure returns (int32[] memory values, uint8[] memory ids) {
    uint len = arr.length;
    uint size = len * 8;
    int32[] memory valuesTmp = new int32[](size);
    uint8[] memory idsTmp = new uint8[](size);
    uint counter;
    for (uint i = 0; i < len; i++) {
      for (uint j; j < 8; ++j) {
        uint idx = i * 8 + j;
        // if (idx >= size) break;  // it looks like a useless check
        valuesTmp[idx] = int32(int24(int(uint(arr[i]) >> (j * 32)))); // getInt32AsInt24(arr, idx);
        idsTmp[idx] = uint8(uint(arr[i]) >> (j * 32 + 24)); // getUnit8From32Step(arr, idx);
        if (valuesTmp[idx] == 0) {
          break;
        }
        counter++;
      }
    }

    values = new int32[](counter);
    ids = new uint8[](counter);
    for (uint i; i < counter; ++i) {
      values[i] = valuesTmp[i];
      ids[i] = idsTmp[i];
    }
  }
  //endregion ------------------------------------ COMPLEX ARRAYS

  //region ------------------------------------ Guilds
  /// @dev ShelterID is uint. But in the code we assume that this ID can be stored as uint64 (see auctions)
  /// @param biome 1, 2, 3...
  /// @param shelterLevel 1, 2 or 3.
  /// @param shelterIndex 0, 1, 2 ...
  function packShelterId(uint8 biome, uint8 shelterLevel, uint8 shelterIndex) internal pure returns (uint) {
    return uint(biome) | (uint(shelterLevel) << 8) | (uint(shelterIndex) << 16);
  }

  function unpackShelterId(uint shelterId) internal pure returns (uint8 biome, uint8 shelterLevel, uint8 shelterIndex) {
    return (uint8(shelterId), uint8(shelterId >> 8), uint8(shelterId >> 16));
  }
  //endregion ------------------------------------ Guilds

  //region ------------------------------------ Metadata of IItemController.OtherSubtypeKind

  function getOtherItemTypeKind(bytes memory packedData) internal pure returns (IItemController.OtherSubtypeKind) {
    bytes32 serialized;
    assembly {
      serialized := mload(add(packedData, 32))
    }
    uint8 kind = uint8(uint(serialized));
    if (kind == 0 || kind >= uint8(IItemController.OtherSubtypeKind.END_SLOT)) revert IAppErrors.IncorrectOtherItemTypeKind(kind);
    return IItemController.OtherSubtypeKind(kind);
  }

  function packOtherItemReduceFragility(uint value) internal pure returns (bytes memory packedData) {
    bytes32 serialized = bytes32(uint(uint8(IItemController.OtherSubtypeKind.REDUCE_FRAGILITY_1)));
    serialized |= bytes32(uint(uint248(value))) << 8;
    return bytes.concat(serialized);
  }

  function unpackOtherItemReduceFragility(bytes memory packedData) internal pure returns (uint) {
    bytes32 serialized;
    assembly {
      serialized := mload(add(packedData, 32))
    }
    uint8 kind = uint8(uint(serialized));
    if (kind != uint8(IItemController.OtherSubtypeKind.REDUCE_FRAGILITY_1)) revert IAppErrors.IncorrectOtherItemTypeKind(kind);
    uint value = uint248(uint(serialized) >> 8);
    return value;
  }
  //endregion ------------------------------------ Metadata of IItemController.OtherSubtypeKind

  //region ------------------------------------ Metadata of IPvpController.PvpAttackInfoDefaultStrategy
  function getPvpBehaviourStrategyKind(bytes memory encodedData) internal pure returns (uint) {
    bytes32 serialized;
    assembly {
      serialized := mload(add(encodedData, 64)) // first 32 bytes contain 0x20 and indicate array, we need to read second 32 bytes to get first uint in the struct
    }

    return uint(serialized);
  }
  //endregion ------------------------------------ Metadata of IPvpController.PvpAttackInfoDefaultStrategy

}
","keccak256":"0x2c8652b2e7d685dc36cefd6c6bb81f116b72909e103879c1afb2e2e92821f83e","license":"BUSL-1.1"},"contracts/lib/PawnShopRouterLib.sol":{"content":"// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.23;

import "../interfaces/IController.sol";
import "../interfaces/IERC721.sol";
import "../interfaces/IPawnShopRouter.sol";
import "../interfaces/IPawnShop.sol";
import "../interfaces/IAppErrors.sol";
import "../interfaces/IERC20.sol";
import "../interfaces/IApplicationEvents.sol";
import "./PackingLib.sol";

library PawnShopRouterLib {
  //region ------------------------ Restrictions
  function userOnly(address msgSender, address user) internal pure {
    if (msgSender != user) revert IAppErrors.ErrorForbidden(msgSender);
  }
  //endregion ------------------------ Restrictions

  //region ------------------------ View
  function getOpenedPositionsLength(address pawnShop) internal view returns (uint) {
    return IPawnShop(pawnShop).borrowerPositionsSize(address(this));
  }

  function getOpenedPosition(address pawnShop, uint index) internal view returns (uint positionId) {
    return IPawnShop(pawnShop).borrowerPositions(address(this), index);
  }

  function getDepositAmount(address pawnShop, uint countPositionsToSell) internal view returns (
    address positionDepositToken,
    uint amount
  ) {
    return (
      IPawnShop(pawnShop).positionDepositToken(),
      IPawnShop(pawnShop).positionDepositAmount() * countPositionsToSell
    );
  }

  function getAmountToBulkBuying(IPawnShopRouter.CoreData memory p, uint[] memory positionIds) internal view returns (uint amount) {
    uint len = positionIds.length;
    for (uint i; i < len; ++i) {
      IPawnShop.Position memory position = _getValidPositionToBuy(p, positionIds[i]);
      amount += position.acquired.acquiredAmount;
    }
    return amount;
  }
  //endregion ------------------------ View

  //region ------------------------ User actions

  /// @notice Transfer given amount of the game token to the receiver
  function transfer(IPawnShopRouter.CoreData memory p, address msgSender, uint amount, address receiver) internal {
    userOnly(msgSender, p.user);

    IERC20(p.gameToken).transfer(receiver, amount);
    emit IApplicationEvents.PawnShopRouterTransfer(p.gameToken, amount, receiver);
  }

  function salvage(IPawnShopRouter.CoreData memory p, address msgSender, address token, uint amount, address receiver) internal {
    userOnly(msgSender, p.user);

    IERC20(token).transfer(receiver, amount);
    emit IApplicationEvents.PawnShopRouterTransfer(token, amount, receiver);
  }

  /// @notice Function to call arbitrary code
  function call(address user, address msgSender, address target, bytes memory callData) internal returns (
    bytes memory data
  ) {
    userOnly(msgSender, user);

    bool success;
    (success, data) = target.call(callData);
    if (!success) {
      revert IAppErrors.CallFailed(data);
    }
  }
  //endregion ------------------------ User actions

  //region ------------------------ Bulk instant selling

  /// @notice Bulk selling of the given set of NFT through PawnShop.sol
  function bulkSell(
    IPawnShopRouter.CoreData memory p,
    address msgSender,
    address[] memory nfts,
    uint[] memory nftIds,
    uint[] memory prices,
    address nftOwner
  ) internal {
    userOnly(msgSender, p.user);

    uint len = nftIds.length;
    if (len != prices.length || len != nfts.length) revert IAppErrors.LengthsMismatch();

    uint positionDepositAmount = IPawnShop(p.pawnShop).positionDepositAmount();

    if (positionDepositAmount != 0) {
      // take deposit from user and approve the deposit for pawnShop
      address token = IPawnShop(p.pawnShop).positionDepositToken();

      IERC20(token).transferFrom(msgSender, address(this), positionDepositAmount * len);
      _approveIfNeeded(token, positionDepositAmount * len, p.pawnShop);
    }

    // open and register positions
    uint[] memory positionIds = new uint[](len);
    for (uint i; i < len; ++i) {
      address nft = nfts[i];
      IERC721(nft).transferFrom(nftOwner, address(this), nftIds[i]);
      _approveIfNeededNft(nft, p.pawnShop);

      positionIds[i] = IPawnShop(p.pawnShop).openPosition(
        nft,
        0,
        nftIds[i],
        p.gameToken,
        prices[i],
        // 0 for instant selling
        0, // posDurationBlocks
        0, // posFee,
        0  // minAuctionAmount
      );
    }

    emit IApplicationEvents.PawnShopRouterBulkSell(nfts, nftIds, prices, nftOwner, positionIds);
  }

  /// @notice Close unsold positions, transfer NFTs to {receiver}
  function closePositions(
    IPawnShopRouter.CoreData memory p,
    address msgSender,
    uint[] memory positionIds,
    address receiver
  ) internal {
    userOnly(msgSender, p.user);

    IPawnShop _pawnShop = IPawnShop(p.pawnShop);

    uint len = positionIds.length;
    for (uint i; i < len; ++i) {
      IPawnShop.Position memory position = _pawnShop.getPosition(positionIds[i]);
      _pawnShop.closePosition(positionIds[i]);
      IERC721(position.collateral.collateralToken).transferFrom(
        address(this),
        receiver,
        position.collateral.collateralTokenId
      );
    }

    emit IApplicationEvents.PawnShopRouterClosePositions(positionIds, receiver);
  }

  //endregion ------------------------ Bulk instant selling

  //region ------------------------ Bulk instant buying
  function bulkBuy(
    IPawnShopRouter.CoreData memory p,
    address msgSender,
    uint[] memory positionIds,
    address receiver
  ) internal {
    userOnly(msgSender, p.user);

    IPawnShop _pawnShop = IPawnShop(p.pawnShop);

    uint len = positionIds.length;
    IPawnShop.Position[] memory positions = new IPawnShop.Position[](len);
    uint totalAmount;

    // get all amounts and total amount required to be approved
    // ensure that all positions are instant and they use game token as the deposit token
    for (uint i; i < len; ++i) {
      positions[i] = _getValidPositionToBuy(p, positionIds[i]);
      totalAmount += positions[i].acquired.acquiredAmount;
    }

    // take deposit from user and approve the deposit for pawnShop
    IERC20(p.gameToken).transferFrom(msgSender, address(this), totalAmount);
    // no need to approve totalAmount, there is infinite approve

    // bulk buying
    for (uint i; i < len; ++i) {
      _pawnShop.bid(positionIds[i], positions[i].acquired.acquiredAmount);
      // transfer purchased nft to the receiver
      IERC721(positions[i].collateral.collateralToken).transferFrom(address(this), receiver, positions[i].collateral.collateralTokenId);
    }

    emit IApplicationEvents.PawnShopRouterBulkBuy(positionIds, receiver);
  }
  //endregion ------------------------ Bulk instant buying

  //region ------------------------ Internal logic
  function _getValidPositionToBuy(IPawnShopRouter.CoreData memory p, uint positionId) internal view returns (IPawnShop.Position memory) {
    IPawnShop.Position memory position = IPawnShop(p.pawnShop).getPosition(positionId);
    if (position.acquired.acquiredAmount == 0) revert IAppErrors.AuctionPositionNotSupported(positionId);
    if (position.acquired.acquiredToken != p.gameToken) revert IAppErrors.PositionNotSupported(positionId);
    if (position.collateral.collateralType != IPawnShop.AssetType.ERC721) revert IAppErrors.NotNftPositionNotSupported(positionId);
    return position;
  }

  /// @notice Make infinite approve of {token} to {spender} if the approved amount is less than {amount}
  function _approveIfNeeded(address token, uint amount, address spender) internal {
    if (IERC20(token).allowance(address(this), spender) < amount) {
      // infinite approve, 2*255 is more gas efficient then type(uint).max
      IERC20(token).approve(spender, 2 ** 255);
    }
  }

  /// @notice Make infinite approve of {token} to {spender} if the approved amount is less than {amount}
  function _approveIfNeededNft(address nft, address spender) internal {
    if (!IERC721(nft).isApprovedForAll(address(this), spender)) {
      IERC721(nft).setApprovalForAll(spender, true);
    }
  }
  //endregion ------------------------ Internal logic
}
","keccak256":"0x30fcd1fc7ecda436d69ca1ddc8543fbf488496dd8877a1e0f659f71135aa6884","license":"BUSL-1.1"},"contracts/lib/SlotsLib.sol":{"content":"// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.23;

/// @title Library for setting / getting slot variables (used in upgradable proxy contracts)
/// @author bogdoslav
library SlotsLib {

  /// @notice Version of the contract
  /// @dev Should be incremented when contract changed
  string public constant SLOT_LIB_VERSION = "1.0.0";

  // ************* GETTERS *******************

  /// @dev Gets a slot as bytes32
  function getBytes32(bytes32 slot) internal view returns (bytes32 result) {
    assembly {
      result := sload(slot)
    }
  }

  /// @dev Gets a slot as an address
  function getAddress(bytes32 slot) internal view returns (address result) {
    assembly {
      result := sload(slot)
    }
  }

  /// @dev Gets a slot as uint256
  function getUint(bytes32 slot) internal view returns (uint result) {
    assembly {
      result := sload(slot)
    }
  }

  // ************* ARRAY GETTERS *******************

  /// @dev Gets an array length
  function arrayLength(bytes32 slot) internal view returns (uint result) {
    assembly {
      result := sload(slot)
    }
  }

  /// @dev Gets a slot array by index as address
  /// @notice First slot is array length, elements ordered backward in memory
  /// @notice This is unsafe, without checking array length.
  function addressAt(bytes32 slot, uint index) internal view returns (address result) {
    bytes32 pointer = bytes32(uint(slot) - 1 - index);
    assembly {
      result := sload(pointer)
    }
  }

  // ************* SETTERS *******************

  /// @dev Sets a slot with bytes32
  /// @notice Check address for 0 at the setter
  function set(bytes32 slot, bytes32 value) internal {
    assembly {
      sstore(slot, value)
    }
  }

  /// @dev Sets a slot with address
  /// @notice Check address for 0 at the setter
  function set(bytes32 slot, address value) internal {
    assembly {
      sstore(slot, value)
    }
  }

  /// @dev Sets a slot with uint
  function set(bytes32 slot, uint value) internal {
    assembly {
      sstore(slot, value)
    }
  }

}
